import { Component, signal, computed, effect } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { Router, ActivatedRoute } from '@angular/router';
import { AuthService } from '../../services/auth.service';
import { TranslationService } from '../../services/translation.service';
import { ButtonComponent } from '../ui/button/button.component';

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule, ButtonComponent],
  templateUrl: './login.component.html',
  styleUrl: './login.component.scss'
})
export class LoginComponent {
  // Signals for component state
  private showRegisterSignal = signal(false);
  
  // Computed values
  readonly showRegister = this.showRegisterSignal.asReadonly();
  readonly isLoading = computed(() => this.authService.isLoading());
  readonly authError = computed(() => this.authService.error());
  readonly sessionMessage = computed(() => {
    if (this.loginReason === 'session_expired') {
      return this.translate('auth.sessionExpired');
    }
    return null;
  });

  // Form groups
  loginForm: FormGroup;
  registerForm: FormGroup;

  private returnUrl: string;
  private loginReason: string | null;

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private router: Router,
    private route: ActivatedRoute,
    private translationService: TranslationService
  ) {
    this.returnUrl = (this.route.snapshot.queryParams['returnUrl'] as string) || '/trading';
    this.loginReason = (this.route.snapshot.queryParams['reason'] as string) || null;
    this.loginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });

    this.registerForm = this.fb.group({
      name: [''],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]]
    });

    // Effect to navigate when authenticated
    effect(() => {
      if (this.authService.isAuthenticated()) {
        this.router.navigate([this.returnUrl]);
      }
    });
  }

  onLogin() {
    if (this.loginForm.valid) {
      const { email, password } = this.loginForm.value;
      if (email && password) {
        this.authService.login(email, password).subscribe();
      }
    }
  }

  onRegister() {
    if (this.registerForm.valid) {
      const { email, password, name } = this.registerForm.value;
      if (email && password) {
        this.authService.register(email, password, name || undefined).subscribe();
      }
    }
  }

  loginWithGoogle() {
    // Placeholder for Google login
    // In a real app, this would integrate with Google Identity Services
    console.log('Google login clicked');
    alert('Google login would be implemented with Google Identity Services');
  }

  toggleMode() {
    this.showRegisterSignal.update(value => !value);
    this.clearError();
    this.resetForms();
  }

  clearError() {
    this.authService.clearError();
  }

  translate(key: string, params?: Record<string, string>): string {
    return this.translationService.translate(key, params);
  }

  private resetForms() {
    this.loginForm.reset();
    this.registerForm.reset();
    this.loginForm.markAsUntouched();
    this.registerForm.markAsUntouched();
  }
}

import { ComponentFixture, fakeAsync, TestBed, tick } from '@angular/core/testing';
import { ReactiveFormsModule, FormsModule } from '@angular/forms';
import { RouterTestingModule } from '@angular/router/testing';
import { of, throwError } from 'rxjs';

import { ProfileComponent } from './profile.component';
import { AuthService } from '../../services/auth.service';
import { TranslationService } from '../../services/translation.service';
import { ThemeService } from '../../services/theme.service';
import { UserService } from '../../services/user.service';
import { GoogleAuthService } from '../../services/google-auth.service';
import { ExchangeCredentialsService } from '../../services/exchange-credentials.service';
import { ExchangeCredential, ExchangeType } from '../../models/exchange-credentials.model';

describe('ProfileComponent - Confirmation Modal Tests', () => {
  let component: ProfileComponent;
  let fixture: ComponentFixture<ProfileComponent>;
  let mockAuthService: jasmine.SpyObj<AuthService>;
  let mockTranslationService: jasmine.SpyObj<TranslationService>;
  let mockThemeService: jasmine.SpyObj<ThemeService>;
  let mockUserService: jasmine.SpyObj<UserService>;
  let mockGoogleAuthService: jasmine.SpyObj<GoogleAuthService>;
  let mockExchangeCredentialsService: jasmine.SpyObj<ExchangeCredentialsService>;

  const mockUser = {
    id: '123',
    email: 'test@example.com',
    firstName: 'Test',
    lastName: 'User',
    roles: ['USER']
  };

  const mockCredential: ExchangeCredential = {
    id: 'cred-123',
    exchange: ExchangeType.BINANCE,
    apiKeyPreview: 'ABC...XYZ',
    label: 'My Binance Account',
    isActive: true,
    userId: '123',
    createdAt: new Date().toISOString(),
    updatedAt: new Date().toISOString()
  };

  beforeEach(async () => {
    // Create mock services
    mockAuthService = jasmine.createSpyObj('AuthService', ['logout', 'hasRole'], {
      currentUser: jasmine.createSpy().and.returnValue(mockUser)
    });
    mockTranslationService = jasmine.createSpyObj('TranslationService', ['translate', 'setLanguage'], {
      currentLanguage: jasmine.createSpy().and.returnValue('en'),
      availableLanguages: ['en', 'es', 'fr']
    });
    mockThemeService = jasmine.createSpyObj('ThemeService', ['toggleTheme'], {
      currentTheme: jasmine.createSpy().and.returnValue('light')
    });
    mockUserService = jasmine.createSpyObj('UserService', ['updateUser']);
    mockGoogleAuthService = jasmine.createSpyObj('GoogleAuthService', ['linkGoogleAccount']);
    mockExchangeCredentialsService = jasmine.createSpyObj('ExchangeCredentialsService', [
      'fetchCredentials',
      'getCredentialById',
      'createCredential',
      'updateCredential',
      'deleteCredential',
      'testConnection'
    ], {
      credentials: jasmine.createSpy().and.returnValue([])
    });

    // Setup default mock return values
    mockAuthService.logout.and.returnValue(of(void 0));
    mockTranslationService.translate.and.callFake((key: string, params?: any) => key);
    mockExchangeCredentialsService.fetchCredentials.and.returnValue(of([]));

    await TestBed.configureTestingModule({
      imports: [
        ProfileComponent,
        ReactiveFormsModule,
        FormsModule,
        RouterTestingModule
      ],
      providers: [
        { provide: AuthService, useValue: mockAuthService },
        { provide: TranslationService, useValue: mockTranslationService },
        { provide: ThemeService, useValue: mockThemeService },
        { provide: UserService, useValue: mockUserService },
        { provide: GoogleAuthService, useValue: mockGoogleAuthService },
        { provide: ExchangeCredentialsService, useValue: mockExchangeCredentialsService }
      ]
    }).compileComponents();

    fixture = TestBed.createComponent(ProfileComponent);
    component = fixture.componentInstance;
    fixture.detectChanges();
  });

  it('should create', () => {
    expect(component).toBeTruthy();
  });

  // ============================================================================
  // CONFIRMATION MODAL TESTS
  // ============================================================================

  describe('Confirmation Modal', () => {
    describe('showConfirmation()', () => {
      it('should set all modal properties correctly with full config', () => {
        const mockCallback = jasmine.createSpy('onConfirm');
        const config = {
          title: 'Delete Item',
          message: 'Are you sure you want to delete this item?',
          confirmText: 'Delete',
          cancelText: 'Cancel',
          variant: 'danger' as const,
          onConfirm: mockCallback
        };

        component.showConfirmation(config);

        expect(component.confirmModalTitle()).toBe('Delete Item');
        expect(component.confirmModalMessage()).toBe('Are you sure you want to delete this item?');
        expect(component.confirmModalConfirmText()).toBe('Delete');
        expect(component.confirmModalCancelText()).toBe('Cancel');
        expect(component.confirmModalVariant()).toBe('danger');
        expect(component.confirmModalCallback()).toBe(mockCallback);
        expect(component.showConfirmModal()).toBe(true);
      });

      it('should set default values when optional properties are not provided', () => {
        const mockCallback = jasmine.createSpy('onConfirm');
        mockTranslationService.translate.and.callFake((key: string) => {
          const translations: Record<string, string> = {
            'modal.confirmTitle': 'Confirm',
            'modal.confirm': 'OK',
            'modal.cancel': 'Cancel'
          };
          return translations[key] || key;
        });

        component.showConfirmation({
          message: 'Proceed with action?',
          onConfirm: mockCallback
        });

        expect(component.confirmModalTitle()).toBe('Confirm');
        expect(component.confirmModalMessage()).toBe('Proceed with action?');
        expect(component.confirmModalConfirmText()).toBe('OK');
        expect(component.confirmModalCancelText()).toBe('Cancel');
        expect(component.confirmModalVariant()).toBe('primary');
        expect(component.confirmModalCallback()).toBe(mockCallback);
        expect(component.showConfirmModal()).toBe(true);
      });

      it('should store the callback function directly without wrapping', () => {
        const mockCallback = jasmine.createSpy('onConfirm');

        component.showConfirmation({
          message: 'Test message',
          onConfirm: mockCallback
        });

        const storedCallback = component.confirmModalCallback();
        expect(storedCallback).toBe(mockCallback);
        expect(typeof storedCallback).toBe('function');
      });

      it('should open the confirmation modal', () => {
        expect(component.showConfirmModal()).toBe(false);

        component.showConfirmation({
          message: 'Test',
          onConfirm: () => {}
        });

        expect(component.showConfirmModal()).toBe(true);
      });

      it('should support warning variant', () => {
        component.showConfirmation({
          message: 'Warning message',
          variant: 'warning',
          onConfirm: () => {}
        });

        expect(component.confirmModalVariant()).toBe('warning');
      });

      it('should support primary variant', () => {
        component.showConfirmation({
          message: 'Primary message',
          variant: 'primary',
          onConfirm: () => {}
        });

        expect(component.confirmModalVariant()).toBe('primary');
      });

      it('should translate title when not provided', () => {
        mockTranslationService.translate.and.callFake((key: string) => {
          if (key === 'modal.confirmTitle') return 'Translated Confirm';
          return key;
        });

        component.showConfirmation({
          message: 'Test',
          onConfirm: () => {}
        });

        expect(mockTranslationService.translate).toHaveBeenCalledWith('modal.confirmTitle');
        expect(component.confirmModalTitle()).toBe('Translated Confirm');
      });

      it('should translate confirm button text when not provided', () => {
        mockTranslationService.translate.and.callFake((key: string) => {
          if (key === 'modal.confirm') return 'Translated OK';
          return key;
        });

        component.showConfirmation({
          message: 'Test',
          onConfirm: () => {}
        });

        expect(mockTranslationService.translate).toHaveBeenCalledWith('modal.confirm');
        expect(component.confirmModalConfirmText()).toBe('Translated OK');
      });

      it('should translate cancel button text when not provided', () => {
        mockTranslationService.translate.and.callFake((key: string) => {
          if (key === 'modal.cancel') return 'Translated Cancel';
          return key;
        });

        component.showConfirmation({
          message: 'Test',
          onConfirm: () => {}
        });

        expect(mockTranslationService.translate).toHaveBeenCalledWith('modal.cancel');
        expect(component.confirmModalCancelText()).toBe('Translated Cancel');
      });
    });

    describe('onConfirmModalConfirm()', () => {
      it('should close the confirmation modal', () => {
        component.showConfirmModal.set(true);

        component.onConfirmModalConfirm();

        expect(component.showConfirmModal()).toBe(false);
      });

      it('should execute the stored callback after closing modal', fakeAsync(() => {
        const mockCallback = jasmine.createSpy('onConfirm');
        component.confirmModalCallback.set(mockCallback);
        component.showConfirmModal.set(true);

        component.onConfirmModalConfirm();

        // Modal should close immediately
        expect(component.showConfirmModal()).toBe(false);
        // Callback should not be called yet
        expect(mockCallback).not.toHaveBeenCalled();

        // Callback should be called after timeout
        tick(0);
        expect(mockCallback).toHaveBeenCalledTimes(1);
      }));

      it('should handle case when no callback is set', fakeAsync(() => {
        component.confirmModalCallback.set(null);
        component.showConfirmModal.set(true);

        expect(() => {
          component.onConfirmModalConfirm();
          tick(0);
        }).not.toThrow();

        expect(component.showConfirmModal()).toBe(false);
      }));

      it('should clear the callback after closing', () => {
        const mockCallback = jasmine.createSpy('onConfirm');
        component.confirmModalCallback.set(mockCallback);

        component.onConfirmModalConfirm();

        expect(component.confirmModalCallback()).toBeNull();
      });

      it('should use setTimeout for proper timing', fakeAsync(() => {
        const mockCallback = jasmine.createSpy('onConfirm');
        component.confirmModalCallback.set(mockCallback);
        component.showConfirmModal.set(true);

        const spySetTimeout = spyOn(window, 'setTimeout').and.callThrough();

        component.onConfirmModalConfirm();

        expect(spySetTimeout).toHaveBeenCalledWith(jasmine.any(Function), 0);

        tick(0);
        expect(mockCallback).toHaveBeenCalled();
      }));

      it('should execute callback with correct context', fakeAsync(() => {
        let executionContext: any = null;
        const mockCallback = function(this: any) {
          executionContext = this;
        };
        component.confirmModalCallback.set(mockCallback);

        component.onConfirmModalConfirm();
        tick(0);

        // Context should be preserved (will be undefined in strict mode or window in non-strict)
        expect(executionContext).toBeDefined();
      }));

      it('should handle callback that throws an error', fakeAsync(() => {
        const errorCallback = jasmine.createSpy('errorCallback').and.throwError('Test error');
        component.confirmModalCallback.set(errorCallback);

        component.onConfirmModalConfirm();

        // Modal should still close
        expect(component.showConfirmModal()).toBe(false);

        // Error should be thrown when callback executes
        expect(() => {
          tick(0);
        }).toThrowError('Test error');
      }));
    });

    describe('onConfirmModalCancel()', () => {
      it('should close the confirmation modal', () => {
        component.showConfirmModal.set(true);

        component.onConfirmModalCancel();

        expect(component.showConfirmModal()).toBe(false);
      });

      it('should NOT execute the callback', fakeAsync(() => {
        const mockCallback = jasmine.createSpy('onConfirm');
        component.confirmModalCallback.set(mockCallback);
        component.showConfirmModal.set(true);

        component.onConfirmModalCancel();

        tick(1000); // Wait for any potential timeouts

        expect(mockCallback).not.toHaveBeenCalled();
      }));

      it('should clear the callback', () => {
        const mockCallback = jasmine.createSpy('onConfirm');
        component.confirmModalCallback.set(mockCallback);

        component.onConfirmModalCancel();

        expect(component.confirmModalCallback()).toBeNull();
      });

      it('should handle multiple cancel calls gracefully', () => {
        component.showConfirmModal.set(true);

        component.onConfirmModalCancel();
        expect(component.showConfirmModal()).toBe(false);

        component.onConfirmModalCancel();
        expect(component.showConfirmModal()).toBe(false);
      });
    });

    describe('closeConfirmModal()', () => {
      it('should set showConfirmModal to false', () => {
        component.showConfirmModal.set(true);

        component.closeConfirmModal();

        expect(component.showConfirmModal()).toBe(false);
      });

      it('should clear the callback', () => {
        const mockCallback = jasmine.createSpy('onConfirm');
        component.confirmModalCallback.set(mockCallback);

        component.closeConfirmModal();

        expect(component.confirmModalCallback()).toBeNull();
      });

      it('should clear all modal state', () => {
        component.showConfirmModal.set(true);
        component.confirmModalCallback.set(() => {});

        component.closeConfirmModal();

        expect(component.showConfirmModal()).toBe(false);
        expect(component.confirmModalCallback()).toBeNull();
      });

      it('should be safe to call when modal is already closed', () => {
        component.showConfirmModal.set(false);
        component.confirmModalCallback.set(null);

        expect(() => {
          component.closeConfirmModal();
        }).not.toThrow();

        expect(component.showConfirmModal()).toBe(false);
        expect(component.confirmModalCallback()).toBeNull();
      });
    });

    // ============================================================================
    // INTEGRATION TESTS
    // ============================================================================

    describe('Integration: Delete Credential Flow', () => {
      it('should show confirmation modal when deleteCredential is called', () => {
        mockTranslationService.translate.and.callFake((key: string, params?: any) => {
          if (key === 'modal.deleteCredentialTitle') return 'Delete Credential';
          if (key === 'modal.deleteCredentialMessage') {
            return `Delete ${params?.exchangeName}${params?.label}?`;
          }
          if (key === 'button.delete') return 'Delete';
          return key;
        });

        component.deleteCredential(mockCredential);

        expect(component.showConfirmModal()).toBe(true);
        expect(component.confirmModalTitle()).toBe('Delete Credential');
        expect(component.confirmModalMessage()).toContain('Binance');
        expect(component.confirmModalConfirmText()).toBe('Delete');
        expect(component.confirmModalVariant()).toBe('danger');
        expect(component.confirmModalCallback()).toBeTruthy();
      });

      it('should delete credential when user confirms', fakeAsync(() => {
        mockExchangeCredentialsService.deleteCredential.and.returnValue(of(void 0));
        mockExchangeCredentialsService.credentials.and.returnValue([]);
        spyOn(component, 'showToast');

        component.deleteCredential(mockCredential);

        expect(component.showConfirmModal()).toBe(true);

        // User confirms deletion
        component.onConfirmModalConfirm();
        tick(0);

        expect(mockExchangeCredentialsService.deleteCredential).toHaveBeenCalledWith('cred-123');
        expect(component.showToast).toHaveBeenCalledWith('Credential deleted successfully!', 'success');
      }));

      it('should NOT delete credential when user cancels', fakeAsync(() => {
        component.deleteCredential(mockCredential);

        expect(component.showConfirmModal()).toBe(true);

        // User cancels deletion
        component.onConfirmModalCancel();
        tick(100);

        expect(mockExchangeCredentialsService.deleteCredential).not.toHaveBeenCalled();
      }));

      it('should show error toast when deletion fails', fakeAsync(() => {
        const errorMessage = 'Network error';
        mockExchangeCredentialsService.deleteCredential.and.returnValue(
          throwError(() => ({ message: errorMessage }))
        );
        spyOn(component, 'showToast');

        component.deleteCredential(mockCredential);
        component.onConfirmModalConfirm();
        tick(0);

        expect(component.showToast).toHaveBeenCalledWith(
          `Failed to delete credential: ${errorMessage}`,
          'error'
        );
      }));

      it('should set deletingCredentialId during deletion', fakeAsync(() => {
        mockExchangeCredentialsService.deleteCredential.and.returnValue(of(void 0));
        mockExchangeCredentialsService.credentials.and.returnValue([]);

        component.deleteCredential(mockCredential);
        component.onConfirmModalConfirm();
        tick(0);

        expect(component.deletingCredentialId()).toBe('cred-123');

        // After successful deletion, it should be cleared
        tick(0);
        expect(component.deletingCredentialId()).toBeNull();
      }));

      it('should include credential label in confirmation message', () => {
        mockTranslationService.translate.and.callFake((key: string, params?: any) => {
          if (key === 'modal.deleteCredentialMessage' && params) {
            return `Delete ${params.exchangeName}${params.label}?`;
          }
          return key;
        });

        const credentialWithLabel = { ...mockCredential, label: 'My Trading Account' };
        component.deleteCredential(credentialWithLabel);

        expect(component.confirmModalMessage()).toContain('My Trading Account');
      });

      it('should handle credential without label', () => {
        mockTranslationService.translate.and.callFake((key: string, params?: any) => {
          if (key === 'modal.deleteCredentialMessage' && params) {
            return `Delete ${params.exchangeName}${params.label}?`;
          }
          return key;
        });

        const credentialWithoutLabel = { ...mockCredential, label: '' };
        component.deleteCredential(credentialWithoutLabel);

        expect(component.confirmModalMessage()).toContain('Binance');
      });
    });

    describe('Integration: Unsaved Changes Flow', () => {
      it('should show confirmation modal when closing Add Credential modal with dirty form', () => {
        mockTranslationService.translate.and.callFake((key: string) => {
          const translations: Record<string, string> = {
            'modal.unsavedChangesTitle': 'Unsaved Changes',
            'modal.unsavedChangesMessage': 'You have unsaved changes. Discard?',
            'modal.confirm': 'Discard'
          };
          return translations[key] || key;
        });

        component.newCredentialForm.markAsDirty();
        component.showAddCredentialModal.set(true);

        component.closeAddCredentialModal();

        expect(component.showConfirmModal()).toBe(true);
        expect(component.confirmModalTitle()).toBe('Unsaved Changes');
        expect(component.confirmModalMessage()).toBe('You have unsaved changes. Discard?');
        expect(component.confirmModalVariant()).toBe('warning');
        expect(component.showAddCredentialModal()).toBe(true); // Should still be open
      });

      it('should close Add Credential modal when user confirms discarding changes', fakeAsync(() => {
        component.newCredentialForm.markAsDirty();
        component.showAddCredentialModal.set(true);

        component.closeAddCredentialModal();
        expect(component.showConfirmModal()).toBe(true);

        // User confirms
        component.onConfirmModalConfirm();
        tick(0);

        expect(component.showAddCredentialModal()).toBe(false);
        expect(component.newCredentialForm.pristine).toBe(true);
      }));

      it('should NOT close Add Credential modal when user cancels', fakeAsync(() => {
        component.newCredentialForm.markAsDirty();
        component.showAddCredentialModal.set(true);

        component.closeAddCredentialModal();
        expect(component.showConfirmModal()).toBe(true);

        // User cancels
        component.onConfirmModalCancel();
        tick(100);

        expect(component.showAddCredentialModal()).toBe(true);
        expect(component.newCredentialForm.dirty).toBe(true);
      }));

      it('should close Add Credential modal without confirmation when form is pristine', () => {
        component.newCredentialForm.markAsPristine();
        component.showAddCredentialModal.set(true);

        component.closeAddCredentialModal();

        expect(component.showConfirmModal()).toBe(false);
        expect(component.showAddCredentialModal()).toBe(false);
      });

      it('should reset form after confirming discard', fakeAsync(() => {
        component.newCredentialForm.setValue({
          exchange: ExchangeType.BINANCE,
          apiKey: 'test-key',
          apiSecret: 'test-secret',
          authToken: '',
          label: 'Test'
        });
        component.newCredentialForm.markAsDirty();
        component.showAddCredentialModal.set(true);

        component.closeAddCredentialModal();
        component.onConfirmModalConfirm();
        tick(0);

        expect(component.newCredentialForm.value.apiKey).toBe('');
        expect(component.newCredentialForm.value.apiSecret).toBe('');
      }));
    });

    describe('Edge Cases and Error Handling', () => {
      it('should handle rapid open/close of confirmation modal', fakeAsync(() => {
        const callback1 = jasmine.createSpy('callback1');
        const callback2 = jasmine.createSpy('callback2');

        component.showConfirmation({ message: 'First', onConfirm: callback1 });
        component.closeConfirmModal();
        component.showConfirmation({ message: 'Second', onConfirm: callback2 });
        component.onConfirmModalConfirm();
        tick(0);

        expect(callback1).not.toHaveBeenCalled();
        expect(callback2).toHaveBeenCalledTimes(1);
      }));

      it('should handle confirmation modal opened multiple times before confirming', fakeAsync(() => {
        const callback1 = jasmine.createSpy('callback1');
        const callback2 = jasmine.createSpy('callback2');

        component.showConfirmation({ message: 'First', onConfirm: callback1 });
        component.showConfirmation({ message: 'Second', onConfirm: callback2 });
        component.onConfirmModalConfirm();
        tick(0);

        expect(callback1).not.toHaveBeenCalled();
        expect(callback2).toHaveBeenCalledTimes(1);
      }));

      it('should handle null callback gracefully', fakeAsync(() => {
        component.confirmModalCallback.set(null);

        expect(() => {
          component.onConfirmModalConfirm();
          tick(0);
        }).not.toThrow();
      }));

      it('should handle undefined callback gracefully', fakeAsync(() => {
        component.confirmModalCallback.set(undefined as any);

        expect(() => {
          component.onConfirmModalConfirm();
          tick(0);
        }).not.toThrow();
      }));

      it('should maintain signal reactivity when updating modal state', () => {
        const callback = jasmine.createSpy('callback');

        component.showConfirmation({ message: 'Test', onConfirm: callback });
        fixture.detectChanges();

        expect(component.showConfirmModal()).toBe(true);

        component.closeConfirmModal();
        fixture.detectChanges();

        expect(component.showConfirmModal()).toBe(false);
      });

      it('should handle confirmation callback that modifies component state', fakeAsync(() => {
        let callbackExecuted = false;
        const callback = () => {
          callbackExecuted = true;
          component.activeTab.set('platforms');
        };

        component.showConfirmation({ message: 'Test', onConfirm: callback });
        component.onConfirmModalConfirm();
        tick(0);

        expect(callbackExecuted).toBe(true);
        expect(component.activeTab()).toBe('platforms');
      }));

      it('should handle confirmation callback that triggers another confirmation', fakeAsync(() => {
        const nestedCallback = jasmine.createSpy('nestedCallback');
        const outerCallback = () => {
          component.showConfirmation({
            message: 'Nested confirmation',
            onConfirm: nestedCallback
          });
        };

        component.showConfirmation({ message: 'Outer', onConfirm: outerCallback });
        component.onConfirmModalConfirm();
        tick(0);

        expect(component.showConfirmModal()).toBe(true);
        expect(component.confirmModalMessage()).toBe('Nested confirmation');

        component.onConfirmModalConfirm();
        tick(0);

        expect(nestedCallback).toHaveBeenCalled();
      }));

      it('should preserve variant type across modal lifecycle', () => {
        component.showConfirmation({
          message: 'Test',
          variant: 'danger',
          onConfirm: () => {}
        });

        expect(component.confirmModalVariant()).toBe('danger');

        component.onConfirmModalCancel();

        // Open another modal with different variant
        component.showConfirmation({
          message: 'Test 2',
          variant: 'warning',
          onConfirm: () => {}
        });

        expect(component.confirmModalVariant()).toBe('warning');
      });

      it('should handle callback that returns a value', fakeAsync(() => {
        const callbackWithReturn = jasmine.createSpy('callbackWithReturn').and.returnValue('some value');
        component.confirmModalCallback.set(callbackWithReturn);

        component.onConfirmModalConfirm();
        tick(0);

        expect(callbackWithReturn).toHaveBeenCalled();
      }));

      it('should handle async callback', fakeAsync(() => {
        let asyncCompleted = false;
        const asyncCallback = async () => {
          await new Promise(resolve => setTimeout(resolve, 100));
          asyncCompleted = true;
        };

        component.confirmModalCallback.set(asyncCallback);
        component.onConfirmModalConfirm();
        tick(0);

        // Callback should be called but might not complete yet
        tick(100);
        expect(asyncCompleted).toBe(true);
      }));
    });

    describe('Modal State Management', () => {
      it('should initialize all modal signals with correct default values', () => {
        expect(component.showConfirmModal()).toBe(false);
        expect(component.confirmModalTitle()).toBe('');
        expect(component.confirmModalMessage()).toBe('');
        expect(component.confirmModalConfirmText()).toBe('');
        expect(component.confirmModalCancelText()).toBe('');
        expect(component.confirmModalVariant()).toBe('primary');
        expect(component.confirmModalCallback()).toBeNull();
      });

      it('should update all signals when showConfirmation is called', () => {
        const callback = jasmine.createSpy('callback');
        component.showConfirmation({
          title: 'Custom Title',
          message: 'Custom Message',
          confirmText: 'Yes',
          cancelText: 'No',
          variant: 'danger',
          onConfirm: callback
        });

        expect(component.showConfirmModal()).toBe(true);
        expect(component.confirmModalTitle()).toBe('Custom Title');
        expect(component.confirmModalMessage()).toBe('Custom Message');
        expect(component.confirmModalConfirmText()).toBe('Yes');
        expect(component.confirmModalCancelText()).toBe('No');
        expect(component.confirmModalVariant()).toBe('danger');
        expect(component.confirmModalCallback()).toBe(callback);
      });

      it('should only clear necessary signals when modal closes', () => {
        component.showConfirmation({
          title: 'Test',
          message: 'Message',
          confirmText: 'OK',
          cancelText: 'Cancel',
          variant: 'warning',
          onConfirm: () => {}
        });

        component.closeConfirmModal();

        expect(component.showConfirmModal()).toBe(false);
        expect(component.confirmModalCallback()).toBeNull();
        // Other signals may retain their values (implementation detail)
      });

      it('should handle signal updates in correct order during confirm', fakeAsync(() => {
        const executionOrder: string[] = [];
        const callback = () => {
          executionOrder.push('callback');
        };

        component.showConfirmation({ message: 'Test', onConfirm: callback });
        executionOrder.push('modal-opened');

        component.onConfirmModalConfirm();
        executionOrder.push('confirm-called');

        expect(component.showConfirmModal()).toBe(false);
        executionOrder.push('modal-closed');

        tick(0);

        expect(executionOrder).toEqual([
          'modal-opened',
          'confirm-called',
          'modal-closed',
          'callback'
        ]);
      }));
    });

    describe('Translation Integration', () => {
      it('should use translation service for all default texts', () => {
        mockTranslationService.translate.and.callFake((key: string) => `translated_${key}`);

        component.showConfirmation({
          message: 'Test message',
          onConfirm: () => {}
        });

        expect(mockTranslationService.translate).toHaveBeenCalledWith('modal.confirmTitle');
        expect(mockTranslationService.translate).toHaveBeenCalledWith('modal.confirm');
        expect(mockTranslationService.translate).toHaveBeenCalledWith('modal.cancel');
      });

      it('should pass parameters to translation service correctly', () => {
        mockTranslationService.translate.and.callFake((key: string, params?: any) => {
          if (params) {
            return `${key}:${JSON.stringify(params)}`;
          }
          return key;
        });

        const credentialWithLabel = { ...mockCredential, label: 'Test Label' };
        component.deleteCredential(credentialWithLabel);

        expect(mockTranslationService.translate).toHaveBeenCalledWith(
          'modal.deleteCredentialMessage',
          jasmine.objectContaining({
            exchangeName: jasmine.any(String),
            label: jasmine.any(String)
          })
        );
      });
    });
  });
});

import { Component, computed, OnInit, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, FormsModule, ReactiveFormsModule, Validators } from '@angular/forms';
import { Router, RouterLink } from '@angular/router';
import { AuthService } from '../../services/auth.service';
import { Language, TranslationService } from '../../services/translation.service';
import { ThemeService } from '../../services/theme.service';
import { UserService } from '../../services/user.service';
import { GoogleAuthService } from '../../services/google-auth.service';
import { ExchangeCredentialsService } from '../../services/exchange-credentials.service';
import {
  CreateExchangeCredentialRequest,
  EXCHANGE_METADATA,
  ExchangeCredential,
  ExchangeType,
  getExchangeName,
  TestConnectionRequest
} from '../../models/exchange-credentials.model';
import { take } from 'rxjs/operators';
import { TradingPlatformInfoModalComponent } from '../trading-platform-info-modal/trading-platform-info-modal.component';
import { ConfirmationModalComponent } from '../ui/confirmation-modal/confirmation-modal.component';
import { ButtonComponent } from '../ui/button/button.component';

interface Tab {
  id: string;
  label: string;
  icon: string;
}

interface MessageLocal {
  id: string;
  type: 'info' | 'warning' | 'success' | 'error' | 'trade';
  title: string;
  content: string;
  timestamp: Date;
  read: boolean;
  actions?: MessageActionLocal[];
}

interface MessageActionLocal {
  label: string;
  type: 'primary' | 'secondary' | 'danger';
  action: string;
}

@Component({
  selector: 'app-profile',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule, FormsModule, RouterLink, TradingPlatformInfoModalComponent, ConfirmationModalComponent, ButtonComponent],
  templateUrl: './profile.component.html',
  styleUrl: './profile.component.scss'
})
export class ProfileComponent implements OnInit {
  // Signals and computed values
  readonly currentUser = computed(() => this.authService.currentUser());
  readonly isDark = computed(() => this.themeService.currentTheme() === 'dark');
  readonly availableLanguages = this.translationService.availableLanguages;
  readonly isAdmin = computed(() => this.authService.hasRole('ADMIN'));

  // Component state
  activeTab = signal<string>('settings');

  // Tab configuration
  tabs: Tab[] = [
    { id: 'settings', label: 'profile.preferences', icon: '' },
    { id: 'userinfo', label: 'profile.personalInfo', icon: '' },
    { id: 'platforms', label: 'profile.tradingPlatforms', icon: '' },
    { id: 'messages', label: 'profile.messages', icon: '' }
  ];

  // Settings
  selectedLanguage: Language = 'en';
  selectedCurrency = 'USD';
  emailNotifications = true;
  priceAlerts = true;
  tradingAlerts = false;

  // Forms
  userInfoForm: FormGroup;
  newCredentialForm: FormGroup;

  // Exchange Credentials State
  credentials = signal<ExchangeCredential[]>([]);
  editingCredentialId = signal<string | null>(null);
  editFormData = signal<Partial<ExchangeCredential> & { apiKey?: string; apiSecret?: string; authToken?: string }>({});
  showAddCredentialModal = signal<boolean>(false);
  testingConnectionId = signal<string | null>(null);
  deletingCredentialId = signal<string | null>(null);
  savingCredentialId = signal<string | null>(null);
  showApiSecret = signal<Record<string, boolean>>({});
  revealedCredentials = signal<Record<string, { apiKey?: string; apiSecret?: string }>>({});

  // Toast notification state
  toastMessage = signal<string>('');
  toastType = signal<'success' | 'error' | 'warning' | 'info'>('info');
  showToastSignal = signal<boolean>(false);

  // Trading Platform Info Modal state
  showPlatformInfoModal = signal<boolean>(false);
  selectedCredentialForModal = signal<ExchangeCredential | null>(null);

  // Confirmation Modal state
  showConfirmModal = signal<boolean>(false);
  confirmModalTitle = signal<string>('');
  confirmModalMessage = signal<string>('');
  confirmModalConfirmText = signal<string>('');
  confirmModalCancelText = signal<string>('');
  confirmModalVariant = signal<'primary' | 'danger' | 'warning'>('primary');
  confirmModalCallback = signal<(() => void) | null>(null);

  // Enum references for template
  readonly ExchangeType = ExchangeType;
  readonly EXCHANGE_METADATA = EXCHANGE_METADATA;
  readonly getExchangeName = getExchangeName;
  readonly exchanges = Object.values(ExchangeType);

  // Data
  messages: MessageLocal[] = [
    {
      id: '1',
      type: 'trade',
      title: 'Order Filled',
      content: 'Your limit order for 0.5 BTC at $45,000 has been successfully filled.',
      timestamp: new Date('2024-12-12T10:30:00'),
      read: false,
      actions: [
        { label: 'View Order', type: 'primary', action: 'viewOrder' },
        { label: 'Trade More', type: 'secondary', action: 'openTrading' }
      ]
    },
    {
      id: '2',
      type: 'warning',
      title: 'API Key Expiring Soon',
      content: 'Your Binance API key will expire in 7 days. Please renew it to continue trading.',
      timestamp: new Date('2024-12-11T14:15:00'),
      read: false,
      actions: [
        { label: 'Renew Key', type: 'primary', action: 'renewApiKey' }
      ]
    },
    {
      id: '3',
      type: 'success',
      title: 'Security Update',
      content: 'Your account security settings have been successfully updated.',
      timestamp: new Date('2024-12-10T09:00:00'),
      read: true
    },
    {
      id: '4',
      type: 'info',
      title: 'New Feature Available',
      content: 'Check out our new advanced charting tools in the trading dashboard.',
      timestamp: new Date('2024-12-09T16:45:00'),
      read: true,
      actions: [
        { label: 'Learn More', type: 'secondary', action: 'learnMore' }
      ]
    }
  ];

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private translationService: TranslationService,
    private themeService: ThemeService,
    private userService: UserService,
    private googleAuthService: GoogleAuthService,
    public exchangeCredentialsService: ExchangeCredentialsService,
    private router: Router
  ) {
    this.userInfoForm = this.fb.group({
      firstName: ['John'],
      lastName: ['Doe'],
      email: [{ value: this.currentUser()?.email || '', disabled: true }],
      phone: ['+1234567890'],
      timezone: ['UTC']
    });

    this.newCredentialForm = this.fb.group({
      exchange: ['', Validators.required],
      apiKey: ['', Validators.required],
      apiSecret: ['', Validators.required],
      authToken: [''], // Browser session token for MEXC
      label: ['', [Validators.maxLength(50)]]
    });

    this.selectedLanguage = this.translationService.currentLanguage();
  }

  ngOnInit(): void {
    // Initialize component
  }

  // Tab navigation
  setActiveTab(tabId: string): void {
    this.activeTab.set(tabId);

    // Load credentials when switching to platforms tab
    if (tabId === 'platforms') {
      this.onPlatformsTabActivated();
    }
  }

  // Translation helper
  translate(key: string, params?: Record<string, string>): string {
    return this.translationService.translate(key, params);
  }

  // Settings operations
  changeLanguage(event: Event): void {
    const target = event.target as HTMLSelectElement;
    const newLanguage = target.value as Language;
    this.translationService.setLanguage(newLanguage);
    this.selectedLanguage = newLanguage;
  }

  toggleTheme(): void {
    this.themeService.toggleTheme();
  }

  changeCurrency(event: Event): void {
    const target = event.target as HTMLSelectElement;
    this.selectedCurrency = target.value;
    // Save currency preference
    localStorage.setItem('preferred-currency', this.selectedCurrency);
  }

  updateNotificationSettings(): void {
    // Save notification settings
    const settings = {
      email: this.emailNotifications,
      priceAlerts: this.priceAlerts,
      tradingAlerts: this.tradingAlerts
    };
    localStorage.setItem('notification-settings', JSON.stringify(settings));
  }

  // User info operations
  updateUserInfo(): void {
    if (this.userInfoForm.valid) {
      console.log('Updating user info:', this.userInfoForm.value);
      // Simulate API call
      this.showToast('User information updated successfully!', 'success');
      this.userInfoForm.markAsPristine();
    }
  }

  resetUserInfo(): void {
    this.userInfoForm.reset();
  }

  changeAvatar(): void {
    // Open file picker or avatar selection modal
    console.log('Change avatar clicked');
    this.showToast('Avatar change functionality would be implemented here', 'info');
  }

  linkGoogleAccount(): void {
    this.googleAuthService.linkGoogleAccount().subscribe({
      next: (response) => {
        if (response.success) {
          this.showToast(`Google account linked successfully! ${response.message}`, 'success');
          // Refresh user data or update UI as needed
        } else {
          this.showToast(`Failed to link Google account: ${response.message}`, 'error');
        }
      },
      error: (error) => {
        console.error('Error linking Google account:', error);
        this.showToast('Failed to link Google account. Please try again.', 'error');
      }
    });
  }

  // Messages operations
  markAsRead(messageId: string): void {
    const message = this.messages.find(m => m.id === messageId);
    if (message) {
      message.read = true;
    }
  }

  markAllAsRead(): void {
    this.messages.forEach(message => message.read = true);
  }

  executeMessageAction(action: MessageActionLocal): void {
    console.log('Executing action:', action);
    switch (action.action) {
      case 'viewOrder':
        this.showToast('Would navigate to order details', 'info');
        break;
      case 'openTrading':
        this.showToast('Would open trading interface', 'info');
        break;
      case 'renewApiKey':
        this.showToast('Would open API key renewal process', 'info');
        break;
      case 'learnMore':
        this.showToast('Would show feature information', 'info');
        break;
    }
  }

  // Logout functionality
  logout(): void {
    this.authService.logout().pipe(take(1)).subscribe({
      next: () => {
        this.router.navigate(['/login']);
      },
      error: () => {
        this.router.navigate(['/login']);
      }
    });
  }

  // ============================================================================
  // EXCHANGE CREDENTIALS METHODS
  // ============================================================================

  /**
   * Load credentials when tab opens
   */
  loadCredentials(): void {
    this.exchangeCredentialsService.fetchCredentials().subscribe({
      next: (creds) => {
        this.credentials.set(creds);
      },
      error: (err) => {
        console.error('Failed to load credentials:', err);
        this.showToast('Failed to load exchange credentials. Please try again.', 'error');
      }
    });
  }

  /**
   * Start editing a credential row
   */
  startEditCredential(credential: ExchangeCredential): void {
    this.editingCredentialId.set(credential.id);
    this.editFormData.set({
      label: credential.label,
      apiKey: '',  // Empty string means "keep current"
      apiSecret: '',  // Empty string means "keep current"
      authToken: '',  // Empty string means "keep current"
      // Store original values for canceling
      _originalLabel: credential.label
    } as any);
  }

  /**
   * Test connection with edited values
   */
  testEditConnection(credential: ExchangeCredential): void {
    const editData = this.editFormData();

    // Get the revealed credentials
    const revealed = this.revealedCredentials()[credential.id];

    // Determine which API key and secret to use
    let apiKey: string;
    let apiSecret: string;

    // If user entered new values, use those
    if (editData.apiKey && editData.apiKey.trim() !== '') {
      apiKey = editData.apiKey.trim();
    } else if (revealed?.apiKey) {
      // Use revealed stored value
      apiKey = revealed.apiKey;
    } else {
      // Need to fetch the credential first
      console.log('No API key available, need to reveal first');
      this.showToast('Please click the eye icon to reveal the API Key first', 'warning');
      return;
    }

    if (editData.apiSecret && editData.apiSecret.trim() !== '') {
      apiSecret = editData.apiSecret.trim();
    } else if (revealed?.apiSecret) {
      apiSecret = revealed.apiSecret;
    } else {
      console.log('No API secret available, need to reveal first');
      this.showToast('Please click the eye icon to reveal the API Secret first', 'warning');
      return;
    }

    const testData: TestConnectionRequest = {
      exchange: credential.exchange,
      apiKey: apiKey,
      apiSecret: apiSecret,
      authToken: editData.authToken && editData.authToken.trim() !== '' ? editData.authToken.trim() : undefined
    };

    this.testingConnectionId.set(credential.id);

    this.exchangeCredentialsService.testConnection(testData).subscribe({
      next: (result) => {
        this.testingConnectionId.set(null);
        if (result.success) {
          this.showToast('Connection successful! You can now save the credentials.', 'success');
        } else {
          this.showToast('Connection failed: ' + (result.message || 'Unknown error'), 'error');
        }
      },
      error: (err) => {
        this.testingConnectionId.set(null);
        this.showToast('Connection test failed: ' + (err.message || 'Unknown error'), 'error');
      }
    });
  }

  /**
   * Cancel editing
   */
  cancelEdit(): void {
    this.editingCredentialId.set(null);
    this.editFormData.set({});
  }

  /**
   * Save edited credential
   */
  saveCredential(credentialId: string): void {
    const editData = this.editFormData();

    // Build update request with only changed fields
    const updateRequest: any = {};

    // Always include label (even if empty)
    if (editData.label !== undefined) {
      updateRequest.label = editData.label || undefined;
    }

    // Include apiKey only if it was changed (non-empty)
    if (editData.apiKey && editData.apiKey.trim() !== '') {
      updateRequest.apiKey = editData.apiKey.trim();
    }

    // Include apiSecret only if it was changed (non-empty)
    if (editData.apiSecret && editData.apiSecret.trim() !== '') {
      updateRequest.apiSecret = editData.apiSecret.trim();
    }

    // Include authToken only if it was changed (non-empty)
    if (editData.authToken && editData.authToken.trim() !== '') {
      updateRequest.authToken = editData.authToken.trim();
    }

    // Set saving state
    this.savingCredentialId.set(credentialId);

    this.exchangeCredentialsService.updateCredential(credentialId, updateRequest).pipe(take(1)).subscribe({
      next: (updated) => {
        // Clear editing and saving state first
        this.editingCredentialId.set(null);
        this.editFormData.set({});
        this.savingCredentialId.set(null);

        // Clear visibility toggles for this credential
        const currentVisibility = this.showApiSecret();
        const updatedVisibility = { ...currentVisibility };
        delete updatedVisibility[credentialId + '_key'];
        delete updatedVisibility[credentialId + '_secret'];
        this.showApiSecret.set(updatedVisibility);

        // Service already updated the state - sync local component state
        this.credentials.set(this.exchangeCredentialsService.credentials());

        this.showToast('Credential updated successfully!', 'success');
      },
      error: (err) => {
        console.error('Failed to update credential:', err);
        this.savingCredentialId.set(null);
        this.showToast('Failed to update credential: ' + err.message, 'error');
      }
    });
  }

  /**
   * Update edit form data
   */
  updateEditField(field: string, value: any): void {
    this.editFormData.set({
      ...this.editFormData(),
      [field]: value
    });
  }

  /**
   * Test connection for a credential
   */
  testConnection(credential: ExchangeCredential): void {
    this.testingConnectionId.set(credential.id);

    // Note: We can't test existing credentials without the secret
    // We need to show a message about this
    this.showToast('Connection testing requires API secret. Please use the "Add New Credential" form to test connections before saving.', 'info');
    this.testingConnectionId.set(null);
  }

  /**
   * Delete a credential
   */
  deleteCredential(credential: ExchangeCredential): void {
    const exchangeName = this.getExchangeName(credential.exchange);
    const labelPart = credential.label ? ` "${credential.label}"` : '';

    this.showConfirmation({
      title: this.translate('modal.deleteCredentialTitle'),
      message: this.translate('modal.deleteCredentialMessage', {
        exchangeName: exchangeName,
        label: labelPart
      }),
      confirmText: this.translate('button.delete'),
      variant: 'danger',
      onConfirm: () => {
        this.deletingCredentialId.set(credential.id);

        this.exchangeCredentialsService.deleteCredential(credential.id).subscribe({
          next: () => {
            // Service already updated the state - sync local component state
            this.credentials.set(this.exchangeCredentialsService.credentials());
            this.deletingCredentialId.set(null);

            this.showToast('Credential deleted successfully!', 'success');
          },
          error: (err) => {
            console.error('Failed to delete credential:', err);
            this.deletingCredentialId.set(null);
            this.showToast('Failed to delete credential: ' + err.message, 'error');
          }
        });
      }
    });
  }

  /**
   * Toggle active status
   */
  toggleActive(credential: ExchangeCredential, e: Event): void {
    const isChecked = (e.target as HTMLInputElement).checked;

    this.exchangeCredentialsService.updateCredential(credential.id, { isActive: isChecked })
      .pipe(take(1)).subscribe({
      next: (updated) => {
        // Service already updated the state - sync local component state
        this.credentials.set(this.exchangeCredentialsService.credentials());

        this.showToast(`Credential ${isChecked ? 'activated' : 'deactivated'} successfully!`, 'success');
      },
      error: (err) => {
        console.error('Failed to toggle credential:', err);
        this.showToast('Failed to toggle credential: ' + err.message, 'error');
      }
    });
  }

  /**
   * Open add credential modal
   */
  openAddCredentialModal(): void {
    this.newCredentialForm.reset({
      exchange: '',
      apiKey: '',
      apiSecret: '',
      authToken: '',
      label: ''
    });
    this.showAddCredentialModal.set(true);
  }

  /**
   * Close add credential modal
   */
  closeAddCredentialModal(): void {
    if (this.newCredentialForm.dirty) {
      this.showConfirmation({
        title: this.translate('modal.unsavedChangesTitle'),
        message: this.translate('modal.unsavedChangesMessage'),
        confirmText: this.translate('modal.confirm'),
        variant: 'warning',
        onConfirm: () => {
          this.showAddCredentialModal.set(false);
          this.newCredentialForm.reset();
        }
      });
    } else {
      this.showAddCredentialModal.set(false);
      this.newCredentialForm.reset();
    }
  }

  /**
   * Test connection in the add form
   */
  testNewConnection(): void {
    if (this.newCredentialForm.invalid) {
      Object.keys(this.newCredentialForm.controls).forEach(key => {
        this.newCredentialForm.get(key)?.markAsTouched();
      });
      this.showToast('Please fill in all required fields before testing connection.', 'warning');
      return;
    }

    const testData: TestConnectionRequest = {
      exchange: this.newCredentialForm.value.exchange,
      apiKey: this.newCredentialForm.value.apiKey,
      apiSecret: this.newCredentialForm.value.apiSecret,
      authToken: this.newCredentialForm.value.authToken || undefined
    };

    this.testingConnectionId.set('new');

    this.exchangeCredentialsService.testConnection(testData).subscribe({
      next: (result) => {
        this.testingConnectionId.set(null);
        if (result.success) {
          this.showToast('Connection successful! You can now save the credentials.', 'success');
        } else {
          this.showToast('Connection failed: ' + result.message, 'error');
        }
      },
      error: (err) => {
        this.testingConnectionId.set(null);
        this.showToast('Connection test failed: ' + err.message, 'error');
      }
    });
  }

  /**
   * Save new credential
   */
  saveNewCredential(): void {
    if (this.newCredentialForm.invalid) {
      Object.keys(this.newCredentialForm.controls).forEach(key => {
        this.newCredentialForm.get(key)?.markAsTouched();
      });
      this.showToast('Please fill in all required fields.', 'warning');
      return;
    }

    const data: CreateExchangeCredentialRequest = {
      exchange: this.newCredentialForm.value.exchange,
      apiKey: this.newCredentialForm.value.apiKey,
      apiSecret: this.newCredentialForm.value.apiSecret,
      authToken: this.newCredentialForm.value.authToken || undefined,
      label: this.newCredentialForm.value.label || undefined,
      isActive: true // Set new credentials as active by default
    };

    this.exchangeCredentialsService.createCredential(data).subscribe({
      next: (newCred) => {
        // Service already updated the state - sync local component state
        this.credentials.set(this.exchangeCredentialsService.credentials());

        // Close modal
        this.showAddCredentialModal.set(false);
        this.newCredentialForm.reset();

        this.showToast('Credential saved successfully!', 'success');
      },
      error: (err) => {
        console.error('Failed to create credential:', err);
        this.showToast('Failed to save credential: ' + err.message, 'error');
      }
    });
  }

  /**
   * Toggle API key visibility (in edit mode)
   */
  toggleApiKeyVisibility(credentialId: string): void {
    const key = credentialId + '_key';
    const current = this.showApiSecret();
    const newVisibility = !current[key];

    this.showApiSecret.set({
      ...current,
      [key]: newVisibility
    });

    // If revealing and we don't have the decrypted value yet, fetch it
    if (newVisibility && !this.revealedCredentials()[credentialId]?.apiKey) {
      this.fetchDecryptedCredential(credentialId);
    }
  }

  /**
   * Toggle API secret visibility (in edit mode)
   */
  toggleApiSecretVisibility(credentialId: string): void {
    const key = credentialId + '_secret';
    const current = this.showApiSecret();
    const newVisibility = !current[key];

    this.showApiSecret.set({
      ...current,
      [key]: newVisibility
    });

    // If revealing and we don't have the decrypted value yet, fetch it
    if (newVisibility && !this.revealedCredentials()[credentialId]?.apiSecret) {
      this.fetchDecryptedCredential(credentialId);
    }
  }

  /**
   * Fetch decrypted credential from backend
   */
  fetchDecryptedCredential(credentialId: string): void {
    this.exchangeCredentialsService.getCredentialById(credentialId).subscribe({
      next: (credential) => {
        // Store the revealed credentials
        const current = this.revealedCredentials();
        this.revealedCredentials.set({
          ...current,
          [credentialId]: {
            apiKey: (credential as any).apiKey || credential.apiKeyPreview,
            apiSecret: (credential as any).apiSecret || '(Secret not returned by server)'
          }
        });
      },
      error: (err) => {
        console.error('Failed to fetch decrypted credential:', err);
        this.showToast('Failed to retrieve credential details: ' + err.message, 'error');
      }
    });
  }

  /**
   * Legacy method for modal form - toggle API secret visibility
   */
  toggleApiSecretVisibilityLegacy(credentialId: string): void {
    const current = this.showApiSecret();
    this.showApiSecret.set({
      ...current,
      [credentialId]: !current[credentialId]
    });
  }

  /**
   * Toggle new form API secret visibility
   */
  toggleNewFormApiSecretVisibility(): void {
    this.toggleApiSecretVisibility('new');
  }

  /**
   * Get masked display for API key/secret
   */
  getMaskedValue(value: string): string {
    if (!value || value.length <= 4) {
      return '****';
    }
    return '...' + value.slice(-4);
  }

  /**
   * Called when platforms tab is activated
   */
  onPlatformsTabActivated(): void {
    this.loadCredentials();
  }

  /**
   * Show toast notification
   */
  showToast(message: string, type: 'success' | 'error' | 'warning' | 'info' = 'info'): void {
    this.toastMessage.set(message);
    this.toastType.set(type);
    this.showToastSignal.set(true);

    // Auto-hide after 5 seconds
    setTimeout(() => {
      this.showToastSignal.set(false);
    }, 5000);
  }

  /**
   * Open trading platform info modal
   * Shows detailed information about the selected exchange credential
   */
  openPlatformInfoModal(credential: ExchangeCredential): void {
    this.selectedCredentialForModal.set(credential);
    this.showPlatformInfoModal.set(true);
  }

  /**
   * Close trading platform info modal
   */
  closePlatformInfoModal(): void {
    this.showPlatformInfoModal.set(false);
    this.selectedCredentialForModal.set(null);
  }

  /**
   * Show confirmation modal
   */
  showConfirmation(config: {
    title?: string;
    message: string;
    confirmText?: string;
    cancelText?: string;
    variant?: 'primary' | 'danger' | 'warning';
    onConfirm: () => void;
  }): void {
    this.confirmModalTitle.set(config.title || this.translate('modal.confirmTitle'));
    this.confirmModalMessage.set(config.message);
    this.confirmModalConfirmText.set(config.confirmText || this.translate('modal.confirm'));
    this.confirmModalCancelText.set(config.cancelText || this.translate('modal.cancel'));
    this.confirmModalVariant.set(config.variant || 'primary');
    this.confirmModalCallback.set(config.onConfirm);
    this.showConfirmModal.set(true);
  }

  /**
   * Handle confirmation modal confirm action
   */
  onConfirmModalConfirm(): void {
    const callback = this.confirmModalCallback();
    this.closeConfirmModal();
    // Execute callback in next tick to ensure modal closes first
    setTimeout(() => {
      if (callback) {
        callback();
      }
    }, 0);
  }

  /**
   * Handle confirmation modal cancel action
   */
  onConfirmModalCancel(): void {
    this.closeConfirmModal();
  }

  /**
   * Close confirmation modal
   */
  closeConfirmModal(): void {
    this.showConfirmModal.set(false);
    this.confirmModalCallback.set(null);
  }

}

