    it('should compute requestPreview correctly', () => {
      component.onExchangeChange(ExchangeType.BINGX);
      component.onCredentialChange('cred-1');
      component.orderForm.patchValue({
        symbol: 'BTC-USDT',
        side: OrderSide.BUY,
