        expect(component.error()).toBeTruthy();
        done();
      }, 100);
    });

    it('should show error for unsupported exchange', () => {
      const binanceCredential = { ...mockCredential, exchange: ExchangeType.BINANCE };
      fixture.componentRef.setInput('credential', binanceCredential);

      component.loadUserInfo();

      expect(component.error()).toBe('Only Bybit exchange is currently supported');
      expect(mockBybitUserService.getUserInfo).not.toHaveBeenCalled();
    });

    it('should refresh user info when requested', () => {
      component.refreshUserInfo();
      expect(mockBybitUserService.getUserInfo).toHaveBeenCalled();
    });
  });

  describe('Tab Navigation', () => {
    beforeEach(() => {
      fixture.componentRef.setInput('credential', mockCredential);
      fixture.detectChanges();
    });

    it('should change active tab', () => {
      component.setActiveTab('balances');
      expect(component.activeTab()).toBe('balances');

      component.setActiveTab('positions');
      expect(component.activeTab()).toBe('positions');
    });

    it('should have correct tab configuration', () => {
      expect(component.tabs.length).toBe(4);
      expect(component.tabs[0].id).toBe('overview');
      expect(component.tabs[1].id).toBe('balances');
      expect(component.tabs[2].id).toBe('positions');
      expect(component.tabs[3].id).toBe('orders');
    });
  });

  describe('Modal Interactions', () => {
    beforeEach(() => {
      fixture.componentRef.setInput('credential', mockCredential);
      fixture.detectChanges();
    });

    it('should emit close event when close is called', () => {
      spyOn(component.closeModal, 'emit');
      component.close();
      expect(component.closeModal.emit).toHaveBeenCalled();
    });

    it('should close modal on overlay click', () => {
      spyOn(component.closeModal, 'emit');
      component.handleOverlayClick();
      expect(component.closeModal.emit).toHaveBeenCalled();
    });

    it('should stop propagation on content click', () => {
      const event = new Event('click');
      spyOn(event, 'stopPropagation');
      component.handleContentClick(event);
      expect(event.stopPropagation).toHaveBeenCalled();
    });

    it('should close modal on ESC key press', () => {
      spyOn(component, 'close');
      fixture.componentRef.setInput('credential', mockCredential);
      component.ngOnInit();

      const event = new KeyboardEvent('keydown', { key: 'Escape' });
      document.dispatchEvent(event);

      expect(component.close).toHaveBeenCalled();
    });

    it('should not close on other key presses', () => {
      spyOn(component, 'close');
      fixture.componentRef.setInput('credential', mockCredential);
      component.ngOnInit();

      const event = new KeyboardEvent('keydown', { key: 'Enter' });
      document.dispatchEvent(event);

      expect(component.close).not.toHaveBeenCalled();
    });
  });

  describe('Error Handling', () => {
    beforeEach(() => {
      fixture.componentRef.setInput('credential', mockCredential);
      fixture.detectChanges();
    });

    it('should clear error when clearError is called', () => {
      component.error.set('Test error');
      component.clearError();
      expect(component.error()).toBeNull();
    });

    it('should retry loading and clear error', () => {
      component.error.set('Test error');
      spyOn(component, 'loadUserInfo');

      component.retryLoad();

      expect(component.error()).toBeNull();
      expect(component.loadUserInfo).toHaveBeenCalled();
    });
  });

  describe('Formatting Utilities', () => {
    beforeEach(() => {
      fixture.componentRef.setInput('credential', mockCredential);
      fixture.detectChanges();
    });

    it('should parse float values', () => {
      expect(component.parseFloat('123.45')).toBe(123.45);
      expect(component.parseFloat('0')).toBe(0);
    });

    it('should format large numbers with units', () => {
      expect(component.formatNumber(1234)).toBe('1.23K');
      expect(component.formatNumber(1234567)).toBe('1.23M');
      expect(component.formatNumber(1234567890)).toBe('1.23B');
      expect(component.formatNumber(123)).toBe('123.00');
    });

    it('should format currency with decimals', () => {
      expect(component.formatCurrency(123.456)).toBe('123.46');
      expect(component.formatCurrency('789.123', 4)).toBe('789.1230');
      expect(component.formatCurrency('invalid')).toBe('0.00');
    });

    it('should format percentage', () => {
      expect(component.formatPercentage(0.1234)).toBe('12.34%');
      expect(component.formatPercentage('0.05')).toBe('5.00%');
    });

    it('should get correct PnL color class', () => {
      expect(component.getPnlColorClass(100)).toBe('text-success');
      expect(component.getPnlColorClass(-100)).toBe('text-danger');
      expect(component.getPnlColorClass(0)).toBe('text-muted');
      expect(component.getPnlColorClass('invalid')).toBe('text-muted');
    });

    it('should get correct order status class', () => {
      expect(component.getOrderStatusClass('Filled')).toBe('badge-success');
      expect(component.getOrderStatusClass('Cancelled')).toBe('badge-secondary');
      expect(component.getOrderStatusClass('New')).toBe('badge-primary');
      expect(component.getOrderStatusClass('Unknown')).toBe('badge-secondary');
    });

    it('should get correct side class', () => {
      expect(component.getSideClass('Buy')).toBe('badge-success');
      expect(component.getSideClass('Sell')).toBe('badge-danger');
    });

    it('should calculate position profit percentage', () => {
      const position = mockUserInfo.data.positions[0];
      const pct = component.calculatePositionProfitPct(position);
      expect(pct).toBeCloseTo(2.0, 1); // (51000 - 50000) / 50000 * 100 = 2%
    });

    it('should calculate negative profit for sell positions', () => {
      const sellPosition = {
        ...mockUserInfo.data.positions[0],
        side: 'Sell' as const,
        entryPrice: '50000.00',
        markPrice: '51000.00'
      };
      const pct = component.calculatePositionProfitPct(sellPosition);
      expect(pct).toBeCloseTo(-2.0, 1);
    });

    it('should handle invalid position prices', () => {
      const invalidPosition = {
        ...mockUserInfo.data.positions[0],
        entryPrice: '0.00',
        markPrice: '51000.00'
      };
      expect(component.calculatePositionProfitPct(invalidPosition)).toBe(0);
    });
  });

  describe('Edge Cases', () => {
    beforeEach(() => {
      fixture.componentRef.setInput('credential', mockCredential);
      fixture.detectChanges();
    });

    it('should handle empty positions array', () => {
      const emptyInfo = {
        ...mockUserInfo,
        data: { ...mockUserInfo.data, positions: [] }
      };
      mockBybitUserService.getActivePositions.and.returnValue([]);
      component.userInfo.set(emptyInfo);
      expect(component.activePositions()).toEqual([]);
    });

    it('should handle empty orders array', () => {
      const emptyInfo = {
        ...mockUserInfo,
        data: { ...mockUserInfo.data, activeOrders: [] }
      };
      component.userInfo.set(emptyInfo);
      expect(component.activeOrders()).toEqual([]);
    });

    it('should handle missing account info', () => {
      component.userInfo.set(null);
      expect(component.accountInfo()).toBeNull();
    });

    it('should handle account info with error', () => {
      const errorInfo = {
        ...mockUserInfo,
        data: {
          ...mockUserInfo.data,
          accountInfo: { error: 'API Error' }
        }
      };
      component.userInfo.set(errorInfo as any);
      expect(component.accountInfo()).toBeNull();
    });
  });
});

import { Component, OnInit, OnDestroy, signal, computed, input, output, inject } from '@angular/core';
import { CommonModule, DecimalPipe, DatePipe } from '@angular/common';
import { Subject, takeUntil } from 'rxjs';
import {
  BybitUserService,
  BybitUserInfo,
  BybitCoinBalance,
  BybitPosition,
  BybitOrder
} from '../../services/bybit-user.service';
import {
  BingXUserService,
  BingXUserInfo,
  BingXPosition
} from '../../services/bingx-user.service';
import {
  ExchangeCredential,
  ExchangeType,
  getExchangeName,
  getExchangeLogo,
  EXCHANGE_METADATA
} from '../../models/exchange-credentials.model';
import { TranslationService } from '../../services/translation.service';

interface Tab {
  id: string;
  label: string;
  icon: string;
}

/**
 * Trading Platform Info Modal Component
 *
 * Displays comprehensive trading platform information in a modal dialog.
 * Supports multiple exchanges (Bybit, Binance, etc.) with testnet/mainnet environments.
 *
 * Features:
 * - Account overview with wallet balance and equity
 * - Coin balances breakdown
 * - Active positions with PnL
 * - Active orders and order history
 * - Loading and error states
 * - Responsive design
 * - Keyboard navigation (ESC to close)
 * - Click outside to close
 */
import { ButtonComponent } from '../ui/button/button.component';

@Component({
  selector: 'app-trading-platform-info-modal',
  standalone: true,
  imports: [
    CommonModule,
    DecimalPipe,
    DatePipe,
    ButtonComponent
  ],
  templateUrl: './trading-platform-info-modal.component.html',
  styleUrl: './trading-platform-info-modal.component.scss'
})
export class TradingPlatformInfoModalComponent implements OnInit, OnDestroy {
  private destroy$ = new Subject<void>();

  // Input: Exchange credential to display information for
  credential = input.required<ExchangeCredential>();

  // Output: Event emitted when modal should be closed
  closeModal = output<void>();

  // Component state signals
  userInfo = signal<BybitUserInfo | BingXUserInfo | null>(null);
  loading = signal<boolean>(false);
  error = signal<string | null>(null);
  activeTab = signal<string>('overview');

  protected translationService = inject(TranslationService);

  // Tab configuration
  tabs: Tab[] = [
    { id: 'overview', label: 'modal.accountOverview', icon: '' },
    { id: 'balances', label: 'modal.balances', icon: '' },
    { id: 'positions', label: 'modal.positions', icon: '' },
    { id: 'orders', label: 'modal.activeOrders', icon: '' }
  ];

  // Computed values
  readonly exchangeName = computed(() => getExchangeName(this.credential().exchange));
  readonly exchangeLogo = computed(() => getExchangeLogo(this.credential().exchange));
  readonly exchangeColor = computed(() => EXCHANGE_METADATA[this.credential().exchange]?.color);

  readonly totalEquity = computed(() => {
    const info = this.userInfo();
    if (!info || !info.success) return '0.00';

    const accountInfo = info.data.accountInfo;
    if ('error' in accountInfo) return '0.00';

    // Check if it's Bybit or BingX
    if ('totalEquity' in accountInfo) {
      // Bybit
      return accountInfo.totalEquity || '0.00';
    } else if ('balance' in accountInfo) {
      // BingX
      return accountInfo.balance.equity || '0.00';
    }

    return '0.00';
  });

  readonly availableBalance = computed(() => {
    const info = this.userInfo();
    if (!info || !info.success) return '0.00';

    const accountInfo = info.data.accountInfo;
    if ('error' in accountInfo) return '0.00';

    // Check if it's Bybit or BingX
    if ('totalAvailableBalance' in accountInfo) {
      // Bybit
      return accountInfo.totalAvailableBalance || '0.00';
    } else if ('balance' in accountInfo) {
      // BingX
      return accountInfo.balance.availableMargin || '0.00';
    }

    return '0.00';
  });

  readonly walletBalance = computed(() => {
    const info = this.userInfo();
    if (!info || !info.success) return '0.00';

    const accountInfo = info.data.accountInfo;
    if ('error' in accountInfo) return '0.00';

    // Check if it's Bybit or BingX
    if ('totalWalletBalance' in accountInfo) {
      // Bybit
      return accountInfo.totalWalletBalance || '0.00';
    } else if ('balance' in accountInfo) {
      // BingX
      return accountInfo.balance.balance || '0.00';
    }

    return '0.00';
  });

  readonly unrealizedPnl = computed(() => {
    const info = this.userInfo();
    if (!info || !info.success) return 0;

    const cred = this.credential();
    if (cred.exchange === ExchangeType.BYBIT) {
      return this.bybitUserService.calculateUnrealizedPnl(info as BybitUserInfo);
    } else if (cred.exchange === ExchangeType.BINGX) {
      return this.bingxUserService.calculateUnrealizedPnl(info as BingXUserInfo);
    }

    return 0;
  });

  readonly nonZeroBalances = computed(() => {
    const info = this.userInfo();
    if (!info) return [];

    const cred = this.credential();
    if (cred.exchange === ExchangeType.BYBIT) {
      return this.bybitUserService.getNonZeroBalances(info as BybitUserInfo);
    }

    // BingX doesn't have coin balances in the same way, return empty
    return [];
  });

  readonly activePositions = computed(() => {
    const info = this.userInfo();
    if (!info) return [];

    const cred = this.credential();
    if (cred.exchange === ExchangeType.BYBIT) {
      return this.bybitUserService.getActivePositions(info as BybitUserInfo);
    } else if (cred.exchange === ExchangeType.BINGX) {
      return this.bingxUserService.getActivePositions(info as BingXUserInfo);
    }

    return [];
  });

  readonly activeOrders = computed(() => {
    const info = this.userInfo();
    if (!info || !info.success) return [];

    // Bybit has activeOrders, BingX doesn't currently fetch orders
    const data = info.data as any;
    return data.activeOrders || [];
  });

  readonly hasError = computed(() => {
    const info = this.userInfo();
    if (!info) return false;

    const accountInfo = info.data.accountInfo;
    return 'error' in accountInfo;
  });

  readonly accountInfo = computed(() => {
    const info = this.userInfo();
    if (!info || !info.success) return null;

    const accInfo = info.data.accountInfo;
    if ('error' in accInfo) return null;

    return accInfo;
  });

  constructor(
    private bybitUserService: BybitUserService,
    private bingxUserService: BingXUserService
  ) {}

  translate(key: string): string {
    return this.translationService.translate(key);
  }

  ngOnInit(): void {
    // Load user info when modal opens
    this.loadUserInfo();

    // Add keyboard event listener for ESC key
    document.addEventListener('keydown', this.handleKeyDown);
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();

    // Remove keyboard event listener
    document.removeEventListener('keydown', this.handleKeyDown);
  }

  /**
   * Handle keyboard events
   */
  private handleKeyDown = (event: KeyboardEvent): void => {
    if (event.key === 'Escape') {
      this.close();
    }
  };

  /**
   * Load user information from exchange
   * Supports Bybit and BingX exchanges
   */
  loadUserInfo(): void {
    const cred = this.credential();

    this.loading.set(true);
    this.error.set(null);

    if (cred.exchange === ExchangeType.BYBIT) {
      // Subscribe to Bybit service observables
      this.bybitUserService.userInfo$
        .pipe(takeUntil(this.destroy$))
        .subscribe(info => this.userInfo.set(info));

      this.bybitUserService.error$
        .pipe(takeUntil(this.destroy$))
        .subscribe(error => this.error.set(error));

      // Fetch Bybit user info
      this.bybitUserService.getUserInfo().subscribe({
        next: () => {
          this.loading.set(false);
        },
        error: (err) => {
          this.loading.set(false);
          console.error('Failed to load Bybit user info:', err);
        }
      });
    } else if (cred.exchange === ExchangeType.BINGX) {
      // Subscribe to BingX service observables
      this.bingxUserService.userInfo$
        .pipe(takeUntil(this.destroy$))
        .subscribe(info => this.userInfo.set(info));

      this.bingxUserService.error$
        .pipe(takeUntil(this.destroy$))
        .subscribe(error => this.error.set(error));

      // Fetch BingX user info (uses active credentials from backend)
      this.bingxUserService.getUserInfo().subscribe({
        next: () => {
          this.loading.set(false);
        },
        error: (err) => {
          this.loading.set(false);
          console.error('Failed to load BingX user info:', err);
        }
      });
    } else {
      this.error.set(`Exchange ${cred.exchange} is not yet supported`);
      this.loading.set(false);
    }
  }

  /**
   * Refresh user information
   */
  refreshUserInfo(): void {
    this.loadUserInfo();
  }

  /**
   * Change active tab
   */
  setActiveTab(tabId: string): void {
    this.activeTab.set(tabId);
  }

  /**
   * Close modal
   */
  close(): void {
    this.closeModal.emit();
  }

  /**
   * Handle overlay click (close modal)
   */
  handleOverlayClick(): void {
    this.close();
  }

  /**
   * Stop event propagation (prevent closing when clicking modal content)
   */
  handleContentClick(event: Event): void {
    event.stopPropagation();
  }

  /**
   * Parse float helper for templates
   */
  parseFloat(value: string): number {
    return parseFloat(value);
  }

  /**
   * Format large numbers with proper units (K, M, B)
   */
  formatNumber(value: string | number): string {
    const num = typeof value === 'string' ? parseFloat(value) : value;

    if (isNaN(num)) return '0';

    const absNum = Math.abs(num);

    if (absNum >= 1000000000) {
      return (num / 1000000000).toFixed(2) + 'B';
    } else if (absNum >= 1000000) {
      return (num / 1000000).toFixed(2) + 'M';
    } else if (absNum >= 1000) {
      return (num / 1000).toFixed(2) + 'K';
    } else {
      return num.toFixed(2);
    }
  }

  /**
   * Format currency with proper decimals
   */
  formatCurrency(value: string | number, decimals = 2): string {
    const num = typeof value === 'string' ? parseFloat(value) : value;

    if (isNaN(num)) return '0.00';

    return num.toFixed(decimals);
  }

  /**
   * Format percentage
   */
  formatPercentage(value: string | number): string {
    const num = typeof value === 'string' ? parseFloat(value) : value;

    if (isNaN(num)) return '0.00%';

    return (num * 100).toFixed(2) + '%';
  }

  /**
   * Get position PnL color class
   */
  getPnlColorClass(pnl: string | number): string {
    const num = typeof pnl === 'string' ? parseFloat(pnl) : pnl;

    if (isNaN(num) || num === 0) return 'text-muted';
    return num > 0 ? 'text-success' : 'text-danger';
  }

  /**
   * Get order status badge class
   */
  getOrderStatusClass(status: string): string {
    const statusClasses: Record<string, string> = {
      'Filled': 'badge-success',
      'PartiallyFilled': 'badge-info',
      'Cancelled': 'badge-secondary',
      'Rejected': 'badge-danger',
      'New': 'badge-primary',
      'PendingCancel': 'badge-warning'
    };

    return statusClasses[status] || 'badge-secondary';
  }

  /**
   * Get side badge class (Buy/Sell)
   */
  getSideClass(side: string): string {
    return side === 'Buy' ? 'badge-success' : 'badge-danger';
  }

  /**
   * Calculate position profit percentage
   */
  calculatePositionProfitPct(position: BybitPosition | BingXPosition): number {
    // Bybit position
    if ('side' in position && 'entryPrice' in position && 'markPrice' in position) {
      const entryPrice = parseFloat(position.entryPrice);
      const markPrice = parseFloat(position.markPrice);

      if (isNaN(entryPrice) || isNaN(markPrice) || entryPrice === 0) {
        return 0;
      }

      const pct = ((markPrice - entryPrice) / entryPrice) * 100;
      return position.side === 'Buy' ? pct : -pct;
    }

    // BingX position
    if ('positionSide' in position && 'entryPrice' in position && 'markPrice' in position) {
      const entryPrice = parseFloat(position.entryPrice);
      const markPrice = parseFloat(position.markPrice);

      if (isNaN(entryPrice) || isNaN(markPrice) || entryPrice === 0) {
        return 0;
      }

      const pct = ((markPrice - entryPrice) / entryPrice) * 100;
      return position.positionSide === 'LONG' ? pct : -pct;
    }

    return 0;
  }

  /**
   * Get position side for display (handles both Bybit and BingX)
   */
  getPositionSide(position: BybitPosition | BingXPosition): string {
    if ('side' in position) {
      return position.side; // Bybit: 'Buy' or 'Sell'
    }
    if ('positionSide' in position) {
      return position.positionSide; // BingX: 'LONG' or 'SHORT'
    }
    return '';
  }

  /**
   * Get position size for display (handles both Bybit and BingX)
   */
  getPositionSize(position: BybitPosition | BingXPosition): string {
    if ('size' in position) {
      return position.size; // Bybit
    }
    if ('positionAmt' in position) {
      return position.positionAmt; // BingX
    }
    return '0';
  }

  /**
   * Get position unrealized PnL (handles both Bybit and BingX)
   */
  getPositionUnrealizedPnl(position: BybitPosition | BingXPosition): string {
    if ('unrealisedPnl' in position) {
      return position.unrealisedPnl; // Bybit
    }
    if ('unrealizedProfit' in position) {
      return position.unrealizedProfit; // BingX
    }
    return '0';
  }

  /**
   * Get account field value (handles both Bybit and BingX)
   */
  getAccountField(field: string): string {
    const info = this.accountInfo();
    if (!info) return '0.00';

    // Bybit fields
    if (field === 'totalEquity' && 'totalEquity' in info) {
      return info.totalEquity || '0.00';
    }
    if (field === 'totalWalletBalance' && 'totalWalletBalance' in info) {
      return info.totalWalletBalance || '0.00';
    }
    if (field === 'totalAvailableBalance' && 'totalAvailableBalance' in info) {
      return info.totalAvailableBalance || '0.00';
    }
    if (field === 'totalMarginBalance' && 'totalMarginBalance' in info) {
      return info.totalMarginBalance || '0.00';
    }
    if (field === 'totalInitialMargin' && 'totalInitialMargin' in info) {
      return info.totalInitialMargin || '0.00';
    }
    if (field === 'totalPerpUPL' && 'totalPerpUPL' in info) {
      return info.totalPerpUPL || '0.00';
    }

    // BingX fields
    if ('balance' in info) {
      const balance = info.balance;
      if (field === 'totalEquity') return balance.equity || '0.00';
      if (field === 'totalWalletBalance') return balance.balance || '0.00';
      if (field === 'totalAvailableBalance') return balance.availableMargin || '0.00';
      if (field === 'totalMarginBalance') return balance.usedMargin || '0.00';
      if (field === 'totalInitialMargin') return balance.usedMargin || '0.00';
      if (field === 'totalPerpUPL') return balance.unrealizedProfit || '0.00';
    }

    return '0.00';
  }

  /**
   * Clear error message
   */
  clearError(): void {
    this.error.set(null);
  }

  /**
   * Retry loading after error
   */
  retryLoad(): void {
    this.clearError();
    this.loadUserInfo();
  }
}

/**
 * Active Price Arbitrage Positions Component
 *
 * Displays and manages active price arbitrage positions across exchanges.
 * Features:
 * - Real-time position monitoring
 * - Unrealized P&L calculations
 * - Position close functionality
 * - Status filtering
 * - Responsive table layout
 */

import { Component, OnInit, OnDestroy, inject, signal, computed } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { interval, Subscription } from 'rxjs';
import { switchMap } from 'rxjs/operators';

import { PriceArbitrageService } from '../../../services/price-arbitrage.service';
import { TranslationService } from '../../../services/translation.service';
import {
  PriceArbitragePositionDTO,
  PriceArbitrageStatus,
  calculateHoldingTime,
  formatPnlDisplay
} from '../../../models/price-arbitrage.model';

import { CardComponent, CardHeaderComponent, CardTitleComponent, CardContentComponent } from '../../ui/card/card.component';
import { ButtonComponent } from '../../ui/button/button.component';
import { DialogComponent, DialogHeaderComponent, DialogTitleComponent, DialogContentComponent, DialogFooterComponent } from '../../ui/dialog/dialog.component';

@Component({
  selector: 'app-active-positions',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    CardComponent,
    CardHeaderComponent,
    CardTitleComponent,
    CardContentComponent,
    ButtonComponent,
    DialogComponent,
    DialogHeaderComponent,
    DialogTitleComponent,
    DialogContentComponent,
    DialogFooterComponent
  ],
  templateUrl: './active-positions.component.html',
  styleUrl: './active-positions.component.scss'
})
export class ActivePositionsComponent implements OnInit, OnDestroy {
  private priceArbitrageService = inject(PriceArbitrageService);
  private translationService = inject(TranslationService);

  // State signals
  positions = signal<PriceArbitragePositionDTO[]>([]);
  isLoading = signal<boolean>(false);
  error = signal<string | null>(null);
  selectedStatus = signal<'ALL' | PriceArbitrageStatus>(PriceArbitrageStatus.ACTIVE);

  // Close confirmation dialog
  showCloseDialog = signal<boolean>(false);
  positionToClose = signal<PriceArbitragePositionDTO | null>(null);
  isClosing = signal<boolean>(false);

  // Auto-refresh subscription
  private refreshSubscription?: Subscription;
  private readonly REFRESH_INTERVAL = 5000; // 5 seconds

  // Computed properties
  filteredPositions = computed(() => {
    const status = this.selectedStatus();
    const allPositions = this.positions();

    if (status === 'ALL') {
      return allPositions;
    }

    return allPositions.filter(p => p.status === status);
  });

  activeCount = computed(() =>
    this.positions().filter(p => p.status === PriceArbitrageStatus.ACTIVE).length
  );

  completedCount = computed(() =>
    this.positions().filter(p => p.status === PriceArbitrageStatus.COMPLETED).length
  );

  errorCount = computed(() =>
    this.positions().filter(p => p.status === PriceArbitrageStatus.ERROR).length
  );

  ngOnInit(): void {
    this.loadPositions();
    this.startAutoRefresh();
  }

  ngOnDestroy(): void {
    this.stopAutoRefresh();
  }

  /**
   * Load positions from API
   */
  loadPositions(): void {
    this.isLoading.set(true);
    this.error.set(null);

    const status = this.selectedStatus() === 'ALL' ? undefined : this.selectedStatus();

    this.priceArbitrageService.getPositions(status as PriceArbitrageStatus | undefined)
      .subscribe({
        next: (positions) => {
          this.positions.set(positions);
          this.isLoading.set(false);
        },
        error: (err) => {
          console.error('Error loading positions:', err);
          this.error.set(err.message || 'Failed to load positions');
          this.isLoading.set(false);
        }
      });
  }

  /**
   * Start auto-refresh interval
   */
  private startAutoRefresh(): void {
    this.refreshSubscription = interval(this.REFRESH_INTERVAL)
      .pipe(
        switchMap(() => {
          const status = this.selectedStatus() === 'ALL' ? undefined : this.selectedStatus();
          return this.priceArbitrageService.getPositions(status as PriceArbitrageStatus | undefined);
        })
      )
      .subscribe({
        next: (positions) => {
          this.positions.set(positions);
        },
        error: (err) => {
          console.error('Error refreshing positions:', err);
        }
      });
  }

  /**
   * Stop auto-refresh
   */
  private stopAutoRefresh(): void {
    if (this.refreshSubscription) {
      this.refreshSubscription.unsubscribe();
    }
  }

  /**
   * Change status filter
   */
  changeStatusFilter(status: 'ALL' | PriceArbitrageStatus): void {
    this.selectedStatus.set(status);
    this.loadPositions();
  }

  /**
   * Open close confirmation dialog
   */
  openCloseDialog(position: PriceArbitragePositionDTO): void {
    this.positionToClose.set(position);
    this.showCloseDialog.set(true);
  }

  /**
   * Cancel close dialog
   */
  cancelClose(): void {
    this.showCloseDialog.set(false);
    this.positionToClose.set(null);
  }

  /**
   * Confirm and close position
   */
  confirmClose(): void {
    const position = this.positionToClose();
    if (!position) return;

    this.isClosing.set(true);

    this.priceArbitrageService.closePosition(position.id)
      .subscribe({
        next: (result) => {
          console.log('Position closed successfully:', result);

          // Update the position in the list
          const updatedPositions = this.positions().map(p =>
            p.id === position.id ? result.position : p
          );
          this.positions.set(updatedPositions);

          // Close dialog
          this.showCloseDialog.set(false);
          this.positionToClose.set(null);
          this.isClosing.set(false);

          // Show success notification (you can implement toast notifications)
          alert(`Position closed successfully! Total P&L: $${result.totalPnl.toFixed(2)}`);
        },
        error: (err) => {
          console.error('Error closing position:', err);
          this.isClosing.set(false);
          alert(`Failed to close position: ${err.message}`);
        }
      });
  }

  /**
   * Calculate unrealized P&L for display
   */
  getUnrealizedPnl(position: PriceArbitragePositionDTO): number {
    return this.priceArbitrageService.calculateUnrealizedPnl(position);
  }

  /**
   * Get P&L display with color coding
   */
  getPnlDisplay(position: PriceArbitragePositionDTO) {
    const pnl = this.getUnrealizedPnl(position);
    return formatPnlDisplay(pnl);
  }

  /**
   * Get holding time display
   */
  getHoldingTimeDisplay(position: PriceArbitragePositionDTO): string {
    if (!position.openedAt) return 'N/A';
    return calculateHoldingTime(position.openedAt).formatted;
  }

  /**
   * Get current spread display
   */
  getCurrentSpreadDisplay(position: PriceArbitragePositionDTO): string {
    if (position.currentSpreadPercent !== undefined) {
      return `${position.currentSpreadPercent.toFixed(4)}%`;
    }
    return 'N/A';
  }

  /**
   * Get entry spread display
   */
  getEntrySpreadDisplay(position: PriceArbitragePositionDTO): string {
    return `${position.entrySpreadPercent.toFixed(4)}%`;
  }

  /**
   * Get status badge class
   */
  getStatusClass(status: PriceArbitrageStatus): string {
    switch (status) {
      case PriceArbitrageStatus.ACTIVE:
        return 'status-active';
      case PriceArbitrageStatus.COMPLETED:
        return 'status-completed';
      case PriceArbitrageStatus.ERROR:
        return 'status-error';
      case PriceArbitrageStatus.PENDING:
        return 'status-pending';
      case PriceArbitrageStatus.CANCELLED:
        return 'status-cancelled';
      default:
        return 'status-unknown';
    }
  }

  /**
   * Translation helper
   */
  translate(key: string): string {
    return this.translationService.translate(key);
  }

  /**
   * Format price for display
   */
  formatPrice(price: number): string {
    return `$${price.toFixed(2)}`;
  }

  /**
   * Format P&L for display
   */
  formatPnl(pnl: number | undefined): string {
    if (pnl === undefined || pnl === null) return 'N/A';
    const sign = pnl >= 0 ? '+' : '';
    return `${sign}$${pnl.toFixed(2)}`;
  }
}

