    it('should return original value when converting same units', () => {
      const result = (component as any).convertQuantity(1.5, 'coin', 'coin', 'primary');
      expect(result).toBe(1.5);
    });

    it('should handle zero price gracefully', () => {
      component['primaryData'].set({
        exchange: 'BYBIT',
        price: 0,
        fundingRate: '0.0001',
        nextFundingTime: Date.now(),
        lastUpdate: new Date()
      });

      const result = (component as any).convertQuantity(1.5, 'coin', 'usdt', 'primary');
      expect(result).toBe(1.5); // Should return original value when price is 0
    });
  });

  describe('Form Integration and User Experience', () => {
    beforeEach(() => {
      fixture.detectChanges();
    });

    it('should maintain form state during quantity unit changes', fakeAsync(() => {
      component.primaryOrderForm.patchValue({
        side: 'long',
        leverage: 25,
        quantity: 0.5,
        graduatedParts: 8,
        graduatedDelayMs: 5
      });

      // Change quantity unit
      component.setPrimaryQuantityUnit('usdt');
      tick();

      // Form state should be maintained except for converted quantity
      expect(component.primaryOrderForm.get('side')?.value).toBe('long');
      expect(component.primaryOrderForm.get('leverage')?.value).toBe(25);
      expect(component.primaryOrderForm.get('graduatedParts')?.value).toBe(8);
      expect(component.primaryOrderForm.get('graduatedDelayMs')?.value).toBe(5);
    }));

    it('should synchronize unit changes between forms', fakeAsync(() => {
      component.setPrimaryQuantityUnit('usdt');
      tick();

      expect(component.hedgeQuantityUnit()).toBe('usdt');

      component.setHedgeQuantityUnit('coin');
      tick();

      expect(component.primaryQuantityUnit()).toBe('coin');
    }));

    it('should validate both forms after unit conversion', fakeAsync(() => {
      spyOn(component as any, 'validatePrimaryOrder');
      spyOn(component as any, 'validateHedgeOrder');

      component.setPrimaryQuantityUnit('usdt');
      tick();

      expect((component as any).validatePrimaryOrder).toHaveBeenCalled();
      expect((component as any).validateHedgeOrder).toHaveBeenCalled();
    }));
  });

  describe('Error Handling and Edge Cases', () => {
    it('should handle invalid graduated parts gracefully', () => {
      component.primaryOrderForm.patchValue({
        quantity: 1,
        graduatedParts: -1
      });

      expect(component.primaryOrderForm.get('graduatedParts')?.hasError('min')).toBe(true);
    });

    it('should handle extremely large graduated parts', () => {
      component.primaryOrderForm.patchValue({
        quantity: 1,
        graduatedParts: 1000
      });

      expect(component.primaryOrderForm.get('graduatedParts')?.hasError('max')).toBe(true);
    });

    it('should handle decimal graduated delay validation', () => {
      const delayControl = component.primaryOrderForm.get('graduatedDelayMs');
      
      // Test very small delay
      delayControl?.setValue(0.05);
      expect(delayControl?.hasError('min')).toBe(true);

      // Test valid decimal delay
      delayControl?.setValue(1.5);
      expect(delayControl?.valid).toBe(true);

      // Test large delay
      delayControl?.setValue(70);
      expect(delayControl?.hasError('max')).toBe(true);
    });

    it('should handle symbol info loading failures', () => {
      symbolInfoService.getSymbolInfo.and.returnValue(of(null));
      
      // Component should handle null symbol info gracefully
      (component as any).fetchSymbolInfo();
      
      expect(component.primarySymbolInfo()).toBeNull();
      expect(component.hedgeSymbolInfo()).toBeNull();
    });
  });

  describe('Performance and Memory Management', () => {
    it('should prevent memory leaks with proper subscription cleanup', () => {
      // Ensure component initializes subscriptions
      fixture.detectChanges();
      
      // Component should have form subscriptions
      expect(component.primaryOrderForm.get('quantity')?.valueChanges).toBeDefined();
      expect(component.hedgeOrderForm.get('quantity')?.valueChanges).toBeDefined();
      
      // Destroy should clean up without errors
      expect(() => {
        component.ngOnDestroy();
      }).not.toThrow();
    });

    it('should handle rapid form value changes efficiently', fakeAsync(() => {
      const quantityControl = component.primaryOrderForm.get('quantity');
      
      // Simulate rapid changes
      for (let i = 0; i < 10; i++) {
        quantityControl?.setValue(i * 0.1);
        tick(10);
      }

      // Should end up with the last value
      expect(quantityControl?.value).toBe(0.9);
    }));
  });

  describe('Funding Time Calculations - UTC Timezone Safety', () => {
    it('should format funding info with correct UTC timestamps (no timezone offset)', () => {
      // Test case: nextFundingTime at 22:00:00 UTC, current time at 21:30:00 UTC
      const nextFundingTime = Date.UTC(2025, 9, 28, 22, 0, 0); // 22:00:00 UTC
      const currentTime = Date.UTC(2025, 9, 28, 21, 30, 0);    // 21:30:00 UTC

      // Mock Date.now() to return our test current time
      spyOn(Date, 'now').and.returnValue(currentTime);

      const result = component.formatFundingInfo('0.0001', nextFundingTime, '8h');

      // Should show 30 minutes remaining (00:30)
      expect(result).toContain('00:30');
      expect(result).toContain('0.0100%');
      expect(result).toContain('8h');
    });

    it('should handle MEXC hourly funding with correct UTC calculation', () => {
      // MEXC has hourly funding (every hour on the hour)
      // Test: Current time 14:45 UTC -> Next funding at 15:00 UTC (15 minutes)
      const currentTime = Date.UTC(2025, 9, 28, 14, 45, 0);
      const expectedNextFunding = Date.UTC(2025, 9, 28, 15, 0, 0);

      spyOn(Date, 'now').and.returnValue(currentTime);

      const result = component.formatFundingInfo('0.0001', expectedNextFunding, '1h');

      // Should show 15 minutes remaining (00:15)
      expect(result).toContain('00:15');
      expect(result).toContain('1h');
    });

    it('should correctly calculate time remaining across hour boundary', () => {
      // Test: Current time 23:45 UTC -> Next funding at 00:00 UTC next day (15 minutes)
      const currentTime = Date.UTC(2025, 9, 28, 23, 45, 0);
      const nextFundingTime = Date.UTC(2025, 9, 29, 0, 0, 0); // Next day

      spyOn(Date, 'now').and.returnValue(currentTime);

      const result = component.formatFundingInfo('0.0001', nextFundingTime, '8h');

      // Should show 15 minutes remaining (00:15), NOT 03:15 or other timezone offset
      expect(result).toContain('00:15');
    });

    it('should show correct hours when funding is more than 1 hour away', () => {
      // Test: Current time 20:15 UTC -> Next funding at 22:00 UTC (1h 45m = 105 minutes)
      const currentTime = Date.UTC(2025, 9, 28, 20, 15, 0);
      const nextFundingTime = Date.UTC(2025, 9, 28, 22, 0, 0);

      spyOn(Date, 'now').and.returnValue(currentTime);

      const result = component.formatFundingInfo('0.0001', nextFundingTime, '8h');

      // Should show 1 hour 45 minutes (01:45)
      expect(result).toContain('01:45');
    });

    it('should handle funding time in the past (show 00:00)', () => {
      // Test: Funding time has already passed
      const currentTime = Date.UTC(2025, 9, 28, 22, 5, 0);
      const nextFundingTime = Date.UTC(2025, 9, 28, 22, 0, 0); // 5 minutes ago

      spyOn(Date, 'now').and.returnValue(currentTime);

      const result = component.formatFundingInfo('0.0001', nextFundingTime, '8h');

      // Should show 00:00 when funding time has passed
      expect(result).toContain('00:00');
    });

    it('should NOT add timezone offset to calculated time (regression test)', () => {
      // Regression test for the bug where timezone offset was added
      // Test in UTC+3 timezone scenario: 21:30 UTC -> 22:00 UTC should show 00:30, NOT 03:30
      const currentTime = Date.UTC(2025, 9, 28, 21, 30, 0);
      const nextFundingTime = Date.UTC(2025, 9, 28, 22, 0, 0);

      spyOn(Date, 'now').and.returnValue(currentTime);

      const result = component.formatFundingInfo('0.0001', nextFundingTime, '8h');

      // CRITICAL: Should be 00:30, NOT 03:30 (which would happen if timezone offset is added)
      expect(result).toContain('00:30');
      expect(result).not.toContain('03:30');
      expect(result).not.toContain('02:30');
    });

    it('should handle different funding intervals correctly', () => {
      const currentTime = Date.UTC(2025, 9, 28, 21, 30, 0);
      const nextFundingTime = Date.UTC(2025, 9, 28, 22, 0, 0);

      spyOn(Date, 'now').and.returnValue(currentTime);

      // Test with 8h interval
      const result8h = component.formatFundingInfo('0.0001', nextFundingTime, '8h');
      expect(result8h).toContain('8h');

      // Test with 1h interval (MEXC)
      const result1h = component.formatFundingInfo('0.0001', nextFundingTime, '1h');
      expect(result1h).toContain('1h');

      // Test with 4h interval
      const result4h = component.formatFundingInfo('0.0001', nextFundingTime, '4h');
      expect(result4h).toContain('4h');
    });

    it('should return N/A when nextFundingTime is 0 or undefined', () => {
      const result1 = component.formatFundingInfo('0.0001', 0, '8h');
      expect(result1).toBe('N/A');

      const result2 = component.formatFundingInfo('', 0, '8h');
      expect(result2).toBe('N/A');
    });

    it('should format funding rate as percentage correctly', () => {
      const nextFundingTime = Date.UTC(2025, 9, 28, 22, 0, 0);
      const currentTime = Date.UTC(2025, 9, 28, 21, 30, 0);

      spyOn(Date, 'now').and.returnValue(currentTime);

      // Positive funding rate
      const resultPositive = component.formatFundingInfo('0.0001', nextFundingTime, '8h');
      expect(resultPositive).toContain('0.0100%');

      // Negative funding rate
      const resultNegative = component.formatFundingInfo('-0.0001', nextFundingTime, '8h');
      expect(resultNegative).toContain('-0.0100%');

      // Zero funding rate
      const resultZero = component.formatFundingInfo('0', nextFundingTime, '8h');
      expect(resultZero).toContain('0.0000%');
    });
  });
});

