      it('should reject even single part below Gate.io minimum', () => {
        const result = symbolInfoService.validateOrderQuantity(
          exchangeConfigs.gateio,
          0.5,
          1
        );

        expect(result.valid).toBe(false);
        expect(result.error).toContain('Each order part (0.5) is below minimum (1)');
        expect(result.suggestion).toBe('Increase total quantity to at least 1');
      });

      it('should allow high-precision trades on MEXC', () => {
        // Scenario: MEXC allows very small quantities with many parts
        const result = symbolInfoService.validateOrderQuantity(
          exchangeConfigs.mexc,
          0.01,
          20
        );

        expect(result.valid).toBe(true);
        // Each part: 0.01 / 20 = 0.0005, which is >= 0.0001
      });
    });

    describe('Edge Cases and Boundary Conditions', () => {
      it('should handle quantity exactly at minimum boundary', () => {
        // Test: Total quantity exactly equals minimum * parts
        const result = symbolInfoService.validateOrderQuantity(
          exchangeConfigs.bybit,
          0.005,
          5
        );

        expect(result.valid).toBe(true);
        // Each part: 0.005 / 5 = 0.001, exactly equals minimum
      });

      it('should calculate optimal parts suggestion correctly', () => {
        // Test: Suggest reducing parts to maximum feasible
        const result = symbolInfoService.validateOrderQuantity(
          exchangeConfigs.bybit,
          0.007,
          15
        );

        expect(result.valid).toBe(false);
        // Max parts: floor(0.007 / 0.001) = 7
        expect(result.suggestion).toContain('reduce graduated parts to 7');
      });

      it('should handle maximum order quantity constraints', () => {
        // Test: Order part exceeds exchange maximum
        const result = symbolInfoService.validateOrderQuantity(
          exchangeConfigs.bybit,
          5000,
          2
        );

        expect(result.valid).toBe(false);
        expect(result.error).toContain('exceeds maximum (1000)');
        expect(result.suggestion).toContain('Decrease quantity or increase graduated parts');
      });

      it('should validate quantity step requirements', () => {
        // Test: Quantity not divisible by step size
        const result = symbolInfoService.validateOrderQuantity(
          exchangeConfigs.bingx,
          0.157,
          3
        );

        expect(result.valid).toBe(false);
        expect(result.error).toContain('must be a multiple of 0.01');
        expect(result.suggestion).toMatch(/Adjust total quantity to \d+\.\d+/);
      });
    });

    describe('Floating Point Precision Handling', () => {
      it('should handle floating point arithmetic correctly', () => {
        // Test: Floating point division that should work
        const result = symbolInfoService.validateOrderQuantity(
          exchangeConfigs.bybit,
          0.003,
          3
        );

        expect(result.valid).toBe(true);
        // Each part: 0.003 / 3 = 0.001, should be valid despite floating point
      });

      it('should use tolerance for step validation', () => {
        // Test: Very small floating point differences should be tolerated
        const customSymbol = {
          ...exchangeConfigs.bybit,
          qtyStep: 0.001,
          qtyPrecision: 6
        };

        const result = symbolInfoService.validateOrderQuantity(
          customSymbol,
          0.003001,
          3
        );

        expect(result.valid).toBe(true);
        // Each part: 0.003001 / 3 = 0.001000333..., should pass with tolerance
      });
    });
  });

  describe('Real-World Trading Scenarios', () => {
    interface TradeScenario {
      name: string;
      exchange: keyof typeof exchangeConfigs;
      totalQuantity: number;
      graduatedParts: number;
      expectedValid: boolean;
      expectedError?: string;
      expectedSuggestion?: string;
    }

    const scenarios: TradeScenario[] = [
      {
        name: 'Small retail arbitrage on Bybit',
        exchange: 'bybit',
        totalQuantity: 0.05,
        graduatedParts: 5,
        expectedValid: true
      },
      {
        name: 'Micro-trading test order',
        exchange: 'bybit',
        totalQuantity: 0.001,
        graduatedParts: 3,
        expectedValid: false,
        expectedError: 'below minimum',
        expectedSuggestion: 'reduce graduated parts to 1'
      },
      {
        name: 'Medium BingX arbitrage position',
        exchange: 'bingx',
        totalQuantity: 2.5,
        graduatedParts: 10,
        expectedValid: true
      },
      {
        name: 'BingX position with too many parts',
        exchange: 'bingx',
        totalQuantity: 0.5,
        graduatedParts: 20,
        expectedValid: false,
        expectedError: 'below minimum',
        expectedSuggestion: 'reduce graduated parts'
      },
      {
        name: 'Gate.io large position',
        exchange: 'gateio',
        totalQuantity: 100,
        graduatedParts: 10,
        expectedValid: true
      },
      {
        name: 'Gate.io insufficient total quantity',
        exchange: 'gateio',
        totalQuantity: 3,
        graduatedParts: 5,
        expectedValid: false,
        expectedError: 'below minimum',
        expectedSuggestion: 'reduce graduated parts to 3'
      },
      {
        name: 'MEXC high-frequency strategy',
        exchange: 'mexc',
        totalQuantity: 0.1,
        graduatedParts: 100,
        expectedValid: true
      },
      {
        name: 'MEXC ultra-micro position',
        exchange: 'mexc',
        totalQuantity: 0.0005,
        graduatedParts: 10,
        expectedValid: false,
        expectedError: 'below minimum',
        expectedSuggestion: 'reduce graduated parts to 5'
      }
    ];

    scenarios.forEach(scenario => {
      it(`should handle: ${scenario.name}`, () => {
        const config = exchangeConfigs[scenario.exchange];
        const result = symbolInfoService.validateOrderQuantity(
          config,
          scenario.totalQuantity,
          scenario.graduatedParts
        );

        expect(result.valid).toBe(scenario.expectedValid);

        if (!scenario.expectedValid) {
          if (scenario.expectedError) {
            expect(result.error).toContain(scenario.expectedError);
          }
          if (scenario.expectedSuggestion) {
            expect(result.suggestion).toContain(scenario.expectedSuggestion);
          }
        }
      });
    });
  });

  describe('Form Control Integration Patterns', () => {
    it('should integrate with Angular FormControl validation', () => {
      // Simulate real form control usage
      const quantityControl = new FormControl(0.001);
      const partsControl = new FormControl(5);

      // Create validator function
      const spotFuturesValidator = () => {
        const quantity = quantityControl.value;
        const parts = partsControl.value;

        if (!quantity || !parts) return null;

        const result = symbolInfoService.validateOrderQuantity(
          exchangeConfigs.bybit,
          quantity,
          parts
        );

        return result.valid ? null : {
          spotFutures: {
            message: result.error,
            suggestion: result.suggestion
          }
        };
      };

      // Test invalid case
      const invalidResult = spotFuturesValidator();
      expect(invalidResult).not.toBeNull();
      expect(invalidResult?.spotFutures.message).toContain('below minimum');

      // Test valid case
      quantityControl.setValue(0.1);
      const validResult = spotFuturesValidator();
      expect(validResult).toBeNull();
    });

    it('should provide helpful error messages for UI display', () => {
      const result = symbolInfoService.validateOrderQuantity(
        exchangeConfigs.gateio,
        5,
        10
      );

      expect(result.valid).toBe(false);
      expect(result.error).toMatch(/Each order part \(\d+(\.\d+)?\) is below minimum \(\d+\)/);
      expect(result.suggestion).toMatch(/Increase total quantity to at least \d+ or reduce graduated parts to \d+/);

      // Verify the error message is user-friendly
      expect(result.error).toContain('Each order part');
      expect(result.error).toContain('is below minimum');
      expect(result.suggestion).toContain('Increase total quantity');
      expect(result.suggestion).toContain('reduce graduated parts');
    });
  });

  describe('Performance Considerations', () => {
    it('should handle rapid validation calls efficiently', () => {
      const startTime = performance.now();

      // Simulate rapid form changes
      for (let i = 0; i < 100; i++) {
        symbolInfoService.validateOrderQuantity(
          exchangeConfigs.bybit,
          0.01 + (i * 0.001),
          5 + (i % 10)
        );
      }

      const endTime = performance.now();
      const duration = endTime - startTime;

      // Should complete 100 validations in reasonable time
      expect(duration).toBeLessThan(100); // Less than 100ms
    });

    it('should handle extreme values without errors', () => {
      // Test very large numbers
      const largeResult = symbolInfoService.validateOrderQuantity(
        exchangeConfigs.mexc,
        1000000,
        1
      );
      expect(largeResult.valid).toBe(true);

      // Test very small numbers
      const smallResult = symbolInfoService.validateOrderQuantity(
        exchangeConfigs.mexc,
        0.0000001,
        1
      );
      expect(smallResult.valid).toBe(false);
      expect(smallResult.error).toContain('below minimum');

      // Test zero values
      const zeroResult = symbolInfoService.validateOrderQuantity(
        exchangeConfigs.bybit,
        0,
        5
      );
      expect(zeroResult.valid).toBe(false);
    });
  });

  describe('Error Recovery and User Guidance', () => {
    it('should provide actionable suggestions for common errors', () => {
      // Test case: User sets too many parts for their quantity
      const result = symbolInfoService.validateOrderQuantity(
        exchangeConfigs.bybit,
        0.01,
        20
      );

      expect(result.valid).toBe(false);
      expect(result.suggestion).toContain('reduce graduated parts to 10');

      // The suggestion should be mathematically correct
      const suggestedParts = 10; // floor(0.01 / 0.001) = 10
      const testResult = symbolInfoService.validateOrderQuantity(
        exchangeConfigs.bybit,
        0.01,
        suggestedParts
      );
      expect(testResult.valid).toBe(true);
    });

    it('should handle edge cases gracefully', () => {
      // Test null/undefined symbol info
      const nullResult = symbolInfoService.validateOrderQuantity(
        null as any,
        1,
        5
      );
      expect(nullResult.valid).toBe(false);
      expect(nullResult.error).toBe('Symbol information not available');

      // Test division by zero
      const zeroPartsResult = symbolInfoService.validateOrderQuantity(
        exchangeConfigs.bybit,
        1,
        0
      );
      expect(zeroPartsResult.valid).toBe(false);
      // Should not throw error
    });
  });
});

import { Component, Input, Output, EventEmitter, OnInit, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';

import { InputComponent } from '../../ui/input/input.component';
import { SelectComponent } from '../../ui/select/select.component';
import { ButtonComponent } from '../../ui/button/button.component';
import { CardComponent, CardHeaderComponent, CardTitleComponent, CardContentComponent } from '../../ui/card/card.component';
import { LightweightChartComponent, GridConfig } from '../../lightweight-chart/lightweight-chart.component';
import { EntryFilterComponent, TradingFilter } from '../entry-filter/entry-filter.component';
import { TranslationService } from '../../../services/translation.service';

@Component({
  selector: 'app-bot-config-form',
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule,
    InputComponent,
    SelectComponent,
    ButtonComponent,
    CardComponent,
    CardHeaderComponent,
    CardTitleComponent,
    CardContentComponent,
    LightweightChartComponent,
    EntryFilterComponent,
  ],
  templateUrl: './bot-config-form.component.html',
  styleUrls: ['./bot-config-form.component.css']
})
export class BotConfigFormComponent implements OnInit {
  @Input() mode: 'create' | 'edit' | 'view' = 'create';
  @Input() botData?: any;
  @Input() strategyData?: any;
  @Output() save = new EventEmitter<any>();
  @Output() cancel = new EventEmitter<void>();
  @Output() edit = new EventEmitter<any>();

  configForm: FormGroup;
  symbols: string[] = ['BTC/USDT', 'ETH/USDT', 'ADA/USDT', 'DOT/USDT', 'MATIC/USDT'];
  symbolOptions: any[] = [];
  strategyOptions: any[] = [
    { value: 'REGULAR', label: 'Regular (Linear)' },
    { value: 'FIBONACCI', label: 'Fibonacci' },
    { value: 'LOGARITHMIC', label: 'Logarithmic' },
    { value: 'MULTIPLICATOR', label: 'Multiplicator' },
    { value: 'MARTINGALE', label: 'Martingale' }
  ];
  currentGridConfig?: GridConfig;
  entryFilters: TradingFilter[] = [];
  exitFilters: TradingFilter[] = [];

  // Grid overlay properties removed

  // Resizable layout properties
  chartWidth = 65;
  chartHeight = 600;
  formWidth = 35;
  private isResizing = false;
  private startX = 0;
  private startChartWidth = 0;

  protected translationService = inject(TranslationService);

  constructor(private fb: FormBuilder) {
    this.configForm = this.createForm();
  }

  translate(key: string): string {
    return this.translationService.translate(key);
  }

  ngOnInit() {
    this.symbolOptions = this.symbols.map(s => ({ value: s, label: s }));
    this.populateFormData();
    this.updateGridConfig();
    this.setupFormValueChanges();
    this.setupResizeListeners();
  }

  private createForm(): FormGroup {
    return this.fb.group({
      name: ['', [Validators.required, Validators.minLength(3)]],
      symbol: ['BTC/USDT', Validators.required],
      gridCount: [10, [Validators.required, Validators.min(2), Validators.max(100)]],
      baseOrderSize: [10, [Validators.required, Validators.min(0.01)]],
      upperBound: [50000, [Validators.required, Validators.min(0)]],
      lowerBound: [30000, [Validators.required, Validators.min(0)]],
      strategyType: ['REGULAR', Validators.required]
    });
  }

  private populateFormData() {
    if (this.mode === 'edit' && this.botData) {
      this.configForm.patchValue({
        name: this.botData.name,
        symbol: this.botData.symbol,
        gridCount: this.botData.config?.gridStrategy?.gridCount || 10,
        baseOrderSize: this.botData.config?.riskManagement?.baseOrderSize || 10,
        upperBound: this.botData.config?.gridRange?.upperBound || 50000,
        lowerBound: this.botData.config?.gridRange?.lowerBound || 30000,
        strategyType: this.botData.config?.gridStrategy?.type || 'REGULAR'
      });

      // Load existing filters
      this.entryFilters = this.botData.config?.entryFilters || [];
      this.exitFilters = this.botData.config?.exitFilters || [];
    }
  }

  private setupFormValueChanges() {
    // Update grid configuration whenever form values change
    this.configForm.valueChanges.subscribe(() => {
      this.updateGridConfig();
    });
  }

  private updateGridConfig() {
    const formValue = this.configForm.value;

    if (formValue.symbol && formValue.upperBound && formValue.lowerBound && formValue.gridCount) {
      this.currentGridConfig = {
        symbol: formValue.symbol,
        upperBound: formValue.upperBound,
        lowerBound: formValue.lowerBound,
        gridCount: formValue.gridCount,
        gridSpacing: 1.0, // Will be calculated based on strategy
        strategyType: formValue.strategyType || 'REGULAR'
      };

      // Grid calculation removed
    }
  }

  onSave() {
    if (this.configForm.valid) {
      const formValue = this.configForm.value;

      const botConfig = {
        id: this.botData?.id,
        name: formValue.name,
        symbol: formValue.symbol,
        config: {
          symbol: formValue.symbol,
          baseAsset: formValue.symbol.split('/')[0] || 'BTC',
          quoteAsset: formValue.symbol.split('/')[1] || 'USDT',
          gridStrategy: {
            type: formValue.strategyType || 'REGULAR',
            gridCount: formValue.gridCount,
            gridSpacing: 1.0
          },
          gridRange: {
            upperBound: formValue.upperBound,
            lowerBound: formValue.lowerBound,
            autoAdjust: false
          },
          entryFilters: this.entryFilters,
          exitFilters: this.exitFilters,
          riskManagement: {
            baseOrderSize: formValue.baseOrderSize,
            maxPositionSize: formValue.baseOrderSize * formValue.gridCount,
            maxOpenOrders: formValue.gridCount
          }
        }
      };

      this.save.emit(botConfig);
    } else {
      Object.keys(this.configForm.controls).forEach(key => {
        const control = this.configForm.get(key);
        control?.markAsTouched();
      });
    }
  }

  onCancel() {
    this.cancel.emit();
  }

  onEdit() {
    this.edit.emit(this.botData);
  }

  onEntryFiltersChange(filters: TradingFilter[]) {
    this.entryFilters = filters;
  }

  onExitFiltersChange(filters: TradingFilter[]) {
    this.exitFilters = filters;
  }

  // Resizable layout methods
  startResize(event: MouseEvent | TouchEvent) {
    this.isResizing = true;
    this.startX = this.getClientX(event);
    this.startChartWidth = this.chartWidth;

    event.preventDefault();
    event.stopPropagation();
  }

  private setupResizeListeners() {
    document.addEventListener('mousemove', this.handleResize.bind(this));
    document.addEventListener('mouseup', this.endResize.bind(this));
    document.addEventListener('touchmove', this.handleResize.bind(this));
    document.addEventListener('touchend', this.endResize.bind(this));
  }

  private handleResize(event: MouseEvent | TouchEvent) {
    if (!this.isResizing) return;

    const currentX = this.getClientX(event);
    const deltaX = currentX - this.startX;
    const containerWidth = window.innerWidth - 48; // Account for padding
    const deltaPercent = (deltaX / containerWidth) * 100;

    let newChartWidth = this.startChartWidth + deltaPercent;

    // Constrain between 30% and 80%
    newChartWidth = Math.max(30, Math.min(80, newChartWidth));

    this.chartWidth = newChartWidth;
    this.formWidth = 100 - newChartWidth;

    event.preventDefault();
  }

  private endResize() {
    this.isResizing = false;
  }

  // Chart-related methods
  getSelectedSymbol(): string {
    const symbol = this.configForm.get('symbol')?.value || 'BTC/USDT';
    return symbol.replace('/', '');
  }

  onChartSymbolChange(symbol: string) {
    // Update form when chart symbol changes
    const formattedSymbol = symbol.includes('/') ? symbol : symbol.replace('USDT', '/USDT');
    this.configForm.patchValue({ symbol: formattedSymbol });
    this.updateGridConfig();
  }

  // Grid calculation methods removed as grid overlay is no longer needed

  private getClientX(event: MouseEvent | TouchEvent): number {
    if (event instanceof MouseEvent) {
      return event.clientX;
    } else {
      return event.touches[0]?.clientX || 0;
    }
  }
}

import { Component, OnInit, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ActivatedRoute, Router } from '@angular/router';
import { BotConfigFormComponent } from '../bot-config-form/bot-config-form.component';
import { ButtonComponent } from '../../ui/button/button.component';
import { CardComponent, CardHeaderComponent, CardTitleComponent, CardContentComponent } from '../../ui/card/card.component';
import { GridBotService } from '../../../services/grid-bot.service';
import { TranslationService } from '../../../services/translation.service';

@Component({
  selector: 'app-bot-config-page',
  standalone: true,
  imports: [
    CommonModule,
    BotConfigFormComponent,
    ButtonComponent,
    CardComponent,
    CardHeaderComponent,
    CardTitleComponent,
    CardContentComponent
  ],
  templateUrl: './bot-config-page.component.html',
  styleUrl: './bot-config-page.component.scss'
})
export class BotConfigPageComponent implements OnInit {
  mode: 'create' | 'edit' | 'view' = 'create';
  botData?: any;
  strategyData?: any;
  loading = false;
  error: string | null = null;
  pageTitle = 'Create New Bot';

  protected translationService = inject(TranslationService);

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private gridBotService: GridBotService
  ) {}

  translate(key: string): string {
    return this.translationService.translate(key);
  }

  ngOnInit() {
    this.route.params.subscribe(params => {
      const botId = params['id'];
      const mode = this.route.snapshot.data['mode'] || 'create';

      this.mode = mode;
      this.updatePageTitle();

      if (botId && (mode === 'edit' || mode === 'view')) {
        this.loadBot(botId);
      }
    });

    // Check for query parameters (strategy data, etc.)
    this.route.queryParams.subscribe(queryParams => {
      if (queryParams['strategy']) {
        try {
          this.strategyData = JSON.parse(queryParams['strategy']);
        } catch (e) {
          console.warn('Invalid strategy data in query params');
        }
      }
    });
  }

  private updatePageTitle() {
    switch (this.mode) {
      case 'create':
        this.pageTitle = this.translate('dashboard.createBot');
        break;
      case 'edit':
        this.pageTitle = this.translate('bot.editConfiguration');
        break;
      case 'view':
        this.pageTitle = this.translate('bot.details');
        break;
    }
  }

  private loadBot(botId: string) {
    this.loading = true;
    this.error = null;

    this.gridBotService.getBot(botId).subscribe({
      next: (bot) => {
        this.botData = bot;
        this.loading = false;

        if (this.mode === 'view' || this.mode === 'edit') {
          this.pageTitle = `${this.mode === 'view' ? this.translate('bot.details') : this.translate('button.edit')} - ${bot.name || bot.symbol}`;
        }
      },
      error: (error) => {
        this.error = this.translate('bot.errorConfiguration');
        this.loading = false;
        console.error('Failed to load bot:', error);
      }
    });
  }

  onBotSave(botData: any) {
    this.loading = true;
    this.error = null;

    if (this.mode === 'edit' && botData.id) {
      this.gridBotService.updateBot(botData.id, 'update', botData.config).subscribe({
        next: (response) => {
          this.loading = false;
          console.log('Bot updated successfully:', response);
          this.router.navigate(['/trading']);
        },
        error: (error) => {
          this.loading = false;
          this.error = this.translate('bot.failedToUpdate');
          console.error('Failed to update bot:', error);
        }
      });
    } else {
      this.gridBotService.createBot(botData).subscribe({
        next: (response) => {
          this.loading = false;
          console.log('Bot created successfully:', response);
          this.router.navigate(['/trading']);
        },
        error: (error) => {
          this.loading = false;
          this.error = this.translate('bot.failedToCreate');
          console.error('Failed to create bot:', error);
        }
      });
    }
  }

  onBotCancel() {
    this.goBack();
  }

  onBotEdit(botData: any) {
    // Switch to edit mode
    this.mode = 'edit';
    this.updatePageTitle();
  }

  goBack() {
    this.router.navigate(['/trading']);
  }

  retry() {
    const botId = this.route.snapshot.params['id'];
    if (botId) {
      this.loadBot(botId);
    } else {
      this.error = null;
    }
  }
}

