}

/**
 * Start Arbitrage Modal Component
 *
 * Modal dialog for configuring and starting a new price arbitrage position.
 * Features:
 * - Display opportunity details (symbol, exchanges, prices, spread)
 * - Configure leverage and margin for both exchanges
 * - Advanced settings: target spread, stop loss, max holding time
 * - Real-time validation and error handling
 * - Success/error feedback
 */

import { Component, inject, signal, computed, effect, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule, ReactiveFormsModule, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatDialogRef, MAT_DIALOG_DATA, MatDialogModule } from '@angular/material/dialog';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatSliderModule } from '@angular/material/slider';
import { MatButtonModule } from '@angular/material/button';
import { MatProgressSpinnerModule } from '@angular/material/progress-spinner';
import { MatExpansionModule } from '@angular/material/expansion';
import { MatTooltipModule } from '@angular/material/tooltip';
import { MatIconModule } from '@angular/material/icon';

import { PriceArbitrageService } from '../../../services/price-arbitrage.service';
import { TranslationService } from '../../../services/translation.service';
import { PriceArbitrageOpportunity, StartPriceArbitrageParams } from '../../../models/price-arbitrage.model';

export interface StartArbitrageDialogData {
  opportunity: PriceArbitrageOpportunity;
}

@Component({
  selector: 'app-start-arbitrage-modal',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    ReactiveFormsModule,
    MatDialogModule,
    MatFormFieldModule,
    MatInputModule,
    MatSliderModule,
    MatButtonModule,
    MatProgressSpinnerModule,
    MatExpansionModule,
    MatTooltipModule,
    MatIconModule
  ],
  templateUrl: './start-arbitrage-modal.component.html',
  styleUrls: ['./start-arbitrage-modal.component.scss']
})
export class StartArbitrageModalComponent implements OnInit {
  // Injected services
  private dialogRef = inject(MatDialogRef<StartArbitrageModalComponent>);
  private data = inject<StartArbitrageDialogData>(MAT_DIALOG_DATA);
  private fb = inject(FormBuilder);
  private priceArbitrageService = inject(PriceArbitrageService);
  private translationService = inject(TranslationService);

  // Opportunity data
  opportunity = signal<PriceArbitrageOpportunity>(this.data.opportunity);

  // Form state
  form!: FormGroup;
  isSubmitting = signal<boolean>(false);
  errorMessage = signal<string | null>(null);
  successMessage = signal<string | null>(null);

  // Advanced settings expansion state
  showAdvancedSettings = signal<boolean>(false);

  // Computed values
  spreadPercent = computed(() => {
    const opp = this.opportunity();
    return (opp.spread * 100).toFixed(4);
  });

  spreadClass = computed(() => {
    const opp = this.opportunity();
    if (opp.spread >= 0.02) return 'spread-high';
    if (opp.spread >= 0.01) return 'spread-medium';
    return 'spread-low';
  });

  // Translation helper
  translate(key: string): string {
    return this.translationService.translate(key);
  }

  ngOnInit(): void {
    this.initializeForm();
  }

  /**
   * Initialize the form with validators
   */
  private initializeForm(): void {
    this.form = this.fb.group({
      // Primary exchange settings
      primaryLeverage: [10, [Validators.required, Validators.min(1), Validators.max(100)]],
      primaryMargin: [100, [Validators.required, Validators.min(10)]],

      // Hedge exchange settings
      hedgeLeverage: [10, [Validators.required, Validators.min(1), Validators.max(100)]],
      hedgeMargin: [100, [Validators.required, Validators.min(10)]],

      // Advanced settings (optional)
      targetSpread: [null, [Validators.min(0), Validators.max(100)]],
      stopLoss: [null, [Validators.min(0), Validators.max(100)]],
      maxHoldingTime: [null, [Validators.min(1)]]
    });
  }

  /**
   * Format leverage value for slider display
   */
  formatLeverageLabel(value: number): string {
    return `${value}x`;
  }

  /**
   * Handle form submission
   */
  async onSubmit(): Promise<void> {
    if (this.form.invalid || this.isSubmitting()) {
      this.form.markAllAsTouched();
      return;
    }

    this.isSubmitting.set(true);
    this.errorMessage.set(null);
    this.successMessage.set(null);

    try {
      const formValue = this.form.value;
      const opp = this.opportunity();

      // Build params for API
      const params: StartPriceArbitrageParams = {
        symbol: opp.symbol,

        // Primary exchange (higher price - SHORT position)
        primaryExchange: opp.primaryExchange.name,
        primaryCredentialId: opp.primaryExchange.credentialId,
        primaryLeverage: formValue.primaryLeverage,
        primaryMargin: formValue.primaryMargin,

        // Hedge exchange (lower price - LONG position)
        hedgeExchange: opp.hedgeExchange.name,
        hedgeCredentialId: opp.hedgeExchange.credentialId,
        hedgeLeverage: formValue.hedgeLeverage,
        hedgeMargin: formValue.hedgeMargin,

        // Entry prices
        entryPrimaryPrice: opp.primaryExchange.price,
        entryHedgePrice: opp.hedgeExchange.price,

        // Optional advanced settings
        ...(formValue.targetSpread && { targetSpread: formValue.targetSpread / 100 }),
        ...(formValue.stopLoss && { stopLoss: formValue.stopLoss / 100 }),
        ...(formValue.maxHoldingTime && { maxHoldingTime: formValue.maxHoldingTime * 3600 }) // Convert hours to seconds
      };

      // Call the service
      const result = await this.priceArbitrageService.startArbitrage(params).toPromise();

      // Success!
      this.successMessage.set(this.translate('arbitrage.startSuccess'));

      // Close modal after short delay
      setTimeout(() => {
        this.dialogRef.close({ success: true, position: result });
      }, 1500);

    } catch (error: any) {
      console.error('Failed to start arbitrage:', error);
      this.errorMessage.set(error.message || this.translate('arbitrage.startError'));
    } finally {
      this.isSubmitting.set(false);
    }
  }

  /**
   * Close the modal without starting arbitrage
   */
  onCancel(): void {
    this.dialogRef.close({ success: false });
  }

  /**
   * Toggle advanced settings panel
   */
  toggleAdvancedSettings(): void {
    this.showAdvancedSettings.update(val => !val);
  }

  /**
   * Get form control error message
   */
  getErrorMessage(controlName: string): string {
    const control = this.form.get(controlName);
    if (!control || !control.errors) return '';

    if (control.errors['required']) {
      return this.translate('validation.required');
    }
    if (control.errors['min']) {
      return this.translate('validation.minValue').replace('{value}', control.errors['min'].min);
    }
    if (control.errors['max']) {
      return this.translate('validation.maxValue').replace('{value}', control.errors['max'].max);
    }

    return '';
  }

  /**
   * Check if form control is invalid and touched
   */
  isFieldInvalid(controlName: string): boolean {
    const control = this.form.get(controlName);
    return !!(control && control.invalid && control.touched);
  }
}

/**
 * Start Bybit Funding Strategy Modal Component
 *
 * Modal dialog for configuring and starting a Bybit funding rate strategy.
 * Features:
 * - Display symbol info (funding rate, time until funding)
 * - Configure strategy type (precise timing vs regular)
 * - Select credentials and configure position parameters
 * - Set leverage, margin, position side, TP/SL
 * - Advanced settings for precise timing strategy
 * - Real-time validation and calculated expected results
 * - Success/error feedback
 */

import {
  Component,
  Input,
  Output,
  EventEmitter,
  inject,
  signal,
  computed,
  OnInit,
} from '@angular/core';
import { CommonModule } from '@angular/common';
import { ReactiveFormsModule, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { firstValueFrom } from 'rxjs';
import {
  DialogComponent,
  DialogContentComponent,
  DialogFooterComponent,
} from '../../ui/dialog/dialog.component';
import { ButtonComponent } from '../../ui/button/button.component';
import { InputComponent } from '../../ui/input/input.component';
import { DropdownComponent } from '../../ui/dropdown/dropdown.component';

import { BybitFundingStrategyService } from '../../../services/bybit-funding-strategy.service';
import { TranslationService } from '../../../services/translation.service';
import type {
  PositionSideConfig,
  StartPreciseTimingStrategyRequest,
} from '../../../models/bybit-funding-strategy.model';

export interface StartBybitStrategyModalData {
  symbol: string;
  fundingRate: number;
  nextFundingTime: number;
  credentialId: string; // Credential ID from parent component
}

@Component({
  selector: 'app-start-bybit-strategy-modal',
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule,
    DialogComponent,
    DialogContentComponent,
    DialogFooterComponent,
    ButtonComponent,
    InputComponent,
    DropdownComponent,
  ],
  templateUrl: './start-bybit-strategy-modal.component.html',
  styleUrls: ['./start-bybit-strategy-modal.component.scss'],
})
export class StartBybitStrategyModalComponent implements OnInit {
  // Component inputs/outputs
  @Input() isOpen = false;
  @Input() data!: StartBybitStrategyModalData;
  @Output() close = new EventEmitter<void>();
  @Output() strategyStarted = new EventEmitter<void>();

  // Injected services
  private fb = inject(FormBuilder);
  private strategyService = inject(BybitFundingStrategyService);
  private translationService = inject(TranslationService);

  // Modal data as signal for computed values
  dataSignal = computed(() => this.data);

  // Form state
  form!: FormGroup;
  isSubmitting = signal<boolean>(false);
  errorMessage = signal<string | null>(null);
  successMessage = signal<string | null>(null);

  // Advanced settings expansion state
  showAdvancedSettings = signal<boolean>(false);

  // Strategy type options
  strategyTypeOptions = [
    { value: 'precise', label: '' },
    { value: 'regular', label: '' },
  ];

  // Position side options
  positionSideOptions = [
    { value: 'Auto', label: '' },
    { value: 'Buy', label: '' },
    { value: 'Sell', label: '' },
  ];

  // Computed values
  expectedFunding = computed(() => {
    const margin = this.form?.get('margin')?.value || 100;
    const leverage = this.form?.get('leverage')?.value || 10;
    const fundingRate = this.dataSignal().fundingRate || 0;
    return this.strategyService.calculateExpectedFunding(margin, leverage, fundingRate);
  });

  takeProfitAmount = computed(() => {
    const takeProfitPercent = this.form?.get('takeProfitPercent')?.value || 90;
    return this.strategyService.calculateTakeProfit(this.expectedFunding(), takeProfitPercent);
  });

  stopLossAmount = computed(() => {
    const stopLossPercent = this.form?.get('stopLossPercent')?.value || 50;
    return this.strategyService.calculateStopLoss(this.expectedFunding(), stopLossPercent);
  });

  secondsUntilFunding = computed(() => {
    const nextFundingTime = this.dataSignal().nextFundingTime;
    if (!nextFundingTime) return 0;
    const now = Date.now();
    return Math.max(0, Math.floor((nextFundingTime - now) / 1000));
  });

  timeRemainingFormatted = computed(() => {
    return this.strategyService.formatTimeRemaining(this.secondsUntilFunding());
  });

  positionValue = computed(() => {
    const margin = this.form?.get('margin')?.value || 100;
    const leverage = this.form?.get('leverage')?.value || 10;
    return margin * leverage;
  });

  // Helper to check if strategy type is 'precise'
  isPreciseStrategy = computed(() => {
    return this.form?.get('strategyType')?.value === 'precise';
  });

  ngOnInit(): void {
    this.initializeTranslations();
    this.initializeForm();
  }

  /**
   * Initialize translations for dropdown options
   */
  private initializeTranslations(): void {
    this.strategyTypeOptions = [
      {
        value: 'precise',
        label: this.translate('bybitStrategy.type.precise'),
      },
      {
        value: 'regular',
        label: this.translate('bybitStrategy.type.regular'),
      },
    ];

    this.positionSideOptions = [
      {
        value: 'Auto',
        label: this.translate('bybitStrategy.side.auto'),
      },
      {
        value: 'Buy',
        label: this.translate('bybitStrategy.side.long'),
      },
      {
        value: 'Sell',
        label: this.translate('bybitStrategy.side.short'),
      },
    ];
  }

  /**
   * Initialize the form with validators
   */
  private initializeForm(): void {
    this.form = this.fb.group({
      // Strategy configuration
      strategyType: ['precise', [Validators.required]],

      // Position settings
      leverage: [10, [Validators.required, Validators.min(1), Validators.max(125)]],
      margin: [100, [Validators.required, Validators.min(1)]],
      positionSide: ['Auto', [Validators.required]],

      // Risk management
      takeProfitPercent: [90, [Validators.required, Validators.min(1), Validators.max(100)]],
      stopLossPercent: [50, [Validators.required, Validators.min(1), Validators.max(100)]],

      // Advanced settings (only for precise timing)
      timingOffset: [20, [Validators.min(0), Validators.max(1000)]],
      autoRepeat: [false],
      enableWebSocketMonitoring: [true],
    });
  }


  /**
   * Format leverage value for slider display
   */
  formatLeverageLabel(value: number): string {
    return `${value}x`;
  }

  /**
   * Handle form submission
   */
  async onSubmit(): Promise<void> {
    if (this.form.invalid || this.isSubmitting()) {
      this.form.markAllAsTouched();
      return;
    }

    this.isSubmitting.set(true);
    this.errorMessage.set(null);
    this.successMessage.set(null);

    try {
      const formValue = this.form.value;
      const modalData = this.dataSignal();

      const params: StartPreciseTimingStrategyRequest = {
        symbol: modalData.symbol,
        leverage: formValue.leverage,
        margin: formValue.margin,
        positionSide: formValue.positionSide,
        takeProfitPercent: formValue.takeProfitPercent,
        stopLossPercent: formValue.stopLossPercent,
        timingOffset: formValue.timingOffset,
        autoRepeat: formValue.autoRepeat,
        enableWebSocketMonitoring: formValue.enableWebSocketMonitoring,
        // Only include credentialId if provided (empty string means use active credential)
        ...(modalData.credentialId && modalData.credentialId !== '' ? { credentialId: modalData.credentialId } : {})
      };

      if (formValue.strategyType === 'precise') {
        await firstValueFrom(this.strategyService.startPreciseTimingStrategy(params));
      } else {
        // Convert to regular strategy params (side is required, not positionSide)
        const regularParams = {
          ...params,
          side: params.positionSide === 'Auto' ? 'Buy' : params.positionSide,
          executionDelay: 5,
        };
        delete (regularParams as any).positionSide;
        delete (regularParams as any).timingOffset;

        await firstValueFrom(this.strategyService.startRegularStrategy(regularParams as any));
      }

      // Success!
      this.successMessage.set(this.translate('bybitStrategy.actions.success'));

      // Emit strategy started event and close after short delay
      console.log('[Modal] Strategy started successfully, waiting 1.5s before emitting events...');
      setTimeout(() => {
        console.log('[Modal] Emitting strategyStarted event');
        this.strategyStarted.emit();
        console.log('[Modal] Emitting close event');
        this.close.emit();
      }, 1500);

    } catch (error: any) {
      console.error('Failed to start strategy:', error);
      this.errorMessage.set(error.message || this.translate('bybitStrategy.actions.error'));
    } finally {
      this.isSubmitting.set(false);
    }
  }

  /**
   * Close the modal without starting strategy
   */
  onCancel(): void {
    this.close.emit();
  }

  /**
   * Toggle advanced settings panel
   */
  toggleAdvancedSettings(): void {
    this.showAdvancedSettings.update(val => !val);
  }

  /**
   * Get form control error message
   */
  getErrorMessage(controlName: string): string {
    const control = this.form.get(controlName);
    if (!control || !control.errors) return '';

    if (control.errors['required']) {
      return this.translate('validation.required');
    }
    if (control.errors['min']) {
      return this.translate('validation.minValue').replace('{value}', control.errors['min'].min);
    }
    if (control.errors['max']) {
      return this.translate('validation.maxValue').replace('{value}', control.errors['max'].max);
    }

    return '';
  }

  /**
   * Check if form control is invalid and touched
   */
  isFieldInvalid(controlName: string): boolean {
    const control = this.form.get(controlName);
    return !!(control && control.invalid && control.touched);
  }

  /**
   * Translate a key using the translation service
   */
  translate(key: string): string {
    return this.translationService.translate(key);
  }
}

import { Component, OnInit, OnDestroy, signal, Input, Output, EventEmitter, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { DialogComponent, DialogHeaderComponent, DialogTitleComponent, DialogContentComponent, DialogFooterComponent } from '../../ui/dialog/dialog.component';
import { ButtonComponent } from '../../ui/button/button.component';
import { CardComponent } from '../../ui/card/card.component';
import { SelectComponent } from '../../ui/select/select.component';
import { TradeHistoryService } from '../../../services/trade-history.service';
import { TradeHistoryRecord } from '../../../models/trade-history.model';

export interface TradeHistoryDialogData {
  symbol?: string;
  exchange?: string;
}

/**
 * Trade History Component
 *
 * Displays historical trading data with comprehensive statistics and filtering options.
 *
 * Features:
 * - Trade history table with sorting
 * - Real-time statistics (P&L, win rate, fees)
 * - Filter by symbol and exchange
 * - Responsive design
 * - Auto-refresh capability
 * - Uses reusable ui-dialog component for consistency
 */
@Component({
  selector: 'app-trade-history',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    DialogComponent,
    DialogHeaderComponent,
    DialogTitleComponent,
    DialogContentComponent,
    DialogFooterComponent,
    ButtonComponent,
    CardComponent,
    SelectComponent
  ],
  templateUrl: './trade-history.component.html',
  styleUrls: ['./trade-history.component.scss']
})
export class TradeHistoryComponent implements OnInit, OnDestroy {
  // Dialog state
  @Input() set open(value: boolean) {
    this._open.set(value);
  }
  get open() {
    return this._open();
  }
  private _open = signal<boolean>(false);

  @Input() initialSymbol = '';
  @Input() initialExchange = '';
  @Output() openChange = new EventEmitter<boolean>();
  @Output() close = new EventEmitter<void>();

  // Component state
  displayedColumns: string[] = [
    'symbol',
    'exchange',
    'positionType',
    'executedAt',
    'closedAt',
    'entryPrice',
    'exitPrice',
    'pnl',
    'funding',
    'status'
  ];

  // Filters
  selectedSymbol = '';
  selectedExchange = '';
  availableSymbols: string[] = [];
  availableExchanges: string[] = ['BYBIT', 'BINGX', 'MEXC', 'BINANCE', 'OKX'];

  // Auto-refresh
  private refreshInterval?: number;
  autoRefreshEnabled = false;

  constructor(public tradeHistoryService: TradeHistoryService) {}

  ngOnInit(): void {
    console.log('[TradeHistoryComponent] Initializing...');

    // Set initial filters from inputs if provided
    if (this.initialSymbol) {
      console.log('[TradeHistoryComponent] Opening with symbol:', this.initialSymbol);
      this.selectedSymbol = this.initialSymbol;
    }
    if (this.initialExchange) {
      console.log('[TradeHistoryComponent] Opening with exchange:', this.initialExchange);
      this.selectedExchange = this.initialExchange;
    }

    this.loadTradeHistory();
  }

  ngOnDestroy(): void {
    this.stopAutoRefresh();
  }

  /**
   * Load trade history from API
   */
  loadTradeHistory(): void {
    const params: any = {
      limit: 100
    };

    if (this.selectedSymbol) {
      params.symbol = this.selectedSymbol;
    }

    if (this.selectedExchange) {
      params.exchange = this.selectedExchange;
    }

    console.log('[TradeHistoryComponent] Loading trades with params:', params);

    this.tradeHistoryService.getTradeHistory(params).subscribe({
      next: (response) => {
        console.log(`[TradeHistoryComponent] Loaded ${response.count} trades`);
        this.updateAvailableFilters();
      },
      error: (error) => {
        console.error('[TradeHistoryComponent] Error loading trades:', error);
      }
    });
  }

  /**
   * Update available filter options based on loaded data
   */
  private updateAvailableFilters(): void {
    const trades = this.tradeHistoryService.trades();

    // Extract unique symbols
    const symbols = new Set<string>();
    trades.forEach(trade => symbols.add(trade.symbol));
    this.availableSymbols = Array.from(symbols).sort();
  }

  /**
   * Apply filters and reload data
   */
  applyFilters(): void {
    console.log('[TradeHistoryComponent] Applying filters:', {
      symbol: this.selectedSymbol,
      exchange: this.selectedExchange
    });
    this.loadTradeHistory();
  }

  /**
   * Clear all filters
   */
  clearFilters(): void {
    this.selectedSymbol = '';
    this.selectedExchange = '';
    this.loadTradeHistory();
  }

  /**
   * Refresh trade history
   */
  refresh(): void {
    console.log('[TradeHistoryComponent] Refreshing trades...');
    this.loadTradeHistory();
  }

  /**
   * Toggle auto-refresh
   */
  toggleAutoRefresh(): void {
    this.autoRefreshEnabled = !this.autoRefreshEnabled;

    if (this.autoRefreshEnabled) {
      this.startAutoRefresh();
    } else {
      this.stopAutoRefresh();
    }
  }

  /**
   * Start auto-refresh (every 30 seconds)
   */
  private startAutoRefresh(): void {
    this.stopAutoRefresh(); // Clear any existing interval

    this.refreshInterval = window.setInterval(() => {
      console.log('[TradeHistoryComponent] Auto-refreshing...');
      this.loadTradeHistory();
    }, 30000); // 30 seconds
  }

  /**
   * Stop auto-refresh
   */
  private stopAutoRefresh(): void {
    if (this.refreshInterval) {
      clearInterval(this.refreshInterval);
      this.refreshInterval = undefined;
    }
  }

  /**
   * Format timestamp to readable date
   */
  formatDate(date: string | null): string {
    if (!date) return '-';

    const d = new Date(date);
    return d.toLocaleString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  }

  /**
   * Format price with 2 decimals
   */
  formatPrice(price: number | null): string {
    if (price === null || price === undefined) return '-';
    return price.toFixed(2);
  }

  /**
   * Format PnL with sign and color
   */
  formatPnl(pnl: number): { text: string; class: string } {
    const formatted = this.tradeHistoryService.formatPnl(pnl);
    return {
      text: `${formatted.value} USDT`,
      class: formatted.isPositive ? 'positive' : 'negative'
    };
  }

  /**
   * Get position type badge class
   */
  getPositionTypeClass(positionType: string | undefined): string {
    if (!positionType) return '';
    return positionType.toLowerCase() === 'long' ? 'long' : 'short';
  }

  /**
   * Get status badge class
   */
  getStatusClass(status: string): string {
    switch (status.toUpperCase()) {
      case 'COMPLETED':
        return 'completed';
      case 'CANCELLED':
        return 'cancelled';
      case 'ERROR':
        return 'error';
      default:
        return 'active';
    }
  }

  /**
   * Calculate trade duration in human-readable format
   */
  getTradeAdvice(trade: TradeHistoryRecord): string {
    const pnl = trade.realizedPnl;
    const duration = trade.duration || 0;

    if (pnl > 10) {
      return ' Great trade!';
    } else if (pnl > 0) {
      return ' Profitable';
    } else if (pnl < -10) {
      return ' Large loss';
    } else {
      return ' Loss';
    }
  }

  /**
   * Export trades to CSV
   */
  exportToCSV(): void {
    const trades = this.tradeHistoryService.trades();
    if (trades.length === 0) {
      console.warn('[TradeHistoryComponent] No trades to export');
      return;
    }

    // CSV headers
    const headers = [
      'Symbol',
      'Exchange',
      'Position Type',
      'Executed At',
      'Closed At',
      'Entry Price',
      'Exit Price',
      'Realized PnL',
      'Funding Earned',
      'Total Fees',
      'Net Profit',
      'Status'
    ];

    // CSV rows
    const rows = trades.map(trade => [
      trade.symbol,
      trade.exchange,
      trade.positionType,
      trade.executedAt || '',
      trade.closedAt || '',
      trade.entryPrice,
      trade.exitPrice || '',
      trade.realizedPnl,
      trade.fundingEarned,
      ((trade.entryFee || 0) + (trade.exitFee || 0)),
      (trade.realizedPnl - ((trade.entryFee || 0) + (trade.exitFee || 0))),
      trade.status
    ]);

    // Combine headers and rows
    const csvContent = [
      headers.join(','),
      ...rows.map(row => row.join(','))
    ].join('\n');

    // Download file
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    const url = URL.createObjectURL(blob);

    link.setAttribute('href', url);
    link.setAttribute('download', `trade-history-${new Date().toISOString()}.csv`);
    link.style.visibility = 'hidden';

    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);

    console.log('[TradeHistoryComponent] Exported trades to CSV');
  }

  /**
   * Handle dialog open/close state changes
   */
  onDialogOpenChange(isOpen: boolean): void {
    this._open.set(isOpen);
    this.openChange.emit(isOpen);
    if (!isOpen) {
      this.close.emit();
    }
  }

  /**
   * Close the dialog
   */
  closeDialog(): void {
    this._open.set(false);
    this.openChange.emit(false);
    this.close.emit();
  }
}

