    usedMargin: 1400,
    unrealizedPnl: 175,
    walletBalance: 10000,
    currency: 'USDT',
    updatedAt: new Date()
  };

  beforeEach(async () => {
    // Create spy objects for services
    const tradingServiceSpy = jasmine.createSpyObj('ManualTradingService', [
      'placeOrder',
      'getPositions',
      'getOrders',
      'getAccountBalance',
      'closePosition',
      'cancelOrder',
      'getMockPositions',
      'getMockOrders',
      'getMockBalance',
      'setupAutoRefresh'
    ], {
      isPlacingOrder: signal(false),
      isLoadingPositions: signal(false),
      isLoadingOrders: signal(false),
      isLoadingBalance: signal(false),
      positions$: of(mockPositions),
      orders$: of(mockOrders),
      balance$: of(mockBalance)
    });

    const translationServiceSpy = jasmine.createSpyObj('TranslationService', ['translate']);

    await TestBed.configureTestingModule({
      imports: [
        TradingDashboardComponent,
        HttpClientTestingModule,
        ReactiveFormsModule
      ],
      providers: [
        { provide: ManualTradingService, useValue: tradingServiceSpy },
        { provide: TranslationService, useValue: translationServiceSpy }
      ]
    }).compileComponents();

    tradingService = TestBed.inject(ManualTradingService) as jasmine.SpyObj<ManualTradingService>;
    translationService = TestBed.inject(TranslationService) as jasmine.SpyObj<TranslationService>;

    // Set up default spy returns
    tradingService.getMockPositions.and.returnValue(mockPositions);
    tradingService.getMockOrders.and.returnValue(mockOrders);
    tradingService.getMockBalance.and.returnValue(mockBalance);
    translationService.translate.and.returnValue('Translated Text');

    fixture = TestBed.createComponent(TradingDashboardComponent);
    component = fixture.componentInstance;
  });

  describe('Component Initialization', () => {
    it('should create the component', () => {
      expect(component).toBeTruthy();
    });

    it('should initialize with default values', () => {
      expect(component.selectedExchange()).toBe('bybit');
      expect(component.selectedSymbol()).toBe('BTCUSDT');
      expect(component.autoRefreshEnabled()).toBe(false);
    });

    it('should initialize the order form with default values', () => {
      fixture.detectChanges();

      expect(component.orderForm).toBeDefined();
      expect(component.orderForm.get('exchange')?.value).toBe('bybit');
      expect(component.orderForm.get('symbol')?.value).toBe('BTCUSDT');
      expect(component.orderForm.get('type')?.value).toBe('Market');
      expect(component.orderForm.get('side')?.value).toBe('Buy');
      expect(component.orderForm.get('timeInForce')?.value).toBe('GTC');
    });

    it('should load mock data on initialization', () => {
      fixture.detectChanges();

      expect(component.positions().length).toBeGreaterThan(0);
      expect(component.orders().length).toBeGreaterThan(0);
      expect(component.balance()).toBeTruthy();
    });
  });

  describe('Order Form Validation', () => {
    beforeEach(() => {
      fixture.detectChanges();
    });

    it('should require quantity field', () => {
      const quantityControl = component.orderForm.get('quantity');
      quantityControl?.setValue(null);

      expect(quantityControl?.valid).toBe(false);
      expect(quantityControl?.errors?.['required']).toBe(true);
    });

    it('should validate quantity minimum value', () => {
      const quantityControl = component.orderForm.get('quantity');
      quantityControl?.setValue(0);

      expect(quantityControl?.valid).toBe(false);
      expect(quantityControl?.errors?.['min']).toBeTruthy();
    });

    it('should require price for limit orders', () => {
      component.orderForm.patchValue({
        type: 'Limit',
        quantity: 0.1
      });
      fixture.detectChanges();

      const priceControl = component.orderForm.get('price');
      expect(priceControl?.hasError('required')).toBe(true);
    });

    it('should not require price for market orders', () => {
      component.orderForm.patchValue({
        type: 'Market',
        quantity: 0.1
      });
      fixture.detectChanges();

      const priceControl = component.orderForm.get('price');
      expect(priceControl?.hasError('required')).toBeFalsy();
    });

    it('should validate stop loss minimum value', () => {
      const stopLossControl = component.orderForm.get('stopLoss');
      stopLossControl?.setValue(0);

      expect(stopLossControl?.valid).toBe(false);
      expect(stopLossControl?.errors?.['min']).toBeTruthy();
    });

    it('should validate take profit minimum value', () => {
      const takeProfitControl = component.orderForm.get('takeProfit');
      takeProfitControl?.setValue(0);

      expect(takeProfitControl?.valid).toBe(false);
      expect(takeProfitControl?.errors?.['min']).toBeTruthy();
    });

    it('should be valid with correct market order data', () => {
      component.orderForm.patchValue({
        exchange: 'bybit',
        symbol: 'BTCUSDT',
        type: 'Market',
        side: 'Buy',
        quantity: 0.1
      });

      expect(component.orderForm.valid).toBe(true);
    });

    it('should be valid with correct limit order data', () => {
      component.orderForm.patchValue({
        exchange: 'bybit',
        symbol: 'BTCUSDT',
        type: 'Limit',
        side: 'Buy',
        quantity: 0.1,
        price: 50000
      });

      expect(component.orderForm.valid).toBe(true);
    });
  });

  describe('Place Order Functionality', () => {
    beforeEach(() => {
      fixture.detectChanges();
    });

    it('should not place order if form is invalid', () => {
      component.orderForm.patchValue({
        quantity: null // Invalid
      });

      component.placeOrder();

      expect(tradingService.placeOrder).not.toHaveBeenCalled();
    });

    it('should place market order with valid data', () => {
      const mockResponse: OrderResponse = {
        orderId: 'test-order-1',
        exchange: 'bybit',
        symbol: 'BTCUSDT',
        side: 'Buy',
        type: 'Market',
        quantity: 0.1,
        status: 'Filled',
        timeInForce: 'GTC',
        createdAt: new Date(),
        updatedAt: new Date()
      };

      tradingService.placeOrder.and.returnValue(of(mockResponse));

      component.orderForm.patchValue({
        exchange: 'bybit',
        symbol: 'BTCUSDT',
        type: 'Market',
        side: 'Buy',
        quantity: 0.1
      });

      component.placeOrder();

      expect(tradingService.placeOrder).toHaveBeenCalledWith(
        jasmine.objectContaining({
          exchange: 'bybit',
          symbol: 'BTCUSDT',
          type: 'Market',
          side: 'Buy',
          quantity: 0.1
        })
      );
    });

    it('should place limit order with price', () => {
      const mockResponse: OrderResponse = {
        orderId: 'test-order-2',
        exchange: 'bybit',
        symbol: 'ETHUSDT',
        side: 'Sell',
        type: 'Limit',
        quantity: 1,
        price: 3000,
        status: 'New',
        timeInForce: 'GTC',
        createdAt: new Date(),
        updatedAt: new Date()
      };

      tradingService.placeOrder.and.returnValue(of(mockResponse));

      component.orderForm.patchValue({
        exchange: 'bybit',
        symbol: 'ETHUSDT',
        type: 'Limit',
        side: 'Sell',
        quantity: 1,
        price: 3000,
        timeInForce: 'GTC'
      });

      component.placeOrder();

      expect(tradingService.placeOrder).toHaveBeenCalledWith(
        jasmine.objectContaining({
          exchange: 'bybit',
          symbol: 'ETHUSDT',
          type: 'Limit',
          side: 'Sell',
          quantity: 1,
          price: 3000,
          timeInForce: 'GTC'
        })
      );
    });

    it('should include stop loss and take profit if provided', () => {
      tradingService.placeOrder.and.returnValue(of({} as OrderResponse));

      component.orderForm.patchValue({
        exchange: 'bybit',
        symbol: 'BTCUSDT',
        type: 'Market',
        side: 'Buy',
        quantity: 0.1,
        stopLoss: 48000,
        takeProfit: 52000
      });

      component.placeOrder();

      expect(tradingService.placeOrder).toHaveBeenCalledWith(
        jasmine.objectContaining({
          stopLoss: 48000,
          takeProfit: 52000
        })
      );
    });

    it('should handle order placement error', () => {
      const errorMessage = 'Insufficient balance';
      tradingService.placeOrder.and.returnValue(
        throwError(() => new Error(errorMessage))
      );

      spyOn(console, 'error');

      component.orderForm.patchValue({
        exchange: 'bybit',
        symbol: 'BTCUSDT',
        type: 'Market',
        side: 'Buy',
        quantity: 0.1
      });

      component.placeOrder();

      expect(console.error).toHaveBeenCalled();
    });
  });

  describe('Position Management', () => {
    beforeEach(() => {
      fixture.detectChanges();
    });

    it('should close position when confirmed', () => {
      spyOn(window, 'confirm').and.returnValue(true);
      tradingService.closePosition.and.returnValue(of({} as OrderResponse));

      const position = mockPositions[0];
      component.closePosition(position);

      expect(tradingService.closePosition).toHaveBeenCalledWith({
        exchange: position.exchange,
        symbol: position.symbol,
        side: position.side
      });
    });

    it('should not close position if not confirmed', () => {
      spyOn(window, 'confirm').and.returnValue(false);

      const position = mockPositions[0];
      component.closePosition(position);

      expect(tradingService.closePosition).not.toHaveBeenCalled();
    });

    it('should calculate total unrealized PnL correctly', () => {
      component.positions.set([
        { ...mockPositions[0], unrealizedPnl: 100 },
        { ...mockPositions[0], id: '2', unrealizedPnl: 50 },
        { ...mockPositions[0], id: '3', unrealizedPnl: -30 }
      ]);

      expect(component.totalUnrealizedPnl()).toBe(120);
    });

    it('should detect if there are open positions', () => {
      component.positions.set(mockPositions);
      expect(component.hasOpenPositions()).toBe(true);

      component.positions.set([]);
      expect(component.hasOpenPositions()).toBe(false);
    });
  });

  describe('Order Management', () => {
    beforeEach(() => {
      fixture.detectChanges();
    });

    it('should cancel order when confirmed', () => {
      spyOn(window, 'confirm').and.returnValue(true);
      tradingService.cancelOrder.and.returnValue(of(void 0));

      const order = mockOrders[0];
      component.cancelOrder(order);

      expect(tradingService.cancelOrder).toHaveBeenCalledWith({
        exchange: order.exchange,
        orderId: order.orderId,
        symbol: order.symbol
      });
    });

    it('should not cancel order if not confirmed', () => {
      spyOn(window, 'confirm').and.returnValue(false);

      const order = mockOrders[0];
      component.cancelOrder(order);

      expect(tradingService.cancelOrder).not.toHaveBeenCalled();
    });

    it('should not cancel order if status is not New', () => {
      const filledOrder = { ...mockOrders[0], status: 'Filled' as const };
      component.cancelOrder(filledOrder);

      expect(tradingService.cancelOrder).not.toHaveBeenCalled();
    });

    it('should correctly identify cancellable orders', () => {
      const newOrder = { ...mockOrders[0], status: 'New' as const };
      const filledOrder = { ...mockOrders[0], status: 'Filled' as const };

      expect(component.canCancelOrder(newOrder)).toBe(true);
      expect(component.canCancelOrder(filledOrder)).toBe(false);
    });

    it('should detect if there are pending orders', () => {
      component.orders.set([{ ...mockOrders[0], status: 'New' }]);
      expect(component.hasPendingOrders()).toBe(true);

      component.orders.set([{ ...mockOrders[0], status: 'Filled' }]);
      expect(component.hasPendingOrders()).toBe(false);
    });
  });

  describe('Auto-Refresh Functionality', () => {
    beforeEach(() => {
      fixture.detectChanges();
    });

    it('should toggle auto-refresh on', () => {
      tradingService.setupAutoRefresh.and.returnValue(of(1));

      expect(component.autoRefreshEnabled()).toBe(false);

      component.toggleAutoRefresh();

      expect(component.autoRefreshEnabled()).toBe(true);
      expect(tradingService.setupAutoRefresh).toHaveBeenCalledWith('bybit', 10000);
    });

    it('should toggle auto-refresh off', () => {
      tradingService.setupAutoRefresh.and.returnValue(of(1));

      component.toggleAutoRefresh(); // Turn on
      component.toggleAutoRefresh(); // Turn off

      expect(component.autoRefreshEnabled()).toBe(false);
    });
  });

  describe('Utility Methods', () => {
    it('should return correct PnL class for positive value', () => {
      expect(component.getPnlClass(100)).toBe('pnl-positive');
    });

    it('should return correct PnL class for negative value', () => {
      expect(component.getPnlClass(-50)).toBe('pnl-negative');
    });

    it('should return correct PnL class for zero', () => {
      expect(component.getPnlClass(0)).toBe('pnl-positive');
    });

    it('should return correct side class for Buy', () => {
      expect(component.getSideClass('Buy')).toBe('side-buy');
    });

    it('should return correct side class for Sell', () => {
      expect(component.getSideClass('Sell')).toBe('side-sell');
    });

    it('should format PnL with correct sign', () => {
      expect(component.formatPnl(100)).toBe('+$100.00');
      expect(component.formatPnl(-50)).toBe('-$50.00');
      expect(component.formatPnl(0)).toBe('+$0.00');
    });

    it('should format PnL percentage correctly', () => {
      expect(component.formatPnlPercent(2.5)).toBe('+2.50%');
      expect(component.formatPnlPercent(-1.25)).toBe('-1.25%');
    });

    it('should format currency correctly', () => {
      expect(component.formatCurrency(1234.56)).toBe('$1234.56');
      expect(component.formatCurrency(0.01)).toBe('$0.01');
    });

    it('should detect limit order type', () => {
      component.orderForm.patchValue({ type: 'Limit' });
      expect(component.isLimitOrder()).toBe(true);

      component.orderForm.patchValue({ type: 'Market' });
      expect(component.isLimitOrder()).toBe(false);
    });
  });

  describe('Component Cleanup', () => {
    it('should unsubscribe on destroy', () => {
      fixture.detectChanges();

      spyOn(component['destroy$'], 'next');
      spyOn(component['destroy$'], 'complete');

      component.ngOnDestroy();

      expect(component['destroy$'].next).toHaveBeenCalled();
      expect(component['destroy$'].complete).toHaveBeenCalled();
    });
  });

  describe('Accessibility', () => {
    beforeEach(() => {
      fixture.detectChanges();
    });

    it('should have proper ARIA labels on buttons', () => {
      const compiled = fixture.nativeElement;
      const refreshButton = compiled.querySelector('[aria-label="Refresh trading data"]');

      expect(refreshButton).toBeTruthy();
    });

    it('should have proper form labels', () => {
      const compiled = fixture.nativeElement;
      // Form labels are handled by ui-input and ui-select components
      expect(component.orderForm.get('quantity')).toBeTruthy();
    });
  });
});

import { Component, OnInit, OnDestroy, signal, computed, inject, effect } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ReactiveFormsModule, FormsModule } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { Subject, takeUntil, Subscription } from 'rxjs';

// UI Components
import { CardComponent, CardHeaderComponent, CardTitleComponent, CardContentComponent } from '../../ui/card/card.component';
import { ButtonComponent } from '../../ui/button/button.component';
import { SelectComponent, SelectOption } from '../../ui/select/select.component';
import { DropdownOption } from '../../ui/dropdown/dropdown.component';
import { InputComponent } from '../../ui/input/input.component';
import { TableComponent, TableColumn } from '../../ui/table/table.component';
import { LightweightChartComponent } from '../../lightweight-chart/lightweight-chart.component';
import { OrderFormComponent } from '../order-form/order-form.component';

// Services
import { ManualTradingService } from '../../../services/manual-trading.service';
import { TranslationService } from '../../../services/translation.service';
import { ExchangeCredentialsService } from '../../../services/exchange-credentials.service';
import { BybitService } from '../../../services/bybit.service';

// Models
import {
  Exchange,
  OrderRequest,
  OrderSide,
  Position,
  Order,
  Balance,
  TradingSymbol
} from '../../../models/trading.model';
import { ExchangeCredential, getExchangeName } from '../../../models/exchange-credentials.model';

/**
 * Trading Dashboard Component
 *
 * Comprehensive manual trading interface for cryptocurrency exchanges.
 * Provides functionality for:
 * - Placing market and limit orders with advanced options
 * - Monitoring open positions with real-time PnL updates
 * - Viewing order history with filtering and pagination
 * - Tracking account balance and margin usage
 *
 * Features:
 * - Reactive forms with real-time validation
 * - Angular Signals for efficient state management
 * - Auto-refresh capability for real-time data
 * - Responsive design for all device sizes
 * - Comprehensive error handling and user feedback
 * - Accessibility support (ARIA labels, keyboard navigation)
 *
 * @example
 * Usage in template:
 * ```html
 * <trading-dashboard></trading-dashboard>
 * ```
 */
@Component({
  selector: 'trading-dashboard',
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule,
    FormsModule,
    CardComponent,
    CardHeaderComponent,
    CardTitleComponent,
    CardContentComponent,
    ButtonComponent,
    SelectComponent,
    InputComponent,
    TableComponent,
    LightweightChartComponent,
    OrderFormComponent
  ],
  templateUrl: './trading-dashboard.component.html',
  styleUrls: ['./trading-dashboard.component.css']
})
export class TradingDashboardComponent implements OnInit, OnDestroy {
  // Injected services
  private http = inject(HttpClient);
  private tradingService = inject(ManualTradingService);
  protected translationService = inject(TranslationService);
  private credentialsService = inject(ExchangeCredentialsService);
  private bybitService = inject(BybitService);

  // Component lifecycle
  private destroy$ = new Subject<void>();
  private autoRefreshSubscription?: Subscription;

  // State signals
  private _selectedCredentialId = signal<string | null>(null);
  private _selectedSymbol = signal<TradingSymbol>('BTCUSDT');
  positions = signal<Position[]>([]);
  orders = signal<Order[]>([]);
  balance = signal<Balance | null>(null);
  autoRefreshEnabled = signal<boolean>(false);

  // Symbol options for dropdown
  symbolOptions = signal<DropdownOption[]>([]);
  isLoadingSymbols = signal<boolean>(false);

  // Getters and setters for ngModel binding
  get selectedCredentialId() { return this._selectedCredentialId(); }
  set selectedCredentialId(value: string | null) { this._selectedCredentialId.set(value); }

  get selectedSymbol() { return this._selectedSymbol(); }
  set selectedSymbol(value: TradingSymbol) { this._selectedSymbol.set(value); }

  // Credentials from service
  credentials = this.credentialsService.credentials;
  isLoadingCredentials = this.credentialsService.loading;

  // Computed signals
  totalUnrealizedPnl = computed(() => {
    return this.positions().reduce((sum, pos) => sum + pos.unrealizedPnl, 0);
  });

  hasOpenPositions = computed(() => this.positions().length > 0);
  hasPendingOrders = computed(() => this.orders().some(o => o.status === 'New'));

  // Computed signal for credential dropdown options
  credentialOptions = computed((): DropdownOption[] => {
    return this.credentials()
      .filter(cred => cred.isActive)
      .map(cred => ({
        value: cred.id,
        label: this.formatCredentialLabel(cred)
      }));
  });

  // Get selected credential object
  selectedCredential = computed(() => {
    const credId = this._selectedCredentialId();
    if (!credId) return null;
    return this.credentials().find(c => c.id === credId) ?? null;
  });

  // Get selected exchange from credential
  selectedExchange = computed(() => {
    const cred = this.selectedCredential();
    return cred ? cred.exchange.toLowerCase() as Exchange : null;
  });

  // Get chart symbol with exchange prefix (computed)
  private _chartSymbol = computed(() => {
    const cred = this.selectedCredential();
    const symbol = this._selectedSymbol();

    if (!cred || !symbol) return '';

    // Map exchange to TradingView exchange code
    const exchangeMap: Record<string, string> = {
      'BYBIT': 'BYBIT',
      'BINANCE': 'BINANCE',
      'OKX': 'OKX',
      'COINBASE': 'COINBASE'
    };

    const exchangeCode = exchangeMap[cred.exchange.toUpperCase()] || 'BYBIT';
    return `${exchangeCode}:${symbol}`;
  });

  // Regular property for chart binding (updated via effect)
  chartSymbol = '';

  // Loading and error states from service
  isPlacingOrder = this.tradingService.isPlacingOrder;
  isLoadingPositions = this.tradingService.isLoadingPositions;
  isLoadingOrders = this.tradingService.isLoadingOrders;
  isLoadingBalance = this.tradingService.isLoadingBalance;

  // Table columns
  positionsColumns: TableColumn[] = [
    { key: 'symbol', label: this.translate('trading.table.symbol'), sortable: true },
    { key: 'side', label: this.translate('trading.table.side'), sortable: true },
    { key: 'size', label: this.translate('trading.table.size'), sortable: true, type: 'number', align: 'right' },
    { key: 'entryPrice', label: this.translate('trading.table.entryPrice'), sortable: true, type: 'currency', align: 'right' },
    { key: 'markPrice', label: this.translate('trading.table.markPrice'), sortable: true, type: 'currency', align: 'right' },
    { key: 'unrealizedPnl', label: this.translate('trading.table.unrealizedPnl'), sortable: true, type: 'currency', align: 'right' },
    { key: 'leverage', label: this.translate('trading.table.leverage'), sortable: true, type: 'number', align: 'center' },
    { key: 'actions', label: this.translate('trading.table.actions'), sortable: false, align: 'center' }
  ];

  ordersColumns: TableColumn[] = [
    { key: 'symbol', label: this.translate('trading.table.symbol'), sortable: true },
    { key: 'type', label: this.translate('trading.table.type'), sortable: true },
    { key: 'side', label: this.translate('trading.table.side'), sortable: true },
    { key: 'quantity', label: this.translate('trading.table.quantity'), sortable: true, type: 'number', align: 'right' },
    { key: 'price', label: this.translate('trading.table.price'), sortable: true, type: 'currency', align: 'right' },
    { key: 'status', label: this.translate('trading.table.status'), sortable: true },
    { key: 'createdAt', label: this.translate('trading.table.time'), sortable: true, type: 'date' },
    { key: 'actions', label: this.translate('trading.table.actions'), sortable: false, align: 'center' }
  ];

  // Pagination
  currentPage = signal<number>(1);
  pageSize = signal<number>(10);

  constructor() {
    // No form initialization needed - using OrderFormComponent

    // Effect to sync computed chartSymbol to property for change detection
    effect(() => {
      const newSymbol = this._chartSymbol();
      if (newSymbol && newSymbol !== this.chartSymbol) {
        console.log('Chart symbol changed:', this.chartSymbol, '->', newSymbol);
        this.chartSymbol = newSymbol;
      }
    });
  }

  ngOnInit(): void {
    // Load credentials first
    this.credentialsService.fetchCredentials().subscribe({
      next: () => {
        // Auto-select first active credential if available
        const firstActive = this.credentialOptions()[0];
        if (firstActive) {
          this._selectedCredentialId.set(firstActive.value);
          this.loadTradingSymbols();
        }
        this.loadMockData();
      },
      error: (err) => console.error('Failed to load credentials:', err)
    });

    // Subscribe to cached data from service
    this.tradingService.positions$
      .pipe(takeUntil(this.destroy$))
      .subscribe(positions => this.positions.set(positions));

    this.tradingService.orders$
      .pipe(takeUntil(this.destroy$))
      .subscribe(orders => this.orders.set(orders));

    this.tradingService.balance$
      .pipe(takeUntil(this.destroy$))
      .subscribe(balance => this.balance.set(balance));
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
    this.stopAutoRefresh();
  }

  /**
   * Load all trading data (positions, orders, balance)
   */
  loadAllData(): void {
    const exchange = this.selectedExchange();
    if (!exchange) return;

    // For development, use mock data
    // In production, uncomment the following:
    // this.tradingService.getPositions(exchange).subscribe();
    // this.tradingService.getOrders(exchange).subscribe();
    // this.tradingService.getAccountBalance(exchange).subscribe();

    this.loadMockData();
  }

  /**
   * Load mock data for development/testing
   */
  private loadMockData(): void {
    this.positions.set(this.tradingService.getMockPositions());
    this.orders.set(this.tradingService.getMockOrders());
    this.balance.set(this.tradingService.getMockBalance());
  }

  /**
   * Handle order placed from OrderFormComponent
   */
  handleOrderPlaced(orderRequest: any): void {
    const credential = this.selectedCredential();

    if (!credential) {
      console.error('No credential selected');
      return;
    }

    // Add credential and exchange to order request
    const completeOrderRequest: OrderRequest = {
      ...orderRequest,
      exchange: credential.exchange.toLowerCase() as Exchange,
      credentialId: credential.id
    };

    this.tradingService.placeOrder(completeOrderRequest).subscribe({
      next: (response) => {
        console.log('Order placed successfully:', response);
        this.loadAllData();
        // TODO: Show success toast notification
      },
      error: (error) => {
        console.error('Failed to place order:', error);
        // TODO: Show error toast notification
      }
    });
  }

  /**
   * Close a position
   */
  closePosition(position: Position): void {
    if (confirm(this.translate('trading.confirmClosePosition'))) {
      this.tradingService.closePosition({
        exchange: position.exchange,
        symbol: position.symbol,
        side: position.side
      }).subscribe({
        next: () => {
          console.log('Position closed successfully');
          this.loadAllData();
          // TODO: Show success toast notification
        },
        error: (error) => {
          console.error('Failed to close position:', error);
          // TODO: Show error toast notification
        }
      });
    }
  }

  /**
   * Cancel an order
   */
  cancelOrder(order: Order): void {
    if (order.status !== 'New') {
      return;
    }

    if (confirm(this.translate('trading.confirmCancelOrder'))) {
      this.tradingService.cancelOrder({
        exchange: order.exchange,
        orderId: order.orderId,
        symbol: order.symbol
      }).subscribe({
        next: () => {
          console.log('Order cancelled successfully');
          this.loadAllData();
          // TODO: Show success toast notification
        },
        error: (error) => {
          console.error('Failed to cancel order:', error);
          // TODO: Show error toast notification
        }
      });
    }
  }

  /**
   * Refresh all data manually
   */
  refreshData(): void {
    this.loadAllData();
  }

  /**
   * Toggle auto-refresh
   */
  toggleAutoRefresh(): void {
    const enabled = !this.autoRefreshEnabled();
    this.autoRefreshEnabled.set(enabled);

    if (enabled) {
      this.startAutoRefresh();
    } else {
      this.stopAutoRefresh();
    }
  }

  /**
   * Start auto-refresh
   */
  private startAutoRefresh(): void {
    const exchange = this.selectedExchange();
    if (!exchange) return;

    this.autoRefreshSubscription = this.tradingService
      .setupAutoRefresh(exchange, 10000) // Refresh every 10 seconds
      .pipe(takeUntil(this.destroy$))
      .subscribe();
  }

  /**
   * Stop auto-refresh
   */
  private stopAutoRefresh(): void {
    this.autoRefreshSubscription?.unsubscribe();
  }

  /**
   * Get CSS class for PnL value (green for profit, red for loss)
   */
  getPnlClass(pnl: number): string {
    return pnl >= 0 ? 'pnl-positive' : 'pnl-negative';
  }

  /**
   * Get CSS class for order side (green for buy, red for sell)
   */
  getSideClass(side: OrderSide): string {
    return side === 'Buy' ? 'side-buy' : 'side-sell';
  }

  /**
   * Format PnL value with + or - sign
   */
  formatPnl(pnl: number): string {
    const sign = pnl >= 0 ? '+' : '';
    return `${sign}$${pnl.toFixed(2)}`;
  }

  /**
   * Format PnL percentage
   */
  formatPnlPercent(percent: number): string {
    const sign = percent >= 0 ? '+' : '';
    return `${sign}${percent.toFixed(2)}%`;
  }

  /**
   * Check if order can be cancelled
   */
  canCancelOrder(order: Order): boolean {
    return order.status === 'New';
  }

  /**
   * Translate a key using the translation service
   */
  protected translate(key: string): string {
    return this.translationService.translate(key);
  }

  /**
   * Format date for display
   */
  formatDate(date: Date): string {
    return new Date(date).toLocaleString();
  }

  /**
   * Format currency value
   */
  formatCurrency(value: number): string {
    return `$${value.toFixed(2)}`;
  }

  /**
   * Format credential label for dropdown
   */
  private formatCredentialLabel(credential: ExchangeCredential): string {
    const exchangeName = getExchangeName(credential.exchange);
    const label = credential.label ? ` - ${credential.label}` : '';
    return `${exchangeName}${label}`;
  }

  /**
   * Handle credential change from dropdown
   */
  onCredentialChange(credentialId: string): void {
    console.log('Credential changed to:', credentialId);
    this._selectedCredentialId.set(credentialId);

    const cred = this.selectedCredential();
    console.log('Selected credential:', cred);
    console.log('Chart symbol before load:', this.chartSymbol);

    this.loadTradingSymbols();
    this.loadAllData();

    // Log after a brief delay to see if computed updated
    setTimeout(() => {
      console.log('Chart symbol after change:', this.chartSymbol);
    }, 100);
  }

  /**
   * Handle symbol change from dropdown
   */
  onSymbolChange(symbol: TradingSymbol): void {
    this._selectedSymbol.set(symbol);
    // Chart will update automatically via binding
  }

  /**
   * Load trading symbols for selected exchange
   */
  private loadTradingSymbols(): void {
    const exchange = this.selectedExchange();
    if (!exchange) {
      this.symbolOptions.set([]);
      return;
    }

    this.isLoadingSymbols.set(true);

    this.tradingService.getSymbols(exchange).subscribe({
      next: (symbols) => {
        const options = symbols.map(s => ({
          value: s.symbol as TradingSymbol,
          label: s.symbol
        }));
        this.symbolOptions.set(options);
        this.isLoadingSymbols.set(false);

        // Auto-select BTCUSDT if available, otherwise first symbol
        const btcOption = options.find(opt => opt.value === 'BTCUSDT');
        if (btcOption) {
          this._selectedSymbol.set('BTCUSDT');
        } else if (options.length > 0) {
          this._selectedSymbol.set(options[0].value);
        }
      },
      error: (error) => {
        console.error('Failed to load trading symbols:', error);
        // Fallback to default popular trading pairs
        this.loadDefaultSymbols();
        this.isLoadingSymbols.set(false);
      }
    });
  }

  /**
   * Load default trading symbols as fallback
   */
  private loadDefaultSymbols(): void {
    const defaultSymbols: SelectOption[] = [
      { value: 'BTCUSDT', label: 'BTCUSDT' },
      { value: 'ETHUSDT', label: 'ETHUSDT' },
      { value: 'BNBUSDT', label: 'BNBUSDT' },
      { value: 'SOLUSDT', label: 'SOLUSDT' },
      { value: 'XRPUSDT', label: 'XRPUSDT' },
      { value: 'ADAUSDT', label: 'ADAUSDT' },
      { value: 'DOGEUSDT', label: 'DOGEUSDT' },
      { value: 'MATICUSDT', label: 'MATICUSDT' },
      { value: 'DOTUSDT', label: 'DOTUSDT' },
      { value: 'AVAXUSDT', label: 'AVAXUSDT' }
    ];

    this.symbolOptions.set(defaultSymbols);
    this._selectedSymbol.set('BTCUSDT');
  }
}

import { Component, Input, Output, EventEmitter, signal, computed, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ThemeService } from '../../../services/theme.service';

export type ButtonVariant = 'primary' | 'secondary' | 'tertiary' | 'danger' | 'warning' | 'link' | 'ghost';
export type ButtonSize = 'small' | 'medium' | 'large';

/**
 * Custom Button Component
 *
 * Features:
 * - Theme support (light/dark mode)
 * - Multiple variants (primary, secondary, tertiary, danger, link, ghost)
 * - Multiple sizes (small, medium, large)
 * - Loading state with spinner
 * - Disabled state
 * - Icon-only mode
 * - Full-width mode
 * - Custom className support
 *
 * @example
 * ```html
 * <ui-button
 *   variant="primary"
 *   size="medium"
 *   [loading]="isLoading"
 *   [disabled]="isDisabled"
 *   (clicked)="handleClick()">
 *   Click Me
 * </ui-button>
 * ```
 */
@Component({
  selector: 'ui-button',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './button.component.html',
  styleUrl: './button.component.scss'
})
export class ButtonComponent {
  @Input() variant: ButtonVariant = 'primary';
  @Input() size: ButtonSize = 'medium';
  @Input() disabled = false;
  @Input() loading = false;
  @Input() iconOnly = false;
  @Input() fullWidth = false;
  @Input() type: 'button' | 'submit' | 'reset' = 'button';
  @Input() className = '';

  @Output() clicked = new EventEmitter<Event>();

  // Inject theme service
  private themeService = inject(ThemeService);
  isDarkMode = computed(() => this.themeService.isDark());

  /**
   * Handle button click
   */
  handleClick(event: Event): void {
    if (this.disabled || this.loading) {
      event.preventDefault();
      event.stopPropagation();
      return;
    }
    this.clicked.emit(event);
  }

  /**
   * Get button classes
   */
  getButtonClasses(): string {
    const classes = [
      'ui-button',
      `ui-button--${this.variant}`,
      `ui-button--${this.size}`,
      this.isDarkMode() ? 'ui-button--dark' : 'ui-button--light'
    ];

    if (this.disabled) classes.push('ui-button--disabled');
    if (this.loading) classes.push('ui-button--loading');
    if (this.iconOnly) classes.push('ui-button--icon-only');
    if (this.fullWidth) classes.push('ui-button--full-width');
    if (this.className) classes.push(this.className);

    return classes.join(' ');
  }
}

