export class WebSocketTestComponent implements OnInit, OnDestroy {
  connectionStatus: 'disconnected' | 'connecting' | 'connected' = 'disconnected';
  activeSubscriptions: string[] = [];
  latestData: any[] = [];
  logs: { time: string, message: string }[] = [];
  isRunning = false;

  private subscription?: Subscription;
  private statusSubscription?: Subscription;

  constructor(private bybitService: BybitService) {}

  ngOnInit(): void {
    this.log('Component initialized');

    // Subscribe to connection status
    this.statusSubscription = this.bybitService.getWebSocketConnectionState().subscribe(status => {
      this.connectionStatus = status;
      this.activeSubscriptions = this.bybitService.getActiveSubscriptions();
      this.log(`Connection status changed to: ${status}`);
    });
  }

  ngOnDestroy(): void {
    this.stopTest();
    this.statusSubscription?.unsubscribe();
  }

  startTest(): void {
    this.log('Starting Bybit V5 WebSocket kline test...');
    this.isRunning = true;
    this.latestData = [];

    // Subscribe to real-time BTCUSDT kline data
    this.subscription = this.bybitService.subscribeToRealtimeKline('BTCUSDT', '1').subscribe({
      next: (data) => {
        this.log(` Kline Data: Close=${data.close}, Volume=${data.volume}`);

        // Add to latest data (keep only last 10)
        this.latestData.unshift(data);
        if (this.latestData.length > 10) {
          this.latestData = this.latestData.slice(0, 10);
        }
      },
      error: (error) => {
        this.log(` Error: ${error.message || error}`);
      }
    });

    this.activeSubscriptions = this.bybitService.getActiveSubscriptions();
  }

  startTickerTest(): void {
    this.log('Starting Bybit V5 WebSocket ticker test...');
    this.isRunning = true;
    this.latestData = [];

    // Subscribe to real-time BTCUSDT ticker data
    this.subscription = this.bybitService.subscribeToRealtimeTicker('BTCUSDT').subscribe({
      next: (data) => {
        this.log(` Ticker Data: Price=${data.lastPrice}, Change=${data.price24hPcnt}%`);

        // Convert ticker data to display format
        const displayData = {
          time: new Date(data.timestamp).toISOString(),
          open: data.prevPrice24h,
          high: data.highPrice24h,
          low: data.lowPrice24h,
          close: data.lastPrice
        };

        // Add to latest data (keep only last 10)
        this.latestData.unshift(displayData);
        if (this.latestData.length > 10) {
          this.latestData = this.latestData.slice(0, 10);
        }
      },
      error: (error) => {
        this.log(` Error: ${error.message || error}`);
      }
    });

    this.activeSubscriptions = this.bybitService.getActiveSubscriptions();
  }

  stopTest(): void {
    this.log('Stopping WebSocket test...');
    this.isRunning = false;

    if (this.subscription) {
      this.subscription.unsubscribe();
    }

    // Unsubscribe from both kline and ticker
    this.bybitService.unsubscribeFromRealtimeKline('BTCUSDT', '1');
    this.bybitService.unsubscribeFromRealtimeTicker('BTCUSDT');
    this.activeSubscriptions = this.bybitService.getActiveSubscriptions();
  }

  formatTime(time: string | number): string {
    const timestamp = typeof time === 'number' ? time * 1000 : new Date(time).getTime();
    return new Date(timestamp).toLocaleTimeString();
  }

  private log(message: string): void {
    this.logs.unshift({
      time: new Date().toLocaleTimeString(),
      message
    });

    // Keep only last 50 logs
    if (this.logs.length > 50) {
      this.logs = this.logs.slice(0, 50);
    }

    console.log(`WebSocket Test: ${message}`);
  }
}

import { HttpInterceptorFn } from '@angular/common/http';
import { inject } from '@angular/core';
import { Router } from '@angular/router';
import { catchError } from 'rxjs/operators';
import { throwError } from 'rxjs';

export const authInterceptor: HttpInterceptorFn = (req, next) => {
  const router = inject(Router);
  const token = localStorage.getItem('auth_token');

  if (token) {
    const authReq = req.clone({
      headers: req.headers
        .set('Authorization', `Bearer ${token}`)
        .set('Cache-Control', 'no-cache, no-store, must-revalidate')
        .set('Pragma', 'no-cache')
        .set('Expires', '0')
    });

    return next(authReq).pipe(
      catchError((error) => {
        console.log('Auth interceptor caught error:', error.status, error);
        if (error.status === 401) {
          console.log('401 error - handling session expiry');

          // Clear auth data from localStorage
          localStorage.removeItem('auth_token');
          localStorage.removeItem('user_data');

          // Only redirect if not already on login page
          if (!router.url.includes('/login')) {
            console.log('Redirecting to login from:', router.url);
            router.navigate(['/login'], {
              queryParams: {
                returnUrl: router.url,
                reason: 'session_expired'
              }
            });
          } else {
            console.log('Already on login page, not redirecting');
          }
        }
        return throwError(() => error);
      })
    );
  }

  // Add cache-control headers even for requests without auth token
  const noCacheReq = req.clone({
    headers: req.headers
      .set('Cache-Control', 'no-cache, no-store, must-revalidate')
      .set('Pragma', 'no-cache')
      .set('Expires', '0')
  });

  return next(noCacheReq);
};

/**
 * Exchange Credentials Type Definitions
 *
 * Complete type system for exchange credentials management with testnet/mainnet support.
 */

// ============================================================================
// ENUMS
// ============================================================================

/**
 * Supported cryptocurrency exchange platforms
 */
export enum ExchangeType {
  BYBIT = 'BYBIT',
  BINANCE = 'BINANCE',
  OKX = 'OKX',
  COINBASE = 'COINBASE',
  KRAKEN = 'KRAKEN',
  BINGX = 'BINGX',
  MEXC = 'MEXC',
  GATEIO = 'GATEIO',
  BITGET = 'BITGET'
}


/**
 * Credential status
 */
export enum CredentialStatus {
  ACTIVE = 'ACTIVE',
  INACTIVE = 'INACTIVE',
  ERROR = 'ERROR',
  EXPIRED = 'EXPIRED',
  INSUFFICIENT_PERMISSIONS = 'INSUFFICIENT_PERMISSIONS'
}

// ============================================================================
// CORE DOMAIN MODELS
// ============================================================================

/**
 * Exchange Credential Entity
 * Represents a single API credential for an exchange
 */
export interface ExchangeCredential {
  id: string;
  userId: string;
  exchange: ExchangeType;
  apiKeyPreview: string;
  label?: string;
  isActive: boolean;
  status?: CredentialStatus;
  createdAt: string;
  updatedAt: string;
  lastConnectedAt?: string;
  metadata?: Record<string, any>;
}

// ============================================================================
// REQUEST/RESPONSE MODELS
// ============================================================================

/**
 * Request to create a new exchange credential
 */
export interface CreateExchangeCredentialRequest {
  exchange: ExchangeType;
  apiKey: string;
  apiSecret: string;
  authToken?: string; // Browser session token (for MEXC futures trading)
  label?: string;
  isActive?: boolean; // Optional flag to set credential as active (defaults to true)
}

/**
 * Request to update an existing credential
 * All fields are optional - only provided fields will be updated
 */
export interface UpdateExchangeCredentialRequest {
  label?: string;
  apiKey?: string;
  apiSecret?: string;
  authToken?: string; // Browser session token (for MEXC)
  isActive?: boolean;  // Set active status (true/false) - no automatic deactivation of others
}

/**
 * Response for single credential operations
 */
export interface ExchangeCredentialResponse {
  success: boolean;
  data: ExchangeCredential;
  message?: string;
  timestamp: string;
}

/**
 * Response for list operations
 */
export interface ExchangeCredentialsListResponse {
  success: boolean;
  data: {
    credentials: ExchangeCredential[];
    totalCount: number;
    activeCredentials?: Record<ExchangeType, ExchangeCredential>;
  };
  timestamp: string;
}

/**
 * Request to test credential connection
 */
export interface TestConnectionRequest {
  exchange: ExchangeType;
  apiKey: string;
  apiSecret: string;
  authToken?: string; // Browser session token (for MEXC)
}

/**
 * Response for connection test
 */
export interface TestConnectionResponse {
  success: boolean;
  message: string;
  accountPreview?: {
    accountId?: string;
    totalEquity?: string;
    totalBalance?: string;
    currency?: string;
    accountType?: string;
    [key: string]: any;
  };
  permissions?: string[];
  warnings?: string[];
  timestamp: string;
}

/**
 * Request to activate a credential
 */
export interface ActivateCredentialRequest {
  credentialId: string;
}

/**
 * Response for delete operation
 */
export interface DeleteCredentialResponse {
  success: boolean;
  message: string;
  timestamp: string;
}

// ============================================================================
// ERROR MODELS
// ============================================================================

/**
 * Generic error response from API
 */
export interface ExchangeCredentialError {
  success: false;
  error: {
    code: string;
    message: string;
    details?: any;
    fieldErrors?: Record<string, string[]>;
  };
  timestamp: string;
}

/**
 * Error codes that can be returned from the API
 */
export enum ExchangeCredentialErrorCode {
  INVALID_API_KEY = 'INVALID_API_KEY',
  INVALID_API_SECRET = 'INVALID_API_SECRET',
  INVALID_EXCHANGE = 'INVALID_EXCHANGE',
  INVALID_ENVIRONMENT = 'INVALID_ENVIRONMENT',
  DUPLICATE_CREDENTIAL = 'DUPLICATE_CREDENTIAL',
  CREDENTIAL_NOT_FOUND = 'CREDENTIAL_NOT_FOUND',
  CANNOT_DELETE_ACTIVE_CREDENTIAL = 'CANNOT_DELETE_ACTIVE_CREDENTIAL',
  EXCHANGE_API_ERROR = 'EXCHANGE_API_ERROR',
  EXCHANGE_CONNECTION_FAILED = 'EXCHANGE_CONNECTION_FAILED',
  EXCHANGE_RATE_LIMITED = 'EXCHANGE_RATE_LIMITED',
  INSUFFICIENT_PERMISSIONS = 'INSUFFICIENT_PERMISSIONS',
  INTERNAL_SERVER_ERROR = 'INTERNAL_SERVER_ERROR',
  DATABASE_ERROR = 'DATABASE_ERROR',
  UNAUTHORIZED = 'UNAUTHORIZED',
  FORBIDDEN = 'FORBIDDEN'
}

// ============================================================================
// UI-SPECIFIC MODELS
// ============================================================================

/**
 * Credential display model with computed properties
 */
export interface CredentialDisplayModel extends ExchangeCredential {
  exchangeName: string;
  exchangeLogo: string;
  statusColor: string;
  createdAtFormatted: string;
  updatedAtFormatted: string;
}

/**
 * Form model for credential creation/editing
 */
export interface CredentialFormModel {
  exchange: ExchangeType | null;
  apiKey: string;
  apiSecret: string;
  label: string;
}

/**
 * Filter options for credential list
 */
export interface CredentialFilterOptions {
  exchange?: ExchangeType;
  isActive?: boolean;
  searchQuery?: string;
  sortBy?: 'createdAt' | 'updatedAt' | 'exchange' | 'label';
  sortDirection?: 'asc' | 'desc';
}

/**
 * Grouped credentials by exchange
 */
export interface GroupedCredentials {
  exchange: ExchangeType;
  exchangeName: string;
  exchangeLogo: string;
  credentials: ExchangeCredential[];
  activeCredential: ExchangeCredential | null;
  count: number;
}

// ============================================================================
// TYPE GUARDS
// ============================================================================

/**
 * Type guard to check if object is ExchangeCredential
 */
export function isExchangeCredential(obj: any): obj is ExchangeCredential {
  return (
    typeof obj === 'object' &&
    obj !== null &&
    typeof obj.id === 'string' &&
    typeof obj.userId === 'string' &&
    Object.values(ExchangeType).includes(obj.exchange) &&
    typeof obj.apiKeyPreview === 'string' &&
    typeof obj.isActive === 'boolean'
  );
}

/**
 * Type guard to check if response is an error
 */
export function isExchangeCredentialError(response: any): response is ExchangeCredentialError {
  return (
    typeof response === 'object' &&
    response !== null &&
    response.success === false &&
    typeof response.error === 'object' &&
    typeof response.error.code === 'string' &&
    typeof response.error.message === 'string'
  );
}

/**
 * Type guard to check if string is valid ExchangeType
 */
export function isValidExchangeType(value: string): value is ExchangeType {
  return Object.values(ExchangeType).includes(value as ExchangeType);
}


// ============================================================================
// CONSTANTS
// ============================================================================

/**
 * Exchange metadata - display names, logos, colors, etc.
 */
export const EXCHANGE_METADATA: Record<ExchangeType, {
