    it('should do nothing if no position to close', () => {
      component.positionToClose.set(null);
      
      component.confirmClose();
      
      expect(mockPriceArbitrageService.closePosition).not.toHaveBeenCalled();
    });
  });

  describe('Display Methods', () => {
    const mockPosition = mockPositions[0];

    it('should get unrealized PnL', () => {
      const result = component.getUnrealizedPnl(mockPosition);
      
      expect(mockPriceArbitrageService.calculateUnrealizedPnl).toHaveBeenCalledWith(mockPosition);
      expect(result).toBe(150.50);
    });

    it('should format entry spread display', () => {
      const result = component.getEntrySpreadDisplay(mockPosition);
      
      expect(result).toBe('0.1500%');
    });

    it('should format current spread display', () => {
      const result = component.getCurrentSpreadDisplay(mockPosition);
      
      expect(result).toBe('0.1200%');
    });

    it('should return N/A for undefined current spread', () => {
      const positionWithoutSpread = { ...mockPosition, currentSpreadPercent: undefined };
      mockTranslationService.translate.and.returnValue('N/A');
      
      const result = component.getCurrentSpreadDisplay(positionWithoutSpread);
      
      expect(result).toBe('N/A');
    });

    it('should format price correctly', () => {
      const result = component.formatPrice(50000.123);
      
      expect(result).toBe('$50000.12');
    });

    it('should format positive PnL correctly', () => {
      const result = component.formatPnl(150.50);
      
      expect(result).toBe('+$150.50');
    });

    it('should format negative PnL correctly', () => {
      const result = component.formatPnl(-75.25);
      
      expect(result).toBe('-$75.25');
    });

    it('should return N/A for undefined PnL', () => {
      mockTranslationService.translate.and.returnValue('N/A');
      
      const result = component.formatPnl(undefined);
      
      expect(result).toBe('N/A');
    });

    it('should get correct status class', () => {
      expect(component.getStatusClass(PriceArbitrageStatus.ACTIVE)).toBe('status-active');
      expect(component.getStatusClass(PriceArbitrageStatus.COMPLETED)).toBe('status-completed');
      expect(component.getStatusClass(PriceArbitrageStatus.ERROR)).toBe('status-error');
      expect(component.getStatusClass(PriceArbitrageStatus.PENDING)).toBe('status-pending');
      expect(component.getStatusClass(PriceArbitrageStatus.CANCELLED)).toBe('status-cancelled');
    });

    it('should call translation service', () => {
      const key = 'test.key';
      
      component.translate(key);
      
      expect(mockTranslationService.translate).toHaveBeenCalledWith(key);
    });
  });

  describe('Auto Refresh', () => {
    it('should refresh positions automatically', fakeAsync(() => {
      component.ngOnInit();
      
      // Initial load
      expect(mockPriceArbitrageService.getPositions).toHaveBeenCalledTimes(1);
      
      // After 5 seconds
      tick(5000);
      expect(mockPriceArbitrageService.getPositions).toHaveBeenCalledTimes(2);
      
      // After another 5 seconds
      tick(5000);
      expect(mockPriceArbitrageService.getPositions).toHaveBeenCalledTimes(3);
    }));

    it('should handle refresh errors silently', fakeAsync(() => {
      component.ngOnInit();
      
      // Make subsequent calls fail
      mockPriceArbitrageService.getPositions.and.returnValue(throwError(() => new Error('Refresh error')));
      
      tick(5000);
      
      // Should not affect component state
      expect(component.positions()).toEqual(mockPositions);
      expect(component.isLoading()).toBe(false);
    }));

    it('should use current filter status in auto refresh', fakeAsync(() => {
      component.selectedStatus.set(PriceArbitrageStatus.COMPLETED);
      component.ngOnInit();
      
      tick(5000);
      
      expect(mockPriceArbitrageService.getPositions).toHaveBeenCalledWith(PriceArbitrageStatus.COMPLETED);
    }));
  });
});

import { Component, OnInit, OnDestroy, inject, signal, computed, effect } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { interval, Subscription, forkJoin } from 'rxjs';
import { switchMap } from 'rxjs/operators';

import { PriceArbitrageService } from '../../services/price-arbitrage.service';
import { GraduatedEntryService, GraduatedEntryPosition } from '../../services/graduated-entry.service';
import { RealtimeService, PriceUpdate } from '../../services/realtime.service';
import { TranslationService } from '../../services/translation.service';
import {
  PriceArbitragePositionDTO,
  PriceArbitrageStatus,
  calculateHoldingTime,
  formatPnlDisplay
} from '../../models/price-arbitrage.model';

import { CardComponent, CardHeaderComponent, CardTitleComponent, CardContentComponent } from '../../components/ui/card/card.component';
import { ButtonComponent } from '../../components/ui/button/button.component';
import { DialogComponent, DialogHeaderComponent, DialogTitleComponent, DialogContentComponent, DialogFooterComponent } from '../../components/ui/dialog/dialog.component';

// Unified position interface combining both Price Arbitrage and Graduated Entry
interface UnifiedArbitragePosition {
  id: string;
  type: 'price' | 'graduated';
  symbol: string;
  primaryExchange: string;
  hedgeExchange: string;
  primaryLeverage: number;
  hedgeLeverage: number;
  primaryMargin: number;
  hedgeMargin: number;
  primarySide: string;
  hedgeSide: string;
  status: string;
  entryPrimaryPrice: number;
  entryHedgePrice: number;
  currentPrimaryPrice?: number;
  currentHedgePrice?: number;
  entrySpreadPercent: number;
  currentSpreadPercent?: number;
  grossProfit: number;
  netProfit: number;
  totalFundingEarned: number;
  totalFees: number;
  unrealizedPnl: number;
  openedAt?: Date;
  // Original position data
  original: PriceArbitragePositionDTO | GraduatedEntryPosition;
}

@Component({
  selector: 'app-active-arbitrage-positions',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    CardComponent,
    CardHeaderComponent,
    CardTitleComponent,
    CardContentComponent,
    ButtonComponent,
    DialogComponent,
    DialogHeaderComponent,
    DialogTitleComponent,
    DialogContentComponent,
    DialogFooterComponent
  ],
  templateUrl: './active-arbitrage-positions.component.html',
  styleUrl: './active-arbitrage-positions.component.scss'
})
export class ActiveArbitragePositionsComponent implements OnInit, OnDestroy {
  private priceArbitrageService = inject(PriceArbitrageService);
  private graduatedEntryService = inject(GraduatedEntryService);
  private realtimeService = inject(RealtimeService);
  private translationService = inject(TranslationService);

  positions = signal<UnifiedArbitragePosition[]>([]);
  isLoading = signal<boolean>(false);
  error = signal<string | null>(null);
  selectedStatus = signal<'ALL' | PriceArbitrageStatus>(PriceArbitrageStatus.ACTIVE);

  showCloseDialog = signal<boolean>(false);
  positionToClose = signal<UnifiedArbitragePosition | null>(null);
  isClosing = signal<boolean>(false);

  // Real-time price cache: Map<exchange:symbol, price>
  private priceCache = signal<Map<string, number>>(new Map());

  // Positions with real-time calculated profits
  positionsWithRealtimeProfit = computed(() => {
    const positions = this.positions();
    const prices = this.priceCache();

    return positions.map(pos => {
      // Get real-time prices from cache
      const primaryKey = `${pos.primaryExchange}:${pos.symbol}`;
      const hedgeKey = `${pos.hedgeExchange}:${pos.symbol}`;

      const primaryPrice = prices.get(primaryKey);
      const hedgePrice = prices.get(hedgeKey);

      // If we have real-time prices, recalculate profits
      if (primaryPrice && hedgePrice) {
        const realtimeProfit = this.calculateRealtimeProfit(pos, primaryPrice, hedgePrice);
        return {
          ...pos,
          currentPrimaryPrice: primaryPrice,
          currentHedgePrice: hedgePrice,
          grossProfit: realtimeProfit.grossProfit,
          netProfit: realtimeProfit.netProfit,
          unrealizedPnl: realtimeProfit.totalUnrealizedPnl
        };
      }

      // Return original position if no real-time prices
      return pos;
    });
  });

  private refreshSubscription?: Subscription;
  private priceSubscription?: Subscription;
  private readonly REFRESH_INTERVAL = 5000;

  // Effect to resubscribe to symbols when positions change
  constructor() {
    effect(() => {
      const positions = this.positions();
      if (positions.length > 0) {
        // Resubscribe to all position symbols whenever positions change
        const symbols = new Set(positions.map(p => p.symbol));
        symbols.forEach(symbol => {
          if (!this.realtimeService.isConnected(symbol)) {
            this.realtimeService.subscribeToSymbol(symbol);
          }
        });
      }
    });
  }

  filteredPositions = computed(() => {
    const status = this.selectedStatus();
    const allPositions = this.positionsWithRealtimeProfit(); // Use real-time calculated positions

    if (status === 'ALL') {
      return allPositions;
    }

    return allPositions.filter(p => p.status === status);
  });

  activeCount = computed(() =>
    this.positions().filter(p => p.status === PriceArbitrageStatus.ACTIVE).length
  );

  completedCount = computed(() =>
    this.positions().filter(p => p.status === PriceArbitrageStatus.COMPLETED).length
  );

  errorCount = computed(() =>
    this.positions().filter(p => p.status === PriceArbitrageStatus.ERROR).length
  );

  ngOnInit(): void {
    this.loadPositions();
    this.startAutoRefresh();
    this.subscribeToRealtimePrices();
  }

  ngOnDestroy(): void {
    this.stopAutoRefresh();
    this.unsubscribeFromRealtimePrices();
  }

  loadPositions(): void {
    this.isLoading.set(true);
    this.error.set(null);

    const status = this.selectedStatus() === 'ALL' ? undefined : this.selectedStatus();

    // Load both Price Arbitrage and Graduated Entry positions in parallel
    forkJoin({
      priceArbitrage: this.priceArbitrageService.getPositions(status as PriceArbitrageStatus | undefined),
      graduatedEntry: this.graduatedEntryService.getPositions(false) // Only active positions
    }).subscribe({
      next: ({ priceArbitrage, graduatedEntry }) => {
        // Convert both types to unified format
        const unifiedPositions: UnifiedArbitragePosition[] = [
          ...this.convertPriceArbitragePositions(priceArbitrage),
          ...this.convertGraduatedEntryPositions(graduatedEntry)
        ];

        this.positions.set(unifiedPositions);
        this.isLoading.set(false);
      },
      error: (err) => {
        console.error('Error loading positions:', err);
        this.error.set(err.message || 'Failed to load positions');
        this.isLoading.set(false);
      }
    });
  }

  /**
   * Convert Price Arbitrage positions to unified format
   */
  private convertPriceArbitragePositions(positions: PriceArbitragePositionDTO[]): UnifiedArbitragePosition[] {
    return positions.map(pos => ({
      id: pos.id,
      type: 'price' as const,
      symbol: pos.symbol,
      primaryExchange: pos.primaryExchange,
      hedgeExchange: pos.hedgeExchange,
      primaryLeverage: pos.primaryLeverage,
      hedgeLeverage: pos.hedgeLeverage,
      primaryMargin: pos.primaryMargin,
      hedgeMargin: pos.hedgeMargin,
      primarySide: 'SHORT', // Price arbitrage is always SHORT primary
      hedgeSide: 'LONG', // and LONG hedge
      status: pos.status,
      entryPrimaryPrice: pos.entryPrimaryPrice,
      entryHedgePrice: pos.entryHedgePrice,
      currentPrimaryPrice: pos.currentPrimaryPrice,
      currentHedgePrice: pos.currentHedgePrice,
      entrySpreadPercent: pos.entrySpreadPercent,
      currentSpreadPercent: pos.currentSpreadPercent,
      grossProfit: pos.totalPnl || 0,
      netProfit: pos.totalPnl || 0,
      totalFundingEarned: 0, // Price arbitrage doesn't track funding separately
      totalFees: pos.primaryFees + pos.hedgeFees,
      unrealizedPnl: this.priceArbitrageService.calculateUnrealizedPnl(pos),
      openedAt: pos.openedAt ? (typeof pos.openedAt === 'string' ? new Date(pos.openedAt) : pos.openedAt) : undefined,
      original: pos
    }));
  }

  /**
   * Convert Graduated Entry positions to unified format
   */
  private convertGraduatedEntryPositions(positions: GraduatedEntryPosition[]): UnifiedArbitragePosition[] {
    return positions.map(pos => {
      // Calculate entry spread percent
      const primaryEntryPrice = pos.primary.entryPrice || 0;
      const hedgeEntryPrice = pos.hedge.entryPrice || 0;
      const entrySpreadPercent = primaryEntryPrice > 0
        ? ((hedgeEntryPrice - primaryEntryPrice) / primaryEntryPrice) * 100
        : 0;

      // Calculate current spread percent
      const primaryCurrentPrice = pos.primary.currentPrice || primaryEntryPrice;
      const hedgeCurrentPrice = pos.hedge.currentPrice || hedgeEntryPrice;
      const currentSpreadPercent = primaryCurrentPrice > 0
        ? ((hedgeCurrentPrice - primaryCurrentPrice) / primaryCurrentPrice) * 100
        : 0;

      // Backend already handles funding check (returns 0 if fundingUpdateCount === 0)
      const totalFundingEarned = pos.primary.totalFundingEarned + pos.hedge.totalFundingEarned;

      return {
        id: pos.positionId,
        type: 'graduated' as const,
        symbol: pos.symbol,
        primaryExchange: pos.primary.exchange,
        hedgeExchange: pos.hedge.exchange,
        primaryLeverage: pos.primary.leverage,
        hedgeLeverage: pos.hedge.leverage,
        primaryMargin: (pos.primary.entryPrice || 0) * (pos.primary.filledQuantity || 0) / pos.primary.leverage,
        hedgeMargin: (pos.hedge.entryPrice || 0) * (pos.hedge.filledQuantity || 0) / pos.hedge.leverage,
        primarySide: pos.primary.side,
        hedgeSide: pos.hedge.side,
        status: pos.status.toUpperCase(),
        entryPrimaryPrice: primaryEntryPrice,
        entryHedgePrice: hedgeEntryPrice,
        currentPrimaryPrice: primaryCurrentPrice,
        currentHedgePrice: hedgeCurrentPrice,
        entrySpreadPercent,
        currentSpreadPercent,
        // Backend already calculated with actual funding only
        grossProfit: pos.grossProfit,
        netProfit: pos.netProfit,
        totalFundingEarned,
        totalFees: pos.primary.tradingFees + pos.hedge.tradingFees,
        unrealizedPnl: pos.totalUnrealizedPnl,
        openedAt: pos.startedAt,
        original: pos
      };
    });
  }

  private startAutoRefresh(): void {
    this.refreshSubscription = interval(this.REFRESH_INTERVAL)
      .pipe(
        switchMap(() => {
          const status = this.selectedStatus() === 'ALL' ? undefined : this.selectedStatus();
          return forkJoin({
            priceArbitrage: this.priceArbitrageService.getPositions(status as PriceArbitrageStatus | undefined),
            graduatedEntry: this.graduatedEntryService.getPositions(false)
          });
        })
      )
      .subscribe({
        next: ({ priceArbitrage, graduatedEntry }) => {
          const unifiedPositions: UnifiedArbitragePosition[] = [
            ...this.convertPriceArbitragePositions(priceArbitrage),
            ...this.convertGraduatedEntryPositions(graduatedEntry)
          ];
          this.positions.set(unifiedPositions);
        },
        error: (err) => {
          console.error('Error refreshing positions:', err);
        }
      });
  }

  private stopAutoRefresh(): void {
    if (this.refreshSubscription) {
      this.refreshSubscription.unsubscribe();
    }
  }

  changeStatusFilter(status: 'ALL' | PriceArbitrageStatus): void {
    this.selectedStatus.set(status);
    this.loadPositions();
  }

  openCloseDialog(position: UnifiedArbitragePosition): void {
    // Only allow closing Price Arbitrage positions for now
    // Graduated Entry positions have their own closing mechanism
    if (position.type !== 'price') {
      alert('Graduated Entry positions cannot be closed from this page.');
      return;
    }
    this.positionToClose.set(position);
    this.showCloseDialog.set(true);
  }

  cancelClose(): void {
    this.showCloseDialog.set(false);
    this.positionToClose.set(null);
  }

  confirmClose(): void {
    const position = this.positionToClose();
    if (!position || position.type !== 'price') return;

    this.isClosing.set(true);

    this.priceArbitrageService.closePosition(position.id)
      .subscribe({
        next: (result) => {
          console.log('Position closed successfully:', result);

          // Reload all positions after closing
          this.loadPositions();

          this.showCloseDialog.set(false);
          this.positionToClose.set(null);
          this.isClosing.set(false);

          alert(`${this.translate('arbitrage.positions.close.success')} Total P&L: $${result.totalPnl.toFixed(2)}`);
        },
        error: (err) => {
          console.error('Error closing position:', err);
          this.isClosing.set(false);
          alert(`${this.translate('arbitrage.positions.close.error')}: ${err.message}`);
        }
      });
  }

  getUnrealizedPnl(position: UnifiedArbitragePosition): number {
    return position.unrealizedPnl;
  }

  getPnlDisplay(position: UnifiedArbitragePosition) {
    const pnl = this.getUnrealizedPnl(position);
    return formatPnlDisplay(pnl);
  }

  getHoldingTimeDisplay(position: UnifiedArbitragePosition): string {
    if (!position.openedAt) return this.translate('arbitrage.positions.na');
    return calculateHoldingTime(position.openedAt).formatted;
  }

  getCurrentSpreadDisplay(position: UnifiedArbitragePosition): string {
    if (position.currentSpreadPercent !== undefined) {
      return `${position.currentSpreadPercent.toFixed(4)}%`;
    }
    return this.translate('arbitrage.positions.na');
  }

  getEntrySpreadDisplay(position: UnifiedArbitragePosition): string {
    return `${position.entrySpreadPercent.toFixed(4)}%`;
  }

  getStatusClass(status: string): string {
    const normalizedStatus = status.toUpperCase();
    switch (normalizedStatus) {
      case 'ACTIVE':
        return 'status-active';
      case 'COMPLETED':
        return 'status-completed';
      case 'ERROR':
        return 'status-error';
      case 'PENDING':
      case 'INITIALIZING':
      case 'EXECUTING':
        return 'status-pending';
      case 'CANCELLED':
        return 'status-cancelled';
      case 'LIQUIDATED':
        return 'status-liquidated';
      default:
        return 'status-unknown';
    }
  }

  translate(key: string): string {
    return this.translationService.translate(key);
  }

  formatPrice(price: number): string {
    return `$${price.toFixed(2)}`;
  }

  formatPnl(pnl: number | undefined): string {
    if (pnl === undefined || pnl === null) return this.translate('arbitrage.positions.na');
    const sign = pnl >= 0 ? '+' : '';
    return `${sign}$${pnl.toFixed(2)}`;
  }

  formatEntryTime(date: Date | undefined): string {
    if (!date) return this.translate('arbitrage.positions.na');

    const entryDate = typeof date === 'string' ? new Date(date) : date;
    const now = new Date();
    const diffMs = now.getTime() - entryDate.getTime();
    const diffMins = Math.floor(diffMs / 60000);

    // If less than 60 minutes, show "X min ago"
    if (diffMins < 60) {
      return `${diffMins}m ago`;
    }

    // Otherwise show time in HH:MM format
    const hours = entryDate.getHours().toString().padStart(2, '0');
    const minutes = entryDate.getMinutes().toString().padStart(2, '0');
    return `${hours}:${minutes}`;
  }

  getFilterEnumValue(status: string): PriceArbitrageStatus {
    return status as PriceArbitrageStatus;
  }

  /**
   * Subscribe to real-time price updates for all position symbols
   */
  private subscribeToRealtimePrices(): void {
    this.priceSubscription = this.realtimeService.price$.subscribe((update: PriceUpdate) => {
      // Update price cache with exchange:symbol key
      const key = `${update.symbol}`;
      const currentCache = this.priceCache();
      const newCache = new Map(currentCache);

      // We need to map this to exchange-specific prices
      // For now, update all exchanges with the same symbol
      this.positions().forEach(pos => {
        if (pos.symbol === update.symbol) {
          newCache.set(`${pos.primaryExchange}:${pos.symbol}`, update.price);
          newCache.set(`${pos.hedgeExchange}:${pos.symbol}`, update.price);
        }
      });

      this.priceCache.set(newCache);
    });

    // Subscribe to symbols from positions
    const symbols = new Set(this.positions().map(p => p.symbol));
    symbols.forEach(symbol => {
      this.realtimeService.subscribeToSymbol(symbol);
    });
  }

  /**
   * Unsubscribe from real-time price updates
   */
  private unsubscribeFromRealtimePrices(): void {
    if (this.priceSubscription) {
      this.priceSubscription.unsubscribe();
    }
    this.realtimeService.closeAllConnections();
  }

  /**
   * Calculate real-time profit for a position using WebSocket prices
   */
  private calculateRealtimeProfit(
    position: UnifiedArbitragePosition,
    primaryPrice: number,
    hedgePrice: number
  ): {
    primaryUnrealizedPnl: number;
    hedgeUnrealizedPnl: number;
    totalUnrealizedPnl: number;
    grossProfit: number;
    netProfit: number;
  } {
    const primaryEntryPrice = position.entryPrimaryPrice || 0;
    const primaryQuantity = position.primaryMargin * position.primaryLeverage / primaryEntryPrice;

    const hedgeEntryPrice = position.entryHedgePrice || 0;
    const hedgeQuantity = position.hedgeMargin * position.hedgeLeverage / hedgeEntryPrice;

    // Calculate unrealized PnL for each side
    const primaryUnrealizedPnl = position.primarySide === 'LONG'
      ? (primaryPrice - primaryEntryPrice) * primaryQuantity
      : (primaryEntryPrice - primaryPrice) * primaryQuantity;

    const hedgeUnrealizedPnl = position.hedgeSide === 'LONG'
      ? (hedgePrice - hedgeEntryPrice) * hedgeQuantity
      : (hedgeEntryPrice - hedgePrice) * hedgeQuantity;

    const totalUnrealizedPnl = primaryUnrealizedPnl + hedgeUnrealizedPnl;

    // Calculate entry spread profit
    const primaryNotional = primaryEntryPrice * primaryQuantity;
    const hedgeNotional = hedgeEntryPrice * hedgeQuantity;
    const entrySpreadProfit = position.primarySide === 'LONG'
      ? hedgeNotional - primaryNotional
      : primaryNotional - hedgeNotional;

    // Total funding earned
    const totalFunding = position.totalFundingEarned;

    // Real-time Gross Profit = Entry Spread + Funding + Unrealized PnL
    const grossProfit = entrySpreadProfit + totalFunding + totalUnrealizedPnl;

    // Real-time Net Profit = Gross Profit - Fees
    const netProfit = grossProfit - position.totalFees;

    return {
      primaryUnrealizedPnl,
      hedgeUnrealizedPnl,
      totalUnrealizedPnl,
      grossProfit,
      netProfit
    };
  }
}

import { Component, OnInit, OnDestroy, ViewEncapsulation, signal, computed, effect, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { RouterModule } from '@angular/router';
import { interval, Subscription } from 'rxjs';
import { startWith, switchMap } from 'rxjs/operators';
import { PublicFundingRatesService } from '../../services/public-funding-rates.service';
import { FundingRateOpportunity, SpreadStabilityMetrics } from '../../models/public-funding-rate.model';
import { CardComponent, CardHeaderComponent, CardTitleComponent, CardContentComponent } from '../../components/ui/card/card.component';
import { ButtonComponent } from '../../components/ui/button/button.component';
import { IconComponent } from '../../components/ui/icon/icon.component';
import { DialogComponent, DialogHeaderComponent, DialogTitleComponent, DialogContentComponent, DialogFooterComponent } from '../../components/ui/dialog/dialog.component';
import { TradingSettingsService, TradingSettings } from '../../services/trading-settings.service';
import { ThemeService } from '../../services/theme.service';
import { FundingRateSpreadChartComponent } from '../../components/trading/funding-rate-spread-chart/funding-rate-spread-chart.component';
import { FundingSpreadDetailsComponent } from '../../components/trading/funding-spread-details/funding-spread-details.component';

/**
 * Arbitrage Funding Page
 *
 * Displays funding rate arbitrage opportunities fetched directly from exchange public APIs.
 * NO API KEYS REQUIRED - all data is public.
 *
 * Features:
 * - Real-time funding rates from multiple exchanges
 * - Automatic calculation of funding spreads
 * - Price spread analysis
 * - Sortable by funding spread, price spread, or symbol
 * - Auto-refresh every 30 seconds
 */
@Component({
  selector: 'app-arbitrage-funding',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    RouterModule,
    CardComponent,
    CardHeaderComponent,
    CardTitleComponent,
    CardContentComponent,
    ButtonComponent,
    IconComponent,
    DialogComponent,
    DialogHeaderComponent,
    DialogTitleComponent,
    DialogContentComponent,
    DialogFooterComponent,
    FundingRateSpreadChartComponent,
    FundingSpreadDetailsComponent,
  ],
  templateUrl: './arbitrage-funding.component.html',
  styleUrls: ['./arbitrage-funding.component.scss'],
  encapsulation: ViewEncapsulation.None,
})
export class ArbitrageFundingComponent implements OnInit, OnDestroy {
  // State
  opportunities = signal<FundingRateOpportunity[]>([]);
  isLoading = signal<boolean>(false);
  error = signal<string | null>(null);
  lastUpdated = signal<Date | null>(null);

  // Current time signal - updates every second to calculate time differences
  private currentTime = signal<number>(Date.now());

  // Auto-refresh
  private refreshSubscription?: Subscription;
  private timeUpdateInterval?: any;
  private readonly REFRESH_INTERVAL_MS = 30 * 1000; // 30 seconds

  // Filters
  searchQuery = signal<string>('');
  minFundingSpread = signal<number | null>(null);

  // Computed list of unique exchanges from all opportunities
  availableExchanges = computed(() => {
    const exchanges = new Set<string>();
    this.opportunities().forEach(opp => {
      opp.exchanges.forEach(ex => exchanges.add(ex.exchange));
    });
    return Array.from(exchanges).sort();
  });

  // Selected exchanges filter
  selectedExchanges = signal<Set<string>>(new Set());

  // Sorting
  sortColumn = signal<'fundingSpread' | 'priceSpread' | 'symbol' | 'estimatedAPR' | 'netAPR'>('fundingSpread');
  sortDirection = signal<'asc' | 'desc'>('asc');

  // PHASE2: Modal for detailed metrics
  showDetailsModal = signal<boolean>(false);
  selectedOpportunity = signal<FundingRateOpportunity | null>(null);
  loadingPhase2Metrics = signal<Set<string>>(new Set());
  phase2MetricsCache = signal<Map<string, FundingRateOpportunity>>(new Map());

  // Settings Dialog
  showSettingsDialog = signal<boolean>(false);
  // Writable settings for the dialog (cloned from service)
  private _subscriptionSettings = signal<TradingSettings>({
    defaultQuantity: 0.01,
    leverage: 3,
    autoCancelThreshold: 0.003,
    enableAutoCancel: true,
    executionDelay: 5,
    arbitrageSpreadThreshold: null
  });

  // Computed time since update - uses currentTime signal to avoid NG0100
  timeSinceUpdate = computed(() => {
    const lastUpdate = this.lastUpdated();
    if (!lastUpdate) return '';

    const now = this.currentTime();
    const seconds = Math.floor((now - lastUpdate.getTime()) / 1000);
    if (seconds < 60) return `${seconds}s ago`;

    const minutes = Math.floor(seconds / 60);
    return `${minutes}m ago`;
  });

  // Computed filtered & sorted opportunities
  filteredOpportunities = computed(() => {
    let filtered = [...this.opportunities()];

    // Filter by search query (symbol)
    const query = this.searchQuery().trim().toUpperCase();
    if (query) {
      filtered = filtered.filter(o => o.symbol.toUpperCase().includes(query));
    }

    // Filter by minimum funding spread
    const minSpread = this.minFundingSpread();
    if (minSpread !== null) {
      filtered = filtered.filter(o => Math.abs(o.maxFundingSpread * 100) >= minSpread);
    }

    // Filter by selected exchanges
    // Show only opportunities where BOTH bestLong and bestShort exchanges are selected
    const selected = this.selectedExchanges();
    if (selected.size > 0) {
      filtered = filtered.filter(opp =>
        selected.has(opp.bestLong.exchange) && selected.has(opp.bestShort.exchange)
      );
    }

    // Sort
    const column = this.sortColumn();
    const direction = this.sortDirection();

    filtered.sort((a, b) => {
      let comparison = 0;

      switch (column) {
        case 'fundingSpread':
          comparison = Math.abs(b.maxFundingSpread) - Math.abs(a.maxFundingSpread);
          break;
        case 'priceSpread':
          comparison = Math.abs(b.priceSpread) - Math.abs(a.priceSpread);
          break;
        case 'estimatedAPR':
          comparison = b.estimatedAPR - a.estimatedAPR;
          break;
        case 'netAPR':
          comparison = b.netAPR - a.netAPR;
          break;
        case 'symbol':
          comparison = a.symbol.localeCompare(b.symbol);
          break;
      }

      return direction === 'asc' ? comparison : -comparison;
    });

    return filtered;
  });

  private themeService = inject(ThemeService);

  constructor(
    private fundingRatesService: PublicFundingRatesService,
    private tradingSettingsService: TradingSettingsService
  ) {
    // Constructor simplified - chart rendering now handled by child component

    // Auto-select all exchanges when they become available
    effect(() => {
      const exchanges = this.availableExchanges();
      if (exchanges.length > 0 && this.selectedExchanges().size === 0) {
        this.selectAllExchanges();
      }
    }, { allowSignalWrites: true });
  }

  ngOnInit(): void {
    this.startAutoRefresh();
    this.startTimeUpdates();
  }

  ngOnDestroy(): void {
    this.stopAutoRefresh();
    this.stopTimeUpdates();
  }

  /**
   * Start updating currentTime every second
   * This allows time-based displays to update without causing NG0100
   */
  private startTimeUpdates(): void {
    this.timeUpdateInterval = setInterval(() => {
      this.currentTime.set(Date.now());
    }, 1000); // Update every second
  }

  /**
   * Stop time update interval
   */
  private stopTimeUpdates(): void {
    if (this.timeUpdateInterval) {
      clearInterval(this.timeUpdateInterval);
      this.timeUpdateInterval = undefined;
    }
  }

  /**
   * Start auto-refresh timer
   * Fetches funding rates every 30 seconds
   */
  private startAutoRefresh(): void {
    this.refreshSubscription = interval(this.REFRESH_INTERVAL_MS)
      .pipe(
        startWith(0), // Load immediately
        switchMap(() => this.fundingRatesService.getFundingRatesOpportunities())
      )
      .subscribe({
        next: (opportunities) => {
          this.opportunities.set(opportunities);
          this.error.set(null);
          this.isLoading.set(false);
          // Update lastUpdated after change detection to avoid NG0100
          setTimeout(() => this.lastUpdated.set(new Date()), 0);
        },
        error: (err) => {
          this.error.set('Failed to load funding rates: ' + err.message);
          this.isLoading.set(false);
        },
      });
  }

  /**
   * Stop auto-refresh timer
   */
  private stopAutoRefresh(): void {
    if (this.refreshSubscription) {
      this.refreshSubscription.unsubscribe();
      this.refreshSubscription = undefined;
    }
  }

  /**
   * Manually refresh funding rates
   */
  refresh(): void {
    this.isLoading.set(true);
    this.error.set(null);

    this.fundingRatesService.getFundingRatesOpportunities().subscribe({
      next: (opportunities) => {
        this.opportunities.set(opportunities);
        this.error.set(null);
        this.isLoading.set(false);
        // Update lastUpdated after change detection to avoid NG0100
        setTimeout(() => this.lastUpdated.set(new Date()), 0);
      },
      error: (err) => {
        this.error.set('Failed to load funding rates: ' + err.message);
        this.isLoading.set(false);
      },
    });
  }

  /**
   * Toggle sort column
   */
  toggleSort(column: 'fundingSpread' | 'priceSpread' | 'symbol' | 'estimatedAPR' | 'netAPR'): void {
    if (this.sortColumn() === column) {
      // Toggle direction
      this.sortDirection.set(this.sortDirection() === 'asc' ? 'desc' : 'asc');
    } else {
      // Change column, default to desc
      this.sortColumn.set(column);
      this.sortDirection.set('desc');
    }
  }

  /**
   * Clear all filters
   */
  clearFilters(): void {
    this.searchQuery.set('');
    this.minFundingSpread.set(null);
    this.selectAllExchanges();
  }

  /**
   * Toggle exchange selection
   */
  toggleExchange(exchange: string): void {
    const selected = new Set(this.selectedExchanges());
    if (selected.has(exchange)) {
      selected.delete(exchange);
    } else {
      selected.add(exchange);
    }
    this.selectedExchanges.set(selected);
  }

  /**
   * Select all exchanges
   */
  selectAllExchanges(): void {
    this.selectedExchanges.set(new Set(this.availableExchanges()));
  }

  /**
   * Deselect all exchanges
   */
  deselectAllExchanges(): void {
    this.selectedExchanges.set(new Set());
  }

  /**
   * Check if exchange is selected
   */
  isExchangeSelected(exchange: string): boolean {
    return this.selectedExchanges().has(exchange);
  }

  /**
   * Format funding rate for display
   */
  formatFundingRate(rate: string): string {
    const rateNum = parseFloat(rate);
    return (rateNum * 100).toFixed(4) + '%';
  }

  /**
   * Get color class for funding rate
   * Negative (paying) = red, Positive (receiving) = green
   */
  getFundingRateColorClass(rate: string): string {
    const rateNum = parseFloat(rate);
    if (rateNum < 0) return 'text-red-600 dark:text-red-400';
    if (rateNum > 0) return 'text-green-600 dark:text-green-400';
    return 'text-gray-600 dark:text-gray-400';
  }

  /**
   * Get color class for funding spread
   */
  getFundingSpreadColorClass(spread: number): string {
    const absSpread = Math.abs(spread * 100);
    if (absSpread >= 0.1) return 'text-green-600 dark:text-green-400';
    if (absSpread >= 0.05) return 'text-yellow-600 dark:text-yellow-400';
    return 'text-gray-600 dark:text-gray-400';
  }

  /**
   * Get color class for APR values
   */
  getAPRColorClass(apr: number): string {
    const aprPercent = apr * 100;
    if (aprPercent >= 50) return 'text-green-600 dark:text-green-400';
    if (aprPercent >= 20) return 'text-yellow-600 dark:text-yellow-400';
    return 'text-gray-600 dark:text-gray-400';
  }


  /**
   * Get exchanges tooltip text
   */
  getExchangesTooltip(opp: FundingRateOpportunity): string {
    return opp.exchanges.map(ex =>
      `${ex.exchange}: ${this.formatFundingRate(ex.fundingRate)} | $${ex.lastPrice}`
    ).join('\n');
  }

  // ===== PHASE 2: Details Modal Methods =====

  /**
   * Open details modal for displaying Phase 2 metrics
   * PHASE2: Triggers loading of historical stability metrics
   */
  openDetailsModal(opp: FundingRateOpportunity): void {
    this.selectedOpportunity.set(opp);
    this.showDetailsModal.set(true);
    // Trigger Phase 2 load
    this.loadPhase2MetricsForSymbol(opp.symbol);
  }

  /**
   * Close details modal
   */
  closeDetailsModal(): void {
    this.showDetailsModal.set(false);
    // Don't clear selectedOpportunity immediately to avoid flicker during close animation
    setTimeout(() => {
      if (!this.showDetailsModal()) {
        this.selectedOpportunity.set(null);
      }
    }, 300);
  }

  /**
   * Load Phase 2 historical stability metrics for a symbol
   * PHASE2: Fetches historical funding rates and calculates stability metrics
   */
  async loadPhase2MetricsForSymbol(symbol: string): Promise<void> {
    // Check cache
    if (this.phase2MetricsCache().has(symbol)) {
      // Data already loaded
      return;
    }

    // Find opportunity
    const opp = this.filteredOpportunities().find(o => o.symbol === symbol);
    if (!opp) return;

    // Set loading
    const loading = new Set(this.loadingPhase2Metrics());
    loading.add(symbol);
    this.loadingPhase2Metrics.set(loading);

    try {
      // Load metrics via service
      const updated = await this.fundingRatesService.loadPhase2Metrics(opp);

      // Update cache
      const cache = new Map(this.phase2MetricsCache());
      cache.set(symbol, updated);
      this.phase2MetricsCache.set(cache);
    } catch (error) {
      console.error('[Phase 2] Load failed:', error);
    } finally {
      // Clear loading
      const loading = new Set(this.loadingPhase2Metrics());
      loading.delete(symbol);
      this.loadingPhase2Metrics.set(loading);
    }
  }

  /**
   * Check if Phase 2 metrics are currently loading for a symbol
   * PHASE2: Used to show loading spinner in UI
   */
  isLoadingPhase2(symbol: string): boolean {
    return this.loadingPhase2Metrics().has(symbol);
  }

  /**
   * Get Phase 2 metrics from cache
   * PHASE2: Returns cached metrics or undefined if not loaded
   */
  getPhase2Metrics(symbol: string): FundingRateOpportunity | undefined {
    return this.phase2MetricsCache().get(symbol);
  }

  /**
   * Get icon name for stability trend
   * PHASE2: Maps trend to Material icon name
   */
  getTrendIcon(trend: string): string {
    switch (trend) {
      case 'improving': return 'trending_up';
      case 'declining': return 'trending_down';
      default: return 'trending_flat';
    }
  }

  // ===== Settings Dialog Methods =====

  /**
   * Get subscription settings (writable for ngModel binding)
   */
  subscriptionSettings() {
    return this._subscriptionSettings();
  }

  /**
   * Open settings dialog
   */
  openSettingsDialog(): void {
    // Clone current settings from service
    const currentSettings = this.tradingSettingsService.getSettings()();
    this._subscriptionSettings.set({ ...currentSettings });
    this.showSettingsDialog.set(true);
  }

  /**
   * Close settings dialog
   */
  closeSettingsDialog(): void {
    this.showSettingsDialog.set(false);
  }

  /**
   * Save settings to service
   */
  saveSettings(): void {
    // Save the modified settings to the service
    this.tradingSettingsService.updateSettings(this._subscriptionSettings());
    this.closeSettingsDialog();
  }

  /**
   * Translation stub method
   * Returns the key as-is for now
   */
  translate(key: string): string {
    return key;
  }

  /**
   * Calculate time to funding for a specific exchange
   * Uses currentTime signal to avoid NG0100
   */
  getTimeToFunding(exchange: { nextFundingTime: number }): string {
    const now = this.currentTime();
    const diff = exchange.nextFundingTime - now;

    if (diff <= 0) return '0m';

    const hours = Math.floor(diff / (1000 * 60 * 60));
    const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));

    if (hours > 0) {
      return `${hours}h ${minutes}m`;
    }
    return `${minutes}m`;
  }

  /**
   * Format time to funding with interval like "2h 15m / 8h"
   * Dynamically calculates the funding interval based on time until next funding
   * Uses currentTime signal to avoid NG0100
   */
  formatTimeToFundingWithInterval(exchange: { nextFundingTime: number; fundingInterval?: string }): string {
    const timeToFunding = this.getTimeToFunding(exchange);

    // Calculate interval dynamically based on time until next funding
    const now = this.currentTime();
    const timeUntilFunding = exchange.nextFundingTime - now;

    let interval = '8h'; // Default
    if (timeUntilFunding > 0) {
      if (timeUntilFunding <= 1 * 60 * 60 * 1000) {
        interval = '1h';
      } else if (timeUntilFunding <= 4 * 60 * 60 * 1000) {
        interval = '4h';
      }
    }

    return `${timeToFunding} / ${interval}`;
  }

  /**
   * Get color class for volatility
   * Higher volatility = higher risk
   */
  getVolatilityColorClass(volatility?: number): string {
    if (!volatility) return 'text-gray-600 dark:text-gray-400';

    const volatilityPercent = volatility * 100;

    if (volatilityPercent < 2) {
      // Low volatility = low risk (green)
      return 'text-green-600 dark:text-green-400';
    } else if (volatilityPercent < 5) {
      // Medium volatility = medium risk (yellow)
      return 'text-yellow-600 dark:text-yellow-400';
    } else {
      // High volatility = high risk (red)
      return 'text-red-600 dark:text-red-400';
    }
  }

  /**
   * Get tooltip for volatility explaining the risk level
   */
  getVolatilityTooltip(volatility?: number): string {
    if (!volatility) return 'Volatility data not available';

    const volatilityPercent = volatility * 100;

    if (volatilityPercent < 2) {
      return 'Low volatility (< 2%) - Low risk, stable price movement';
    } else if (volatilityPercent < 5) {
      return 'Medium volatility (2-5%) - Moderate risk, normal price fluctuations';
    } else {
      return 'High volatility (> 5%) - High risk, significant price swings';
    }
  }

  /**
   * Convert symbol to BingX format (e.g., BTCUSDT -> BTC-USDT)
   * Also handles CCXT perpetual contract format (e.g., BTC/USDT:USDT -> BTC-USDT)
   */
  private normalizeSymbolForBingX(symbol: string): string {
    // First, remove CCXT perpetual contract suffix (e.g., :USDT)
    let cleanSymbol = symbol.replace(/:.*$/, '');

    // Remove slashes (CCXT format BTC/USDT -> BTCUSDT)
    cleanSymbol = cleanSymbol.replace(/\//g, '');

    // If already has hyphen, return as-is
    if (cleanSymbol.includes('-')) {
      return cleanSymbol;
    }

    // BingX perpetual futures use USDT or USDC as quote currency
    if (cleanSymbol.endsWith('USDT')) {
      const base = cleanSymbol.slice(0, -4);
      return `${base}-USDT`;
    }

    if (cleanSymbol.endsWith('USDC')) {
      const base = cleanSymbol.slice(0, -4);
      return `${base}-USDC`;
    }

    return cleanSymbol;
  }

  /**
   * Get exchange URL for opening in new window
   */
  getExchangeUrl(exchange: string, symbol: string): string {
    const exchangeUpper = exchange.toUpperCase();

    switch (exchangeUpper) {
      case 'BYBIT':
        return `https://www.bybit.com/trade/usdt/${symbol}`;
      case 'BINGX':
        const bingxSymbol = this.normalizeSymbolForBingX(symbol);
        return `https://bingx.com/en/perpetual/${bingxSymbol}`;
      case 'MEXC':
        const mexcSymbol = symbol.includes('_') ? symbol : symbol.replace(/USDT$/, '_USDT').replace(/USDC$/, '_USDC');
        return `https://www.mexc.com/exchange/${mexcSymbol}`;
      case 'GATEIO':
        const gateioSymbol = symbol.includes('_') ? symbol : symbol.replace(/USDT$/, '_USDT').replace(/USDC$/, '_USDC');
        return `https://www.gate.io/trade/${gateioSymbol}`;
      case 'BITGET':
        return `https://www.bitget.com/futures/usdt/${symbol}`;
      case 'OKX':
        return `https://www.okx.com/trade-swap/${symbol.toLowerCase()}`;
      default:
        return '#';
    }
  }

  /**
   * Open exchange page in new window
   */
  openExchange(exchange: string, symbol: string): void {
    const url = this.getExchangeUrl(exchange, symbol);
    if (url !== '#') {
      window.open(url, '_blank', 'noopener,noreferrer');
    }
  }

  /**
   * Map FundingRateOpportunity to format expected by FundingSpreadDetailsComponent
   */
  mapToDetailsOpportunity(opp: FundingRateOpportunity): any {
    // Get Phase 2 metrics if available
    const metrics = this.phase2MetricsCache().get(opp.symbol);
    const spreadHistory7d = metrics?.spreadHistory7d;
    const spreadHistory30d = metrics?.spreadHistory30d;

    return {
      symbol: opp.symbol,
      spread: opp.maxFundingSpread,
      spreadPercentage: Math.abs(opp.maxFundingSpread * 100),
      bestLongExchange: opp.bestLong.exchange,
      bestShortExchange: opp.bestShort.exchange,
      longFundingRate: parseFloat(opp.bestLong.fundingRate),
      shortFundingRate: parseFloat(opp.bestShort.fundingRate),
      currentPrice: parseFloat(opp.bestLong.lastPrice),
      volume24h: opp.volume24h,
      metrics: {
        historicalStability: spreadHistory7d?.stabilityScore ? spreadHistory7d.stabilityScore / 100 : 0.5,
        avgSpread7d: spreadHistory7d?.average || 0,
        avgSpread30d: spreadHistory30d?.average || 0,
        spreadVolatility: spreadHistory7d?.standardDeviation || 0,
        trendDirection: metrics?.spreadStabilityTrend === 'improving' ? 'increasing' :
                       metrics?.spreadStabilityTrend === 'declining' ? 'decreasing' : 'stable'
      }
    };
  }
}

import { Component, OnInit, OnDestroy, ViewEncapsulation, signal, computed, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router, RouterModule } from '@angular/router';
import { interval, Subscription, forkJoin, of } from 'rxjs';
import { startWith, switchMap, catchError } from 'rxjs/operators';
import { PublicFundingRatesService } from '../../services/public-funding-rates.service';
import { FundingRateOpportunity } from '../../models/public-funding-rate.model';
import { IconComponent } from '../../components/ui/icon/icon.component';
import { ButtonComponent } from '../../components/ui/button/button.component';
import { DialogComponent, DialogHeaderComponent, DialogTitleComponent, DialogContentComponent, DialogFooterComponent } from '../../components/ui/dialog/dialog.component';
import { FundingSpreadDetailsComponent } from '../../components/trading/funding-spread-details/funding-spread-details.component';
import { DropdownComponent, DropdownOption } from '../../components/ui/dropdown/dropdown.component';
import { InputComponent } from '../../components/ui/input/input.component';

/**
 * Unified opportunity type that supports all strategies
 */
type UnifiedOpportunity = FundingRateOpportunity & {
  // Additional fields beyond FundingRateOpportunity
  maxFundingSpread?: number;
  maxFundingSpreadPercent?: string;
  priceSpread?: number;
  priceSpreadPercent?: string;
  priceSpreadUsdt?: string;
  combinedScore?: number;
  volatility24h?: number;
  volatility24hFormatted?: string;
  // NEW FIELDS FOR SPOT+FUTURES STRATEGY DISPLAY
  bestFundingExchange?: {
    exchange: string;
    fundingRate: number;
    nextFundingTime: number;
    currentPrice: number;
  };
  bestShortExchange?: {
    exchange: string;
    fundingRate: number;
    nextFundingTime: number;
    currentPrice: number;
  };
};

/**
 * Price Arbitrage Opportunities Component
 *
 * Displays price arbitrage opportunities with support for multiple strategies:
 * - Cross-Exchange Arbitrage (combined price spread + funding rates)
 * - Graduated Entry (Spot + Futures on same exchange)
 * Features:
 * - Real-time price and funding rate data
 * - Combined score calculation (price + funding)
 * - Strategy type filtering (Combined / Price Only / Spot + Futures / Graduated Entry)
 * - Sortable columns
 * - Auto-refresh every 30 seconds
 */
@Component({
  selector: 'app-price-arbitrage-opportunities',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    RouterModule,
    IconComponent,
    ButtonComponent,
    DialogComponent,
    DialogHeaderComponent,
    DialogTitleComponent,
    DialogContentComponent,
    DialogFooterComponent,
    FundingSpreadDetailsComponent,
    DropdownComponent,
    InputComponent,
  ],
  templateUrl: './price-arbitrage-opportunities.component.html',
  styleUrls: ['./price-arbitrage-opportunities.component.scss'],
  encapsulation: ViewEncapsulation.None
})

export class PriceArbitrageOpportunitiesComponent implements OnInit, OnDestroy {
  private fundingRatesService = inject(PublicFundingRatesService);
  private router = inject(Router);

  // State
  opportunities = signal<UnifiedOpportunity[]>([]);
  isLoading = signal<boolean>(false);
  error = signal<string | null>(null);
  lastUpdated = signal<Date | null>(null);

  // Stats from API
  stats = signal<{
    totalOpportunities: number;
    combinedStrategy: number;
    priceOnly: number;
    fundingDataAvailable: boolean;
  } | null>(null);

  // Auto-refresh
  private refreshSubscription?: Subscription;
  private readonly REFRESH_INTERVAL_MS = 30 * 1000; // 30 seconds

  // LocalStorage keys
  private readonly SELECTED_EXCHANGES_KEY = 'priceArbitrage_selectedExchanges';

  // Available exchanges
  availableExchanges = ['BINGX', 'BYBIT', 'BINANCE', 'MEXC', 'GATEIO', 'BITGET'];

  // Strategy type dropdown options
  strategyTypeOptions: DropdownOption[] = [
    { value: 'combined', label: 'Combined' },
    { value: 'price_only', label: 'Price Only' },
    { value: 'spot_futures', label: 'Spot + Futures' },
    { value: 'funding_farm', label: 'Funding Farm (-Neutral)' },
  ];

  // Filters
  searchQuery = signal<string>('');
  minCombinedScore = signal<number | null>(null);
  minFundingRate = signal<number | null>(null);
  minPriceSpread = signal<number | null>(null);
  strategyTypeFilter = signal<'combined' | 'price_only' | 'spot_futures' | 'funding_farm'>('combined');
  selectedExchanges = signal<string[]>([]);

  // Sorting
  sortColumn = signal<'symbol' | 'spreadPercent' | 'fundingDifferential' | 'combinedScore' | 'expectedDailyReturn' | 'estimatedMonthlyROI' | 'volatility'>('combinedScore');
  sortDirection = signal<'asc' | 'desc'>('desc');

  // Current time for time-since-update display
  private currentTime = signal<number>(Date.now());
  private timeUpdateInterval?: any;
  private _debugLogged = false;

  // Modal for detailed spread stability metrics
  showDetailsModal = signal<boolean>(false);
  selectedOpportunity = signal<FundingRateOpportunity | null>(null);
  loadingPhase2Metrics = signal<Set<string>>(new Set());
  phase2MetricsCache = signal<Map<string, FundingRateOpportunity>>(new Map());

  // User-selected exchange pairs for each symbol (overrides backend defaults)
  // Map<symbol, { longExchange: string, shortExchange: string }>
  selectedExchangePairs = signal<Map<string, { longExchange: string; shortExchange: string }>>(new Map());

  // Computed time since update
  timeSinceUpdate = computed(() => {
    const lastUpdate = this.lastUpdated();
    if (!lastUpdate) return '';

    const now = this.currentTime();
    const seconds = Math.floor((now - lastUpdate.getTime()) / 1000);
    if (seconds < 60) return `${seconds}s ago`;

    const minutes = Math.floor(seconds / 60);
    return `${minutes}m ago`;
  });

  // Check if spot-futures filter is active
  isSpotFuturesMode = computed(() => {
    return this.strategyTypeFilter() === 'spot_futures';
  });

  // Check if funding farm filter is active
  isFundingFarmMode = computed(() => {
    return this.strategyTypeFilter() === 'funding_farm';
  });


  // Computed filtered & sorted opportunities
  filteredOpportunities = computed(() => {
    let filtered = [...this.opportunities()];

    // Filter by search query (symbol)
    const query = this.searchQuery().trim().toUpperCase();
    if (query) {
      filtered = filtered.filter(o => o.symbol.toUpperCase().includes(query));
    }

    // Filter by strategy type
    const strategyFilter = this.strategyTypeFilter();
    if (strategyFilter === 'spot_futures') {
      // For spot+futures, already filtered by loadOpportunities()
      // Keep all opportunities as they're already spot+futures type
    } else if (strategyFilter === 'funding_farm') {
      // For funding farm, show all opportunities with positive funding differential
      // (we'll compensate price difference with delta-neutral hedging)
      filtered = filtered.filter(o => o.maxFundingSpread && o.maxFundingSpread > 0);
    } else {
      filtered = filtered.filter(o => o.strategyType === strategyFilter);
    }

    // Filter by selected exchanges (case-insensitive comparison)
    const exchanges = this.selectedExchanges();
    if (exchanges.length > 0 && strategyFilter !== 'spot_futures') {
      const exchangesUpper = exchanges.map(e => e.toUpperCase());
      filtered = filtered.filter(o => {
        // Type guard: check if this is a FundingRateOpportunity with bestShort/bestLong
        if ('bestShort' in o && 'bestLong' in o && o.bestShort && o.bestLong) {
          return exchangesUpper.includes(o.bestShort.exchange.toUpperCase()) &&
                 exchangesUpper.includes(o.bestLong.exchange.toUpperCase());
        }
        return false;
      });
    } else if (exchanges.length > 0 && strategyFilter === 'spot_futures') {
      // For spot+futures, filter by futures exchange (bestShort)
      const exchangesUpper = exchanges.map(e => e.toUpperCase());
      filtered = filtered.filter(o => {
        // Type guard: check if this is a spot+futures opportunity with bestShort
        if ('bestShort' in o && o.bestShort && typeof o.bestShort.exchange === 'string') {
          return exchangesUpper.includes(o.bestShort.exchange.toUpperCase());
        }
        return false;
      });
    }

    // Filter by minimum combined score
    const minScore = this.minCombinedScore();
    if (minScore !== null) {
      filtered = filtered.filter(o => {
        const score = o.combinedScore !== undefined ? o.combinedScore : ((o.priceSpread || 0) * 100);
        return score >= minScore;
      });
    }

    // Filter by minimum funding rate (by absolute value)
    const minFunding = this.minFundingRate();
    if (minFunding !== null) {
      filtered = filtered.filter(o => {
        const fundingRate = o.maxFundingSpread ? o.maxFundingSpread * 100 : 0;
        return Math.abs(fundingRate) >= minFunding;
      });
    }

    // Filter by minimum price spread
    const minSpread = this.minPriceSpread();
    if (minSpread !== null) {
      filtered = filtered.filter(o => {
        const priceSpread = (o.priceSpread || 0) * 100;
        return priceSpread >= minSpread;
      });
    }

    // Sort
    const column = this.sortColumn();
    const direction = this.sortDirection();

    filtered.sort((a, b) => {
      let aValue: number;
      let bValue: number;

      switch (column) {
        case 'symbol':
          return direction === 'asc'
            ? a.symbol.localeCompare(b.symbol)
            : b.symbol.localeCompare(a.symbol);

        case 'spreadPercent':
          aValue = (a.priceSpread || 0) * 100;
          bValue = (b.priceSpread || 0) * 100;
          break;

        case 'fundingDifferential':
          aValue = (a.maxFundingSpread ?? 0) * 100;
          bValue = (b.maxFundingSpread ?? 0) * 100;
          break;

        case 'combinedScore':
          aValue = a.combinedScore ?? ((a.priceSpread || 0) * 100);
          bValue = b.combinedScore ?? ((b.priceSpread || 0) * 100);
          break;

        case 'expectedDailyReturn':
          aValue = a.expectedDailyReturn ?? ((a.priceSpread || 0) * 100);
          bValue = b.expectedDailyReturn ?? ((b.priceSpread || 0) * 100);
          break;

        case 'estimatedMonthlyROI':
          aValue = a.estimatedMonthlyROI ?? ((a.priceSpread || 0) * 100);
          bValue = b.estimatedMonthlyROI ?? ((b.priceSpread || 0) * 100);
          break;

        case 'volatility':
          aValue = a.volatility24h ?? 0;
          bValue = b.volatility24h ?? 0;
          break;

        default:
          aValue = 0;
          bValue = 0;
      }

      return direction === 'asc' ? aValue - bValue : bValue - aValue;
    });

    return filtered;
  });

  ngOnInit(): void {
    // Load saved exchange filters from localStorage
    this.loadSavedExchangeFilters();

    // Start time update interval
    this.timeUpdateInterval = setInterval(() => {
      const now = Date.now();
      this.currentTime.set(now);
      // DEBUG: Log every 10 seconds to check if time is correct
      if (now % 10000 < 1000) {
        console.error('[DEBUG currentTime]', {
          now,
          nowUTC: new Date(now).toISOString(),
          nowLocal: new Date(now).toString()
        });
      }
    }, 1000);

    // Initial load
    this.loadOpportunities();

    // Setup auto-refresh
    this.refreshSubscription = interval(this.REFRESH_INTERVAL_MS)
      .pipe(
        startWith(0),
        switchMap(() => {
          this.loadOpportunities();
          return [];
        })
      )
      .subscribe();
  }

  ngOnDestroy(): void {
    if (this.refreshSubscription) {
      this.refreshSubscription.unsubscribe();
    }
    if (this.timeUpdateInterval) {
      clearInterval(this.timeUpdateInterval);
    }
  }

  /**
   * Load arbitrage opportunities from public APIs (with combined strategy metrics)
   */
  loadOpportunities(): void {
    this.isLoading.set(true);
    this.error.set(null);

    const strategyFilter = this.strategyTypeFilter();

    if (strategyFilter === 'spot_futures') {
      // Load spot + futures opportunities (filter for positive funding only)
      this.fundingRatesService.getFundingRatesOpportunities().subscribe({
        next: (allOpportunities: FundingRateOpportunity[]) => {
          // Filter: only show coins with positive funding rate on at least one exchange
          const spotFuturesOpportunities = allOpportunities
            .map((opp: FundingRateOpportunity) => {
              // Find exchange with highest positive funding rate
              const positiveFundingExchanges = opp.exchanges.filter((ex: any) => {
                const rate = parseFloat(ex.fundingRate);
                return rate > 0;
              });

              if (positiveFundingExchanges.length === 0) {
                return null; // Skip coins without positive funding
              }

              // Sort by funding rate (highest first)
              const bestFutures = positiveFundingExchanges.sort((a: any, b: any) =>
                parseFloat(b.fundingRate) - parseFloat(a.fundingRate)
              )[0];

              return {
                ...opp,
                strategyType: 'spot_futures' as const,
                bestShort: bestFutures, // Best exchange for SHORT (highest funding)
                // TODO: Add bestLong for SPOT (lowest price) when spot prices available
              };
            })
            .filter(opp => opp !== null) as UnifiedOpportunity[];

          this.opportunities.set(spotFuturesOpportunities);
          this.error.set(null);
          this.isLoading.set(false);
          this.lastUpdated.set(new Date());
        },
        error: (err: any) => {
          this.error.set('Failed to load spot+futures opportunities: ' + err.message);
          this.isLoading.set(false);
        },
      });
    } else {
      // Load cross-exchange opportunities (existing behavior)
      this.fundingRatesService.getFundingRatesOpportunities().subscribe({
        next: (opportunities: FundingRateOpportunity[]) => {
          this.opportunities.set(opportunities as UnifiedOpportunity[]);
          this.error.set(null);
          this.isLoading.set(false);
          this.lastUpdated.set(new Date());
        },
        error: (err: any) => {
          this.error.set('Failed to load opportunities: ' + err.message);
          this.isLoading.set(false);
        },
      });
    }
  }

  /**
   * Set sorting column
   */
  setSortColumn(column: 'symbol' | 'spreadPercent' | 'fundingDifferential' | 'combinedScore' | 'expectedDailyReturn' | 'estimatedMonthlyROI' | 'volatility'): void {
    if (this.sortColumn() === column) {
      // Toggle direction
      this.sortDirection.set(this.sortDirection() === 'asc' ? 'desc' : 'asc');
    } else {
      // New column, default to descending
      this.sortColumn.set(column);
      this.sortDirection.set('desc');
    }
  }

  /**
   * Format percentage for display
   */
  formatPercent(value: number | undefined, decimals = 2): string {
    if (value === undefined || value === null) return '';
    return `${value.toFixed(decimals)}%`;
  }

  /**
   * Format funding rate for display (convert from decimal to %)
   * Now shows NORMALIZED rate (per 8h) for fair comparison
   */
  formatFundingRate(rate: string | undefined): string {
    if (rate === undefined || rate === null) return '';
    const rateNum = parseFloat(rate);
    return (rateNum * 100).toFixed(4) + '%';
  }

  /**
   * Format NORMALIZED funding rate from ExchangeFundingRate object
   * Shows the normalized rate (per 8h) for fair comparison across exchanges
   */
  formatNormalizedFundingRate(exchange: any): string {
    if (!exchange) return '';

    // Use normalized rate if available, otherwise fall back to original
    const rate = exchange.fundingRateNormalized !== undefined
      ? exchange.fundingRateNormalized
      : parseFloat(exchange.fundingRate);

    return (rate * 100).toFixed(4) + '%';
  }

  /**
   * Get strategy type badge class
   */
  getStrategyBadgeClass(strategyType: string): string {
    switch (strategyType) {
      case 'combined':
        return 'badge-combined';
      case 'price_only':
        return 'badge-price-only';
      case 'funding_only':
        return 'badge-funding-only';
      default:
        return 'badge-default';
    }
  }

  /**
   * Get strategy type display text
   */
  getStrategyTypeText(strategyType: string): string {
    switch (strategyType) {
      case 'combined':
        return 'Combined';
      case 'price_only':
        return 'Price Only';
      case 'funding_only':
        return 'Funding Only';
      case 'spot_futures':
        return 'Spot+Futures';
      default:
        return 'Unknown';
    }
  }

  /**
   * Get time to funding for graduated entry opportunity
   */
  getSpotFuturesTimeToFunding(nextFundingTime: Date | number): string {
    const now = this.currentTime();
    // Handle both Date objects and timestamps
    const fundingTime = typeof nextFundingTime === 'number'
      ? nextFundingTime
      : (typeof nextFundingTime === 'string' ? new Date(nextFundingTime).getTime() : nextFundingTime.getTime());
    const diff = fundingTime - now;

    // DEBUG
    if (!this._debugLogged) {
      this._debugLogged = true;
      console.error('[DEBUG getSpotFuturesTimeToFunding]', {
        nextFundingTime,
        nextFundingTimeType: typeof nextFundingTime,
        fundingTime,
        fundingTimeUTC: new Date(fundingTime).toISOString(),
        now,
        nowUTC: new Date(now).toISOString(),
        diff,
        diffMinutes: Math.floor(diff / (1000 * 60))
      });
    }

    if (diff <= 0) return '0m';

    const hours = Math.floor(diff / (1000 * 60 * 60));
    const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));

    if (hours > 0) {
      return `${hours}h ${minutes}m`;
    }
    return `${minutes}m`;
  }

  /**
   * Format time to funding with interval for graduated entry
   */
  formatSpotFuturesFundingTime(nextFundingTime: Date | number, fundingInterval: number): string {
    const timeToFunding = this.getSpotFuturesTimeToFunding(nextFundingTime);
    return `${timeToFunding} / ${fundingInterval}h`;
  }

  /**
   * Type guard: check if opportunity has spot_futures strategy
   */
  isSpotFuturesStrategy(opp: UnifiedOpportunity): boolean {
    return opp.strategyType === 'spot_futures';
  }

  /**
   * Cast to FundingRateOpportunity for template use
   */
  asFundingRateOpportunity(opp: UnifiedOpportunity): FundingRateOpportunity {
    return opp;
  }

  /**
   * Cast to UnifiedOpportunity with spot_futures strategy for template use
   */
  asSpotFuturesOpportunity(opp: UnifiedOpportunity): UnifiedOpportunity | null {
    return this.isSpotFuturesStrategy(opp) ? opp : null;
  }

  /**
   * Parse funding rate string to number for display
   */
  parseFundingRateToNumber(rate: string | undefined): number {
    if (!rate) return 0;
    return parseFloat(rate) * 100; // Convert decimal to percentage
  }

  /**
   * Convert timestamp to Date and format funding time with interval
   */
  formatFundingTimeFromTimestamp(timestamp: number, intervalStr: string | undefined): string {
    const intervalNum = intervalStr ? parseInt(intervalStr) : 8; // Default to 8h
    return this.formatSpotFuturesFundingTime(timestamp, intervalNum);
  }

  /**
   * Refresh opportunities manually
   */
  refresh(): void {
    this.loadOpportunities();
  }

  /**
   * Handle strategy type change
   */
  onStrategyTypeChange(value: 'combined' | 'price_only' | 'spot_futures' | 'funding_farm'): void {
    this.strategyTypeFilter.set(value);
    this.loadOpportunities();
  }

  /**
   * Clear all filters
   */
  clearFilters(): void {
    this.searchQuery.set('');
    this.minCombinedScore.set(null);
    this.minFundingRate.set(null);
    this.minPriceSpread.set(null);
    this.strategyTypeFilter.set('combined');
    this.selectedExchanges.set([...this.availableExchanges]); // Select all exchanges
    this.saveExchangeFilters(); // Save cleared state to localStorage
  }

  /**
   * Load saved exchange filters from localStorage
   */
  private loadSavedExchangeFilters(): void {
    try {
      const saved = localStorage.getItem(this.SELECTED_EXCHANGES_KEY);
      if (saved) {
        const exchanges = JSON.parse(saved) as string[];
        // Validate that saved exchanges are still in availableExchanges
        const validExchanges = exchanges.filter(ex => this.availableExchanges.includes(ex));
        this.selectedExchanges.set(validExchanges);
      } else {
        // Default: select all exchanges on first load
        this.selectedExchanges.set([...this.availableExchanges]);
      }
    } catch (error) {
      console.error('Failed to load saved exchange filters:', error);
      // On error, default to all exchanges
      this.selectedExchanges.set([...this.availableExchanges]);
    }
  }

  /**
   * Save exchange filters to localStorage
   */
  private saveExchangeFilters(): void {
    try {
      localStorage.setItem(this.SELECTED_EXCHANGES_KEY, JSON.stringify(this.selectedExchanges()));
    } catch (error) {
      console.error('Failed to save exchange filters:', error);
    }
  }

  /**
   * Toggle exchange selection
   */
  toggleExchange(exchange: string): void {
    const current = this.selectedExchanges();
    if (current.includes(exchange)) {
      this.selectedExchanges.set(current.filter(e => e !== exchange));
    } else {
      this.selectedExchanges.set([...current, exchange]);
    }
    this.saveExchangeFilters();
  }

  /**
   * Check if exchange is selected
   */
  isExchangeSelected(exchange: string): boolean {
    return this.selectedExchanges().includes(exchange);
  }

  /**
   * Select all exchanges
   */
  selectAllExchanges(): void {
    this.selectedExchanges.set([...this.availableExchanges]);
    this.saveExchangeFilters();
  }

  /**
   * Deselect all exchanges
   */
  deselectAllExchanges(): void {
    this.selectedExchanges.set([]);
    this.saveExchangeFilters();
  }

  /**
   * Get funding spread color class
   */
  getFundingSpreadColorClass(spread: number | undefined): string {
    if (!spread) return 'text-gray-600';
    if (spread >= 0.001) return 'text-green-600';
    if (spread >= 0.0005) return 'text-yellow-600';
    return 'text-gray-600';
  }

  /**
   * Get funding rate color class
   */
  getFundingRateColorClass(rate: string | undefined): string {
    if (!rate) return '';
    const numRate = parseFloat(rate);
    if (numRate > 0) return 'positive';
    if (numRate < 0) return 'negative';
    return '';
  }

  /**
   * Get spot price for an opportunity
   */
  getSpotPrice(opp: UnifiedOpportunity): number | null {
    if (!opp.bestLong || !opp.bestLong.lastPrice) {
      return null;
    }

    if (this.isSpotFuturesStrategy(opp)) {
      // For spot_futures strategy: return bestLong price (lowest spot price)
      // TODO: Implement actual spot prices when available
      return parseFloat(opp.bestLong.lastPrice);
    } else if (!this.isSpotFuturesStrategy(opp)) {
      // For cross-exchange, the lower price is considered "spot"
      return parseFloat(opp.bestLong.lastPrice);
    }
    return null;
  }

  /**
   * Get futures price for an opportunity
   */
  getFuturesPrice(opp: UnifiedOpportunity): number | null {
    if (!opp.bestShort || !opp.bestShort.lastPrice) {
      return null;
    }

    if (this.isSpotFuturesStrategy(opp)) {
      // For spot_futures strategy: return bestShort price (futures with highest funding)
      return parseFloat(opp.bestShort.lastPrice);
    } else if (!this.isSpotFuturesStrategy(opp)) {
      // For cross-exchange, the higher price is considered "futures"
      return parseFloat(opp.bestShort.lastPrice);
    }
    return null;
  }

  /**
   * Get price spread with direction indicator
   * Returns object with spread value, percentage, and indicator
   */
  getPriceSpreadInfo(opp: UnifiedOpportunity): {
    spread: number;
    spreadPercent: number;
    spreadUsdt: number;
    isFavorable: boolean;
    indicator: string;
    colorClass: string;
  } {
    const spotPrice = this.getSpotPrice(opp);
    const futuresPrice = this.getFuturesPrice(opp);

    if (!spotPrice || !futuresPrice) {
      return {
        spread: 0,
        spreadPercent: 0,
        spreadUsdt: 0,
        isFavorable: false,
        indicator: '',
        colorClass: 'neutral'
      };
    }

    const spreadUsdt = futuresPrice - spotPrice;
    const spreadPercent = (spreadUsdt / spotPrice) * 100;

    // For funding arbitrage: LONG price > SHORT price is favorable (inverted from price arbitrage)
    // spotPrice = bestLong (LONG position), futuresPrice = bestShort (SHORT position)
    // We want LONG price > SHORT price for entry spread profit
    const isFavorable = spotPrice > futuresPrice; // LONG > SHORT is favorable

    return {
      spread: opp.priceSpread || 0,
      spreadPercent,
      spreadUsdt,
      isFavorable,
      indicator: isFavorable ? '' : '',
      colorClass: isFavorable ? 'favorable' : 'unfavorable'
    };
  }

  /**
   * Format price for display
   */
  formatPrice(price: number | null): string {
    if (price === null || price === undefined) return '';

    // For very large prices (> 1000), show 2 decimals
    if (price >= 1000) {
      return `$${price.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;
    }

    // For medium prices (10-1000), show 2-4 decimals
    if (price >= 10) {
      return `$${price.toFixed(2)}`;
    }

    // For small prices (< 10), show up to 6 decimals
    return `$${price.toFixed(6)}`;
  }

  /**
   * Get exchange label for spot/futures display
   */
  getExchangeLabel(opp: UnifiedOpportunity, type: 'spot' | 'futures'): string {
    if (this.isSpotFuturesStrategy(opp)) {
      // For spot_futures: bestShort has the futures exchange (highest funding)
      // TODO: bestLong should have the spot exchange (lowest price) when implemented
      return type === 'futures'
        ? (opp.bestShort?.exchange || '')
        : (opp.bestLong?.exchange || '');
    } else if (!this.isSpotFuturesStrategy(opp)) {
      return type === 'spot' ? opp.bestLong.exchange : opp.bestShort.exchange;
    }
    return '';
  }

  /**
   * Calculate delta-neutral hedge ratio for funding farm strategy
   * Returns the ratio of long position size to short position size
   */
  calculateHedgeRatio(opportunity: UnifiedOpportunity): number {
    // Type guard: only calculate for FundingRateOpportunity
    if ('bestLong' in opportunity && 'bestShort' in opportunity && opportunity.bestLong && opportunity.bestShort) {
      const longPrice = parseFloat(opportunity.bestLong.lastPrice);
      const shortPrice = parseFloat(opportunity.bestShort.lastPrice);

      if (longPrice === 0 || shortPrice === 0) return 1;

      // To be delta-neutral: Long value = Short value
      // If Short price is higher, we need MORE long contracts
      return shortPrice / longPrice;
    }
    return 1;
  }

  /**
   * Format hedge ratio for display
   */
  formatHedgeRatio(opportunity: UnifiedOpportunity): string {
    const ratio = this.calculateHedgeRatio(opportunity);
    return ratio.toFixed(4);
  }

  /**
   * Calculate time to funding for a specific exchange
   * Uses currentTime signal to avoid NG0100
   */
  getTimeToFunding(exchange: { nextFundingTime: number }): string {
    const now = this.currentTime();
    const diff = exchange.nextFundingTime - now;

    if (diff <= 0) return '0m';

    const hours = Math.floor(diff / (1000 * 60 * 60));
    const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));

    if (hours > 0) {
      return `${hours}h ${minutes}m`;
    }
    return `${minutes}m`;
  }

  /**
   * Format time to funding with interval like "2h 15m / 8h"
   * Uses fundingInterval from exchange if available, otherwise estimates it
   * Uses currentTime signal to avoid NG0100
   */
  formatTimeToFundingWithInterval(exchange: { nextFundingTime: number; fundingInterval?: string }): string {
    const timeToFunding = this.getTimeToFunding(exchange);

    // Use provided funding interval if available
    if (exchange.fundingInterval) {
      return `${timeToFunding} / ${exchange.fundingInterval}`;
    }

    // Fallback: estimate interval based on time until next funding
    const now = this.currentTime();
    const timeUntilFunding = exchange.nextFundingTime - now;

    let interval = '8h'; // Default
    if (timeUntilFunding > 0) {
      if (timeUntilFunding <= 60 * 60 * 1000) {
        interval = '1h';
      } else if (timeUntilFunding <= 4 * 60 * 60 * 1000) {
        interval = '4h';
      }
    }

    return `${timeToFunding} / ${interval}`;
  }

  /**
   * Start arbitrage position - navigate to arbitrage chart page
   */
  startPosition(opportunity: UnifiedOpportunity): void {
    console.log('[PriceArbitrageOpportunities] Starting position for:', opportunity.symbol);

    // Determine strategy type based on current filter
    const strategy = this.strategyTypeFilter();

    if (strategy === 'spot_futures') {
      // For spot_futures strategy (spot + futures)
      // Navigate with spot_futures strategy parameter
      const graduatedOpp = opportunity as UnifiedOpportunity & { strategyType: 'spot_futures' };
      // Use bestShort exchange (highest funding rate) for futures
      const futuresExchange = graduatedOpp.bestShort?.exchange || '';
      const spotExchange = graduatedOpp.bestLong?.exchange || futuresExchange; // TODO: use actual spot exchange
      this.router.navigate([
        '/arbitrage/chart',
        graduatedOpp.symbol,
        spotExchange,
        futuresExchange,
        'spot_futures' // Use spot_futures strategy for graduated entry
      ]);
    } else {
      // For cross-exchange arbitrage
      // Use effective exchanges (respects user's dropdown selection)
      const longExchange = this.getEffectiveLongExchange(opportunity);
      const shortExchange = this.getEffectiveShortExchange(opportunity);

      // Navigate to arbitrage chart with symbol, exchanges, and strategy
      // Format: /arbitrage/chart/:symbol/:longExchange/:shortExchange/:strategy
      this.router.navigate([
        '/arbitrage/chart',
        opportunity.symbol,
        longExchange.exchange,
        shortExchange.exchange,
        strategy
      ]);

      console.log(`[PriceArbitrageOpportunities] Navigating with: ${longExchange.exchange} (LONG) / ${shortExchange.exchange} (SHORT)`);
    }
  }

  /**
   * Open details modal for displaying spread stability metrics
   * Triggers loading of historical stability metrics
   */
  openDetailsModal(opp: UnifiedOpportunity): void {
    // Only open modal for cross-exchange opportunities
    if ('bestShort' in opp && 'bestLong' in opp) {
      this.selectedOpportunity.set(opp as FundingRateOpportunity);
      this.showDetailsModal.set(true);
      // Trigger Phase 2 load
      this.loadPhase2MetricsForSymbol(opp.symbol);
    }
  }

  /**
   * Close details modal
   */
  closeDetailsModal(): void {
    this.showDetailsModal.set(false);
    // Don't clear selectedOpportunity immediately to avoid flicker during close animation
    setTimeout(() => {
      if (!this.showDetailsModal()) {
        this.selectedOpportunity.set(null);
      }
    }, 300);
  }

  /**
   * Load Phase 2 historical stability metrics for a symbol
   * Fetches historical funding rates and calculates stability metrics
   */
  async loadPhase2MetricsForSymbol(symbol: string): Promise<void> {
    // Check cache
    if (this.phase2MetricsCache().has(symbol)) {
      // Data already loaded
      return;
    }

    // Find opportunity
    const opp = this.filteredOpportunities().find(o => o.symbol === symbol);
    if (!opp) return;

    // Only load for FundingRateOpportunity (not SpotFutures)
    if (this.isSpotFuturesStrategy(opp)) return;

    // Set loading
    const loading = new Set(this.loadingPhase2Metrics());
    loading.add(symbol);
    this.loadingPhase2Metrics.set(loading);

    try {
      // Load metrics via service
      const updated = await this.fundingRatesService.loadPhase2Metrics(opp);

      // Update cache
      const cache = new Map(this.phase2MetricsCache());
      cache.set(symbol, updated);
      this.phase2MetricsCache.set(cache);
    } catch (error) {
      console.error('[Phase 2] Load failed:', error);
    } finally {
      // Clear loading
      const loading = new Set(this.loadingPhase2Metrics());
      loading.delete(symbol);
      this.loadingPhase2Metrics.set(loading);
    }
  }

  /**
   * Check if Phase 2 metrics are currently loading for a symbol
   * Used to show loading spinner in UI
   */
  isLoadingPhase2(symbol: string): boolean {
    return this.loadingPhase2Metrics().has(symbol);
  }

  /**
   * Get Phase 2 metrics from cache
   * Returns cached metrics or undefined if not loaded
   */
  getPhase2Metrics(symbol: string): FundingRateOpportunity | undefined {
    return this.phase2MetricsCache().get(symbol);
  }

  /**
   * Map FundingRateOpportunity to format expected by FundingSpreadDetailsComponent
   */
  mapToDetailsOpportunity(opp: FundingRateOpportunity): any {
    // Get Phase 2 metrics if available
    const metrics = this.phase2MetricsCache().get(opp.symbol);
    const spreadHistory7d = metrics?.spreadHistory7d;
    const spreadHistory30d = metrics?.spreadHistory30d;

    return {
      symbol: opp.symbol,
      spread: opp.maxFundingSpread,
      spreadPercentage: Math.abs((opp.maxFundingSpread || 0) * 100),
      bestLongExchange: opp.bestLong.exchange,
      bestShortExchange: opp.bestShort.exchange,
      longFundingRate: parseFloat(opp.bestLong.fundingRate || '0'),
      shortFundingRate: parseFloat(opp.bestShort.fundingRate || '0'),
      currentPrice: parseFloat(opp.bestLong.lastPrice),
      volume24h: opp.volume24h,
      metrics: {
        historicalStability: spreadHistory7d?.stabilityScore ? spreadHistory7d.stabilityScore / 100 : 0.5,
        avgSpread7d: spreadHistory7d?.average || 0,
        avgSpread30d: spreadHistory30d?.average || 0,
        spreadVolatility: spreadHistory7d?.standardDeviation || 0,
        trendDirection: metrics?.spreadStabilityTrend === 'improving' ? 'increasing' :
                       metrics?.spreadStabilityTrend === 'declining' ? 'decreasing' : 'stable'
      }
    };
  }

  /**
   * Format funding rate as percentage
   */
  formatFundingRatePercent(rate: number | undefined): string {
    if (rate === undefined || rate === null) return '';
    return `${(rate * 100).toFixed(4)}%`;
  }

  /**
   * Get funding rate color class
   */
  getFundingRateColorClassFromNumber(rate: number | undefined): string {
    if (rate === undefined || rate === null) return '';
    if (rate > 0) return 'positive';
    if (rate < 0) return 'negative';
    return '';
  }

  /**
   * Format time to next funding with interval
   */
  formatTimeToNextFunding(nextFundingTime: number | undefined): string {
    if (!nextFundingTime) return '';

    const now = this.currentTime();
    const diff = nextFundingTime - now;

    // DEBUG: Log first call to see values
    if (!this._debugLogged) {
      this._debugLogged = true;
      console.error('[DEBUG formatTimeToNextFunding]', {
        nextFundingTime,
        nextFundingTimeType: typeof nextFundingTime,
        nextFundingTimeUTC: new Date(nextFundingTime).toISOString(),
        now,
        nowUTC: new Date(now).toISOString(),
        diff,
        diffMinutes: Math.floor(diff / (1000 * 60))
      });
    }

    if (diff <= 0) return '0m';

    const hours = Math.floor(diff / (1000 * 60 * 60));
    const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));

    // Calculate interval dynamically based on time until next funding
    let interval = '8h'; // Default
    if (diff > 0) {
      if (diff <= 1 * 60 * 60 * 1000) {
        interval = '1h';
      } else if (diff <= 4 * 60 * 60 * 1000) {
        interval = '4h';
      }
    }

    const timeStr = hours > 0 ? `${hours}h ${minutes}m` : `${minutes}m`;
    return `${timeStr} / ${interval}`;
  }

  /**
   * Get effective long exchange for a symbol (user selection or backend default)
   */
  getEffectiveLongExchange(opp: UnifiedOpportunity): any {
    const fundingOpp = this.asFundingRateOpportunity(opp);
    if (!fundingOpp) return null;

    const selectedPair = this.selectedExchangePairs().get(opp.symbol);
    if (selectedPair) {
      // Find the selected exchange in exchanges array
      return fundingOpp.exchanges.find(e => e.exchange === selectedPair.longExchange) || fundingOpp.bestLong;
    }
    return fundingOpp.bestLong;
  }

  /**
   * Get effective short exchange for a symbol (user selection or backend default)
   */
  getEffectiveShortExchange(opp: UnifiedOpportunity): any {
    const fundingOpp = this.asFundingRateOpportunity(opp);
    if (!fundingOpp) return null;

    const selectedPair = this.selectedExchangePairs().get(opp.symbol);
    if (selectedPair) {
      // Find the selected exchange in exchanges array
      return fundingOpp.exchanges.find(e => e.exchange === selectedPair.shortExchange) || fundingOpp.bestShort;
    }
    return fundingOpp.bestShort;
  }

  /**
   * Get available exchanges for long position (sorted by normalized funding rate - lowest first)
   */
  getAvailableExchangesForLong(opp: UnifiedOpportunity): any[] {
    const fundingOpp = this.asFundingRateOpportunity(opp);
    if (!fundingOpp || !fundingOpp.exchanges) return [];

    // Sort by normalized rate (lowest first) - best for LONG position
    return [...fundingOpp.exchanges].sort((a, b) => {
      const rateA = a.fundingRateNormalized !== undefined ? a.fundingRateNormalized : parseFloat(a.fundingRate);
      const rateB = b.fundingRateNormalized !== undefined ? b.fundingRateNormalized : parseFloat(b.fundingRate);
      return rateA - rateB;
    });
  }

  /**
   * Get available exchanges for short position (sorted by normalized funding rate - highest first)
   */
  getAvailableExchangesForShort(opp: UnifiedOpportunity): any[] {
    const fundingOpp = this.asFundingRateOpportunity(opp);
    if (!fundingOpp || !fundingOpp.exchanges) return [];

    // Sort by normalized rate (highest first) - best for SHORT position
    return [...fundingOpp.exchanges].sort((a, b) => {
      const rateA = a.fundingRateNormalized !== undefined ? a.fundingRateNormalized : parseFloat(a.fundingRate);
      const rateB = b.fundingRateNormalized !== undefined ? b.fundingRateNormalized : parseFloat(b.fundingRate);
      return rateB - rateA;
    });
  }

  /**
   * Get dropdown options for long exchanges
   */
  getLongExchangeOptions(opp: UnifiedOpportunity): DropdownOption[] {
    const exchanges = this.getAvailableExchangesForLong(opp);
    return exchanges.map(ex => ({
      value: ex.exchange,
      label: `<span class="exchange">${ex.exchange}</span> (${this.formatFundingRate(ex.fundingRate)} | ${ex.fundingInterval || '8h'})`
    }));
  }

  /**
   * Get dropdown options for short exchanges
   */
  getShortExchangeOptions(opp: UnifiedOpportunity): DropdownOption[] {
    const exchanges = this.getAvailableExchangesForShort(opp);
    return exchanges.map(ex => ({
      value: ex.exchange,
      label: `<span class="exchange">${ex.exchange}</span> (${this.formatFundingRate(ex.fundingRate)} | ${ex.fundingInterval || '8h'})`
    }));
  }

  /**
   * Handle exchange selection change
   */
  onExchangeChange(symbol: string, type: 'long' | 'short', newExchange: string): void {
    const currentPairs = this.selectedExchangePairs();
    const currentPair = currentPairs.get(symbol);

    const newPair = {
      longExchange: type === 'long' ? newExchange : (currentPair?.longExchange || ''),
      shortExchange: type === 'short' ? newExchange : (currentPair?.shortExchange || '')
    };

    // Update the map
    const updatedPairs = new Map(currentPairs);
    updatedPairs.set(symbol, newPair);
    this.selectedExchangePairs.set(updatedPairs);

    console.log(`[ExchangeSelection] ${symbol}: ${type} changed to ${newExchange}`, newPair);
  }

  /**
   * Get recalculated metrics for custom exchange pair
   * Returns formatted metrics for display
   */
  getRecalculatedMetrics(opp: UnifiedOpportunity): {
    fundingSpread: string;
    fundingSpreadPercent: string;
    estimatedAPR: string;
    netAPR: string;
    combinedScore: number;
    dailyReturn: number;
    monthlyROI: number;
  } | null {
    const longExchange = this.getEffectiveLongExchange(opp);
    const shortExchange = this.getEffectiveShortExchange(opp);

    if (!longExchange || !shortExchange) return null;

    // Calculate funding spread using normalized rates
    const longRate = longExchange.fundingRateNormalized !== undefined
      ? longExchange.fundingRateNormalized
      : parseFloat(longExchange.fundingRate);

    const shortRate = shortExchange.fundingRateNormalized !== undefined
      ? shortExchange.fundingRateNormalized
      : parseFloat(shortExchange.fundingRate);

    const fundingSpread = shortRate - longRate;
    const fundingSpreadPercent = (fundingSpread * 100).toFixed(4);

    // Estimated APR (gross)
    const fundingPeriodsPerYear = 3 * 365; // 8h intervals (normalized)
    const estimatedAPR = fundingSpread * fundingPeriodsPerYear;
    const estimatedAPRFormatted = (estimatedAPR * 100).toFixed(2) + '%';

    // Total Fees
    const totalFees = this.calculateTotalFees(longExchange.exchange, shortExchange.exchange);

    // Net APR
    const annualFees = totalFees * 3 * 365;
    const netAPR = estimatedAPR - annualFees;
    const netAPRFormatted = (netAPR * 100).toFixed(2) + '%';

    // Combined Score (price spread + funding spread for 7 days)
    const priceSpread = opp.priceSpread || 0;
    const fundingFor7Days = fundingSpread * 3 * 7; // 3 funding periods per day * 7 days
    const combinedScore = (priceSpread + fundingFor7Days) * 100;

    // Daily Return (funding * 3 periods per day)
    const dailyReturn = fundingSpread * 3 * 100;

    // Monthly ROI (price spread + funding for 30 days)
    const fundingFor30Days = fundingSpread * 3 * 30;
    const monthlyROI = (priceSpread + fundingFor30Days) * 100;

    return {
      fundingSpread: fundingSpread.toFixed(6),
      fundingSpreadPercent: fundingSpreadPercent + '%',
      estimatedAPR: estimatedAPRFormatted,
      netAPR: netAPRFormatted,
      combinedScore,
      dailyReturn,
      monthlyROI
    };
  }

  /**
   * Calculate total trading fees for arbitrage (4 trades total)
   */
  private calculateTotalFees(longExchange: string, shortExchange: string): number {
    const EXCHANGE_FEES: Record<string, number> = {
      'BYBIT': 0.055,
      'BINANCE': 0.04,
      'BINGX': 0.045,
      'MEXC': 0.02,
      'GATEIO': 0.05,
      'BITGET': 0.04,
      'OKX': 0.05,
    };

    const longFee = EXCHANGE_FEES[longExchange] || 0.055;
    const shortFee = EXCHANGE_FEES[shortExchange] || 0.055;

    // 4 trades: open long, open short, close long, close short
    return (longFee + shortFee) * 2 / 100; // Convert from percentage to decimal
  }
}

