import { Component, Input, Output, EventEmitter, signal, computed } from '@angular/core';
import { CommonModule } from '@angular/common';
import { TriangularOpportunity, ScannerStatus } from '../../../../models/triangular-arbitrage.model';
import { TranslationService } from '../../../../services/translation.service';
import { ButtonComponent } from '../../../../components/ui/button/button.component';
import {
  DialogComponent,
  DialogHeaderComponent,
  DialogTitleComponent,
  DialogContentComponent,
  DialogFooterComponent
} from '../../../../components/ui/dialog/dialog.component';
import { TriangleVisualizerComponent } from '../triangle-visualizer/triangle-visualizer.component';

/**
 * Opportunity List Component
 *
 * Displays real-time triangular arbitrage opportunities in a sortable table.
 * Features:
 * - Auto-refresh display
 * - Sort by profit, slippage, or time
 * - Execute opportunities
 * - Visual indicators for profitability
 */
@Component({
  selector: 'app-opportunity-list',
  standalone: true,
  imports: [
    CommonModule,
    ButtonComponent,
    DialogComponent,
    DialogHeaderComponent,
    DialogTitleComponent,
    DialogContentComponent,
    DialogFooterComponent,
    TriangleVisualizerComponent
  ],
  templateUrl: './opportunity-list.component.html',
  styleUrls: ['./opportunity-list.component.scss']
})
export class OpportunityListComponent {
  @Input() set opportunities(value: TriangularOpportunity[]) {
    console.log('[OpportunityList] Received opportunities:', value.length);
    this._opportunities = value;

    // Log deduplication stats
    const deduplicated = this.deduplicateOpportunities(value);
    console.log('[OpportunityList] After deduplication:', {
      original: value.length,
      deduplicated: deduplicated.length,
      removed: value.length - deduplicated.length
    });
  }
  get opportunities(): TriangularOpportunity[] {
    return this._opportunities;
  }
  private _opportunities: TriangularOpportunity[] = [];

  @Input() set isLoading(value: boolean) {
    console.log('[OpportunityList] isLoading changed to:', value);
    this._isLoading = value;
  }
  get isLoading(): boolean {
    return this._isLoading;
  }
  private _isLoading = false;

  @Input() set scannerStatus(value: ScannerStatus) {
    console.log('[OpportunityList] scannerStatus changed to:', value);
    this._scannerStatus = value;
  }
  get scannerStatus(): ScannerStatus {
    return this._scannerStatus;
  }
  private _scannerStatus: ScannerStatus = 'stopped';

  @Output() executeOpportunity = new EventEmitter<TriangularOpportunity>();

  // Sort configuration
  readonly sortColumn = signal<'profitPercentage' | 'profitAmount' | 'realisticProfitPercentage' | 'detectedAt'>('profitPercentage');
  readonly sortDirection = signal<'asc' | 'desc'>('desc');

  // Filter configuration
  readonly showProblematicTokens = signal<boolean>(true);

  // Problematic tokens with high minimum order sizes
  // These tokens often cause "order size too small" errors with small positions (50-100 USDT)
  private readonly PROBLEMATIC_TOKENS = [
    'FLOKI',  // min: 1.0+
    'SAGA',   // min: 1.0+
    'WLFI',   // min: 0.01
    'BTRST',  // min: 0.001
    'OMG',    // min: 0.001
    'SHIB',   // min: varies, often high
    'PEPE',   // min: varies, often high
    'BONK',   // min: varies, often high
  ];

  // Selected opportunity for detail modal
  readonly selectedOpportunity = signal<TriangularOpportunity | null>(null);
  readonly showDetailModal = signal<boolean>(false);

  // Current time for relative timestamps
  private currentTime = signal<number>(Date.now());
  private timeUpdateInterval?: any;

  constructor(private translationService: TranslationService) {
    // Update current time every second for relative timestamps
    this.timeUpdateInterval = setInterval(() => {
      this.currentTime.set(Date.now());
    }, 1000);
  }

  ngOnDestroy(): void {
    if (this.timeUpdateInterval) {
      clearInterval(this.timeUpdateInterval);
    }
  }

  /**
   * Check if opportunity contains problematic tokens
   */
  hasProblematicToken(opp: TriangularOpportunity): boolean {
    const { assetA, assetB, assetC } = opp.triangle;
    const assets = [assetA, assetB, assetC];
    return assets.some(asset => this.PROBLEMATIC_TOKENS.includes(asset));
  }

  /**
   * Toggle problematic tokens filter
   */
  toggleProblematicFilter(): void {
    this.showProblematicTokens.set(!this.showProblematicTokens());
  }

  /**
   * Get unique triangle key for deduplication
   */
  private getTriangleKey(opp: TriangularOpportunity): string {
    const { assetA, assetB, assetC, direction } = opp.triangle;
    return `${opp.exchange}:${assetA}-${assetB}-${assetC}:${direction}`;
  }

  /**
   * Deduplicate opportunities - keep only latest version of each triangle
   */
  private deduplicateOpportunities(opportunities: TriangularOpportunity[]): TriangularOpportunity[] {
    const latestByTriangle = new Map<string, TriangularOpportunity>();

    for (const opp of opportunities) {
      const key = this.getTriangleKey(opp);
      const existing = latestByTriangle.get(key);

      // Keep the one with latest detection time
      if (!existing || opp.detectedAt > existing.detectedAt) {
        latestByTriangle.set(key, opp);
      }
    }

    return Array.from(latestByTriangle.values());
  }

  /**
   * Get filtered opportunities (by problematic tokens and profit)
   */
  get filteredOpportunities(): TriangularOpportunity[] {
    // First deduplicate
    const deduplicated = this.deduplicateOpportunities(this.opportunities);

    // Filter out opportunities with negative profit
    const positiveProfit = deduplicated.filter(opp => {
      const profit = opp.realisticProfitPercentage ?? opp.profitPercentage;
      return profit > 0;
    });

    // Then filter by problematic tokens if needed
    if (this.showProblematicTokens()) {
      return positiveProfit;
    }
    return positiveProfit.filter(opp => !this.hasProblematicToken(opp));
  }

  /**
   * Sorted opportunities (after filtering)
   */
  get sortedOpportunities(): TriangularOpportunity[] {
    const sorted = [...this.filteredOpportunities];
    const column = this.sortColumn();
    const direction = this.sortDirection();

    sorted.sort((a, b) => {
      let comparison = 0;

      switch (column) {
        case 'profitPercentage':
          comparison = b.profitPercentage - a.profitPercentage;
          break;
        case 'profitAmount':
          comparison = b.profitAmount - a.profitAmount;
          break;
        case 'realisticProfitPercentage':
          const aRealistic = a.realisticProfitPercentage ?? -Infinity;
          const bRealistic = b.realisticProfitPercentage ?? -Infinity;
          comparison = bRealistic - aRealistic;
          break;
        case 'detectedAt':
          comparison = b.detectedAt - a.detectedAt;
          break;
      }

      return direction === 'asc' ? -comparison : comparison;
    });

    return sorted;
  }

  /**
   * Toggle sort column
   */
  toggleSort(column: 'profitPercentage' | 'profitAmount' | 'realisticProfitPercentage' | 'detectedAt'): void {
    if (this.sortColumn() === column) {
      this.sortDirection.set(this.sortDirection() === 'asc' ? 'desc' : 'asc');
    } else {
      this.sortColumn.set(column);
      this.sortDirection.set('desc');
    }
  }

  /**
   * Get sort icon for column
   */
  getSortIcon(column: string): string {
    if (this.sortColumn() !== column) return '';
    return this.sortDirection() === 'asc' ? '' : '';
  }

  /**
   * Format triangle path
   */
  formatTriangle(opp: TriangularOpportunity): string {
    const { assetA, assetB, assetC } = opp.triangle;
    return `${assetA}  ${assetB}  ${assetC}`;
  }

  /**
   * Get direction icon
   */
  getDirectionIcon(opp: TriangularOpportunity): string {
    return opp.triangle.direction === 'forward' ? '' : '';
  }

  /**
   * Get profit color class
   */
  getProfitColorClass(profit: number): string {
    if (profit >= 0.5) return 'profit-excellent';
    if (profit >= 0.2) return 'profit-good';
    if (profit >= 0.1) return 'profit-moderate';
    return 'profit-low';
  }

  /**
   * Calculate execution cost (theoretical - realistic profit)
   */
  getExecutionCost(opp: TriangularOpportunity): number {
    if (opp.realisticProfitPercentage === undefined) {
      return 0;
    }
    return opp.profitPercentage - opp.realisticProfitPercentage;
  }

  /**
   * Get relative time string
   */
  getRelativeTime(timestamp: number): string {
    const now = this.currentTime();
    const diff = now - timestamp;
    const seconds = Math.floor(diff / 1000);

    if (seconds < 5) return 'just now';
    if (seconds < 60) return `${seconds}s ago`;

    const minutes = Math.floor(seconds / 60);
    if (minutes < 60) return `${minutes}m ago`;

    const hours = Math.floor(minutes / 60);
    return `${hours}h ago`;
  }

  /**
   * Check if opportunity is fresh (< 10 seconds)
   */
  isFresh(opp: TriangularOpportunity): boolean {
    const now = this.currentTime();
    const diff = now - opp.detectedAt;
    return diff < 10000; // 10 seconds
  }

  /**
   * Open opportunity detail modal
   */
  openDetail(opp: TriangularOpportunity): void {
    this.selectedOpportunity.set(opp);
    this.showDetailModal.set(true);
  }

  /**
   * Close detail modal
   */
  closeDetail(): void {
    this.showDetailModal.set(false);
    setTimeout(() => {
      if (!this.showDetailModal()) {
        this.selectedOpportunity.set(null);
      }
    }, 300);
  }

  /**
   * Execute opportunity
   */
  onExecute(opp: TriangularOpportunity): void {
    console.log('[OpportunityList] Execute button clicked:', {
      id: opp.id,
      exchange: opp.exchange,
      isExecutable: opp.isExecutable,
      profitPercentage: opp.profitPercentage,
      triangle: opp.triangle
    });
    this.executeOpportunity.emit(opp);
  }

  /**
   * Execute from modal
   */
  executeFromModal(): void {
    const opp = this.selectedOpportunity();
    if (opp) {
      this.onExecute(opp);
      this.closeDetail();
    }
  }

  /**
   * Translation helper
   */
  translate(key: string): string {
    return this.translationService.translate(key);
  }
}

import { Component, Input, Output, EventEmitter, OnInit, signal, computed } from '@angular/core';
import { CommonModule, DatePipe } from '@angular/common';
import { FormsModule, ReactiveFormsModule, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ScannerConfig, ScannerStats, ScannerStatus, Exchange } from '../../../../models/triangular-arbitrage.model';
import { TranslationService } from '../../../../services/translation.service';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

// UI Components
import { ButtonComponent } from '../../../../components/ui/button/button.component';
import { InputComponent } from '../../../../components/ui/input/input.component';
import { SelectComponent } from '../../../../components/ui/select/select.component';

/**
 * Exchange Scanner State
 * Tracks the state of a scanner for a specific exchange
 */
export interface ExchangeScannerState {
  exchange: Exchange;
  status: ScannerStatus;
  stats: ScannerStats | null;
  config: ScannerConfig | null;
}

/**
 * Opportunity Scanner Component
 *
 * Provides controls for starting/stopping arbitrage scanners on multiple exchanges simultaneously.
 *
 * Note: Credentials are automatically selected based on active exchange credentials.
 */
@Component({
  selector: 'app-opportunity-scanner',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    ReactiveFormsModule,
    ButtonComponent,
    InputComponent,
    SelectComponent
  ],
  templateUrl: './opportunity-scanner.component.html',
  styleUrls: ['./opportunity-scanner.component.scss']
})
export class OpportunityScannerComponent implements OnInit {
  // Track scanner states for all exchanges
  readonly activeScanners = signal<Map<Exchange, ExchangeScannerState>>(new Map());

  // Computed: List of active scanner states
  readonly activeScannersList = computed(() => Array.from(this.activeScanners().values()));

  // Computed: Check if any scanner is running
  readonly isAnyScanning = computed(() =>
    Array.from(this.activeScanners().values()).some(s => s.status === 'scanning')
  );

  @Output() startScanning = new EventEmitter<ScannerConfig>();
  @Output() stopScanning = new EventEmitter<Exchange | null>(); // null = stop all

  configForm!: FormGroup;
  readonly availableExchanges: Exchange[] = ['BYBIT', 'BINANCE', 'BINGX', 'MEXC', 'GATEIO', 'BITGET', 'OKX'];
  readonly showAdvanced = signal<boolean>(false);

  constructor(
    private fb: FormBuilder,
    private translationService: TranslationService,
    private http: HttpClient
  ) {
    this.initForm();
  }

  ngOnInit(): void {
    // No need to load credentials - backend auto-selects active credential
  }

  /**
   * Initialize the configuration form
   */
  private initForm(): void {
    this.configForm = this.fb.group({
      exchange: ['GATEIO', Validators.required], // Default to Gate.io (optimized for this exchange)
      minProfitPercentage: [0.5, [Validators.required, Validators.min(-10), Validators.max(10)]], // 0.5% min realistic profit (optimized)
      maxSlippage: [0.5, [Validators.required, Validators.min(0.01), Validators.max(5)]], // 0.5% max slippage (based on real data)
      positionSize: [50, [Validators.required, Validators.min(10), Validators.max(100000)]], // $50 default (safer for testing)
      autoExecute: [false]
    });
  }

  /**
   * Start scanning on selected exchange
   */
  onStart(): void {
    if (this.configForm.invalid) {
      this.configForm.markAllAsTouched();
      return;
    }

    const exchange = this.configForm.value.exchange;

    // DEBUG: Log what exchange we're actually sending
    console.log(' [Scanner] Form values:', {
      exchange: exchange,
      allFormValues: this.configForm.value,
      formRawValue: this.configForm.getRawValue()
    });

    // Check if already scanning this exchange
    const current = this.activeScanners().get(exchange);
    if (current && current.status === 'scanning') {
      console.log(`Already scanning ${exchange}`);
      return;
    }

    const config: ScannerConfig = {
      exchange: exchange,
      minProfitPercentage: this.configForm.value.minProfitPercentage,
      maxSlippage: this.configForm.value.maxSlippage,
      positionSize: this.configForm.value.positionSize,
      autoExecute: this.configForm.value.autoExecute
    };

    console.log(' [Scanner] Sending config to backend:', config);

    this.startScanning.emit(config);
  }

  /**
   * Stop scanning for a specific exchange
   */
  onStopExchange(exchange: Exchange): void {
    this.stopScanning.emit(exchange);
  }

  /**
   * Stop all active scanners
   */
  onStopAll(): void {
    this.stopScanning.emit(null);
  }

  /**
   * Update scanner state for a specific exchange
   * Called from parent component when receiving SSE updates
   */
  updateScannerState(exchange: Exchange, status: ScannerStatus, stats: ScannerStats | null = null, config: ScannerConfig | null = null): void {
    const scanners = this.activeScanners();

    if (status === 'stopped') {
      // Remove scanner if stopped
      scanners.delete(exchange);
    } else {
      // Update or add scanner
      const existing = scanners.get(exchange);
      scanners.set(exchange, {
        exchange,
        status,
        stats: stats || existing?.stats || null,
        config: config || existing?.config || null
      });
    }

    // Trigger update
    this.activeScanners.set(new Map(scanners));
  }

  /**
   * Check if a specific exchange is scanning
   */
  isExchangeScanning(exchange: Exchange): boolean {
    const scanner = this.activeScanners().get(exchange);
    return scanner?.status === 'scanning';
  }

  /**
   * Get available exchanges (not currently scanning)
   */
  getAvailableExchanges(): Exchange[] {
    return this.availableExchanges.filter(ex => !this.isExchangeScanning(ex));
  }

  /**
   * Toggle advanced settings
   */
  toggleAdvanced(): void {
    this.showAdvanced.set(!this.showAdvanced());
  }

  /**
   * Get status indicator class for a scanner
   */
  getStatusClass(status: ScannerStatus): string {
    switch (status) {
      case 'scanning': return 'status-scanning';
      case 'stopped': return 'status-stopped';
      case 'error': return 'status-error';
      default: return '';
    }
  }

  /**
   * Get status text for a scanner
   */
  getStatusText(status: ScannerStatus): string {
    switch (status) {
      case 'scanning': return this.translate('triangularArbitrage.scanner.status.scanning');
      case 'stopped': return this.translate('triangularArbitrage.scanner.status.stopped');
      case 'error': return this.translate('triangularArbitrage.scanner.status.error');
      default: return '';
    }
  }

  /**
   * Get exchange display name
   */
  getExchangeName(exchange: Exchange): string {
    return exchange;
  }

  /**
   * Format duration in ms to human-readable string
   */
  formatDuration(ms: number): string {
    const seconds = Math.floor(ms / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);

    if (hours > 0) {
      return `${hours}h ${minutes % 60}m`;
    } else if (minutes > 0) {
      return `${minutes}m ${seconds % 60}s`;
    } else {
      return `${seconds}s`;
    }
  }

  /**
   * Translation helper
   */
  translate(key: string): string {
    return this.translationService.translate(key);
  }
}

