import { Injectable, signal, computed } from '@angular/core';
import { HttpClient, HttpParams } from '@angular/common/http';
import { Observable, Subject, BehaviorSubject } from 'rxjs';
import { map, catchError } from 'rxjs/operators';
import {
  TriangularOpportunity,
  TriangularPosition,
  ScannerConfig,
  ScannerStats,
  PositionFilter,
  PositionSummary,
  ExecuteOpportunityRequest,
  ExecuteOpportunityResponse,
  TriangularArbitrageUpdate,
  ScannerStatus
} from '../../../models/triangular-arbitrage.model';

/**
 * Triangular Arbitrage Service
 *
 * Manages all API interactions for the triangular arbitrage feature:
 * - Scanner control (start/stop)
 * - Opportunity detection and retrieval
 * - Position execution and management
 * - Real-time updates via WebSocket
 */
@Injectable({
  providedIn: 'root'
})
export class TriangularArbitrageService {
  private readonly API_BASE = '/api/triangular-arbitrage';
  private ws?: EventSource | any;
  private reconnectAttempts = 0;
  private readonly MAX_RECONNECT_ATTEMPTS = 5;
  private reconnectTimeout?: any;

  // State signals
  private _scannerStatus = signal<ScannerStatus>('stopped');
  readonly scannerStatus = computed(() => {
    const status = this._scannerStatus();
    console.log('[Service] scannerStatus getter called, returning:', status);
    return status;
  });
  readonly currentConfig = signal<ScannerConfig | null>(null);
  readonly scannerStats = signal<ScannerStats>({
    status: 'stopped',
    opportunitiesDetectedToday: 0,
    opportunitiesExecutedToday: 0,
    totalProfitToday: 0,
    avgProfitPercentage: 0,
    scanningDuration: 0
  });

  // Real-time data streams
  private readonly opportunitySubject = new Subject<TriangularOpportunity>();
  private readonly positionSubject = new Subject<TriangularPosition>();
  private readonly statsSubject = new Subject<ScannerStats>();
  private readonly scannerUpdateSubject = new Subject<{ exchange: string; status: ScannerStatus; stats?: ScannerStats }>();

  // Public observables
  readonly opportunities$ = this.opportunitySubject.asObservable();
  readonly positions$ = this.positionSubject.asObservable();
  readonly stats$ = this.statsSubject.asObservable();
  readonly scannerUpdates$ = this.scannerUpdateSubject.asObservable();

  // Connection status
  readonly isConnected = signal<boolean>(false);
  readonly connectionError = signal<string | null>(null);

  constructor(private http: HttpClient) {
    this.initializeWebSocket();
  }

  // ============= Scanner Control =============

  /**
   * Start scanning for arbitrage opportunities
   */
  startScanning(config: ScannerConfig): Observable<{ success: boolean; message?: string }> {
    return this.http.post<{ success: boolean; message?: string }>(
      `${this.API_BASE}/scan/start`,
      config
    ).pipe(
      map(response => {
        if (response.success) {
          this.currentConfig.set(config);
          console.log('[Service] Setting scannerStatus to: scanning (from startScanning)');
          this._scannerStatus.set('scanning');
        }
        return response;
      }),
      catchError(error => {
        console.log('[Service] Setting scannerStatus to: error (from startScanning error)');
        this._scannerStatus.set('error');
        throw error;
      })
    );
  }

  /**
   * Stop scanning
   * @param exchange - Optional: specific exchange to stop. If not provided, stops all scanners
   */
  stopScanning(exchange?: string): Observable<{ success: boolean; message?: string }> {
    const body = exchange ? { exchange } : {};
    return this.http.post<{ success: boolean; message?: string }>(
      `${this.API_BASE}/scan/stop`,
      body
    ).pipe(
      map(response => {
        if (response.success) {
          // Only update global status if stopping all
          if (!exchange) {
            console.log('[Service] Setting scannerStatus to: stopped (from stopScanning)');
            this._scannerStatus.set('stopped');
            this.currentConfig.set(null);
          }
        }
        return response;
      })
    );
  }

  /**
   * Get current scanner status
   */
  getScannerStatus(): Observable<any> {
    return this.http.get<any>(`${this.API_BASE}/scan/status`).pipe(
      map(response => {
        // API returns { success, isScanning, scanners, stats }
        // Derive status from isScanning
        const status: ScannerStatus = response.isScanning ? 'scanning' : 'stopped';
        console.log('[Service] Setting scannerStatus to:', status, '(from getScannerStatus, isScanning:', response.isScanning + ')');
        this._scannerStatus.set(status);

        // Set aggregated stats with derived status
        const stats: ScannerStats = {
          status,
          opportunitiesDetectedToday: response.stats?.opportunitiesDetectedToday || 0,
          opportunitiesExecutedToday: response.stats?.opportunitiesExecutedToday || 0,
          totalProfitToday: response.stats?.totalProfitToday || 0,
          avgProfitPercentage: response.stats?.avgProfitPercentage || 0,
          scanningDuration: response.stats?.runningFor || 0,
        };
        this.scannerStats.set(stats);

        // Return full response including scanners array
        return response;
      })
    );
  }

  // ============= Opportunities =============

  /**
   * Get current opportunities
   */
  getOpportunities(filters?: {
    minProfit?: number;
    maxSlippage?: number;
    exchange?: string;
  }): Observable<TriangularOpportunity[]> {
    let params = new HttpParams();

    if (filters?.minProfit !== undefined) {
      params = params.set('minProfit', filters.minProfit.toString());
    }
    if (filters?.maxSlippage !== undefined) {
      params = params.set('maxSlippage', filters.maxSlippage.toString());
    }
    if (filters?.exchange) {
      params = params.set('exchange', filters.exchange);
    }

    return this.http.get<{ opportunities: TriangularOpportunity[]; count: number }>(
      `${this.API_BASE}/opportunities`,
      { params }
    ).pipe(
      map(response => response.opportunities)
    );
  }

  /**
   * Execute a specific opportunity
   */
  executeOpportunity(
    request: ExecuteOpportunityRequest
  ): Observable<ExecuteOpportunityResponse> {
    return this.http.post<ExecuteOpportunityResponse>(
      `${this.API_BASE}/execute`,
      request
    );
  }

  // ============= Positions =============

  /**
   * Get positions with optional filters
   */
  getPositions(filters?: PositionFilter): Observable<TriangularPosition[]> {
    let params = new HttpParams();

    if (filters?.status && filters.status.length > 0) {
      params = params.set('status', filters.status.join(','));
    }
    if (filters?.exchange && filters.exchange.length > 0) {
      params = params.set('exchange', filters.exchange.join(','));
    }
    if (filters?.dateFrom) {
      params = params.set('dateFrom', filters.dateFrom.toISOString());
    }
    if (filters?.dateTo) {
      params = params.set('dateTo', filters.dateTo.toISOString());
    }
    if (filters?.minProfit !== undefined) {
      params = params.set('minProfit', filters.minProfit.toString());
    }
    if (filters?.maxProfit !== undefined) {
      params = params.set('maxProfit', filters.maxProfit.toString());
    }

    return this.http.get<{ positions: TriangularPosition[]; count: number; summary: PositionSummary }>(
      `${this.API_BASE}/positions`,
      { params }
    ).pipe(
      map(response => response.positions)
    );
  }

  /**
   * Get a specific position by ID
   */
  getPosition(id: string): Observable<TriangularPosition> {
    return this.http.get<TriangularPosition>(`${this.API_BASE}/positions/${id}`);
  }

  /**
   * Cancel an in-progress position
   */
  cancelPosition(id: string): Observable<{ success: boolean; message?: string }> {
    return this.http.delete<{ success: boolean; message?: string }>(
      `${this.API_BASE}/positions/${id}`
    );
  }

  /**
   * Delete a completed position from history
   */
  deletePosition(id: string): Observable<{ success: boolean; message?: string }> {
    return this.http.delete<{ success: boolean; message?: string }>(
      `${this.API_BASE}/positions/${id}/delete`
    );
  }

  /**
   * Get position summary statistics
   */
  getPositionSummary(filters?: PositionFilter): Observable<PositionSummary> {
    let params = new HttpParams();

    if (filters?.dateFrom) {
      params = params.set('dateFrom', filters.dateFrom.toISOString());
    }
    if (filters?.dateTo) {
      params = params.set('dateTo', filters.dateTo.toISOString());
    }

    return this.http.get<PositionSummary>(
      `${this.API_BASE}/positions/summary`,
      { params }
    );
  }

  // ============= Server-Sent Events Connection =============

  /**
   * Initialize SSE connection for real-time updates
   */
  private initializeWebSocket(): void {
    // For SSE, we need to use full URL in development because EventSource
    // doesn't go through Angular proxy like HttpClient does
    const isDev = window.location.port === '4200';
    const baseUrl = isDev ? 'http://localhost:3000' : '';

    // Get auth token from localStorage
    const token = localStorage.getItem('auth_token');
    if (!token) {
      console.error('[TriangularArbitrage] No auth token found');
      this.connectionError.set('Authentication required');
      return;
    }

    // Add token as query parameter since EventSource doesn't support headers
    const sseUrl = `${baseUrl}${this.API_BASE}/stream?token=${encodeURIComponent(token)}`;

    console.log('[TriangularArbitrage] Connecting to SSE:', sseUrl.replace(/token=[^&]+/, 'token=***'));

    try {
      const eventSource = new EventSource(sseUrl, {
        withCredentials: true
      });

      this.ws = eventSource as any; // Store as 'ws' to maintain compatibility

      eventSource.onopen = () => {
        console.log('[TriangularArbitrage] SSE connected');
        this.isConnected.set(true);
        this.connectionError.set(null);
        this.reconnectAttempts = 0;
      };

      eventSource.onmessage = (event) => {
        try {
          const update: TriangularArbitrageUpdate = JSON.parse(event.data);
          this.handleWebSocketUpdate(update);
        } catch (error) {
          console.error('[TriangularArbitrage] Failed to parse SSE message:', error);
        }
      };

      eventSource.onerror = (error) => {
        console.error('[TriangularArbitrage] SSE error:', error);
        this.connectionError.set('Connection error');
        this.isConnected.set(false);

        // Close and attempt reconnect
        eventSource.close();
        this.attemptReconnect();
      };
    } catch (error) {
      console.error('[TriangularArbitrage] Failed to create SSE:', error);
      this.connectionError.set('Failed to create connection');
    }
  }

  /**
   * Handle incoming SSE updates
   */
  private handleWebSocketUpdate(update: TriangularArbitrageUpdate): void {
    switch (update.type) {
      case 'connected':
        console.log('[TriangularArbitrage] Connected:', update.data);
        break;

      case 'heartbeat':
        // Update connection timestamp
        this.isConnected.set(true);
        break;

      case 'opportunity':
        console.log('[TriangularArbitrage] Received opportunity event:', update.data);
        this.opportunitySubject.next(update.data as TriangularOpportunity);
        break;

      case 'position':
        this.positionSubject.next(update.data as TriangularPosition);
        break;

      case 'scanner':
        const scannerData = update.data as any;
        console.log('[Service] Received scanner event:', scannerData);
        if (scannerData.status) {
          console.log('[Service] Setting scannerStatus to:', scannerData.status);
          this._scannerStatus.set(scannerData.status);
        } else {
          console.warn('[Service] Scanner event has no status!', scannerData);
        }
        if (scannerData.stats) {
          this.scannerStats.set(scannerData.stats);
        }
        // Emit scanner update with exchange information
        if (scannerData.exchange) {
          this.scannerUpdateSubject.next({
            exchange: scannerData.exchange,
            status: scannerData.status,
            stats: scannerData.stats
          });
        }
        break;

      case 'stats':
        this.statsSubject.next(update.data as ScannerStats);
        break;

      case 'error':
        console.error('[TriangularArbitrage] Server error:', update.data);
        const errorData = update.data as { message?: string };
        this.connectionError.set(errorData?.message || 'Server error');
        break;

      default:
        console.warn('[TriangularArbitrage] Unknown update type:', update.type);
    }
  }

  /**
   * Send a message (not used with SSE - SSE is one-way)
   */
  private sendMessage(message: any): void {
    // SSE is server-to-client only, no need to send messages
    console.log('[TriangularArbitrage] SSE does not support sending messages');
  }

  /**
   * Attempt to reconnect WebSocket
   */
  private attemptReconnect(): void {
    if (this.reconnectAttempts >= this.MAX_RECONNECT_ATTEMPTS) {
      console.error('[TriangularArbitrage] Max reconnection attempts reached');
      this.connectionError.set('Connection lost. Please refresh the page.');
      return;
    }

    this.reconnectAttempts++;
    const delay = Math.min(1000 * Math.pow(2, this.reconnectAttempts), 30000);

    console.log(`[TriangularArbitrage] Reconnecting in ${delay}ms (attempt ${this.reconnectAttempts})`);

    this.reconnectTimeout = setTimeout(() => {
      this.initializeWebSocket();
    }, delay);
  }

  /**
   * Manually reconnect SSE
   */
  reconnect(): void {
    this.reconnectAttempts = 0;
    if (this.ws) {
      (this.ws as any).close();
    }
    this.initializeWebSocket();
  }

  /**
   * Cleanup when service is destroyed
   */
  ngOnDestroy(): void {
    if (this.reconnectTimeout) {
      clearTimeout(this.reconnectTimeout);
    }
    if (this.ws) {
      (this.ws as any).close();
    }
  }

  // ============= Utility Methods =============

  /**
   * Format triangle path for display
   */
  formatTriangle(assetA: string, assetB: string, assetC: string): string {
    return `${assetA}  ${assetB}  ${assetC}`;
  }

  /**
   * Calculate estimated execution time based on historical data
   */
  getEstimatedExecutionTime(): number {
    // Returns estimated time in milliseconds
    // TODO: Calculate from historical position data
    return 5000; // 5 seconds default
  }

  /**
   * Export positions to CSV
   */
  exportPositionsToCSV(positions: TriangularPosition[]): string {
    const headers = [
      'Position ID',
      'Exchange',
      'Triangle',
      'Status',
      'Entry Amount',
      'Expected Profit',
      'Actual Profit',
      'Profit %',
      'Total Fees',
      'Total Slippage',
      'Execution Time (ms)',
      'Created At',
      'Completed At'
    ];

    const rows = positions.map(pos => [
      pos.id,
      pos.exchange,
      this.formatTriangle(pos.triangle.assetA, pos.triangle.assetB, pos.triangle.assetC),
      pos.status,
      pos.entryAmount.toFixed(2),
      pos.expectedProfit.toFixed(2),
      pos.actualProfit?.toFixed(2) || 'N/A',
      pos.actualProfitPercentage?.toFixed(2) || 'N/A',
      pos.totalFees.toFixed(4),
      pos.totalSlippage.toFixed(4),
      pos.executionTimeMs || 'N/A',
      new Date(pos.createdAt).toISOString(),
      pos.completedAt ? new Date(pos.completedAt).toISOString() : 'N/A'
    ]);

    const csvContent = [
      headers.join(','),
      ...rows.map(row => row.join(','))
    ].join('\n');

    return csvContent;
  }

  /**
   * Download CSV file
   */
  downloadCSV(csvContent: string, filename = 'triangular-arbitrage-positions.csv'): void {
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    const url = URL.createObjectURL(blob);

    link.setAttribute('href', url);
    link.setAttribute('download', filename);
    link.style.visibility = 'hidden';

    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  }
}

import { Component, OnInit, OnDestroy, ViewChild, signal, computed } from '@angular/core';
import { CommonModule } from '@angular/common';
import { TriangularArbitrageService } from './services/triangular-arbitrage.service';
import { TranslationService } from '../../services/translation.service';
import {
  TriangularOpportunity,
  TriangularPosition,
  ScannerConfig,
  PositionFilter,
  Exchange,
  ScannerStatus,
  ScannerStats
} from '../../models/triangular-arbitrage.model';
import { Subscription } from 'rxjs';

// Child components
import { OpportunityScannerComponent } from './components/opportunity-scanner/opportunity-scanner.component';
import { OpportunityListComponent } from './components/opportunity-list/opportunity-list.component';
import { PositionHistoryComponent } from './components/position-history/position-history.component';

// UI components
import {
  CardComponent,
  CardHeaderComponent,
  CardTitleComponent,
  CardContentComponent
} from '../../components/ui/card/card.component';

/**
 * Triangular Arbitrage Main Page
 *
 * Container component that orchestrates all triangular arbitrage functionality:
 * - Opportunity scanning controls
 * - Real-time opportunity display
 * - Position tracking and history
 */
@Component({
  selector: 'app-triangular-arbitrage',
  standalone: true,
  imports: [
    CommonModule,
    CardComponent,
    CardHeaderComponent,
    CardTitleComponent,
    CardContentComponent,
    OpportunityScannerComponent,
    OpportunityListComponent,
    PositionHistoryComponent
  ],
  templateUrl: './triangular-arbitrage.component.html',
  styleUrls: ['./triangular-arbitrage.component.scss']
})
export class TriangularArbitrageComponent implements OnInit, OnDestroy {
  // Child component reference
  @ViewChild(OpportunityScannerComponent) scannerComponent?: OpportunityScannerComponent;

  // State
  readonly opportunities = signal<TriangularOpportunity[]>([]);
  readonly positions = signal<TriangularPosition[]>([]);
  readonly isLoadingOpportunities = signal<boolean>(false);
  readonly isLoadingPositions = signal<boolean>(false);
  readonly error = signal<string | null>(null);

  // Service state
  readonly scannerStatus = this.arbitrageService.scannerStatus;
  readonly scannerStats = this.arbitrageService.scannerStats;
  readonly isConnected = this.arbitrageService.isConnected;
  readonly connectionError = this.arbitrageService.connectionError;

  // Active tab
  readonly activeTab = signal<'opportunities' | 'positions'>('opportunities');

  // Filters
  readonly positionFilter = signal<PositionFilter>({});

  // Scanner configuration per exchange (to track position size)
  private scannerConfigs = new Map<Exchange, ScannerConfig>();

  // Computed values
  readonly activePositions = computed(() =>
    this.positions().filter(p => p.status === 'executing' || p.status === 'pending')
  );

  readonly completedPositions = computed(() =>
    this.positions().filter(p => p.status === 'completed')
  );

  readonly hasActivePositions = computed(() => this.activePositions().length > 0);

  private subscriptions = new Subscription();

  constructor(
    private arbitrageService: TriangularArbitrageService,
    private translationService: TranslationService
  ) {}

  ngOnInit(): void {
    this.loadInitialData();
    this.subscribeToRealtimeUpdates();
  }

  ngOnDestroy(): void {
    this.subscriptions.unsubscribe();
  }

  /**
   * Load initial data
   */
  private loadInitialData(): void {
    // Load scanner status
    this.arbitrageService.getScannerStatus().subscribe({
      next: (response: any) => {
        // Response has: { isScanning, scanners: [...], stats }
        const activeExchanges = new Set<string>();

        if (response.isScanning && response.scanners && Array.isArray(response.scanners)) {
          // Restore only actively running scanners from backend
          for (const scannerData of response.scanners) {
            const exchange = scannerData.exchange as Exchange;
            const config = scannerData.config as ScannerConfig;
            const backendStats = scannerData.stats;

            activeExchanges.add(exchange);

            // Store config in memory
            if (config) {
              this.scannerConfigs.set(exchange, config);
              // Also persist to localStorage for future use
              localStorage.setItem(`scanner_config_${exchange}`, JSON.stringify(config));
            }

            // Transform backend stats to frontend ScannerStats format
            const stats: ScannerStats | null = backendStats ? {
              status: 'scanning',
              opportunitiesDetectedToday: backendStats.opportunitiesDetectedToday || 0,
              opportunitiesExecutedToday: 0, // Not yet tracked by backend
              totalProfitToday: backendStats.totalProfitToday || 0,
              avgProfitPercentage: 0, // Not yet tracked by backend
              scanningDuration: ((backendStats as any).runningFor || 0) * 1000, // Convert seconds to ms
              lastOpportunityAt: undefined // Not yet tracked by backend
            } : null;

            // Update scanner component with current state
            this.scannerComponent?.updateScannerState(
              exchange,
              'scanning',
              stats,
              config || null
            );
          }
        }

        // Clean up localStorage for inactive scanners
        const allExchanges: Exchange[] = ['BYBIT', 'BINANCE', 'BINGX', 'MEXC', 'GATEIO', 'BITGET', 'OKX'];
        for (const exchange of allExchanges) {
          if (!activeExchanges.has(exchange)) {
            // Remove inactive scanner from localStorage
            localStorage.removeItem(`scanner_config_${exchange}`);
            // Ensure scanner component doesn't show it
            this.scannerComponent?.updateScannerState(exchange, 'stopped');
          }
        }
      },
      error: (err) => console.error('Failed to load scanner status:', err)
    });

    // Load opportunities if scanner is active
    if (this.scannerStatus() === 'scanning') {
      this.loadOpportunities();
    }

    // Load recent positions
    this.loadPositions();
  }

  /**
   * Subscribe to real-time WebSocket updates
   */
  private subscribeToRealtimeUpdates(): void {
    // Opportunity updates
    this.subscriptions.add(
      this.arbitrageService.opportunities$.subscribe({
        next: (opportunity) => {
          console.log('[Component] Received opportunity from observable:', opportunity);
          const current = this.opportunities();
          console.log('[Component] Current opportunities count:', current.length);
          const index = current.findIndex(o => o.id === opportunity.id);

          if (index >= 0) {
            // Update existing
            console.log('[Component] Updating existing opportunity at index:', index);
            const updated = [...current];
            updated[index] = opportunity;
            this.opportunities.set(updated);
          } else {
            // Add new
            console.log('[Component] Adding new opportunity, total will be:', current.length + 1);
            this.opportunities.set([opportunity, ...current]);
          }
        },
        error: (err) => console.error('Opportunity stream error:', err)
      })
    );

    // Position updates
    this.subscriptions.add(
      this.arbitrageService.positions$.subscribe({
        next: (position) => {
          const current = this.positions();
          const index = current.findIndex(p => p.id === position.id);

          if (index >= 0) {
            // Update existing
            const updated = [...current];
            updated[index] = position;
            this.positions.set(updated);
          } else {
            // Add new
            this.positions.set([position, ...current]);
          }
        },
        error: (err) => console.error('Position stream error:', err)
      })
    );

    // Scanner stats updates
    this.subscriptions.add(
      this.arbitrageService.stats$.subscribe({
        next: (stats) => {
          // Stats are automatically updated in the service
        },
        error: (err) => console.error('Stats stream error:', err)
      })
    );

    // Scanner updates (for multi-exchange support)
    this.subscriptions.add(
      this.arbitrageService.scannerUpdates$.subscribe({
        next: (update) => {
          // Get config from memory or localStorage
          let config = this.scannerConfigs.get(update.exchange as Exchange);
          if (!config) {
            const stored = localStorage.getItem(`scanner_config_${update.exchange}`);
            if (stored) {
              config = JSON.parse(stored) as ScannerConfig;
              this.scannerConfigs.set(update.exchange as Exchange, config);
            }
          }

          // Transform backend stats to frontend ScannerStats format
          const backendStats: any = update.stats; // Backend uses different format
          const stats: ScannerStats | null = backendStats ? {
            status: update.status,
            opportunitiesDetectedToday: backendStats.opportunitiesDetectedToday || 0,
            opportunitiesExecutedToday: 0, // Not yet tracked by backend
            totalProfitToday: backendStats.totalProfitToday || 0,
            avgProfitPercentage: 0, // Not yet tracked by backend
            scanningDuration: (backendStats.runningFor || 0) * 1000, // Convert seconds to ms
            lastOpportunityAt: undefined // Not yet tracked by backend
          } : null;

          // Update scanner component with exchange-specific status
          this.scannerComponent?.updateScannerState(
            update.exchange as Exchange,
            update.status,
            stats,
            config || null
          );
        },
        error: (err) => console.error('Scanner update stream error:', err)
      })
    );
  }

  /**
   * Load opportunities from API
   */
  loadOpportunities(): void {
    this.isLoadingOpportunities.set(true);
    this.error.set(null);

    this.arbitrageService.getOpportunities().subscribe({
      next: (opportunities) => {
        this.opportunities.set(opportunities);
        this.isLoadingOpportunities.set(false);
      },
      error: (err) => {
        this.error.set('Failed to load opportunities: ' + err.message);
        this.isLoadingOpportunities.set(false);
      }
    });
  }

  /**
   * Load positions from API
   */
  loadPositions(filter?: PositionFilter): void {
    this.isLoadingPositions.set(true);
    this.error.set(null);

    this.arbitrageService.getPositions(filter).subscribe({
      next: (positions) => {
        this.positions.set(positions);
        this.isLoadingPositions.set(false);
      },
      error: (err) => {
        this.error.set('Failed to load positions: ' + err.message);
        this.isLoadingPositions.set(false);
      }
    });
  }

  /**
   * Handle scanner start
   */
  onScannerStart(config: ScannerConfig): void {
    // Store scanner config for later use (e.g., when executing opportunities)
    this.scannerConfigs.set(config.exchange, config);

    // Persist to localStorage to survive page reloads
    localStorage.setItem(`scanner_config_${config.exchange}`, JSON.stringify(config));

    this.arbitrageService.startScanning(config).subscribe({
      next: (response) => {
        if (response.success) {
          // Update scanner component with initial state
          this.scannerComponent?.updateScannerState(
            config.exchange,
            'scanning',
            null,
            config
          );
          this.loadOpportunities();
        }
      },
      error: (err) => {
        this.error.set('Failed to start scanner: ' + err.message);
        // Update scanner component with error state
        this.scannerComponent?.updateScannerState(
          config.exchange,
          'error',
          null,
          config
        );
      }
    });
  }

  /**
   * Handle scanner stop
   * @param exchange - Specific exchange to stop, or null to stop all
   */
  onScannerStop(exchange: Exchange | null): void {
    this.arbitrageService.stopScanning(exchange || undefined).subscribe({
      next: (response) => {
        if (response.success) {
          // Update scanner component state
          if (exchange) {
            // Stopped specific exchange
            this.scannerComponent?.updateScannerState(exchange, 'stopped');
          } else {
            // Stopped all - clear all scanners in component
            // Get all active scanners and update them
            const activeScanners = this.scannerComponent?.activeScannersList() || [];
            activeScanners.forEach(scanner => {
              this.scannerComponent?.updateScannerState(scanner.exchange, 'stopped');
            });
            // Clear opportunities only when stopping all
            this.opportunities.set([]);
          }
        }
      },
      error: (err) => {
        this.error.set('Failed to stop scanner: ' + err.message);
      }
    });
  }

  /**
   * Handle opportunity execution
   */
  onExecuteOpportunity(opportunity: TriangularOpportunity): void {
    // Get position size from scanner config for this exchange
    let config = this.scannerConfigs.get(opportunity.exchange);
    let restoredFromStorage = false;

    // If not in memory, try to restore from localStorage
    if (!config) {
      const stored = localStorage.getItem(`scanner_config_${opportunity.exchange}`);
      if (stored) {
        const parsedConfig = JSON.parse(stored) as ScannerConfig;
        config = parsedConfig;
        this.scannerConfigs.set(opportunity.exchange, parsedConfig);
        restoredFromStorage = true;
      }
    }

    const positionSize = config?.positionSize;

    console.log('[TriArb Execute] Executing opportunity:', {
      exchange: opportunity.exchange,
      hasConfig: !!config,
      positionSize: positionSize,
      scannerConfigsSize: this.scannerConfigs.size,
      allExchanges: Array.from(this.scannerConfigs.keys()),
      restoredFromStorage
    });

    // Build execution request
    const request: any = { opportunityId: opportunity.id };
    if (positionSize) {
      request.positionSize = positionSize;
    }

    console.log('[TriArb Execute] Request payload:', request);

    this.arbitrageService.executeOpportunity(request).subscribe({
      next: (response) => {
        if (response.success && response.position) {
          // Add to positions list
          this.positions.set([response.position, ...this.positions()]);
          // Switch to positions tab to show execution
          this.activeTab.set('positions');
        }
      },
      error: (err) => {
        this.error.set('Failed to execute opportunity: ' + err.message);
      }
    });
  }

  /**
   * Handle position cancellation
   */
  onCancelPosition(positionId: string): void {
    this.arbitrageService.cancelPosition(positionId).subscribe({
      next: (response) => {
        if (response.success) {
          this.loadPositions();
        }
      },
      error: (err) => {
        this.error.set('Failed to cancel position: ' + err.message);
      }
    });
  }

  /**
   * Handle position deletion
   */
  onDeletePosition(positionId: string): void {
    this.arbitrageService.deletePosition(positionId).subscribe({
      next: (response) => {
        if (response.success) {
          // Remove from local state
          this.positions.set(this.positions().filter(p => p.id !== positionId));
        }
      },
      error: (err) => {
        this.error.set('Failed to delete position: ' + err.message);
      }
    });
  }

  /**
   * Handle filter change
   */
  onFilterChange(filter: PositionFilter): void {
    this.positionFilter.set(filter);
    this.loadPositions(filter);
  }

  /**
   * Switch active tab
   */
  setActiveTab(tab: 'opportunities' | 'positions'): void {
    this.activeTab.set(tab);
  }

  /**
   * Reconnect WebSocket
   */
  reconnect(): void {
    this.arbitrageService.reconnect();
  }

  /**
   * Translation helper
   */
  translate(key: string): string {
    return this.translationService.translate(key);
  }
}

import { Injectable, OnDestroy } from '@angular/core';
import { BehaviorSubject, Observable, Subject } from 'rxjs';
import { environment } from '../../environments/environment';

/**
 * Real-time price update from SSE stream
 */
export interface PriceUpdate {
  type: 'price_update';
  symbol: string;
  exchange: string;
  credentialId: string;
  price: number;
  spread: string;
  spreadPercent: string;
  timestamp: number;
}

/**
 * Connection status message
 */
export interface ConnectionMessage {
  type: 'connected';
  userId: string;
  symbols: string[];
  exchangeCount: number;
  timestamp: number;
}

/**
 * Error message from stream
 */
export interface ErrorMessage {
  type: 'error';
  exchange: string;
  symbol: string;
  message: string;
  timestamp: number;
}

/**
 * Any stream message type
 */
export type StreamMessage = PriceUpdate | ConnectionMessage | ErrorMessage;

/**
 * Stream configuration for subscribing to arbitrage opportunities
 */
export interface StreamSubscription {
  symbols: string[];
  exchanges: {
    exchange: string;
    symbol: string;
    credentialId: string;
    apiKey?: string;
    apiSecret?: string;
    authToken?: string;
    environment: string;
  }[];
}

/**
 * ArbitrageStreamService
 *
 * Service for consuming real-time arbitrage price updates via Server-Sent Events (SSE).
 * Manages the connection lifecycle, handles reconnection, and distributes updates to subscribers.
 */
@Injectable({
  providedIn: 'root'
})
export class ArbitrageStreamService implements OnDestroy {
  private eventSource: EventSource | null = null;
  private reconnectTimer: any = null;
  private destroy$ = new Subject<void>();

  // Connection state
  private connectionState$ = new BehaviorSubject<'disconnected' | 'connecting' | 'connected'>('disconnected');

  // Stream data
  private priceUpdates$ = new Subject<PriceUpdate>();
  private errors$ = new Subject<ErrorMessage>();

  // Price cache for quick access
  private priceCache = new Map<string, { price: number; timestamp: number; change: 'up' | 'down' | 'neutral' }>();

  constructor() {
    console.log('[ArbitrageStream] Service initialized');
  }

  ngOnDestroy(): void {
    this.disconnect();
    this.destroy$.next();
    this.destroy$.complete();
  }

  /**
   * Get connection state observable
   */
  getConnectionState(): Observable<'disconnected' | 'connecting' | 'connected'> {
    return this.connectionState$.asObservable();
  }

  /**
   * Get price updates observable
   */
  getPriceUpdates(): Observable<PriceUpdate> {
    return this.priceUpdates$.asObservable();
  }

  /**
   * Get errors observable
   */
  getErrors(): Observable<ErrorMessage> {
    return this.errors$.asObservable();
  }

  /**
   * Get cached price for a specific exchange-symbol pair
   */
  getCachedPrice(exchange: string, symbol: string): { price: number; timestamp: number; change: 'up' | 'down' | 'neutral' } | null {
    const key = `${exchange}:${symbol}`;
    return this.priceCache.get(key) || null;
  }

  /**
   * Subscribe to real-time price updates for arbitrage opportunities
   *
   * @param subscription - Configuration for symbols and exchanges to monitor
   * @param authToken - JWT authentication token
   */
  subscribe(subscription: StreamSubscription, authToken: string): void {
    // Disconnect existing connection if any
    if (this.eventSource) {
      console.log('[ArbitrageStream] Closing existing connection');
      this.disconnect();
    }

    console.log('[ArbitrageStream] Connecting to SSE stream');
    this.connectionState$.next('connecting');

    // Build query parameters
    const symbols = subscription.symbols.join(',');
    const exchanges = JSON.stringify(subscription.exchanges);

    // Construct SSE URL with auth token as query parameter
    // Note: EventSource doesn't support custom headers, so we pass token via URL
    const baseUrl = environment.apiUrl || '/api';
    const url = `${baseUrl}/arbitrage/funding-rates/stream?symbols=${encodeURIComponent(symbols)}&exchanges=${encodeURIComponent(exchanges)}&token=${encodeURIComponent(authToken)}`;

    console.log('[ArbitrageStream] Connecting to:', url);

    // Create EventSource connection
    this.eventSource = new EventSource(url);

    // Handle connection open
    this.eventSource.onopen = () => {
      console.log('[ArbitrageStream]  Connected to SSE stream');
      this.connectionState$.next('connected');

      // Clear reconnect timer
      if (this.reconnectTimer) {
        clearTimeout(this.reconnectTimer);
        this.reconnectTimer = null;
      }
    };

    // Handle incoming messages
    this.eventSource.onmessage = (event: MessageEvent) => {
      try {
        const message: StreamMessage = JSON.parse(event.data);

        if (message.type === 'connected') {
          console.log('[ArbitrageStream] Connection confirmed:', message);
        } else if (message.type === 'price_update') {
          this.handlePriceUpdate(message as PriceUpdate);
        } else if (message.type === 'error') {
          console.error('[ArbitrageStream] Stream error:', message);
          this.errors$.next(message as ErrorMessage);
        }
      } catch (error: any) {
        console.error('[ArbitrageStream] Error parsing SSE message:', error.message, event.data);
      }
    };

    // Handle errors
    this.eventSource.onerror = (error: Event) => {
      console.error('[ArbitrageStream]  SSE connection error:', error);
      this.connectionState$.next('disconnected');

      // Close the connection
      if (this.eventSource) {
        this.eventSource.close();
        this.eventSource = null;
      }

      // Attempt to reconnect after 5 seconds
      console.log('[ArbitrageStream] Attempting to reconnect in 5 seconds...');
      this.reconnectTimer = setTimeout(() => {
        console.log('[ArbitrageStream] Reconnecting...');
        this.subscribe(subscription, authToken);
      }, 5000);
    };
  }

  /**
   * Handle price update from stream
   */
  private handlePriceUpdate(update: PriceUpdate): void {
    const key = `${update.exchange}:${update.symbol}`;

    // Determine price change direction
    const cached = this.priceCache.get(key);
    let change: 'up' | 'down' | 'neutral' = 'neutral';

    if (cached) {
      if (update.price > cached.price) {
        change = 'up';
      } else if (update.price < cached.price) {
        change = 'down';
      }
    }

    // Update cache
    this.priceCache.set(key, {
      price: update.price,
      timestamp: update.timestamp,
      change,
    });

    // Emit update to subscribers
    this.priceUpdates$.next(update);

    console.log(`[ArbitrageStream] Price update: ${update.exchange} ${update.symbol} = $${update.price.toFixed(2)} (${change})`);
  }

  /**
   * Disconnect from SSE stream
   */
  disconnect(): void {
    console.log('[ArbitrageStream] Disconnecting from SSE stream');

    // Clear reconnect timer
    if (this.reconnectTimer) {
      clearTimeout(this.reconnectTimer);
      this.reconnectTimer = null;
    }

    // Close EventSource
    if (this.eventSource) {
      this.eventSource.close();
      this.eventSource = null;
    }

    // Update state
    this.connectionState$.next('disconnected');

    // Clear cache
    this.priceCache.clear();

    console.log('[ArbitrageStream] Disconnected');
  }

  /**
   * Check if currently connected
   */
  isConnected(): boolean {
    return this.connectionState$.value === 'connected';
  }

  /**
   * Clear price cache
   */
  clearCache(): void {
    this.priceCache.clear();
  }
}

import { computed, effect, Injectable, signal } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { BehaviorSubject, catchError, Observable, tap, throwError, map, take, of } from 'rxjs';
import { getEndpointUrl } from '../config/app.config';

export interface User {
  id: string;
  email: string;
  name?: string;
  role: string;
  avatar?: string;
  subscriptionActive: boolean;
  subscriptionExpiry?: string;
  googleLinked?: boolean;
}

export interface AuthResponse {
  token: string;
  user: User;
}

export interface AuthState {
  user: User | null;
  token: string | null;
  isLoading: boolean;
  error: string | null;
}

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  public isReady = new BehaviorSubject<boolean>(false);
  private readonly tokenKey = 'auth_token';
  private readonly userKey = 'auth_user';
  private _authState = signal<AuthState>({
    user: null,
    token: null,
    isLoading: false,
    error: null
  });

  private sessionCheckInterval?: number;
  private readonly SESSION_CHECK_INTERVAL = 5 * 60 * 1000; // 5 minutes

  // Public computed signals
  public readonly authState = this._authState.asReadonly();
  public readonly currentUser = computed(() => this._authState().user);
  public readonly isAuthenticated = computed(() => !!this._authState().token);
  public readonly isLoading = computed(() => this._authState().isLoading);
  public readonly error = computed(() => this._authState().error);

  constructor(private http: HttpClient) {
    this.loadAuthDataFromStorage();
    this.setupWindowFocusListener();

    effect(() => {
      const state = this._authState();

      // Persist token
      if (state.token) {
        localStorage.setItem(this.tokenKey, state.token);
        this.startSessionMonitoring();
      } else {
        localStorage.removeItem(this.tokenKey);
        this.stopSessionMonitoring();
      }

      // Persist user data
      if (state.user) {
        localStorage.setItem(this.userKey, JSON.stringify(state.user));
      } else {
        localStorage.removeItem(this.userKey);
      }
    });
  }

  login(email: string, password: string): Observable<AuthResponse> {
    this.updateAuthState({ isLoading: true, error: null, user: null });

    return this.http.post<AuthResponse>(getEndpointUrl('auth', 'login'), {
      email,
      password
    }).pipe(
      take(1),
      tap(response => this.setAuthData(response)),
      catchError(error => {
        this.updateAuthState({
          isLoading: false,
          error: error.error?.message || 'Login failed'
        });
        return throwError(() => error);
      })
    );
  }

  register(email: string, password: string, name?: string): Observable<AuthResponse> {
    this.updateAuthState({ isLoading: true, error: null });

    return this.http.post<AuthResponse>(getEndpointUrl('auth', 'register'), {
      email,
      password,
      name
    }).pipe(
      take(1),
      tap(response => this.setAuthData(response)),
      catchError(error => {
        this.updateAuthState({
          isLoading: false,
          error: error.error?.message || 'Registration failed'
        });
        return throwError(() => error);
      })
    );
  }

  loginWithGoogle(idToken: string): Observable<AuthResponse> {
    this.updateAuthState({ isLoading: true, error: null });

    return this.http.post<AuthResponse>(getEndpointUrl('google', 'auth'), {
      idToken
    }).pipe(
      take(1),
      tap(response => this.setAuthData(response)),
      catchError(error => {
        this.updateAuthState({
          isLoading: false,
          error: error.error?.message || 'Google login failed'
        });
        return throwError(() => error);
      })
    );
  }

  logout(): Observable<any> {
    return this.http.post(getEndpointUrl('auth', 'logout'), {}).pipe(
      take(1),
      tap(() => {
        this.clearAuthData();
      }),
      catchError(() => {
        this.clearAuthData();
        return throwError(() => 'Logout completed locally');
      })
    );
  }

  getUserProfile(): Observable<User> {
    const url = getEndpointUrl('user', 'profile');

    return this.http.get<User>(url).pipe(
      take(1),
      tap((user: User) => {
        this.updateAuthState({ user })
      }),
      catchError((error) => {
        return throwError(() => error);
      })
    );
  }

  updateProfile(data: { name?: string; avatar?: string }): Observable<User> {
    return this.http.put<User>(getEndpointUrl('user', 'profile'), data).pipe(
      take(1),
      tap(user => this.updateAuthState({ user }))
    );
  }

  hasRole(role: string): boolean {
    const user = this.currentUser();
    if (!user) return false;

    const roleHierarchy = {
      'BASIC': 0,
      'PREMIUM': 1,
      'ENTERPRISE': 2,
      'ADMIN': 3,
    } as const;

    const userLevel = roleHierarchy[user.role as keyof typeof roleHierarchy] ?? 0;
    const requiredLevel = roleHierarchy[role as keyof typeof roleHierarchy] ?? 0;

    return userLevel >= requiredLevel;
  }

  clearError() {
    this.updateAuthState({ error: null });
  }

  handleSessionExpired() {
    console.log('handleSessionExpired called');
    this.clearAuthData();
    this.updateAuthState({
      error: 'Your session has expired. Please log in again.'
    });
    console.log('Session expiry handled, auth state cleared');
  }

  private handleExpiredToken() {
    console.log('handleExpiredToken called - clearing local storage and auth state');
    localStorage.removeItem(this.tokenKey);
    localStorage.removeItem(this.userKey);
    this.updateAuthState({
      user: null,
      token: null,
      isLoading: false,
      error: 'Your session has expired. Please log in again.'
    });
  }

  // For testing - simulate session expiry
  simulateSessionExpiry() {
    console.log('Simulating session expiry...');
    this.handleSessionExpired();
  }

  // For testing - validate current token manually
  validateCurrentToken(): boolean {
    const token = localStorage.getItem(this.tokenKey);
    if (!token) {
      console.log('No token found for validation');
      return false;
    }

    const isValid = this.isTokenValid(token);
    console.log('Current token validation result:', isValid);

    if (!isValid) {
      const expTime = this.getTokenExpirationTime(token);
      if (expTime) {
        console.log('Token expired at:', expTime);
        console.log('Current time:', new Date());
      }
    }

    return isValid;
  }

  // For testing - get token info
  getTokenInfo(): any {
    const token = localStorage.getItem(this.tokenKey);
    if (!token) {
      return null;
    }

    const payload = this.decodeJwtPayload(token);
    if (payload) {
      return {
        exp: payload.exp,
        expirationDate: new Date(payload.exp * 1000),
        currentDate: new Date(),
        isValid: this.isTokenValid(token),
        timeUntilExpiry: payload.exp ? (payload.exp * 1000 - Date.now()) / 1000 : null
      };
    }

    return null;
  }

  // For testing - get stored auth data
  getStoredAuthData(): { token: string | null; user: User | null } {
    const token = localStorage.getItem(this.tokenKey);
    const userDataStr = localStorage.getItem(this.userKey);

    let user: User | null = null;
    if (userDataStr) {
      try {
        user = JSON.parse(userDataStr);
      } catch (error) {
        console.log('Failed to parse stored user data:', error);
      }
    }

    return { token, user };
  }

  // Force session validation check
  forceSessionCheck(): Observable<boolean> {
    console.log('Force session check triggered');
    if (!this.isAuthenticated()) {
      console.log('Not authenticated, skipping session check');
      return of(false);
    }

    return this.getUserProfile().pipe(
      map(() => {
        console.log('Session check passed - user profile retrieved');
        return true;
      }),
      catchError((error) => {
        console.log('Session check failed:', error);
        if (error.status === 401) {
          console.log('401 during session check - handling session expiry');
          this.handleSessionExpired();
        }
        return throwError(() => error);
      })
    );
  }

  private loadAuthDataFromStorage() {
    const token = localStorage.getItem(this.tokenKey);
    const userDataStr = localStorage.getItem(this.userKey);

    if (token) {
      console.log('Found token in localStorage, validating...');

      if (this.isTokenValid(token)) {
        console.log('Token is valid, loading auth data from localStorage');

        // Load user data from localStorage if available
        let savedUser: User | null = null;
        if (userDataStr) {
          try {
            savedUser = JSON.parse(userDataStr);
            console.log('Loaded user data from localStorage:', savedUser);
          } catch (error) {
            console.log('Failed to parse saved user data:', error);
            localStorage.removeItem(this.userKey);
          }
        }

        // Set auth state with saved data
        this.updateAuthState({ token, user: savedUser });

        // Fetch fresh user profile from server to ensure data is up-to-date
        this.getUserProfile().subscribe({
          next: (user) => {
            console.log('User profile fetched successfully and updated');
            this.isReady.next(true);
            this.updateAuthState({ user });
          },
          error: (error) => {
            console.log('Failed to fetch user profile:', error);
            if (error.status === 401) {
              console.log('401 error - token might be invalid on server side');
              this.handleExpiredToken();
            } else {
              console.log('Network error - using cached user data if available');
              // Keep using cached user data if network error
              if (savedUser) {
                console.log('Using cached user data due to network error');
              }
            }
            this.isReady.next(true);
          }
        });
      } else {
        console.log('Token is expired or invalid, clearing auth data');
        this.handleExpiredToken();
        this.isReady.next(true);
      }
    } else {
      console.log('No token found in localStorage');
      this.isReady.next(true);
    }
  }

  private updateAuthState(partial: Partial<AuthState>) {
    this._authState.update(state => {
      const newState = { ...state, ...partial };
      // Ensure isLoading is false if there's an error
      if (newState.error) {
        newState.isLoading = false;
      }
      return newState;
    });
  }

  private setAuthData(response: AuthResponse) {
    this.updateAuthState({
      token: response.token,
      user: response.user,
      isLoading: false,
      error: null
    });
  }

  clearAuthData() {
    localStorage.removeItem(this.tokenKey);
    localStorage.removeItem(this.userKey);
    this.updateAuthState({
      user: null,
      token: null,
      isLoading: false,
      error: null
    });
  }

  private isTokenValid(token: string): boolean {
    try {
      const payload = this.decodeJwtPayload(token);
      if (!payload || !payload.exp) {
        console.log('Token missing expiration claim');
        return false;
      }

      const currentTime = Math.floor(Date.now() / 1000);
      const isValid = payload.exp > currentTime;

      if (!isValid) {
        console.log('Token expired:', new Date(payload.exp * 1000), 'Current:', new Date());
      } else {
        console.log('Token valid until:', new Date(payload.exp * 1000));
      }

      return isValid;
    } catch (error) {
      console.log('Token validation error:', error);
      return false;
    }
  }

  private decodeJwtPayload(token: string): any {
    try {
      const parts = token.split('.');
      if (parts.length !== 3) {
        throw new Error('Invalid JWT format');
      }

      const payload = parts[1];
      const decoded = atob(payload.replace(/-/g, '+').replace(/_/g, '/'));
      return JSON.parse(decoded);
    } catch (error) {
      console.log('JWT decode error:', error);
      return null;
    }
  }

  private getTokenExpirationTime(token: string): Date | null {
    try {
      const payload = this.decodeJwtPayload(token);
      if (payload && payload.exp) {
        return new Date(payload.exp * 1000);
      }
    } catch (error) {
      console.log('Error getting token expiration:', error);
    }
    return null;
  }

  private startSessionMonitoring() {
    if (this.sessionCheckInterval) {
      clearInterval(this.sessionCheckInterval);
    }

    console.log('Starting session monitoring with 5-minute intervals');
    this.sessionCheckInterval = window.setInterval(() => {
      console.log('Periodic session check triggered');
      this.forceSessionCheck().subscribe({
        next: (valid) => {
          console.log('Periodic session check result:', valid ? 'valid' : 'invalid');
        },
        error: (error) => {
          console.log('Periodic session check error:', error);
        }
      });
    }, this.SESSION_CHECK_INTERVAL);
  }

  private stopSessionMonitoring() {
    if (this.sessionCheckInterval) {
      console.log('Stopping session monitoring');
      clearInterval(this.sessionCheckInterval);
      this.sessionCheckInterval = undefined;
    }
  }

  private setupWindowFocusListener() {
    window.addEventListener('focus', () => {
      if (this.isAuthenticated()) {
        console.log('Window focused - performing session check');
        this.forceSessionCheck().subscribe({
          next: (valid) => {
            console.log('Window focus session check result:', valid ? 'valid' : 'invalid');
          },
          error: (error) => {
            console.log('Window focus session check error:', error);
          }
        });
      }
    });
  }
}

