  /**
   * Get current user info from cache (if available)
   */
  getCurrentUserInfo(): BybitUserInfo | null {
    return this.userInfoSubject.value;
  }

  /**
   * Clear cached user info
   */
  clearUserInfo(): void {
    this.userInfoSubject.next(null);
    this.errorSubject.next(null);
  }

  /**
   * Check if user info is currently loading
   */
  isLoading(): boolean {
    return this.loadingSubject.value;
  }

  /**
   * Get current error (if any)
   */
  getCurrentError(): string | null {
    return this.errorSubject.value;
  }

  /**
   * Calculate total portfolio value in USD
   */
  calculateTotalValue(userInfo: BybitUserInfo): number {
    if (!userInfo.success || !userInfo.data.accountInfo) {
      return 0;
    }

    const accountInfo = userInfo.data.accountInfo;
    if ('error' in accountInfo) {
      return 0;
    }

    return parseFloat(accountInfo.totalEquity || '0');
  }

  /**
   * Get all non-zero coin balances
   */
  getNonZeroBalances(userInfo: BybitUserInfo): BybitCoinBalance[] {
    if (!userInfo.success || !userInfo.data.walletBalance) {
      return [];
    }

    const walletBalance = userInfo.data.walletBalance;
    if ('error' in walletBalance || !walletBalance.list || walletBalance.list.length === 0) {
      return [];
    }

    const coins = walletBalance.list[0].coin || [];
    return coins.filter(coin => parseFloat(coin.walletBalance) > 0);
  }

  /**
   * Get active (non-zero) positions
   */
  getActivePositions(userInfo: BybitUserInfo): BybitPosition[] {
    if (!userInfo.success || !userInfo.data.positions) {
      return [];
    }

    return userInfo.data.positions.filter(
      position => parseFloat(position.size) > 0 && position.side !== 'None'
    );
  }

  /**
   * Calculate total unrealized PnL from positions
   */
  calculateUnrealizedPnl(userInfo: BybitUserInfo): number {
    const activePositions = this.getActivePositions(userInfo);
    return activePositions.reduce(
      (total, position) => total + parseFloat(position.unrealisedPnl || '0'),
      0
    );
  }

  /**
   * Save API keys to the database
   * This method now validates AND saves the keys in a single operation
   * The backend tests the credentials before storing them
   * @param apiKey - Bybit API key
   * @param apiSecret - Bybit API secret
   * @param testnet - Whether to use testnet
   * @returns Observable<BybitSaveApiKeysResponse>
   */
  saveApiKeys(apiKey: string, apiSecret: string, testnet: boolean): Observable<BybitSaveApiKeysResponse> {
    this.loadingSubject.next(true);
    this.errorSubject.next(null);

    const request: BybitSaveApiKeysRequest = {
      apiKey,
      apiSecret,
      testnet
    };

    return this.http.post<BybitSaveApiKeysResponse>(
      getEndpointUrl('bybit', 'apiKeys'),
      request
    ).pipe(
      tap(response => {
        this.loadingSubject.next(false);
        if (response.success) {
          // Update stored keys status
          this.storedKeysSubject.next({
            success: true,
            data: {
              hasKeys: true,
              testnet,
              apiKeyPreview: this.maskApiKey(apiKey)
            },
            timestamp: new Date().toISOString()
          });
        }
      }),
      catchError(error => {
        this.loadingSubject.next(false);
        const errorMessage = this.handleError(error);
        this.errorSubject.next(errorMessage);
        return throwError(() => new Error(errorMessage));
      })
    );
  }

  /**
   * Get stored API keys information (without exposing actual keys)
   * @returns Observable<BybitStoredApiKeysResponse>
   */
  getStoredApiKeys(): Observable<BybitStoredApiKeysResponse> {
    this.loadingSubject.next(true);
    this.errorSubject.next(null);

    return this.http.get<BybitStoredApiKeysResponse>(
      getEndpointUrl('bybit', 'storedApiKeys')
    ).pipe(
      tap(response => {
        this.loadingSubject.next(false);
        this.storedKeysSubject.next(response);
      }),
      catchError(error => {
        this.loadingSubject.next(false);
        const errorMessage = this.handleError(error);
        this.errorSubject.next(errorMessage);
        return throwError(() => new Error(errorMessage));
      })
    );
  }

  /**
   * Delete stored API keys from the database
   * @returns Observable<BybitDeleteApiKeysResponse>
   */
  deleteApiKeys(): Observable<BybitDeleteApiKeysResponse> {
    this.loadingSubject.next(true);
    this.errorSubject.next(null);

    return this.http.delete<BybitDeleteApiKeysResponse>(
      getEndpointUrl('bybit', 'deleteApiKeys')
    ).pipe(
      tap(response => {
        this.loadingSubject.next(false);
        if (response.success) {
          // Clear stored keys status
          this.storedKeysSubject.next(null);
          // Clear user info as well
          this.userInfoSubject.next(null);
        }
      }),
      catchError(error => {
        this.loadingSubject.next(false);
        const errorMessage = this.handleError(error);
        this.errorSubject.next(errorMessage);
        return throwError(() => new Error(errorMessage));
      })
    );
  }

  /**
   * Get current stored keys status
   */
  getStoredKeysStatus(): BybitStoredApiKeysResponse | null {
    return this.storedKeysSubject.value;
  }

  /**
   * Check if user has stored API keys
   */
  hasStoredKeys(): boolean {
    const storedKeys = this.storedKeysSubject.value;
    return storedKeys?.data?.hasKeys ?? false;
  }

  /**
   * Get detailed wallet balance from Bybit
   * @param accountType - Account type (UNIFIED or CONTRACT)
   * @param coin - Optional specific coin to query
   * @returns Observable<BybitWalletBalanceResponse>
   */
  getWalletBalance(accountType: 'UNIFIED' | 'CONTRACT' = 'UNIFIED', coin?: string): Observable<BybitWalletBalanceResponse> {
    const baseUrl = getEndpointUrl('bybit', 'walletBalance');
    const params: Record<string, string> = {
      accountType
    };
    if (coin) {
      params['coin'] = coin;
    }
    const url = buildUrlWithQuery(baseUrl, params);

    return this.http.get<BybitWalletBalanceResponse>(url).pipe(
      catchError(error => {
        console.error('Error fetching wallet balance:', error);
        return throwError(() => error);
      })
    );
  }

  /**
   * Get asset information from Bybit
   * Retrieves detailed asset information for different account types
   * @param accountType - Optional account type (SPOT, CONTRACT, UNIFIED, etc.)
   * @param coin - Optional specific coin to query (BTC, ETH, USDT, etc.)
   * @returns Observable<BybitAssetInfoResponse>
   */
  getAssetInfo(accountType?: string, coin?: string): Observable<BybitAssetInfoResponse> {
    const baseUrl = getEndpointUrl('bybit', 'assetInfo');
    const params: Record<string, string> = {};
    if (accountType) {
      params['accountType'] = accountType.toUpperCase();
    }
    if (coin) {
      params['coin'] = coin.toUpperCase();
    }
    const url = buildUrlWithQuery(baseUrl, params);

    return this.http.get<BybitAssetInfoResponse>(url).pipe(
      catchError(error => {
        console.error('Error fetching asset info:', error);
        const errorMessage = this.handleError(error);
        return throwError(() => new Error(errorMessage));
      })
    );
  }

  /**
   * Get all coins balance from Bybit
   * Retrieves comprehensive balance information for all coins in a specific account type.
   * This endpoint can query the FUND (Funding wallet) where balances may be stored.
   *
   * @param accountType - Optional account type (UNIFIED, SPOT, CONTRACT, FUND, OPTION, etc.)
   *                      Default: FUND (Funding wallet)
   * @param coin - Optional specific coin to query (BTC, ETH, USDT, etc.)
   * @returns Observable<BybitAllCoinsBalanceResponse>
   */
  getAllCoinsBalance(accountType?: string, coin?: string): Observable<BybitAllCoinsBalanceResponse> {
    const baseUrl = getEndpointUrl('bybit', 'allCoinsBalance');
    const params: Record<string, string> = {};
    if (accountType) {
      params['accountType'] = accountType.toUpperCase();
    }
    if (coin) {
      params['coin'] = coin.toUpperCase();
    }
    const url = buildUrlWithQuery(baseUrl, params);

    return this.http.get<BybitAllCoinsBalanceResponse>(url).pipe(
      catchError(error => {
        console.error('Error fetching all coins balance:', error);
        const errorMessage = this.handleError(error);
        return throwError(() => new Error(errorMessage));
      })
    );
  }

  /**
   * Mask API key for display (show only last 4 characters)
   */
  private maskApiKey(apiKey: string): string {
    if (apiKey.length <= 4) {
      return '****';
    }
    return '****' + apiKey.slice(-4);
  }

  /**
   * Handle HTTP errors and return user-friendly messages
   */
  private handleError(error: HttpErrorResponse): string {
    console.error('Bybit User Service Error:', error);

    if (error.error?.message) {
      return error.error.message;
    }

    if (error.status === 0) {
      return 'Unable to connect to the server. Please check your internet connection.';
    }

    if (error.status === 401) {
      return 'Invalid API credentials. Please check your Bybit API key and secret.';
    }

    if (error.status === 403) {
      return 'Access forbidden. Please check your API key permissions.';
    }

    if (error.status === 429) {
      return 'Too many requests. Please wait a moment and try again.';
    }

    if (error.status >= 500) {
      return 'Server error. Please try again later.';
    }

    return error.message || 'An unexpected error occurred. Please try again.';
  }
}

import { Injectable, OnDestroy } from '@angular/core';
import { BehaviorSubject, Observable, Subject, timer, EMPTY, Subscription } from 'rxjs';
import { webSocket, WebSocketSubject } from 'rxjs/webSocket';
import { catchError, retry, takeUntil, map, filter } from 'rxjs/operators';

/**
 * Bybit WebSocket V5 kline/candlestick data structure
 * Raw format received from WebSocket stream
 */
export interface BybitWsKlineData {
  start: number; // Bybit V5 uses 'start' for the candle start time (milliseconds)
  end: number;   // Bybit V5 uses 'end' for the candle end time (milliseconds)
  interval: string;
  open: string;
  close: string;
  high: string;
  low: string;
  volume: string;
  turnover: string;
  confirm: boolean; // true means this kline is closed
  timestamp: number; // Message timestamp
}

export interface BybitWsTickerData {
  symbol: string;
  lastPrice: string;
  highPrice24h: string;
  lowPrice24h: string;
  prevPrice24h: string;
  volume24h: string;
  turnover24h: string;
  price24hPcnt: string;
  usdIndexPrice: string;
}

export interface BybitWsOrderbookData {
  symbol: string;
  bids: [string, string][]; // [price, size]
  asks: [string, string][]; // [price, size]
  ts: number;
  u: number; // update id
}

export interface BybitWsTradeData {
  symbol: string;
  execId: string;
  price: string;
  size: string;
  side: 'Buy' | 'Sell';
  time: number;
  isBlockTrade: boolean;
}

export interface BybitWsMessage {
  topic: string;
  type: string;
  data: BybitWsKlineData[] | BybitWsTickerData[] | BybitWsOrderbookData | BybitWsTradeData[];
  cs: number;
  ts: number;
}

export interface CandlestickData {
  time: number;
  open: number;
  high: number;
  low: number;
  close: number;
  volume?: number;
}

export interface TickerData {
  symbol: string;
  lastPrice: number;
  highPrice24h: number;
  lowPrice24h: number;
  prevPrice24h: number;
  volume24h: number;
  turnover24h: number;
  price24hPcnt: number;
  usdIndexPrice: number;
  timestamp: number;
}

export interface OrderbookData {
  symbol: string;
  bids: { price: number; size: number }[];
  asks: { price: number; size: number }[];
  timestamp: number;
}

export interface TradeData {
  symbol: string;
  execId: string;
  price: number;
  size: number;
  side: 'Buy' | 'Sell';
  timestamp: number;
  isBlockTrade: boolean;
}

/**
 * Bybit WebSocket Service - Low-level WebSocket connection manager
 *
 * This service manages the WebSocket connection to Bybit V5 public API, handling:
 * - Connection lifecycle (connect, disconnect, reconnect)
 * - Subscription management for multiple data streams
 * - Message routing and transformation
 * - Heartbeat/ping mechanism to keep connection alive
 * - Auto-reconnection on connection failure
 * - Data caching for latest values
 *
 * The service maintains a single WebSocket connection and multiplexes multiple
 * subscriptions through it, providing separate Observable streams for each subscription.
 *
 * @example
 * ```typescript
 * // Subscribe to kline data
 * this.wsService.subscribeToKline('BTCUSDT', '1').subscribe(candle => {
 *   console.log('New candle:', candle);
 * });
 *
 * // Check connection state
 * this.wsService.getConnectionState().subscribe(state => {
 *   console.log('Connection state:', state);
 * });
 *
 * // Clean up on destroy
 * this.wsService.unsubscribeFromKline('BTCUSDT', '1');
 * ```
 *
 * @see {@link BybitService} for high-level API combining REST and WebSocket
 */
@Injectable({
  providedIn: 'root'
})
export class BybitWebSocketService implements OnDestroy {
  // WebSocket URLs for different environments
  private readonly WS_URLS = {
    mainnet: 'wss://stream.bybit.com/v5/public/spot',
    testnet: 'wss://stream-testnet.bybit.com/v5/public/spot'
  };

  private currentEnvironment: 'mainnet' | 'testnet' = 'mainnet';
  private socket$: WebSocketSubject<any> | null = null;
  private destroy$ = new Subject<void>();
  private pingTimer?: Subscription;

  // Connection state
  private connectionState$ = new BehaviorSubject<'disconnected' | 'connecting' | 'connected'>('disconnected');

  // Data streams
  private klineData$ = new Subject<CandlestickData>();
  private tickerData$ = new Subject<TickerData>();
  private orderbookData$ = new Subject<OrderbookData>();
  private tradeData$ = new Subject<TradeData>();

  // Subscription management
  private subscriptions = new Map<string, Subject<any>>();
  private dataCache = new Map<string, any>(); // Cache latest data for each stream

  constructor() {
    console.log('BybitWebSocketService initialized');
  }

  ngOnDestroy(): void {
    this.stopPing();
    this.disconnect();
    this.destroy$.next();
    this.destroy$.complete();
  }

  /**
   * Get connection state as observable
   */
  getConnectionState(): Observable<'disconnected' | 'connecting' | 'connected'> {
    return this.connectionState$.asObservable();
  }

  /**
   * Set WebSocket environment (mainnet or testnet)
   * Will disconnect and reconnect if already connected
   */
  setEnvironment(environment: 'MAINNET' | 'TESTNET'): void {
    const normalizedEnv = environment.toLowerCase() as 'mainnet' | 'testnet';

    if (this.currentEnvironment === normalizedEnv) {
      console.log(`Already using ${normalizedEnv} WebSocket`);
      return;
    }

    console.log(`Switching WebSocket from ${this.currentEnvironment} to ${normalizedEnv}`);
    this.currentEnvironment = normalizedEnv;

    // If currently connected, disconnect and reconnect with new environment
    if (this.socket$ && !this.socket$.closed) {
      const activeSubscriptions = this.getActiveSubscriptions();
      console.log('Reconnecting with active subscriptions:', activeSubscriptions);

      this.disconnect();

      // Give a moment for clean disconnect
      setTimeout(() => {
        this.connect();
      }, 500);
    }
  }

  /**
   * Get current environment
   */
  getCurrentEnvironment(): 'mainnet' | 'testnet' {
    return this.currentEnvironment;
  }

  /**
   * Connect to Bybit WebSocket
   */
  private connect(): void {
    if (this.socket$ && !this.socket$.closed) {
      console.log('WebSocket already connected');
      return;
    }

    const wsUrl = this.WS_URLS[this.currentEnvironment];
    console.log(`Connecting to Bybit WebSocket (${this.currentEnvironment}):`, wsUrl);
    this.connectionState$.next('connecting');

    this.socket$ = webSocket({
      url: wsUrl,
      openObserver: {
        next: () => {
          console.log(` Connected to Bybit V5 WebSocket (${this.currentEnvironment}):`, wsUrl);
          this.connectionState$.next('connected');
          this.startPing(); // Start ping immediately after connection
        }
      },
      closeObserver: {
        next: (event) => {
          console.log(' Disconnected from Bybit WebSocket:', event);
          this.connectionState$.next('disconnected');
        }
      }
    });

    // Handle incoming messages
    this.socket$.pipe(
      takeUntil(this.destroy$),
      catchError(error => {
        console.error('WebSocket error:', error);
        this.connectionState$.next('disconnected');

        // Auto-reconnect after 5 seconds
        timer(5000).pipe(takeUntil(this.destroy$)).subscribe(() => {
          console.log('Attempting to reconnect...');
          this.connect();
        });

        return EMPTY;
      })
    ).subscribe((message: BybitWsMessage) => {
      this.handleMessage(message);
    });
  }

  /**
   * Disconnect from WebSocket
   */
  disconnect(): void {
    this.stopPing();
    if (this.socket$) {
      this.socket$.complete();
      this.socket$ = null;
    }
    this.subscriptions.clear();
    this.connectionState$.next('disconnected');
    console.log(' WebSocket disconnected');
  }

  /**
   * Subscribe to kline data for a specific symbol and interval
   */
  subscribeToKline(symbol: string, interval = '1'): Observable<CandlestickData> {
    const topic = `kline.${interval}.${symbol}`;
    const subscriptionKey = `${symbol}_${interval}`;

    console.log(`[WS SERVICE] subscribeToKline called - topic: ${topic}, key: ${subscriptionKey}`);

    // Create or get existing subject for this subscription
    if (!this.subscriptions.has(subscriptionKey)) {
      console.log(`[WS SERVICE] Creating new Subject for ${subscriptionKey}`);
      this.subscriptions.set(subscriptionKey, new Subject<CandlestickData>());
    } else {
      console.log(`[WS SERVICE] Reusing existing Subject for ${subscriptionKey}`);
    }

    // Connect if not already connected
    if (!this.socket$ || this.socket$.closed) {
      console.log('[WS SERVICE] WebSocket not connected, initiating connection...');
      this.connect();
    } else {
      console.log('[WS SERVICE] WebSocket already connected');
    }

    // Wait for connection and then subscribe
    console.log('[WS SERVICE] Setting up connection state listener...');
    this.connectionState$.pipe(
      filter(state => {
        console.log(`[WS SERVICE] Connection state changed: ${state}`);
        return state === 'connected';
      }),
      takeUntil(this.destroy$)
    ).subscribe(() => {
      console.log(`[WS SERVICE] Connection established, sending subscription for ${topic}`);
      this.sendSubscription(topic);
    });

    const observable = this.subscriptions.get(subscriptionKey)!.asObservable();
    console.log(`[WS SERVICE] Returning observable for ${subscriptionKey}`);

    // Add logging to the returned observable
    return new Observable<CandlestickData>(observer => {
      console.log(`[WS SERVICE] Observer subscribed to ${subscriptionKey}`);

      const subscription = observable.subscribe({
        next: (value) => {
          console.log(`[WS SERVICE] Passing data through observable for ${subscriptionKey}:`, value);
          observer.next(value);
        },
        error: (err) => {
          console.error(`[WS SERVICE] Error in observable for ${subscriptionKey}:`, err);
          observer.error(err);
        },
        complete: () => {
          console.log(`[WS SERVICE] Observable completed for ${subscriptionKey}`);
          observer.complete();
        }
      });

      return () => {
        console.log(`[WS SERVICE] Observer unsubscribed from ${subscriptionKey}`);
        subscription.unsubscribe();
      };
    });
  }

  /**
   * Unsubscribe from kline data
   */
  unsubscribeFromKline(symbol: string, interval = '1'): void {
    const topic = `kline.${interval}.${symbol}`;
    const subscriptionKey = `${symbol}_${interval}`;

    console.log(`Unsubscribing from kline data: ${topic}`);

    // Send unsubscribe message
    if (this.socket$ && !this.socket$.closed) {
      this.sendUnsubscription(topic);
    }

    // Clean up subscription
    const subject = this.subscriptions.get(subscriptionKey);
    if (subject) {
      subject.complete();
      this.subscriptions.delete(subscriptionKey);
    }
  }

  /**
   * Send subscription message
   */
  private sendSubscription(topic: string): void {
    if (!this.socket$ || this.socket$.closed) {
      console.error('Cannot subscribe: WebSocket not connected');
      return;
    }

    const subscribeMessage = {
      op: 'subscribe',
      args: [topic]
    };

    console.log('Sending subscription:', subscribeMessage);
    this.socket$.next(subscribeMessage);
  }

  /**
   * Send unsubscription message
   */
  private sendUnsubscription(topic: string): void {
    if (!this.socket$ || this.socket$.closed) {
      console.error('Cannot unsubscribe: WebSocket not connected');
      return;
    }

    const unsubscribeMessage = {
      op: 'unsubscribe',
      args: [topic]
    };

    console.log('Sending unsubscription:', unsubscribeMessage);
    this.socket$.next(unsubscribeMessage);
  }

  /**
   * Handle incoming WebSocket messages (Bybit V5 format)
   */
  private handleMessage(message: any): void {
    try {
      // Handle pong responses
      if (message.op === 'pong') {
        console.log(' Received pong from Bybit WebSocket');
        return;
      }

      // Handle subscription confirmations
      if (message.success !== undefined) {
        if (message.success) {
          console.log(' Subscription confirmed:', message);
          if (message.conn_id) {
            console.log(' Connection ID:', message.conn_id);
          }
        } else {
          console.error(' Subscription failed:', message);
        }
        return;
      }

      // Handle data updates by topic
      if (message.topic && message.data) {
        console.log('[WS SERVICE] Received data message:', {
          topic: message.topic,
          type: message.type,
          dataLength: Array.isArray(message.data) ? message.data.length : 1,
          timestamp: message.ts
        });

        if (message.topic.startsWith('kline.')) {
          console.log('[WS SERVICE] Routing to kline handler, raw data:', JSON.stringify(message.data).substring(0, 500));
          this.handleKlineData(message);
        } else if (message.topic.startsWith('tickers.')) {
          this.handleTickerData(message);
        } else if (message.topic.startsWith('orderbook.')) {
          this.handleOrderbookData(message);
        } else if (message.topic.startsWith('publicTrade.')) {
          this.handleTradeData(message);
        } else {
          console.log(' Unknown topic:', message.topic, message);
        }
      } else if (message.type === 'error') {
        console.error(' WebSocket error message:', message);
      } else {
        console.log(' Received message:', message);
      }
    } catch (error) {
      console.error('Error handling WebSocket message:', error, message);
    }
  }

  /**
   * Handle kline data from WebSocket
   */
  private handleKlineData(message: BybitWsMessage): void {
    try {
      console.log('[WS SERVICE] Received kline message:', message.topic);

      const topicParts = message.topic.split('.');
      if (topicParts.length < 3) {
        console.error('[WS SERVICE] Invalid kline topic format:', message.topic);
        return;
      }

      const interval = topicParts[1];
      const symbol = topicParts[2];
      const subscriptionKey = `${symbol}_${interval}`;

      console.log(`[WS SERVICE] Looking for subscription: ${subscriptionKey}`);
      console.log('[WS SERVICE] Active subscriptions:', Array.from(this.subscriptions.keys()));

      // Find the subscription subject
      const subject = this.subscriptions.get(subscriptionKey);
      if (!subject) {
        console.warn(`[WS SERVICE] No subscription found for ${subscriptionKey}`);
        console.warn('[WS SERVICE] Available subscriptions:', this.getActiveSubscriptions());
        return;
      }

      console.log(`[WS SERVICE] Found subject for ${subscriptionKey}, processing data...`);

      // Process each kline data point
      const klineDataArray = Array.isArray(message.data) ? message.data : [message.data];
      console.log(`[WS SERVICE] Processing ${klineDataArray.length} kline data points`);

      klineDataArray.forEach((klineData: any) => {
        const candlestick: CandlestickData = {
          time: Math.floor(klineData.start / 1000), // Use start time, not closeTime
          open: parseFloat(klineData.open),
          high: parseFloat(klineData.high),
          low: parseFloat(klineData.low),
          close: parseFloat(klineData.close),
          volume: parseFloat(klineData.volume)
        };

        console.log(`[WS SERVICE] Emitting kline update for ${symbol} (${interval}):`, {
          time: new Date(candlestick.time * 1000).toISOString(),
          open: candlestick.open,
          high: candlestick.high,
          low: candlestick.low,
          close: candlestick.close,
          volume: candlestick.volume,
          confirmed: klineData.confirm
        });

        console.log(`[WS SERVICE] Calling subject.next() for ${subscriptionKey}`);
        subject.next(candlestick);
        console.log(`[WS SERVICE] subject.next() completed for ${subscriptionKey}`);
      });
    } catch (error) {
      console.error('[WS SERVICE] Error processing kline data:', error, message);
    }
  }

  /**
   * Get list of active subscriptions
   */
  getActiveSubscriptions(): string[] {
    return Array.from(this.subscriptions.keys());
  }

  /**
   * Check if WebSocket is connected
   */
  isConnected(): boolean {
    return this.connectionState$.value === 'connected';
  }

  /**
   * Ping to keep connection alive (Bybit V5 format)
   */
  private ping(): void {
    if (this.socket$ && !this.socket$.closed) {
      this.socket$.next({ op: 'ping' });
      console.log(' Sent ping to Bybit WebSocket');
    }
  }

  /**
   * Start periodic ping to keep connection alive (Bybit V5 requires ping every 20s)
   */
  private startPing(): void {
    // Clear any existing ping timer
    if (this.pingTimer) {
      this.pingTimer.unsubscribe();
    }

    // Start pinging every 20 seconds as per Bybit documentation
    this.pingTimer = timer(20000, 20000) // First ping after 20s, then every 20s
      .pipe(takeUntil(this.destroy$))
      .subscribe(() => {
        if (this.isConnected()) {
          this.ping();
        }
      });
  }

  /**
   * Stop ping timer
   */
  private stopPing(): void {
    if (this.pingTimer) {
      this.pingTimer.unsubscribe();
      this.pingTimer = undefined;
    }
  }

  /**
   * Subscribe to ticker data for a specific symbol (Bybit V5 format)
   */
  subscribeToTicker(symbol: string): Observable<TickerData> {
    const topic = `tickers.${symbol}`;
    const subscriptionKey = `ticker_${symbol}`;

    console.log(` Subscribing to ticker data: ${topic}`);

    if (!this.subscriptions.has(subscriptionKey)) {
      this.subscriptions.set(subscriptionKey, new Subject<TickerData>());
    }

    if (!this.socket$ || this.socket$.closed) {
      this.connect();
    }

    this.connectionState$.pipe(
      filter(state => state === 'connected'),
      takeUntil(this.destroy$)
    ).subscribe(() => {
      this.sendSubscription(topic);
    });

    return this.subscriptions.get(subscriptionKey)!.asObservable();
  }

  /**
   * Subscribe to orderbook data for a specific symbol (Bybit V5 format)
   */
  subscribeToOrderbook(symbol: string, depth = 50): Observable<OrderbookData> {
    const topic = `orderbook.${depth}.${symbol}`;
    const subscriptionKey = `orderbook_${symbol}_${depth}`;

    console.log(` Subscribing to orderbook data: ${topic}`);

    if (!this.subscriptions.has(subscriptionKey)) {
      this.subscriptions.set(subscriptionKey, new Subject<OrderbookData>());
    }

    if (!this.socket$ || this.socket$.closed) {
      this.connect();
    }

    this.connectionState$.pipe(
      filter(state => state === 'connected'),
      takeUntil(this.destroy$)
    ).subscribe(() => {
      this.sendSubscription(topic);
    });

    return this.subscriptions.get(subscriptionKey)!.asObservable();
  }

  /**
   * Subscribe to trade data for a specific symbol (Bybit V5 format)
   */
  subscribeToTrades(symbol: string): Observable<TradeData> {
    const topic = `publicTrade.${symbol}`;
    const subscriptionKey = `trades_${symbol}`;

    console.log(` Subscribing to trade data: ${topic}`);

    if (!this.subscriptions.has(subscriptionKey)) {
      this.subscriptions.set(subscriptionKey, new Subject<TradeData>());
    }

    if (!this.socket$ || this.socket$.closed) {
      this.connect();
    }

    this.connectionState$.pipe(
      filter(state => state === 'connected'),
      takeUntil(this.destroy$)
    ).subscribe(() => {
      this.sendSubscription(topic);
    });

    return this.subscriptions.get(subscriptionKey)!.asObservable();
  }

  /**
   * Handle ticker data from WebSocket
   */
  private handleTickerData(message: BybitWsMessage): void {
    try {
      const topicParts = message.topic.split('.');
      if (topicParts.length < 2) {
        console.error('Invalid ticker topic format:', message.topic);
        return;
      }

      const symbol = topicParts[1];
      const subscriptionKey = `ticker_${symbol}`;
      const subject = this.subscriptions.get(subscriptionKey);

      if (!subject) {
        console.warn(`No ticker subscription found for ${subscriptionKey}`);
        return;
      }

      const rawData: any = Array.isArray(message.data) ? message.data[0] : message.data;
      if (!rawData) return;

      const tickerData: TickerData = {
        symbol: rawData.symbol || symbol,
        lastPrice: parseFloat(rawData.lastPrice || '0'),
        highPrice24h: parseFloat(rawData.highPrice24h || '0'),
        lowPrice24h: parseFloat(rawData.lowPrice24h || '0'),
        prevPrice24h: parseFloat(rawData.prevPrice24h || '0'),
        volume24h: parseFloat(rawData.volume24h || '0'),
        turnover24h: parseFloat(rawData.turnover24h || '0'),
        price24hPcnt: parseFloat(rawData.price24hPcnt || '0'),
        usdIndexPrice: parseFloat(rawData.usdIndexPrice || '0'),
        timestamp: message.ts
      };

      this.dataCache.set(subscriptionKey, tickerData);
      subject.next(tickerData);

      console.log(` Ticker update for ${symbol}:`, {
        lastPrice: tickerData.lastPrice,
        change24h: tickerData.price24hPcnt
      });
    } catch (error) {
      console.error('Error processing ticker data:', error, message);
    }
  }

  /**
   * Handle orderbook data from WebSocket
   */
  private handleOrderbookData(message: BybitWsMessage): void {
    try {
      const topicParts = message.topic.split('.');
      if (topicParts.length < 3) {
        console.error('Invalid orderbook topic format:', message.topic);
        return;
      }

      const depth = topicParts[1];
      const symbol = topicParts[2];
      const subscriptionKey = `orderbook_${symbol}_${depth}`;
      const subject = this.subscriptions.get(subscriptionKey);

      if (!subject) {
        console.warn(`No orderbook subscription found for ${subscriptionKey}`);
        return;
      }

      const rawData = message.data as BybitWsOrderbookData;
      if (!rawData) return;

      const orderbookData: OrderbookData = {
        symbol: rawData.symbol,
        bids: rawData.bids.map(([price, size]) => ({
          price: parseFloat(price),
          size: parseFloat(size)
        })),
        asks: rawData.asks.map(([price, size]) => ({
          price: parseFloat(price),
          size: parseFloat(size)
        })),
        timestamp: rawData.ts
      };

      this.dataCache.set(subscriptionKey, orderbookData);
      subject.next(orderbookData);

      console.log(` Orderbook update for ${symbol}:`, {
        bestBid: orderbookData.bids[0]?.price,
        bestAsk: orderbookData.asks[0]?.price,
        spread: orderbookData.asks[0]?.price - orderbookData.bids[0]?.price
      });
    } catch (error) {
      console.error('Error processing orderbook data:', error, message);
    }
  }

  /**
   * Handle trade data from WebSocket
   */
  private handleTradeData(message: BybitWsMessage): void {
    try {
      const topicParts = message.topic.split('.');
      if (topicParts.length < 2) {
        console.error('Invalid trade topic format:', message.topic);
        return;
      }

      const symbol = topicParts[1];
      const subscriptionKey = `trades_${symbol}`;
      const subject = this.subscriptions.get(subscriptionKey);

      if (!subject) {
        console.warn(`No trade subscription found for ${subscriptionKey}`);
        return;
      }

      const tradesArray = Array.isArray(message.data) ? message.data : [message.data];

      tradesArray.forEach((rawTrade: any) => {
        const tradeData: TradeData = {
          symbol: rawTrade.symbol,
          execId: rawTrade.execId,
          price: parseFloat(rawTrade.price),
          size: parseFloat(rawTrade.size),
          side: rawTrade.side,
          timestamp: rawTrade.time,
          isBlockTrade: rawTrade.isBlockTrade
        };

        this.dataCache.set(`${subscriptionKey}_${rawTrade.execId}`, tradeData);
        subject.next(tradeData);

        console.log(` Trade for ${symbol}:`, {
          price: tradeData.price,
          size: tradeData.size,
          side: tradeData.side
        });
      });
    } catch (error) {
      console.error('Error processing trade data:', error, message);
    }
  }

  /**
   * Unsubscribe from ticker data
   */
  unsubscribeFromTicker(symbol: string): void {
    const topic = `tickers.${symbol}`;
    const subscriptionKey = `ticker_${symbol}`;

    if (this.socket$ && !this.socket$.closed) {
      this.sendUnsubscription(topic);
    }

    const subject = this.subscriptions.get(subscriptionKey);
    if (subject) {
      subject.complete();
      this.subscriptions.delete(subscriptionKey);
    }
  }

  /**
   * Unsubscribe from orderbook data
   */
  unsubscribeFromOrderbook(symbol: string, depth = 50): void {
    const topic = `orderbook.${depth}.${symbol}`;
    const subscriptionKey = `orderbook_${symbol}_${depth}`;

    if (this.socket$ && !this.socket$.closed) {
      this.sendUnsubscription(topic);
    }

    const subject = this.subscriptions.get(subscriptionKey);
    if (subject) {
      subject.complete();
      this.subscriptions.delete(subscriptionKey);
    }
  }

  /**
   * Unsubscribe from trade data
   */
  unsubscribeFromTrades(symbol: string): void {
    const topic = `publicTrade.${symbol}`;
    const subscriptionKey = `trades_${symbol}`;

    if (this.socket$ && !this.socket$.closed) {
      this.sendUnsubscription(topic);
    }

    const subject = this.subscriptions.get(subscriptionKey);
    if (subject) {
      subject.complete();
      this.subscriptions.delete(subscriptionKey);
    }
  }

  /**
   * Get cached data for a subscription
   */
  getCachedData(subscriptionKey: string): any {
    return this.dataCache.get(subscriptionKey);
  }

  /**
   * Clear cache for a specific subscription or all cache
   */
  clearCache(subscriptionKey?: string): void {
    if (subscriptionKey) {
      this.dataCache.delete(subscriptionKey);
    } else {
      this.dataCache.clear();
    }
  }
}

import { Injectable } from '@angular/core';
import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { Observable, throwError, of, combineLatest, BehaviorSubject } from 'rxjs';
import { catchError, map, timeout, startWith, take, switchMap } from 'rxjs/operators';
import { BybitWebSocketService, TickerData, OrderbookData, TradeData } from './bybit-websocket.service';

/**
 * Bybit API kline/candlestick data structure
 * Represents the raw response format from Bybit V5 REST API
 */
export interface BybitKlineData {
  symbol: string;
  category: string;
  list: string[][]; // [timestamp, open, high, low, close, volume, turnover]
}

/**
 * Bybit API response wrapper for kline endpoint
 */
export interface BybitKlineResponse {
  retCode: number;
  retMsg: string;
  result: BybitKlineData;
  time: number;
}

/**
 * Normalized candlestick data format used throughout the application
 * Compatible with lightweight-charts library
 */
export interface CandlestickData {
  time: string | number; // Support both string (YYYY-MM-DD) and number (timestamp)
  open: number;
  high: number;
  low: number;
  close: number;
  volume?: number;
}

/**
 * Bybit Service - Main integration layer for Bybit exchange
 *
 * This service provides a high-level API for interacting with Bybit exchange,
 * combining both REST API and WebSocket real-time data streams. It handles:
 * - Historical kline/candlestick data retrieval
 * - Real-time market data subscriptions
 * - Data transformation and normalization
 * - Error handling and fallback mechanisms
 *
 * @example
 * ```typescript
 * // Get real-time candlestick data
 * this.bybitService.getRealTimeKlineData('BTCUSDT', '1').subscribe(candles => {
 *   console.log('Received candles:', candles);
 * });
 *
 * // Get available symbols
 * this.bybitService.getSymbols().subscribe(symbols => {
 *   console.log('Available symbols:', symbols);
 * });
 * ```
 *
 * @see {@link BybitWebSocketService} for WebSocket connection management
 */
@Injectable({
  providedIn: 'root'
})
export class BybitService {
  private readonly baseUrl = 'https://api.bybit.com';
  private readonly timeout = 10000; // 10 seconds timeout

  constructor(
    private http: HttpClient,
    private webSocketService: BybitWebSocketService
  ) {}

  /**
   * Fetch historical kline/candlestick data from Bybit REST API
   *
   * Retrieves historical market data for a specified trading pair and time interval.
   * Data is automatically transformed to application format and validated for correctness.
   *
   * @param symbol - Trading pair symbol (e.g., 'BTCUSDT', 'ETHUSDT')
   * @param interval - Time interval for candles: '1', '5', '15', '30', '60', '240', 'D'
   * @param limit - Number of candles to retrieve (max 1000, default 720)
   * @returns Observable<CandlestickData[]> - Stream of candlestick data, sorted in ascending time order
   *
   * @throws Error if API returns error code or invalid data
   * @example
   * ```typescript
   * // Get 100 hourly candles for Bitcoin
   * this.bybitService.getKlineData('BTCUSDT', '60', 100).subscribe(candles => {
   *   console.log('First candle:', candles[0]);
   *   console.log('Latest candle:', candles[candles.length - 1]);
   * });
   * ```
   */
  getKlineData(
    symbol = 'BTCUSDT',
    interval = '60',
    limit: number = 24*30
  ): Observable<CandlestickData[]> {
    // console.log(`Fetching Bybit data for ${symbol}, interval: ${interval}, limit: ${limit}`);

    const params = {
      category: 'spot',
      symbol: symbol,
      interval: interval,
      limit: limit.toString()
    };

    const url = `${this.baseUrl}/v5/market/kline`;

    return this.http.get<BybitKlineResponse>(url, { params }).pipe(
      timeout(this.timeout),
      map(response => this.transformBybitData(response)),
      catchError(error => this.handleError(error, symbol))
    );
  }

  /**
   * Get available trading symbols from Bybit
   */
  getSymbols(): Observable<string[]> {
    const url = `${this.baseUrl}/v5/market/instruments-info`;
    const params = {
      category: 'spot',
      limit: '100'
    };

    return this.http.get<any>(url, { params }).pipe(
      timeout(this.timeout),
      map(response => {
        if (response.retCode === 0 && response.result?.list) {
          return response.result.list
            .filter((item: any) => item.symbol.endsWith('USDT'))
            .map((item: any) => item.symbol)
            .slice(0, 20); // Limit to top 20 USDT pairs
        }
        return ['BTCUSDT', 'ETHUSDT', 'ADAUSDT', 'DOTUSDT', 'MATICUSDT']; // Fallback
      }),
      catchError(error => {
        console.error('Failed to fetch symbols from Bybit:', error);
        return of(['BTCUSDT', 'ETHUSDT', 'ADAUSDT', 'DOTUSDT', 'MATICUSDT']); // Fallback
      })
    );
  }

  /**
   * Transform Bybit API response to our CandlestickData format
   */
  private transformBybitData(response: BybitKlineResponse): CandlestickData[] {
    // console.log('Bybit API response:', response);

    if (response.retCode !== 0 || !response.result?.list) {
      throw new Error(`Bybit API error: ${response.retMsg || 'Invalid response'}`);
    }

    const data = response.result.list.map(item => {
      const [timestamp, open, high, low, close, volume] = item;

      // Always use Unix timestamp in seconds for lightweight-charts
      // This ensures proper ordering for all time intervals
      const timestampMs = parseInt(timestamp);
      const timestampSeconds = Math.floor(timestampMs / 1000);

      return {
        time: timestampSeconds, // Use Unix timestamp in seconds
        open: parseFloat(open),
        high: parseFloat(high),
        low: parseFloat(low),
        close: parseFloat(close),
        volume: parseFloat(volume),
        originalTimestamp: timestampMs // Keep original for debugging
      };
    });

    // Sort by time to ensure ascending order
    const sortedData = data.sort((a, b) => (a.time as number) - (b.time as number));

    // Remove duplicates based on time
    const uniqueData = sortedData.filter((item, index, arr) => {
      if (index === 0) return true;
      return item.time !== arr[index - 1].time;
    });

    // Remove the temporary originalTimestamp property
    const finalData = uniqueData.map(({ originalTimestamp, ...rest }) => rest);

    // console.log(`Transformed ${finalData.length} data points from Bybit (removed ${data.length - finalData.length} duplicates)`);
    // console.log('First data point:', finalData[0]);
    // console.log('Last data point:', finalData[finalData.length - 1]);

    // Validate data order
    for (let i = 1; i < finalData.length; i++) {
      const current = finalData[i].time as number;
      const previous = finalData[i - 1].time as number;

      if (current <= previous) {
        console.warn(`Data ordering issue at index ${i}: current=${current}, previous=${previous}`);
        console.warn('Current item:', finalData[i]);
        console.warn('Previous item:', finalData[i - 1]);
      }
    }

    return finalData;
  }

  /**
   * Handle HTTP errors and provide fallback data
   */
  private handleError(error: HttpErrorResponse, symbol: string): Observable<CandlestickData[]> {
    console.error('Bybit API Error:', error);

    // Return fallback data when API fails
    // console.log('Returning fallback data due to API error');
    return of(this.generateFallbackData(symbol));
  }

  /**
   * Generate fallback data when Bybit API is unavailable
   */
  private generateFallbackData(symbol: string): CandlestickData[] {
    // console.log(`Generating fallback data for ${symbol}`);

    const data: CandlestickData[] = [];
    let currentPrice = this.getBasePrice(symbol);

    // Generate 30 days of fallback data
    for (let i = 29; i >= 0; i--) {
      const date = new Date();
      date.setDate(date.getDate() - i);
      const time = date.toISOString().split('T')[0];

      // Generate realistic OHLC data
      const open = currentPrice;
      const volatility = 0.03;
      const dailyRange = currentPrice * volatility;

      const high = open + (Math.random() * dailyRange);
      const low = open - (Math.random() * dailyRange);
      const closeVariation = (Math.random() - 0.5) * dailyRange * 0.5;
      const close = Math.max(low, Math.min(high, open + closeVariation));

      currentPrice = close;

      data.push({
        time,
        open: Math.round(open * 100) / 100,
        high: Math.round(high * 100) / 100,
        low: Math.round(low * 100) / 100,
        close: Math.round(close * 100) / 100,
        volume: Math.round(Math.random() * 1000000)
      });
    }

    return data;
  }

  /**
   * Get base price for different symbols
   */
  private getBasePrice(symbol: string): number {
    const basePrices: Record<string, number> = {
      'BTCUSDT': 45000,
      'ETHUSDT': 2500,
      'ADAUSDT': 0.5,
      'DOTUSDT': 15,
      'MATICUSDT': 0.8,
      'BNBUSDT': 300,
      'SOLUSDT': 100,
      'XRPUSDT': 0.6,
      'DOGEUSDT': 0.1,
      'AVAXUSDT': 25
    };

    return basePrices[symbol] || 100; // Default fallback price
  }

  /**
   * Check if Bybit API is accessible
   */
  checkApiHealth(): Observable<boolean> {
    const url = `${this.baseUrl}/v5/market/time`;

    return this.http.get<any>(url).pipe(
      timeout(5000),
      map(response => response.retCode === 0),
      catchError(() => of(false))
    );
  }

  /**
   * Get real-time kline data using WebSocket
   * This method combines initial REST API data with real-time WebSocket updates
   * @param symbol Trading pair symbol (e.g., 'BTCUSDT')
   * @param interval Time interval ('1', '5', '15', '30', '60', '240', 'D')
   * @returns Observable<CandlestickData[]> - Emits initial data then real-time updates
   */
  getRealTimeKlineData(
    symbol = 'BTCUSDT',
    interval = '1'
  ): Observable<CandlestickData[]> {
    // console.log(`[BYBIT SERVICE] Starting real-time kline data for ${symbol}, interval: ${interval}`);

    // First get initial historical data via REST API
    const initialData$ = this.getKlineData(symbol, interval, 100).pipe(
      take(1),
      catchError(error => {
        console.error('[BYBIT SERVICE] Failed to get initial data, using fallback:', error);
        return of(this.generateFallbackData(symbol));
      })
    );

    // Then subscribe to real-time updates via WebSocket
    const realtimeUpdates$ = this.webSocketService.subscribeToKline(symbol, interval);

    // Combine initial data with real-time updates using merge strategy
    return initialData$.pipe(
      switchMap(initialData => {
        // console.log(`[BYBIT SERVICE] Initial data loaded: ${initialData.length} candles`);
        // console.log('[BYBIT SERVICE] Setting up real-time update stream...');

        const dataSubject = new BehaviorSubject<CandlestickData[]>(initialData);

        // Subscribe to real-time updates and emit through the subject
        const subscription = realtimeUpdates$.subscribe({
          next: (newCandle) => {
            // console.log('[BYBIT SERVICE] Received WebSocket candle update:', {
            //   time: new Date(newCandle.time * 1000).toISOString(),
            //   close: newCandle.close,
            //   volume: newCandle.volume
            // });

            const currentData = dataSubject.value;
            const updatedData = this.updateCandlestickData(currentData, newCandle);

            // console.log(`[BYBIT SERVICE] Emitting updated data array with ${updatedData.length} candles`);
            dataSubject.next(updatedData);
          },
          error: (error) => {
            // console.error('[BYBIT SERVICE] WebSocket error:', error);
          },
          complete: () => {
            // console.log('[BYBIT SERVICE] WebSocket stream completed');
          }
        });

        // Return the subject as observable and clean up subscription on unsubscribe
        return new Observable<CandlestickData[]>(observer => {
          const subjectSubscription = dataSubject.subscribe(observer);

          return () => {
            // console.log('[BYBIT SERVICE] Cleaning up real-time kline subscription');
            subscription.unsubscribe();
            subjectSubscription.unsubscribe();
          };
        });
      })
    );
  }

  /**
   * Subscribe to real-time kline updates only (no initial data)
   * @param symbol Trading pair symbol
   * @param interval Time interval
   * @returns Observable<CandlestickData> - Emits individual candle updates
   */
  subscribeToRealtimeKline(
    symbol = 'BTCUSDT',
    interval = '1'
  ): Observable<CandlestickData> {
    // console.log(`Subscribing to real-time updates for ${symbol}, interval: ${interval}`);
    return this.webSocketService.subscribeToKline(symbol, interval);
  }

  /**
   * Unsubscribe from real-time kline data
   * @param symbol Trading pair symbol
   * @param interval Time interval
   */
  unsubscribeFromRealtimeKline(symbol: string, interval = '1'): void {
    // console.log(`Unsubscribing from real-time updates for ${symbol}, interval: ${interval}`);
    this.webSocketService.unsubscribeFromKline(symbol, interval);
  }

  /**
   * Get WebSocket connection state
   */
  getWebSocketConnectionState(): Observable<'disconnected' | 'connecting' | 'connected'> {
    return this.webSocketService.getConnectionState();
  }

  /**
   * Check if WebSocket is connected
   */
  isWebSocketConnected(): boolean {
    return this.webSocketService.isConnected();
  }

  /**
   * Get list of active WebSocket subscriptions
   */
  getActiveSubscriptions(): string[] {
    return this.webSocketService.getActiveSubscriptions();
  }

  /**
   * Update candlestick data array with new candle
   * This handles both new candles and updates to the current candle
   */
  private updateCandlestickData(
    currentData: CandlestickData[],
    newCandle: CandlestickData
  ): CandlestickData[] {
    if (currentData.length === 0) {
      return [newCandle];
    }

    const updatedData = [...currentData];
    const lastCandle = updatedData[updatedData.length - 1];

    // Convert time to number for comparison
    const lastCandleTime = typeof lastCandle.time === 'number' ? lastCandle.time : parseInt(lastCandle.time);
    const newCandleTime = typeof newCandle.time === 'number' ? newCandle.time : parseInt(newCandle.time);

    // Check if this is an update to the current candle or a new candle
    if (lastCandleTime === newCandleTime) {
      // Update the current candle
      updatedData[updatedData.length - 1] = newCandle;
      // console.log(` Updated current candle at ${new Date(newCandleTime * 1000).toISOString()}`);
    } else if (newCandleTime > lastCandleTime) {
      // Add new candle and limit the array size
      updatedData.push(newCandle);

      // Keep only the last 1000 candles to prevent memory issues
      if (updatedData.length > 1000) {
        updatedData.shift();
      }

      // console.log(` Added new candle at ${new Date(newCandleTime * 1000).toISOString()}`);
    } else {
      // console.warn('Received candle with older timestamp, ignoring:', newCandle);
    }

    return updatedData;
  }

  /**
   * Convert WebSocket interval format to REST API format if needed
   */
  private convertIntervalFormat(interval: string): string {
    // Bybit WebSocket and REST API use the same interval format
    // But we can add conversion logic here if needed
    const intervalMap: Record<string, string> = {
      '1m': '1',
      '5m': '5',
      '15m': '15',
      '30m': '30',
      '1h': '60',
      '4h': '240',
      '1d': 'D'
    };

    return intervalMap[interval] || interval;
  }

  /**
   * Subscribe to real-time ticker data
   * @param symbol Trading pair symbol
   * @returns Observable<TickerData> - Emits ticker updates
   */
  subscribeToRealtimeTicker(symbol = 'BTCUSDT'): Observable<any> {
    // console.log(`Subscribing to real-time ticker for ${symbol}`);
    return this.webSocketService.subscribeToTicker(symbol);
  }

  /**
   * Subscribe to real-time orderbook data
   * @param symbol Trading pair symbol
   * @param depth Orderbook depth (1, 50, 200)
   * @returns Observable<OrderbookData> - Emits orderbook updates
   */
  subscribeToRealtimeOrderbook(symbol = 'BTCUSDT', depth = 50): Observable<any> {
    // console.log(`Subscribing to real-time orderbook for ${symbol}, depth: ${depth}`);
    return this.webSocketService.subscribeToOrderbook(symbol, depth);
  }

  /**
   * Subscribe to real-time trade data
   * @param symbol Trading pair symbol
   * @returns Observable<TradeData> - Emits individual trades
   */
  subscribeToRealtimeTrades(symbol = 'BTCUSDT'): Observable<any> {
    // console.log(`Subscribing to real-time trades for ${symbol}`);
    return this.webSocketService.subscribeToTrades(symbol);
  }

  /**
   * Unsubscribe from real-time ticker data
   */
  unsubscribeFromRealtimeTicker(symbol: string): void {
    // console.log(`Unsubscribing from real-time ticker for ${symbol}`);
    this.webSocketService.unsubscribeFromTicker(symbol);
  }

  /**
   * Unsubscribe from real-time orderbook data
   */
  unsubscribeFromRealtimeOrderbook(symbol: string, depth = 50): void {
    // console.log(`Unsubscribing from real-time orderbook for ${symbol}`);
    this.webSocketService.unsubscribeFromOrderbook(symbol, depth);
  }

  /**
   * Unsubscribe from real-time trade data
   */
  unsubscribeFromRealtimeTrades(symbol: string): void {
    // console.log(`Unsubscribing from real-time trades for ${symbol}`);
    this.webSocketService.unsubscribeFromTrades(symbol);
  }

  /**
   * Get cached data from WebSocket service
   * @param subscriptionKey Key for cached data
   * @returns Cached data or null
   */
  getCachedWebSocketData(subscriptionKey: string): any {
    return this.webSocketService.getCachedData(subscriptionKey);
  }

  /**
   * Clear WebSocket cache
   * @param subscriptionKey Optional specific key to clear
   */
  clearWebSocketCache(subscriptionKey?: string): void {
    this.webSocketService.clearCache(subscriptionKey);
  }

  /**
   * Get comprehensive market data stream combining multiple data types
   * @param symbol Trading pair symbol
   * @returns Observable with combined market data
   */
  getComprehensiveMarketStream(symbol = 'BTCUSDT'): Observable<any> {
    // console.log(`Starting comprehensive market stream for ${symbol}`);

    return combineLatest([
      this.subscribeToRealtimeKline(symbol, '1'),
      this.subscribeToRealtimeTicker(symbol),
      this.subscribeToRealtimeOrderbook(symbol, 50)
    ]).pipe(
      map(([kline, ticker, orderbook]) => ({
        symbol,
        timestamp: Date.now(),
        kline,
        ticker,
        orderbook,
        spread: orderbook?.asks?.[0]?.price - orderbook?.bids?.[0]?.price || 0
      }))
    );
  }

  /**
   * Set WebSocket environment (mainnet or testnet)
   * Delegates to the underlying WebSocketService
   */
  setWebSocketEnvironment(environment: 'MAINNET' | 'TESTNET'): void {
    this.webSocketService.setEnvironment(environment);
  }

  /**
   * Get current WebSocket environment
   */
  getWebSocketEnvironment(): 'mainnet' | 'testnet' {
    return this.webSocketService.getCurrentEnvironment();
  }
}

import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, BehaviorSubject } from 'rxjs';
import { map, catchError } from 'rxjs/operators';

// TradingView-compatible interfaces based on official documentation

// TradingView LibrarySymbolInfo structure
export interface LibrarySymbolInfo {
  name: string;
  full_name: string;
  description: string;
  exchange: string;
  type: string;
  session: string;
  timezone: string;
  ticker?: string;
  minmov: number;
  pricescale: number;
  has_intraday: boolean;
  has_daily: boolean;
  has_weekly_and_monthly: boolean;
  supported_resolutions: string[];
  intraday_multipliers: string[];
  data_status: string;
  currency_code?: string;
}

// Search result symbol structure
export interface SearchSymbolResultItem {
  symbol: string;
  full_name: string;
  description: string;
  exchange: string;
  type: string;
}

// Basic symbol info for API responses
export interface ChartSymbol {
  symbol: string;
  full_name: string;
  description: string;
  exchange: string;
  type: string;
  currency_code?: string;
}

// TradingView DatafeedConfiguration structure
export interface DatafeedConfiguration {
  supported_resolutions: string[];
  supports_group_request?: boolean;
  supports_marks?: boolean;
  supports_search?: boolean;
  supports_timescale_marks?: boolean;
  supports_time?: boolean;
  exchanges?: Exchange[];
  symbols_types?: SymbolType[];
  currency_codes?: string[];
}

export interface Exchange {
  value: string;
  name: string;
  desc: string;
}

export interface SymbolType {
  name: string;
  value: string;
}

export interface ChartDataRequest {
  symbol: string;
  resolution: string;
  from: number;
  to: number;
  firstDataRequest?: boolean;
}

// TradingView Bar structure
export interface Bar {
  time: number; // Unix timestamp in milliseconds
  open: number;
  high: number;
  low: number;
  close: number;
  volume?: number;
}

// TradingView GetBarsResult structure
export interface GetBarsResult {
  bars: Bar[];
  meta: HistoryMetadata;
}

export interface HistoryMetadata {
  noData?: boolean;
  nextTime?: number;
}

// API response structure
export interface ChartDataResponse {
  s: string; // status: 'ok', 'no_data', 'error'
  t?: number[]; // time
  o?: number[]; // open
  h?: number[]; // high
  l?: number[]; // low
  c?: number[]; // close
  v?: number[]; // volume
  errmsg?: string;
  nextTime?: number;
}

export interface RealtimeData {
  symbol: string;
  price: number;
  volume: number;
  timestamp: number;
  change: number;
  changePercent: number;
}

@Injectable({
  providedIn: 'root'
})
export class ChartService {
  private apiUrl = '/api/trading';
  private realtimeDataSubject = new BehaviorSubject<RealtimeData | null>(null);
  public realtimeData$ = this.realtimeDataSubject.asObservable();
  private subscriberMap = new Map<string, any>();
  private intervalMap = new Map<string, any>();

  constructor(private http: HttpClient) {}

  // Get TradingView datafeed configuration
  getDatafeedConfiguration(): Observable<DatafeedConfiguration> {
    return this.http.get<DatafeedConfiguration>(`${this.apiUrl}/config`).pipe(
      catchError(error => {
        console.error('Failed to get datafeed configuration:', error);
        // Return default configuration with proper typing
        const defaultConfig: DatafeedConfiguration = {
          supported_resolutions: ['1', '5', '15', '30', '60', '240', '1D', '1W', '1M'],
          supports_group_request: false,
          supports_marks: false,
          supports_search: true,
          supports_timescale_marks: false,
          supports_time: false,
          exchanges: [{
            value: 'BYBIT',
            name: 'Bybit',
            desc: 'Bybit Exchange'
          }],
          symbols_types: [{
            name: 'crypto',
            value: 'crypto'
          }],
          currency_codes: ['USDT', 'BTC', 'ETH']
        };
        return new Observable<DatafeedConfiguration>(subscriber => {
          subscriber.next(defaultConfig);
          subscriber.complete();
        });
      })
    );
  }

  // Get TradingView configuration (legacy method)
  getConfig(): Observable<DatafeedConfiguration> {
    return this.getDatafeedConfiguration();
  }

  // Get available symbols
  getSymbols(): Observable<ChartSymbol[]> {
    return this.http.get<ChartSymbol[]>(`${this.apiUrl}/symbols`);
  }

  // Get specific symbol info
  getSymbolInfo(symbol: string): Observable<ChartSymbol> {
    return this.http.get<ChartSymbol>(`${this.apiUrl}/symbols?symbol=${symbol}`);
  }

  // Search symbols (legacy method)
  searchSymbols(query: string): Observable<ChartSymbol[]> {
    return this.getSymbols().pipe(
      map(symbols => symbols.filter(symbol =>
        symbol.symbol.toLowerCase().includes(query.toLowerCase()) ||
        symbol.description.toLowerCase().includes(query.toLowerCase())
      ))
    );
  }

  // Search symbols with TradingView filters
  searchSymbolsWithFilters(query: string, exchange?: string, symbolType?: string): Observable<SearchSymbolResultItem[]> {
    return this.getSymbols().pipe(
      map(symbols => {
        let filtered = symbols.filter(symbol =>
          symbol.symbol.toLowerCase().includes(query.toLowerCase()) ||
          symbol.description.toLowerCase().includes(query.toLowerCase())
        );

        if (exchange && exchange !== '') {
          filtered = filtered.filter(symbol => symbol.exchange === exchange);
        }

        if (symbolType && symbolType !== '') {
          filtered = filtered.filter(symbol => symbol.type === symbolType);
        }

        return filtered.map(symbol => ({
          symbol: symbol.symbol,
          full_name: symbol.full_name,
          description: symbol.description,
          exchange: symbol.exchange,
          type: symbol.type
        }));
      }),
      catchError(error => {
        console.error('Symbol search failed:', error);
        return new Observable<SearchSymbolResultItem[]>(subscriber => {
          subscriber.next([]);
          subscriber.complete();
        });
      })
    );
  }

  // Get chart data for TradingView
  getChartData(request: ChartDataRequest): Observable<ChartDataResponse> {
    const params = new URLSearchParams({
      symbol: request.symbol,
      resolution: request.resolution,
      from: request.from.toString(),
      to: request.to.toString()
    });

    if (request.firstDataRequest) {
      params.append('firstDataRequest', 'true');
    }

    return this.http.get<ChartDataResponse>(`${this.apiUrl}/chart?${params.toString()}`);
  }

  // Convert API response to TradingView bars format
  private convertToBars(response: ChartDataResponse): GetBarsResult {
    if (response.s === 'ok' && response.t && response.o && response.h && response.l && response.c) {
      const bars: Bar[] = response.t.map((time: number, index: number) => ({
        time: time * 1000, // Convert to milliseconds
        open: response.o![index],
        high: response.h![index],
        low: response.l![index],
        close: response.c![index],
        volume: response.v ? response.v[index] : 0
      }));

      return {
        bars,
        meta: {
          noData: false,
          nextTime: response.nextTime
        }
      };
    } else if (response.s === 'no_data') {
      return {
        bars: [],
        meta: {
          noData: true,
          nextTime: response.nextTime
        }
      };
    } else {
      throw new Error(response.errmsg || 'Failed to get chart data');
    }
  }

  private calculatePricescale(symbol: string): number {
    // Determine appropriate price scale based on symbol
    if (symbol.includes('USDT') || symbol.includes('USD')) {
      return 100; // 2 decimal places
    } else if (symbol.includes('BTC')) {
      return 100000000; // 8 decimal places
    }
    return 10000; // 4 decimal places default
  }

  // Resolve symbol to TradingView LibrarySymbolInfo format
  resolveSymbolInfo(symbolName: string): Observable<LibrarySymbolInfo> {
    return this.getSymbolInfo(symbolName).pipe(
      map(symbolInfo => {
        const pricescale = this.calculatePricescale(symbolInfo.symbol);
        return {
          name: symbolInfo.symbol,
          full_name: symbolInfo.full_name,
          description: symbolInfo.description,
          exchange: symbolInfo.exchange,
          type: symbolInfo.type,
          session: '24x7',
          timezone: 'UTC',
          ticker: symbolInfo.symbol,
          minmov: 1,
          pricescale: pricescale,
          has_intraday: true,
          has_daily: true,
          has_weekly_and_monthly: true,
          supported_resolutions: ['1', '5', '15', '30', '60', '240', '1D', '1W'],
          intraday_multipliers: ['1', '5', '15', '30', '60', '240'],
          data_status: 'streaming',
          currency_code: symbolInfo.currency_code
        };
      }),
      catchError(error => {
        console.error('Symbol resolve failed:', error);
        throw new Error(`Unknown symbol: ${symbolName}`);
      })
    );
  }

  // Get bars in TradingView format
  getChartBars(symbolInfo: LibrarySymbolInfo, resolution: string, from: number, to: number, firstDataRequest = false): Observable<GetBarsResult> {
    const request: ChartDataRequest = {
      symbol: symbolInfo.name,
      resolution: resolution,
      from: from,
      to: to,
      firstDataRequest: firstDataRequest
    };

    return this.getChartData(request).pipe(
      map(response => this.convertToBars(response)),
      catchError(error => {
        console.error('Chart data failed:', error);
        throw error;
      })
    );
  }

  // Create TradingView datafeed with enhanced error handling
  createDatafeed() {
    return {
      onReady: (callback: (config: DatafeedConfiguration) => void) => {
        this.getDatafeedConfiguration().subscribe({
          next: (config) => {
            console.log('Datafeed configuration loaded:', config);
            callback(config);
          },
          error: (error) => {
            console.error('Failed to get datafeed configuration:', error);
            // Fallback configuration
            callback({
              supported_resolutions: ['1', '5', '15', '30', '60', '240', '1D', '1W'],
              supports_group_request: false,
              supports_marks: false,
              supports_search: true,
              supports_timescale_marks: false,
              supports_time: false
            });
          }
        });
      },

      searchSymbols: (userInput: string, exchange: string, symbolType: string, onResult: (symbols: SearchSymbolResultItem[]) => void) => {
        this.searchSymbolsWithFilters(userInput, exchange, symbolType).subscribe({
          next: (symbols) => {
            console.log(`Search results for "${userInput}":`, symbols.length, 'symbols found');
            onResult(symbols);
          },
          error: (error) => {
            console.error('Symbol search failed:', error);
            onResult([]);
          }
        });
      },

      resolveSymbol: (symbolName: string, onResolve: (symbolInfo: LibrarySymbolInfo) => void, onError: (error: string) => void) => {
        console.log('Resolving symbol:', symbolName);
        this.resolveSymbolInfo(symbolName).subscribe({
          next: (symbolInfo) => {
            console.log('Symbol resolved:', symbolInfo);
            onResolve(symbolInfo);
          },
          error: (error) => {
            console.error('Symbol resolve failed:', error);
            onError(`Unknown symbol: ${symbolName}`);
          }
        });
      },

      getBars: (symbolInfo: LibrarySymbolInfo, resolution: string, periodParams: any, onResult: (bars: Bar[], meta: HistoryMetadata) => void, onError: (error: string) => void) => {
        console.log('Getting bars for:', symbolInfo.name, resolution, periodParams);
        this.getChartBars(symbolInfo, resolution, periodParams.from, periodParams.to, periodParams.firstDataRequest).subscribe({
          next: (result) => {
            console.log('Bars loaded:', result.bars.length, 'bars');
            onResult(result.bars, result.meta);
          },
          error: (error) => {
            console.error('Failed to get bars:', error);
            onError(error.message || 'Failed to fetch chart data');
          }
        });
      },

      subscribeBars: (symbolInfo: LibrarySymbolInfo, resolution: string, onRealtimeCallback: (bar: Bar) => void, listenerGUID: string, onResetCacheNeededCallback?: () => void) => {
        console.log('Subscribing to real-time data:', symbolInfo.name, listenerGUID);

        // Store subscriber info for cleanup
        this.subscriberMap.set(listenerGUID, {
          symbol: symbolInfo.name,
          resolution: resolution,
          callback: onRealtimeCallback
        });

        // Start polling for real-time data
        this.startRealtimeSubscription(symbolInfo.name, resolution, onRealtimeCallback, listenerGUID);
      },

      unsubscribeBars: (listenerGUID: string) => {
        console.log('Unsubscribing from real-time data:', listenerGUID);

        // Clean up interval if exists
        const intervalId = this.intervalMap.get(listenerGUID);
        if (intervalId) {
          clearInterval(intervalId);
          this.intervalMap.delete(listenerGUID);
        }

        // Remove subscriber info
        this.subscriberMap.delete(listenerGUID);
      }
    };
  }

  // Enhanced real-time subscription management
  private startRealtimeSubscription(symbol: string, resolution: string, callback: (bar: Bar) => void, listenerGUID: string) {
    // This is a simplified implementation using polling
    // In production, you would use WebSocket connections for real-time data

    const interval = setInterval(() => {
      // Fetch latest price data
      const request: ChartDataRequest = {
        symbol: symbol,
        resolution: resolution,
        from: Math.floor(Date.now() / 1000) - 300, // Last 5 minutes
        to: Math.floor(Date.now() / 1000),
        firstDataRequest: false
      };

      this.getChartData(request).subscribe({
        next: (response) => {
          if (response.s === 'ok' && response.t && response.t.length > 0) {
            const lastIndex = response.t.length - 1;
            const bar: Bar = {
              time: response.t[lastIndex] * 1000, // Convert to milliseconds
              open: response.o![lastIndex],
              high: response.h![lastIndex],
              low: response.l![lastIndex],
              close: response.c![lastIndex],
              volume: response.v ? response.v[lastIndex] : 0
            };

            // Only call callback if subscriber still exists
            if (this.subscriberMap.has(listenerGUID)) {
              callback(bar);
            }
          }
        },
        error: (error) => {
          console.error('Real-time data error for', symbol, ':', error);
        }
      });
    }, 5000); // Update every 5 seconds

    // Store interval ID for cleanup
    this.intervalMap.set(listenerGUID, interval);
  }

  // Cleanup all subscriptions (call on component destroy)
  cleanupAllSubscriptions() {
    this.intervalMap.forEach(intervalId => clearInterval(intervalId));
    this.intervalMap.clear();
    this.subscriberMap.clear();
  }

  // Get current market data
  getCurrentPrice(symbol: string): Observable<number> {
    return this.getChartData({
      symbol: symbol,
      resolution: '1',
      from: Math.floor(Date.now() / 1000) - 60,
      to: Math.floor(Date.now() / 1000)
    }).pipe(
      map(response => {
        if (response.s === 'ok' && response.c && response.c.length > 0) {
          return response.c[response.c.length - 1];
        }
        throw new Error('No price data available');
      }),
      catchError(error => {
        console.error('Failed to get current price:', error);
        throw error;
      })
    );
  }

  // Helper method to format price with appropriate decimal places
  formatPrice(price: number, symbol: string): string {
    // Default to 2 decimal places for USDT pairs, adjust as needed
    const decimals = symbol.includes('USDT') ? 2 : 8;
    return price.toFixed(decimals);
  }

  // Helper method to format volume
  formatVolume(volume: number): string {
    if (volume >= 1e9) {
      return (volume / 1e9).toFixed(2) + 'B';
    } else if (volume >= 1e6) {
      return (volume / 1e6).toFixed(2) + 'M';
    } else if (volume >= 1e3) {
      return (volume / 1e3).toFixed(2) + 'K';
    }
    return volume.toFixed(2);
  }
}

import { Injectable, Signal, WritableSignal, signal, computed, effect } from '@angular/core';
import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { Observable, take, throwError } from 'rxjs';
import { catchError, tap, map } from 'rxjs/operators';
import { getEndpointUrl, getParameterizedUrl } from '../config/app.config';
import {
  ExchangeCredential,
  ExchangeType,
  CreateExchangeCredentialRequest,
  UpdateExchangeCredentialRequest,
  ExchangeCredentialResponse,
  ExchangeCredentialsListResponse,
  TestConnectionRequest,
  TestConnectionResponse
} from '../models/exchange-credentials.model';

/**
 * Service to manage exchange credentials for multiple exchanges and environments.
 *
 * This service provides:
 * - CRUD operations for exchange credentials
 * - Connection testing
 * - Active credential management
 * - Environment-aware state synchronization
 * - Reactive state via Angular signals
 */
@Injectable({
  providedIn: 'root'
})
export class ExchangeCredentialsService {
  // ============================================================================
  // SIGNALS - State Management
  // ============================================================================

  private readonly _credentials = signal<ExchangeCredential[]>([]);
  public readonly credentials = this._credentials.asReadonly();

  private readonly _activeCredential = signal<ExchangeCredential | null>(null);
  public readonly activeCredential = this._activeCredential.asReadonly();

  private readonly _loading = signal<boolean>(false);
  public readonly loading = this._loading.asReadonly();

  private readonly _error = signal<string | null>(null);
  public readonly error = this._error.asReadonly();

  // ============================================================================
  // COMPUTED SIGNALS - Derived State
  // ============================================================================

  public readonly hasCredentials = computed(() => this.credentials().length > 0);

  public readonly activeExchange = computed(() =>
    this.activeCredential()?.exchange ?? null
  );

  public readonly credentialsByExchange = computed(() =>
    this.groupByExchange(this.credentials())
  );

  public readonly credentialCountByExchange = computed(() => {
    const grouped = this.credentialsByExchange();
    const counts = new Map<ExchangeType, number>();

    grouped.forEach((creds, exchange) => {
      counts.set(exchange, creds.length);
    });

    return counts;
  });

  public readonly availableExchanges = computed(() =>
    Array.from(this.credentialsByExchange().keys())
  );

  // ============================================================================
  // CONSTRUCTOR
  // ============================================================================

  constructor(
    private http: HttpClient
  ) {
    // Credentials service manages exchange credentials
  }

  // ============================================================================
  // PUBLIC METHODS - CRUD Operations
  // ============================================================================

  /**
   * Fetch all credentials from the backend
   * Returns credentials for all environments
   */
  public fetchCredentials(): Observable<ExchangeCredential[]> {
    this._loading.set(true);
    this._error.set(null);

    const url = getEndpointUrl('exchangeCredentials', 'list');

    return this.http.get<ExchangeCredentialsListResponse>(url).pipe(
      tap(response => {
        console.log('[ExchangeCredentialsService] Fetched credentials:', response.data.credentials.length);
        this.updateCredentialsState(response.data.credentials);
        this._loading.set(false);
      }),
      map(response => response.data.credentials),
      catchError(error => {
        this._loading.set(false);
        return this.handleError(error, 'Failed to fetch credentials');
      })
    );
  }

  /**
   * Get a single credential by ID
   */
  public getCredentialById(id: string): Observable<ExchangeCredential> {
    this._error.set(null);

    const url = getParameterizedUrl('exchangeCredentials', 'getById', { id });

    return this.http.get<ExchangeCredentialResponse>(url).pipe(
      map(response => response.data),
      catchError(error => {
        return this.handleError(error, 'Failed to fetch credential');
      })
    );
  }

  /**
   * Create a new exchange credential
   */
  public createCredential(data: CreateExchangeCredentialRequest): Observable<ExchangeCredential> {
    this._error.set(null);

    const url = getEndpointUrl('exchangeCredentials', 'create');

    return this.http.post<ExchangeCredentialResponse>(url, data).pipe(
      tap(response => {
        console.log('[ExchangeCredentialsService] Created credential:', response.data.id);

        // Add new credential to existing list
        const updatedCredentials = [...this.credentials(), response.data];
        this._credentials.set(updatedCredentials);
      }),
      map(response => response.data),
      catchError(error => {
        return this.handleError(error, 'Failed to create credential');
      })
    );
  }

  /**
   * Update an existing credential (label, apiKey, apiSecret, isActive)
   * Note: isActive is just a boolean flag - no automatic deactivation of other credentials
   */
  public updateCredential(
    id: string,
    data: UpdateExchangeCredentialRequest
  ): Observable<ExchangeCredential> {
    this._error.set(null);

    const url = getParameterizedUrl('exchangeCredentials', 'update', { id });

    return this.http.patch<ExchangeCredentialResponse>(url, data).pipe(
      tap(response => {
        console.log('[ExchangeCredentialsService] Updated credential:', id);

        // Update the specific credential in the list
        const currentCredentials = this.credentials();
        const index = currentCredentials.findIndex(cred => cred.id === id);

        if (index !== -1) {
          const updatedCredentials = [...currentCredentials];
          updatedCredentials[index] = response.data;
          this._credentials.set(updatedCredentials);
        }

        // Update active credential reference if needed
        if (this.activeCredential()?.id === id) {
          this._activeCredential.set(response.data);
        }
      }),
      map(response => response.data),
      catchError(error => {
        return this.handleError(error, 'Failed to update credential');
      })
    );
  }

  /**
   * Delete a credential
   */
  public deleteCredential(id: string): Observable<void> {
    this._error.set(null);

    const url = getParameterizedUrl('exchangeCredentials', 'delete', { id });

    return this.http.delete<{ success: boolean }>(url).pipe(
      tap(() => {
        console.log('[ExchangeCredentialsService] Deleted credential:', id);

        // Remove credential from list
        const updatedCredentials = this.credentials().filter(cred => cred.id !== id);
        this._credentials.set(updatedCredentials);

        // Clear active credential if it was deleted
        if (this.activeCredential()?.id === id) {
          this._activeCredential.set(null);
        }
      }),
      map(() => undefined),
      catchError(error => {
        return this.handleError(error, 'Failed to delete credential');
      })
    );
  }

  /**
   * Activate a credential (set as active for its exchange and environment)
   * This now uses updateCredential with isActive: true
   */
  public activateCredential(id: string): Observable<ExchangeCredential> {
    return this.updateCredential(id, { isActive: true });
  }

  // ============================================================================
  // PUBLIC METHODS - Utility Functions
  // ============================================================================

  /**
   * Test credential connection before saving
   */
  public testConnection(data: TestConnectionRequest): Observable<TestConnectionResponse> {
    this._error.set(null);

    const url = getEndpointUrl('exchangeCredentials', 'testConnection');

    return this.http.post<TestConnectionResponse>(url, data).pipe(
      tap(response => {
        console.log('[ExchangeCredentialsService] Connection test result:', response.success);
      }),
      catchError(error => {
        return this.handleError(error, 'Failed to test connection');
      })
    );
  }

  /**
   * Get all credentials for a specific exchange
   */
  public getCredentialsForExchange(exchange: ExchangeType): ExchangeCredential[] {
    return this.credentials().filter(cred => cred.exchange === exchange);
  }

  /**
   * Get active credential for a specific exchange
   */
  public getActiveCredentialForExchange(exchange: ExchangeType): ExchangeCredential | null {
    return this.credentials().find(cred => cred.exchange === exchange && cred.isActive) ?? null;
  }

  /**
   * Check if user has credentials for a specific exchange
   */
  public hasCredentialsForExchange(exchange: ExchangeType): boolean {
    return this.credentials().some(cred => cred.exchange === exchange);
  }

  /**
   * Refresh credentials (re-fetch from backend)
   */
  public refreshCredentials(): void {
    this.fetchCredentials().subscribe({
      error: (err) => console.error('Failed to refresh credentials:', err)
    });
  }

  /**
   * Clear error state
   */
  public clearError(): void {
    this._error.set(null);
  }

  /**
   * Clear all state (useful for logout)
   */
  public clearState(): void {
    this._credentials.set([]);
    this._activeCredential.set(null);
    this._loading.set(false);
    this._error.set(null);
  }

  // ============================================================================
  // PRIVATE METHODS - Internal Helpers
  // ============================================================================

  /**
   * Group credentials by exchange
   */
  private groupByExchange(credentials: ExchangeCredential[]): Map<ExchangeType, ExchangeCredential[]> {
    const grouped = new Map<ExchangeType, ExchangeCredential[]>();

    credentials.forEach(cred => {
      const existing = grouped.get(cred.exchange) ?? [];
      grouped.set(cred.exchange, [...existing, cred]);
    });

    return grouped;
  }

  /**
   * Update internal state with new credentials
   */
  private updateCredentialsState(credentials: ExchangeCredential[]): void {
    this._credentials.set(credentials);

    // Find and set active credential (if any)
    const active = credentials.find(cred => cred.isActive) ?? null;
    this._activeCredential.set(active);
  }

  /**
   * Centralized error handling
   */
  private handleError(error: HttpErrorResponse, context: string): Observable<never> {
    console.error(`[ExchangeCredentialsService] ${context}:`, error);

    let errorMessage = 'An unexpected error occurred';

    if (error.error?.error?.message) {
      errorMessage = error.error.error.message;
    } else if (error.error?.message) {
      errorMessage = error.error.message;
    } else if (error.status === 0) {
      errorMessage = 'Unable to connect to server. Please check your internet connection.';
    } else if (error.status === 401) {
      errorMessage = 'Authentication failed. Please log in again.';
    } else if (error.status === 403) {
      errorMessage = 'You do not have permission to perform this action.';
    } else if (error.status === 404) {
      errorMessage = 'The requested credential was not found.';
    } else if (error.status === 409) {
      errorMessage = 'A credential for this exchange and environment already exists.';
    } else if (error.status === 422) {
      errorMessage = 'Invalid data provided. Please check your input.';
    } else if (error.status >= 500) {
      errorMessage = 'Server error. Please try again later.';
    } else if (error.message) {
      errorMessage = error.message;
    }

    this._error.set(errorMessage);
    return throwError(() => new Error(errorMessage));
  }
}

import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders, HttpErrorResponse } from '@angular/common/http';
import { Observable, throwError, BehaviorSubject } from 'rxjs';
import { catchError, map, tap } from 'rxjs/operators';
import { getEndpointUrl, buildUrlWithQuery } from '../config/app.config';

/**
 * Individual Deal Information
 */
export interface FundingArbitrageDeal {
  id: string;
  symbol: string;
  primaryExchange: string;
  hedgeExchange: string;
  fundingRate: number;
  positionType: 'long' | 'short';
  quantity: number;
  entryPrice: number;
  hedgeEntryPrice: number;
  primaryExitPrice: number | null;
  hedgeExitPrice: number | null;
  fundingEarned: number;
  realizedPnl: number;
  primaryTradingFees: number;
  hedgeTradingFees: number;
  executedAt: string | null;
  closedAt: string | null;
  duration: number | null; // in seconds
}

/**
 * Best/Worst Deal Summary
 */
export interface DealSummary {
  symbol: string;
  revenue: number;
  date: string | null;
}

/**
 * Revenue Summary Statistics
 */
export interface RevenueSummary {
