  data: {
    subscriptionId: string;
    symbol: string;
    fundingRate: number;
    nextFundingTime: number;
    positionType: string;
    quantity: number;
    leverage: number;
    margin: number;
    status: string;
    createdAt: string;
  };
  message: string;
  timestamp: string;
}

/**
 * Funding Arbitrage Service
 *
 * Handles all funding arbitrage revenue operations including:
 * - Fetching revenue statistics
 * - Filtering by date range, exchange, and symbol
 * - Managing revenue state with reactive BehaviorSubjects
 */
@Injectable({
  providedIn: 'root'
})
export class FundingArbitrageService {
  private revenueSubject = new BehaviorSubject<FundingArbitrageRevenueResponse | null>(null);
  private loadingSubject = new BehaviorSubject<boolean>(false);
  private errorSubject = new BehaviorSubject<string | null>(null);

  public readonly revenue$ = this.revenueSubject.asObservable();
  public readonly loading$ = this.loadingSubject.asObservable();
  public readonly error$ = this.errorSubject.asObservable();

  constructor(private http: HttpClient) {}

  /**
   * Get comprehensive revenue statistics for funding arbitrage
   * @param startDate - Optional start date (ISO string)
   * @param endDate - Optional end date (ISO string)
   * @param exchange - Optional exchange filter
   * @param symbol - Optional symbol filter
   * @returns Observable<FundingArbitrageRevenueResponse>
   */
  getRevenue(
    startDate?: string,
    endDate?: string,
    exchange?: string,
    symbol?: string
  ): Observable<FundingArbitrageRevenueResponse> {
    this.loadingSubject.next(true);
    this.errorSubject.next(null);

    const baseUrl = getEndpointUrl('fundingArbitrage', 'revenue');
    const params: Record<string, string> = {};

    if (startDate) params['startDate'] = startDate;
    if (endDate) params['endDate'] = endDate;
    if (exchange) params['exchange'] = exchange;
    if (symbol) params['symbol'] = symbol;

    const url = Object.keys(params).length > 0 ? buildUrlWithQuery(baseUrl, params) : baseUrl;

    return this.http.get<FundingArbitrageRevenueResponse>(url).pipe(
      tap(response => {
        this.revenueSubject.next(response);
        this.loadingSubject.next(false);
      }),
      catchError(error => {
        this.loadingSubject.next(false);
        const errorMessage = this.handleError(error);
        this.errorSubject.next(errorMessage);
        return throwError(() => new Error(errorMessage));
      })
    );
  }

  /**
   * Refresh revenue data with current filters
   */
  refreshRevenue(): Observable<FundingArbitrageRevenueResponse> {
    const currentRevenue = this.revenueSubject.value;
    if (!currentRevenue) {
      return this.getRevenue();
    }

    const filters = currentRevenue.filters;
    return this.getRevenue(
      filters.startDate,
      filters.endDate,
      filters.exchange || undefined,
      filters.symbol || undefined
    );
  }

  /**
   * Subscribe to funding arbitrage (start hedged arbitrage position)
   * @param request - Subscription request parameters
   * @returns Observable<FundingArbitrageSubscribeResponse>
   */
  subscribe(request: FundingArbitrageSubscribeRequest): Observable<FundingArbitrageSubscribeResponse> {
    const url = getEndpointUrl('fundingArbitrage', 'subscribe');

    return this.http.post<FundingArbitrageSubscribeResponse>(url, request).pipe(
      catchError(error => {
        const errorMessage = this.handleError(error);
        return throwError(() => new Error(errorMessage));
      })
    );
  }

  /**
   * Get current revenue data from cache (if available)
   */
  getCurrentRevenue(): FundingArbitrageRevenueResponse | null {
    return this.revenueSubject.value;
  }

  /**
   * Clear cached revenue data
   */
  clearRevenue(): void {
    this.revenueSubject.next(null);
    this.errorSubject.next(null);
  }

  /**
   * Check if revenue data is currently loading
   */
  isLoading(): boolean {
    return this.loadingSubject.value;
  }

  /**
   * Get current error (if any)
   */
  getCurrentError(): string | null {
    return this.errorSubject.value;
  }

  /**
   * Calculate total fees from deals
   */
  calculateTotalFees(deals: FundingArbitrageDeal[]): number {
    return deals.reduce(
      (total, deal) => total + deal.primaryTradingFees + deal.hedgeTradingFees,
      0
    );
  }

  /**
   * Calculate net profit (revenue - fees)
   */
  calculateNetProfit(totalRevenue: number, totalFees: number): number {
    return totalRevenue - totalFees;
  }

  /**
   * Format duration in seconds to human-readable string
   */
  formatDuration(seconds: number): string {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;

    if (hours > 0) {
      return `${hours}h ${minutes}m`;
    } else if (minutes > 0) {
      return `${minutes}m ${secs}s`;
    } else {
      return `${secs}s`;
    }
  }

  /**
   * Handle HTTP errors and return user-friendly messages
   */
  private handleError(error: HttpErrorResponse): string {
    console.error('Funding Arbitrage Service Error:', error);

    if (error.error?.message) {
      return error.error.message;
    }

    if (error.status === 0) {
      return 'Unable to connect to the server. Please check your internet connection.';
    }

    if (error.status === 401) {
      return 'Unauthorized. Please log in again.';
    }

    if (error.status === 403) {
      return 'Access forbidden. You do not have permission to view this data.';
    }

    if (error.status === 400) {
      return error.error?.message || 'Invalid request parameters.';
    }

    if (error.status >= 500) {
      return 'Server error. Please try again later.';
    }

    return error.message || 'An unexpected error occurred. Please try again.';
  }
}

import { Injectable, signal } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable, tap, catchError, throwError } from 'rxjs';
import { getEndpointUrl } from '../config/app.config';
import { AuthService } from './auth.service';
import { environment } from '../../environments/environment';

export interface GoogleAuthResponse {
  success: boolean;
  user?: any;
  message?: string;
}

export interface GoogleLinkResponse {
  success: boolean;
  message: string;
  googleEmail?: string;
  linkedAt?: Date;
}

@Injectable({
  providedIn: 'root'
})
export class GoogleAuthService {
  private isLinking = signal<boolean>(false);
  private isUnlinking = signal<boolean>(false);

  readonly isLinking$ = this.isLinking.asReadonly();
  readonly isUnlinking$ = this.isUnlinking.asReadonly();

  constructor(
    private http: HttpClient,
    private authService: AuthService
  ) {}

  private getAuthHeaders(): HttpHeaders {
    const token = this.authService.authState().token;
    return new HttpHeaders({
      'Authorization': `Bearer ${token}`,
      'Content-Type': 'application/json'
    });
  }

  /**
   * Initiate Google OAuth login
   * This should redirect to Google OAuth or return a URL for redirect
   */
  loginWithGoogle(): Observable<{ redirectUrl?: string; success?: boolean }> {
    return this.http.post<{ redirectUrl?: string; success?: boolean }>(
      getEndpointUrl('google', 'auth'),
      {}
    ).pipe(
      catchError(error => {
        console.error('Error initiating Google login:', error);
        return throwError(() => error);
      })
    );
  }

  /**
   * Handle Google OAuth callback
   * This processes the authorization code received from Google
   */
  handleGoogleCallback(code: string, state?: string): Observable<GoogleAuthResponse> {
    return this.http.post<GoogleAuthResponse>(
      `${getEndpointUrl('google', 'auth')}/callback`,
      { code, state }
    ).pipe(
      catchError(error => {
        console.error('Error handling Google callback:', error);
        return throwError(() => error);
      })
    );
  }

  /**
   * Link existing account with Google
   * For users who are already logged in and want to connect their Google account
   */
  linkGoogleAccount(googleToken?: string): Observable<GoogleLinkResponse> {
    this.isLinking.set(true);
    const headers = this.getAuthHeaders();
    
    return this.http.post<GoogleLinkResponse>(
      getEndpointUrl('google', 'link'),
      { googleToken },
      { headers }
    ).pipe(
      tap(() => this.isLinking.set(false)),
      catchError(error => {
        this.isLinking.set(false);
        console.error('Error linking Google account:', error);
        return throwError(() => error);
      })
    );
  }

  /**
   * Unlink Google account from current user
   */
  unlinkGoogleAccount(): Observable<GoogleLinkResponse> {
    this.isUnlinking.set(true);
    const headers = this.getAuthHeaders();
    
    return this.http.post<GoogleLinkResponse>(
      getEndpointUrl('google', 'unlink'),
      {},
      { headers }
    ).pipe(
      tap(() => this.isUnlinking.set(false)),
      catchError(error => {
        this.isUnlinking.set(false);
        console.error('Error unlinking Google account:', error);
        return throwError(() => error);
      })
    );
  }

  /**
   * Get Google account linking status
   */
  getGoogleLinkStatus(): Observable<{ linked: boolean; googleEmail?: string; linkedAt?: Date }> {
    const headers = this.getAuthHeaders();
    
    return this.http.get<{ linked: boolean; googleEmail?: string; linkedAt?: Date }>(
      `${getEndpointUrl('google', 'link')}/status`,
      { headers }
    ).pipe(
      catchError(error => {
        console.error('Error getting Google link status:', error);
        return throwError(() => error);
      })
    );
  }

  /**
   * Initialize Google Sign-In button
   * This sets up the Google Sign-In button for client-side authentication
   */
  initializeGoogleSignIn(containerId: string): void {
    // Load Google Sign-In script if not already loaded
    if (typeof window !== 'undefined' && !(window as any).google) {
      const script = document.createElement('script');
      script.src = 'https://accounts.google.com/gsi/client';
      script.async = true;
      script.defer = true;
      script.onload = () => {
        this.renderGoogleButton(containerId);
      };
      document.head.appendChild(script);
    } else if ((window as any).google) {
      this.renderGoogleButton(containerId);
    }
  }

  private renderGoogleButton(containerId: string): void {
    if (typeof window !== 'undefined' && (window as any).google) {
      (window as any).google.accounts.id.initialize({
        client_id: environment.googleClientId,
        callback: (response: any) => {
          this.handleClientSideGoogleResponse(response);
        }
      });

      (window as any).google.accounts.id.renderButton(
        document.getElementById(containerId),
        {
          theme: 'outline',
          size: 'large',
          type: 'standard',
          text: 'continue_with',
          shape: 'rectangular'
        }
      );
    }
  }

  private handleClientSideGoogleResponse(response: any): void {
    // Handle the JWT token from Google
    const credential = response.credential;
    if (credential) {
      this.linkGoogleAccount(credential).subscribe({
        next: (result) => {
          console.log('Google account linked successfully:', result);
          // Update user data or show success message
        },
        error: (error) => {
          console.error('Failed to link Google account:', error);
          // Show error message
        }
      });
    }
  }

  /**
   * Create Google OAuth URL for manual redirect
   */
  createGoogleAuthUrl(redirectUri: string, state?: string): string {
    const params = new URLSearchParams({
      client_id: environment.googleClientId,
      redirect_uri: redirectUri,
      response_type: 'code',
      scope: 'openid email profile',
      access_type: 'offline',
      prompt: 'consent'
    });

    if (state) {
      params.append('state', state);
    }

    return `https://accounts.google.com/o/oauth2/v2/auth?${params.toString()}`;
  }
}

