  /**
   * Calculate funding interval based on time until next funding
   * Uses REAL data from exchange API instead of hardcoded assumptions
   */
  private calculateFundingInterval(nextFundingTime: number): string {
    const now = Date.now();
    const timeUntilFunding = nextFundingTime - now;

    if (timeUntilFunding <= 0) {
      return '8h'; // Default if funding time is in the past
    }

    const hoursUntilFunding = timeUntilFunding / (1000 * 60 * 60);

    // Determine interval based on actual time until next funding
    if (hoursUntilFunding <= 1.5) {
      return '1h';  // GATEIO, some MEXC symbols
    } else if (hoursUntilFunding <= 5) {
      return '4h';  // Some MEXC symbols
    } else {
      return '8h';  // BYBIT, BINANCE, BINGX, OKX, most symbols
    }
  }

  /**
   * Normalize funding rate to 8-hour interval for fair comparison
   * Different exchanges use different intervals (1h, 4h, 8h)
   */
  private normalizeFundingRateTo8h(fundingRate: string, fundingInterval = '8h'): number {
    const rate = parseFloat(fundingRate);

    // Normalization multipliers to convert to 8h interval
    const multipliers: Record<string, number> = {
      '1h': 8,   // 8 hourly payments = 1 eight-hour period
      '4h': 2,   // 2 four-hour payments = 1 eight-hour period
      '8h': 1,   // Already 8h interval
    };

    const multiplier = multipliers[fundingInterval] || 1;
    return rate * multiplier;
  }

  /**
   * Calculate arbitrage opportunities from exchange data
   * Groups by symbol and calculates spreads
   * IMPORTANT: Normalizes all funding rates to 8h intervals for fair comparison
   */
  private calculateOpportunities(rates: ExchangeFundingRate[]): FundingRateOpportunity[] {
    // Group by normalized symbol
    const symbolMap = new Map<string, ExchangeFundingRate[]>();

    // Add normalized funding rates and calculate intervals based on real data
    rates.forEach(rate => {
      if (!symbolMap.has(rate.symbol)) {
        symbolMap.set(rate.symbol, []);
      }

      // Calculate funding interval from actual nextFundingTime (not hardcoded!)
      if (!rate.fundingInterval && rate.nextFundingTime) {
        rate.fundingInterval = this.calculateFundingInterval(rate.nextFundingTime);
      }

      // Normalize funding rate to 8h interval using calculated interval
      rate.fundingRateNormalized = this.normalizeFundingRateTo8h(
        rate.fundingRate,
        rate.fundingInterval || '8h'
      );

      console.log(`[FundingInterval] ${rate.exchange} ${rate.symbol}: interval=${rate.fundingInterval}, nextFunding=${new Date(rate.nextFundingTime).toLocaleTimeString()}, rate=${rate.fundingRate}, normalized=${rate.fundingRateNormalized.toFixed(6)}`);

      symbolMap.get(rate.symbol)!.push(rate);
    });

    const opportunities: FundingRateOpportunity[] = [];

    // Calculate opportunities for each symbol
    symbolMap.forEach((exchanges, symbol) => {
      // Skip symbols with only one exchange
      if (exchanges.length < 2) return;

      // Sort by NORMALIZED funding rate (lowest to highest) - CRITICAL for fair comparison!
      const sortedExchanges = [...exchanges].sort(
        (a, b) => (a.fundingRateNormalized || 0) - (b.fundingRateNormalized || 0)
      );

      const bestLong = sortedExchanges[0]; // Lowest (most negative) normalized funding rate
      const bestShort = sortedExchanges[sortedExchanges.length - 1]; // Highest normalized funding rate

      // Calculate funding spread using NORMALIZED rates
      const fundingSpread = (bestShort.fundingRateNormalized || 0) - (bestLong.fundingRateNormalized || 0);
      const fundingSpreadPercent = (fundingSpread * 100).toFixed(4);

      // Calculate price spread
      const bestLongPrice = parseFloat(bestLong.lastPrice);
      const bestShortPrice = parseFloat(bestShort.lastPrice);
      const priceSpread = (bestShortPrice - bestLongPrice) / bestLongPrice;
      const priceSpreadPercent = (priceSpread * 100).toFixed(2);
      const priceSpreadUsdt = (bestShortPrice - bestLongPrice).toFixed(2);

      // Get earliest next funding time
      const fundingTimes = exchanges.map(e => e.nextFundingTime).filter(t => t > 0);
      const nextFundingTime = fundingTimes.length > 0 ? Math.min(...fundingTimes) : 0;

      // Calculate funding periodicity
      const fundingPeriodicity = this.calculateFundingPeriodicity(nextFundingTime);

      // Filter out unrealistic spreads (> 100%)
      if (Math.abs(priceSpread) > 1.0) {
        console.debug(`[PublicFundingRates] Filtered ${symbol}: unrealistic price spread ${priceSpreadPercent}%`);
        return;
      }

      // Filter out invalid prices
      if (bestLongPrice <= 0 || bestShortPrice <= 0) {
        console.debug(`[PublicFundingRates] Filtered ${symbol}: invalid prices`);
        return;
      }

      // ===== Phase 1 Advanced Metrics Calculations =====

      // 1. Time to Funding
      const timeToFunding = this.calculateTimeToFunding(nextFundingTime);

      // 2. Estimated APR (gross, before fees)
      const estimatedAPR = this.calculateEstimatedAPR(fundingSpread);
      const estimatedAPRFormatted = (estimatedAPR * 100).toFixed(2) + '%';

      // 3. Total Fees
      const totalFees = this.calculateTotalFees(bestLong.exchange, bestShort.exchange);
      const totalFeesFormatted = (totalFees * 100).toFixed(2) + '%';

      // 4. Net APR (after fees)
      const netAPR = this.calculateNetAPR(estimatedAPR, totalFees);
      const netAPRFormatted = (netAPR * 100).toFixed(2) + '%';

      // 5. 24h Volume (optional)
      const volume24h = this.calculateVolume24h(exchanges);
      const volume24hFormatted = volume24h !== undefined ? '$' + this.formatLargeNumber(volume24h) : undefined;

      // 6. Open Interest (optional)
      const openInterest = this.calculateOpenInterest(exchanges);
      const openInterestFormatted = openInterest !== undefined ? '$' + this.formatLargeNumber(openInterest) : undefined;

      // 7. Volatility 24h (optional)
      const volatility24h = this.calculateVolatility24h(exchanges);
      const volatility24hFormatted = volatility24h !== undefined ? (volatility24h * 100).toFixed(2) + '%' : undefined;

      // 8. Determine Recommended Strategy (spot-futures vs cross-exchange)
      let recommendedStrategy: 'cross-exchange' | 'spot-futures' = 'cross-exchange';
      let spotFuturesBestExchange: ExchangeFundingRate | undefined;

      const bestLongFundingNormalized = bestLong.fundingRateNormalized || 0;
      const bestShortFundingNormalized = bestShort.fundingRateNormalized || 0;

      // If BOTH funding rates are positive, spot-futures is more profitable
      // Spot-futures: Buy spot + Short futures on same exchange = earn full funding rate
      // Cross-exchange: Short on high funding - Long on low funding = earn only the spread
      if (bestLongFundingNormalized > 0 && bestShortFundingNormalized > 0) {
        recommendedStrategy = 'spot-futures';
        // Choose exchange with highest funding rate for spot-futures
        spotFuturesBestExchange = bestShort; // Highest funding rate
        console.log(`[PublicFundingRates] ${symbol}: Spot-futures recommended (both normalized fundings positive: ${(bestLongFundingNormalized*100).toFixed(4)}%, ${(bestShortFundingNormalized*100).toFixed(4)}% per 8h)`);
      } else {
        recommendedStrategy = 'cross-exchange';
        console.log(`[PublicFundingRates] ${symbol}: Cross-exchange recommended (normalized fundings: ${(bestLongFundingNormalized*100).toFixed(4)}%, ${(bestShortFundingNormalized*100).toFixed(4)}% per 8h)`);
      }

      // 9. Combined Strategy Metrics (price spread + funding differential)
      // Calculate combined score only if we have both price and funding data
      let combinedScore: number | undefined;
      let expectedDailyReturn: number | undefined;
      let estimatedMonthlyROI: number | undefined;
      let strategyType: 'price_only' | 'funding_only' | 'combined' = 'price_only';

      // Check if we have valid funding spread (not just price data)
      if (Math.abs(fundingSpread) > 0.0001) {
        // We have funding data - calculate combined metrics

        // Convert price spread to percentage
        const priceSpreadPercent = priceSpread * 100;

        // Funding differential per 8h period (as percentage)
        const fundingDifferentialPercent = fundingSpread * 100;

        // Expected daily return = price spread (one-time) + funding differential (3 times per day)
        // Assumption: price convergence happens within 1 day
        const dailyFundingReturn = fundingDifferentialPercent * 3; // 3 funding periods per day (every 8h)
        expectedDailyReturn = priceSpreadPercent + dailyFundingReturn;

        // Estimated monthly ROI = price spread (one-time) + funding for 30 days
        estimatedMonthlyROI = priceSpreadPercent + (dailyFundingReturn * 30);

        // Combined score = immediate price spread + projected funding for 7 days
        combinedScore = priceSpreadPercent + (dailyFundingReturn * 7);

        strategyType = 'combined';
      } else if (Math.abs(priceSpread) > 0.0001) {
        // We have price spread but minimal/no funding data
        strategyType = 'price_only';
        combinedScore = priceSpread * 100; // Just the price spread
        expectedDailyReturn = priceSpread * 100;
        estimatedMonthlyROI = priceSpread * 100;
      } else if (Math.abs(fundingSpread) > 0.0001) {
        // We have funding but minimal/no price spread
        strategyType = 'funding_only';
        const dailyFundingReturn = fundingSpread * 100 * 3;
        combinedScore = dailyFundingReturn * 7;
        expectedDailyReturn = dailyFundingReturn;
        estimatedMonthlyROI = dailyFundingReturn * 30;
      }

      opportunities.push({
        symbol,
        exchanges: sortedExchanges,
        maxFundingSpread: fundingSpread, // LEGACY - kept for backward compatibility
        maxFundingSpreadPercent: fundingSpreadPercent + '%', // LEGACY
        fundingSpread: fundingSpread, // Normalized funding spread (8h basis)
        fundingSpreadPercent: fundingSpreadPercent + '%', // Normalized funding spread percentage
        bestLong,
        bestShort,
        priceSpread,
        priceSpreadPercent: priceSpreadPercent + '%',
        priceSpreadUsdt: '$' + priceSpreadUsdt,
        fundingPeriodicity,
        nextFundingTime,
        // Phase 1 Advanced Metrics
        timeToFunding,
        estimatedAPR,
        estimatedAPRFormatted,
        totalFees,
        totalFeesFormatted,
        netAPR,
        netAPRFormatted,
        volume24h,
        volume24hFormatted,
        openInterest,
        openInterestFormatted,
        volatility24h,
        volatility24hFormatted,
        // Combined Strategy Metrics
        combinedScore,
        expectedDailyReturn,
        estimatedMonthlyROI,
        strategyType,
        // Recommended Strategy
        recommendedStrategy,
        spotFuturesBestExchange,
      });
    });

    // Sort by NORMALIZED funding spread (highest first) - uses fundingSpread which is now normalized
    opportunities.sort((a, b) => Math.abs((b.fundingSpread || 0)) - Math.abs((a.fundingSpread || 0)));

    console.log(`[PublicFundingRates] Calculated ${opportunities.length} opportunities`);

    return opportunities;
  }

  /**
   * Calculate human-readable funding periodicity
   * @param nextFundingTime - Next funding time timestamp
   * @returns Formatted string like "2 35/8h"
   */
  private calculateFundingPeriodicity(nextFundingTime: number): string {
    if (!nextFundingTime || nextFundingTime <= 0) {
      return '';
    }

    const now = Date.now();
    const timeUntilFunding = nextFundingTime - now;

    if (timeUntilFunding < 0) {
      return '';
    }

    const hours = Math.floor(timeUntilFunding / (60 * 60 * 1000));
    const minutes = Math.floor((timeUntilFunding % (60 * 60 * 1000)) / (60 * 1000));

    // Determine funding interval based on time until next funding
    let interval = '8h'; // Default
    if (timeUntilFunding <= 1 * 60 * 60 * 1000) {
      interval = '1h';
    } else if (timeUntilFunding <= 4 * 60 * 60 * 1000) {
      interval = '4h';
    }

    return `${hours} ${minutes}/${interval}`;
  }

  /**
   * Calculate time until next funding in human-readable format
   * @param nextFundingTime - Next funding timestamp
   * @returns Formatted string like "2h 15m" or "" if unavailable
   */
  private calculateTimeToFunding(nextFundingTime: number): string {
    if (!nextFundingTime || nextFundingTime <= 0) {
      return '';
    }

    const now = Date.now();
    const timeUntilFunding = nextFundingTime - now;

    if (timeUntilFunding < 0) {
      return '';
    }

    const hours = Math.floor(timeUntilFunding / (60 * 60 * 1000));
    const minutes = Math.floor((timeUntilFunding % (60 * 60 * 1000)) / (60 * 1000));

    return `${hours}h ${minutes}m`;
  }

  /**
   * Calculate Estimated APR from funding spread
   * Formula: funding spread  funding periods per year  100
   * 8h intervals = 3 times per day  365 days = 1095 funding periods per year
   * @param fundingSpread - Funding rate spread (decimal)
   * @returns Annual Percentage Rate (decimal)
   */
  private calculateEstimatedAPR(fundingSpread: number): number {
    const fundingPeriodsPerYear = 3 * 365; // 8h intervals
    return fundingSpread * fundingPeriodsPerYear;
  }

  /**
   * Calculate total trading fees for arbitrage (4 trades total)
   * @param longExchange - Exchange for long position
   * @param shortExchange - Exchange for short position
   * @returns Total fees as decimal (e.g., 0.0022 = 0.22%)
   */
  private calculateTotalFees(longExchange: string, shortExchange: string): number {
    const longFee = (this.EXCHANGE_FEES as any)[longExchange] || 0.055;
    const shortFee = (this.EXCHANGE_FEES as any)[shortExchange] || 0.055;

    // 4 trades: open long, open short, close long, close short
    return (longFee + shortFee) * 2 / 100; // Convert from percentage to decimal
  }

  /**
   * Calculate Net APR (after fees deduction)
   * @param grossAPR - Gross APR (before fees)
   * @param totalFees - Total trading fees (decimal)
   * @returns Net APR (decimal)
   */
  private calculateNetAPR(grossAPR: number, totalFees: number): number {
    // Convert fees to annual equivalent
    const annualFees = totalFees * 3 * 365; // Assuming 3 cycles per day
    return grossAPR - annualFees;
  }

  /**
   * Format large numbers with K/M/B suffixes
   * @param value - Number to format
   * @returns Formatted string (e.g., "1.2M", "45.3K")
   */
  private formatLargeNumber(value: number): string {
    if (value >= 1_000_000_000) {
      return `${(value / 1_000_000_000).toFixed(2)}B`;
    } else if (value >= 1_000_000) {
      return `${(value / 1_000_000).toFixed(2)}M`;
    } else if (value >= 1_000) {
      return `${(value / 1_000).toFixed(2)}K`;
    }
    return value.toFixed(2);
  }

  /**
   * Calculate combined 24h volume from exchanges
   * @param exchanges - Array of exchange data
   * @returns Combined volume or undefined if not available
   */
  private calculateVolume24h(exchanges: ExchangeFundingRate[]): number | undefined {
    const volumes = exchanges
      .map(ex => ex.volume24h ? parseFloat(ex.volume24h) : 0)
      .filter(v => v > 0);

    if (volumes.length === 0) return undefined;

    return volumes.reduce((sum, v) => sum + v, 0);
  }

  /**
   * Calculate combined open interest from exchanges
   * @param exchanges - Array of exchange data
   * @returns Combined OI or undefined if not available
   */
  private calculateOpenInterest(exchanges: ExchangeFundingRate[]): number | undefined {
    const ois = exchanges
      .map(ex => ex.openInterest ? parseFloat(ex.openInterest) : 0)
      .filter(oi => oi > 0);

    if (ois.length === 0) return undefined;

    return ois.reduce((sum, oi) => sum + oi, 0);
  }

  /**
   * Calculate simple 24h volatility
   * Formula: (high - low) / low  100
   * @param exchanges - Array of exchange data
   * @returns Volatility as decimal or undefined if not available
   */
  private calculateVolatility24h(exchanges: ExchangeFundingRate[]): number | undefined {
    // Get high/low from exchanges that provide it
    const validData = exchanges
      .filter(ex => ex.high24h && ex.low24h)
      .map(ex => ({
        high: parseFloat(ex.high24h!),
        low: parseFloat(ex.low24h!)
      }))
      .filter(d => d.high > 0 && d.low > 0);

    if (validData.length === 0) return undefined;

    // Calculate average volatility across exchanges
    const volatilities = validData.map(d => (d.high - d.low) / d.low);
    const avgVolatility = volatilities.reduce((sum, v) => sum + v, 0) / volatilities.length;

    return avgVolatility;
  }

  // ===== PHASE 2: Historical Spread Stability Methods =====

  /**
   * Load Phase 2 historical stability metrics for an opportunity
   * Fetches 7d and 30d funding rate history and calculates stability metrics
   *
   * @param opportunity - Funding rate opportunity to analyze
   * @returns Updated opportunity with Phase 2 metrics
   */
  async loadPhase2Metrics(
    opportunity: FundingRateOpportunity
  ): Promise<FundingRateOpportunity> {
    try {
      const symbol = opportunity.symbol;
      const longExchange = opportunity.bestLong.exchange;
      const shortExchange = opportunity.bestShort.exchange;

      console.log(`[Phase 2] Loading metrics for ${symbol} (${longExchange} vs ${shortExchange})`);

      // Fetch historical funding rates for both exchanges (7d and 30d in parallel)
      const [
        longRates7d,
        shortRates7d,
        longRates30d,
        shortRates30d
      ] = await Promise.all([
        this.fetchFundingRateHistory(symbol, longExchange, 7),
        this.fetchFundingRateHistory(symbol, shortExchange, 7),
        this.fetchFundingRateHistory(symbol, longExchange, 30),
        this.fetchFundingRateHistory(symbol, shortExchange, 30)
      ]);

      // Calculate spreads from funding rates
      const spreads7d = this.calculateSpreads(longRates7d, shortRates7d);
      const spreads30d = this.calculateSpreads(longRates30d, shortRates30d);

      console.log(`[Phase 2] Spreads calculated: 7d=${spreads7d.length}, 30d=${spreads30d.length}`);

      // Calculate stability metrics using StatisticalUtilsService
      const metrics7d = this.calculateStabilityMetrics(spreads7d, 7, 168); // 7 days  24h  1 sample/h = 168
      const metrics30d = this.calculateStabilityMetrics(spreads30d, 30, 720); // 30  24 = 720

      // Calculate trend and confidence
      const trend = this.statisticalUtils.calculateStabilityTrend(
        metrics7d.stabilityScore,
        metrics30d.stabilityScore
      );
      const confidence = this.statisticalUtils.calculateConfidenceScore(
        metrics7d.dataQuality,
        metrics30d.dataQuality
      );

      console.log(`[Phase 2] Metrics loaded successfully for ${symbol}`);

      // Return updated opportunity
      return {
        ...opportunity,
        spreadHistory7d: metrics7d,
        spreadHistory30d: metrics30d,
        spreadStabilityTrend: trend,
        spreadStabilityConfidence: confidence
      };
    } catch (error) {
      console.error('[Phase 2] Failed to load metrics:', error);
      // Return unchanged opportunity on error (graceful degradation)
      return opportunity;
    }
  }

  /**
   * Fetch historical funding rates from backend API
   *
   * @param symbol - Trading symbol (e.g., BTCUSDT)
   * @param exchange - Exchange name (BYBIT, BINGX, MEXC)
   * @param days - Period length (7 or 30)
   * @returns Array of historical funding rates with timestamps
   */
  private async fetchFundingRateHistory(
    symbol: string,
    exchange: string,
    days: 7 | 30
  ): Promise<{ timestamp: number; fundingRate: number }[]> {
    const url = `/api/arbitrage/funding-rates/history?symbol=${symbol}&exchange=${exchange}&days=${days}`;

    try {
      const response = await firstValueFrom(
        this.http.get<{
          success: boolean;
          data: { timestamp: number; fundingRate: number }[];
        }>(url)
      );

      if (response.success) {
        console.log(`[Phase 2] Fetched ${response.data.length} funding rates for ${exchange} ${symbol} (${days}d)`);
        return response.data;
      } else {
        console.warn(`[Phase 2] No data for ${exchange} ${symbol} (${days}d)`);
        return [];
      }
    } catch (error) {
      console.error(`[Phase 2] Error fetching history for ${exchange} ${symbol}:`, error);
      return [];
    }
  }

  /**
   * Calculate spread values from long and short funding rates
   * Matches timestamps and calculates absolute spread difference
   *
   * @param longRates - Funding rates from long position exchange
   * @param shortRates - Funding rates from short position exchange
   * @returns Array of spread data points with timestamps and values
   */
  private calculateSpreads(
    longRates: { timestamp: number; fundingRate: number }[],
    shortRates: { timestamp: number; fundingRate: number }[]
  ): { timestamp: number; spread: number }[] {
    // Match timestamps and calculate spreads
    const spreads: { timestamp: number; spread: number }[] = [];

    for (const longRate of longRates) {
      const shortRate = shortRates.find(s => s.timestamp === longRate.timestamp);
      if (shortRate) {
        const spread = Math.abs(shortRate.fundingRate - longRate.fundingRate);
        spreads.push({
          timestamp: longRate.timestamp,
          spread: spread
        });
      }
    }

    return spreads;
  }

  /**
   * Calculate stability metrics from spread data using statistical analysis
   *
   * @param spreads - Array of spread data points with timestamps
   * @param periodDays - Period length (7 or 30 days)
   * @param expectedSamples - Expected number of samples for this period
   * @returns Complete spread stability metrics
   */
  private calculateStabilityMetrics(
    spreads: { timestamp: number; spread: number }[],
    periodDays: 7 | 30,
    expectedSamples: number
  ): SpreadStabilityMetrics {
    // Extract spread values for statistical calculations
    const spreadValues = spreads.map(s => s.spread);

    const avg = this.statisticalUtils.calculateAverage(spreadValues);
    const stdDev = this.statisticalUtils.calculateStandardDeviation(spreadValues);

    if (avg === null || stdDev === null) {
      // No data available
      return {
        average: 0,
        averageFormatted: '0.00%',
        standardDeviation: 0,
        standardDeviationFormatted: '0.00%',
        stabilityScore: 0,
        stabilityRating: 'poor',
        sampleSize: 0,
        dataQuality: 'low',
        periodDays,
        historicalData: []
      };
    }

    const score = this.statisticalUtils.calculateStabilityScore(avg, stdDev);
    const rating = this.statisticalUtils.getStabilityRating(score);
    const quality = this.statisticalUtils.getDataQuality(spreads.length, expectedSamples);

    return {
      average: avg,
      averageFormatted: this.statisticalUtils.formatPercentage(avg, 2),
      standardDeviation: stdDev,
      standardDeviationFormatted: this.statisticalUtils.formatPercentage(stdDev, 2),
      stabilityScore: score,
      stabilityRating: rating,
      sampleSize: spreads.length,
      dataQuality: quality,
      periodDays,
      startTimestamp: spreads.length > 0 ? Date.now() - (periodDays * 24 * 60 * 60 * 1000) : undefined,
      endTimestamp: spreads.length > 0 ? Date.now() : undefined,
      historicalData: spreads // Include historical data for charting
    };
  }
}

import { Injectable, OnDestroy } from '@angular/core';
import { BehaviorSubject, Observable, Subject } from 'rxjs';
import { filter, map, takeUntil } from 'rxjs/operators';

export interface TickerData {
  type: 'ticker' | 'connected' | 'error';
  symbol: string;
  price?: number;
  volume?: number;
  change24h?: number;
  bid?: number;
  ask?: number;
  high24h?: number;
  low24h?: number;
  timestamp: number;
  message?: string;
}

export interface PriceUpdate {
  symbol: string;
  price: number;
  change: number;
  changePercent: number;
  volume: number;
  timestamp: number;
}

@Injectable({
  providedIn: 'root'
})
export class RealtimeService implements OnDestroy {
  private eventSources = new Map<string, EventSource>();
  private destroy$ = new Subject<void>();

  // Subject for all ticker updates
  private tickerSubject = new BehaviorSubject<TickerData | null>(null);
  public ticker$ = this.tickerSubject.asObservable().pipe(
    filter(data => data !== null)
  ) as Observable<TickerData>;

  // Subject for price updates only
  private priceSubject = new BehaviorSubject<PriceUpdate | null>(null);
  public price$ = this.priceSubject.asObservable().pipe(
    filter(data => data !== null)
  ) as Observable<PriceUpdate>;

  // Connection status
  private connectionStatusSubject = new BehaviorSubject<Record<string, boolean>>({});
  public connectionStatus$ = this.connectionStatusSubject.asObservable();

  private baseUrl = '/api/trading';

  constructor() {}

  ngOnDestroy() {
    this.destroy$.next();
    this.destroy$.complete();
    this.closeAllConnections();
  }

  // Subscribe to real-time data for a symbol
  subscribeToSymbol(symbol: string): Observable<TickerData> {
    const normalizedSymbol = symbol.toUpperCase();

    // Close existing connection if any
    this.unsubscribeFromSymbol(normalizedSymbol);

    // Create new EventSource connection
    const eventSource = new EventSource(`${this.baseUrl}/stream?symbol=${normalizedSymbol}`);
    this.eventSources.set(normalizedSymbol, eventSource);

    // Update connection status
    this.updateConnectionStatus(normalizedSymbol, false);

    return new Observable<TickerData>(observer => {
      eventSource.onopen = () => {
        console.log(`Connected to real-time stream for ${normalizedSymbol}`);
        this.updateConnectionStatus(normalizedSymbol, true);
      };

      eventSource.onmessage = (event) => {
        try {
          const data: TickerData = JSON.parse(event.data);

          // Emit to main ticker subject
          this.tickerSubject.next(data);

          // Emit to specific symbol observer
          observer.next(data);

          // If it's ticker data, also emit to price subject
          if (data.type === 'ticker' && data.price !== undefined) {
            const priceUpdate: PriceUpdate = {
              symbol: data.symbol,
              price: data.price,
              change: 0, // Calculate based on previous price if needed
              changePercent: data.change24h || 0,
              volume: data.volume || 0,
              timestamp: data.timestamp
            };
            this.priceSubject.next(priceUpdate);
          }

        } catch (error) {
          console.error('Error parsing stream data:', error);
          observer.error(error);
        }
      };

      eventSource.onerror = (error) => {
        console.error(`Stream error for ${normalizedSymbol}:`, error);
        this.updateConnectionStatus(normalizedSymbol, false);

        // Attempt to reconnect after a delay
        setTimeout(() => {
          if (this.eventSources.has(normalizedSymbol)) {
            console.log(`Attempting to reconnect to ${normalizedSymbol}`);
            this.subscribeToSymbol(normalizedSymbol);
          }
        }, 5000);

        observer.error(error);
      };

      // Cleanup function
      return () => {
        this.unsubscribeFromSymbol(normalizedSymbol);
      };
    }).pipe(
      takeUntil(this.destroy$)
    );
  }

  // Unsubscribe from a specific symbol
  unsubscribeFromSymbol(symbol: string): void {
    const normalizedSymbol = symbol.toUpperCase();
    const eventSource = this.eventSources.get(normalizedSymbol);

    if (eventSource) {
      eventSource.close();
      this.eventSources.delete(normalizedSymbol);
      this.updateConnectionStatus(normalizedSymbol, false);
      console.log(`Unsubscribed from ${normalizedSymbol}`);
    }
  }

  // Get real-time price for a specific symbol
  getSymbolPrice$(symbol: string): Observable<PriceUpdate> {
    return this.price$.pipe(
      filter(update => update.symbol === symbol.toUpperCase())
    );
  }

  // Get connection status for a symbol
  isConnected(symbol: string): boolean {
    const status = this.connectionStatusSubject.value;
    return status[symbol.toUpperCase()] || false;
  }

  // Get all connected symbols
  getConnectedSymbols(): string[] {
    const status = this.connectionStatusSubject.value;
    return Object.keys(status).filter(symbol => status[symbol]);
  }

  // Subscribe to multiple symbols
  subscribeToMultipleSymbols(symbols: string[]): Observable<TickerData> {
    symbols.forEach(symbol => {
      this.subscribeToSymbol(symbol);
    });

    return this.ticker$;
  }

  // Close all connections
  closeAllConnections(): void {
    this.eventSources.forEach((eventSource, symbol) => {
      eventSource.close();
      console.log(`Closed connection for ${symbol}`);
    });
    this.eventSources.clear();
    this.connectionStatusSubject.next({});
  }

  // Get current price from cache (last received price)
  getCurrentPrice(symbol: string): number | null {
    const currentTicker = this.tickerSubject.value;
    if (currentTicker && currentTicker.symbol === symbol.toUpperCase() && currentTicker.price) {
      return currentTicker.price;
    }
    return null;
  }

  // Get market summary for connected symbols
  getMarketSummary(): Observable<Record<string, PriceUpdate>> {
    return this.price$.pipe(
      map(update => {
        // This is a simplified implementation
        // In a real app, you'd maintain a map of all symbols
        return { [update.symbol]: update };
      })
    );
  }

  // Helper method to format price change
  formatPriceChange(change: number, changePercent: number): string {
    const sign = change >= 0 ? '+' : '';
    return `${sign}${change.toFixed(2)} (${sign}${changePercent.toFixed(2)}%)`;
  }

  // Helper method to get price change color class
  getPriceChangeClass(change: number): string {
    if (change > 0) return 'price-up';
    if (change < 0) return 'price-down';
    return 'price-neutral';
  }

  private updateConnectionStatus(symbol: string, connected: boolean): void {
    const currentStatus = this.connectionStatusSubject.value;
    const newStatus = { ...currentStatus, [symbol]: connected };
    this.connectionStatusSubject.next(newStatus);
  }
}

import { TestBed } from '@angular/core/testing';
import { StatisticalUtilsService } from './statistical-utils.service';

describe('StatisticalUtilsService', () => {
  let service: StatisticalUtilsService;

  beforeEach(() => {
    TestBed.configureTestingModule({
      providers: [StatisticalUtilsService]
    });
    service = TestBed.inject(StatisticalUtilsService);
  });

  it('should be created', () => {
    expect(service).toBeTruthy();
  });

  describe('calculateAverage', () => {
    it('should calculate correct average for positive numbers', () => {
      expect(service.calculateAverage([1, 2, 3, 4, 5])).toBe(3);
    });

    it('should calculate correct average for decimal values', () => {
      const result = service.calculateAverage([0.1, 0.2, 0.3]);
      expect(result).toBeCloseTo(0.2, 5);
    });

    it('should handle empty array', () => {
      expect(service.calculateAverage([])).toBeNull();
    });

    it('should handle single value', () => {
      expect(service.calculateAverage([5])).toBe(5);
    });

    it('should handle negative numbers', () => {
      expect(service.calculateAverage([-1, -2, -3])).toBe(-2);
    });

    it('should handle mixed positive and negative numbers', () => {
      expect(service.calculateAverage([-2, 0, 2])).toBe(0);
    });

    it('should handle very small decimal values', () => {
      const result = service.calculateAverage([0.0001, 0.0002, 0.0003]);
      expect(result).toBeCloseTo(0.0002, 6);
    });
  });

  describe('calculateStandardDeviation', () => {
    it('should calculate correct standard deviation', () => {
      // Example: [2, 4, 4, 4, 5, 5, 7, 9]
      // Mean = 5, Variance = 4, StdDev = 2
      const result = service.calculateStandardDeviation([2, 4, 4, 4, 5, 5, 7, 9]);
      expect(result).toBeCloseTo(2.0, 1);
    });

    it('should return 0 for identical values', () => {
      expect(service.calculateStandardDeviation([5, 5, 5, 5])).toBe(0);
    });

    it('should handle empty array', () => {
      expect(service.calculateStandardDeviation([])).toBeNull();
    });

    it('should handle single value', () => {
      expect(service.calculateStandardDeviation([10])).toBe(0);
    });

    it('should handle two values', () => {
      const result = service.calculateStandardDeviation([1, 3]);
      expect(result).toBeCloseTo(1.0, 5);
    });

    it('should handle decimal values', () => {
      const result = service.calculateStandardDeviation([0.1, 0.2, 0.3, 0.4]);
      expect(result).toBeGreaterThan(0);
      expect(result).toBeLessThan(0.2);
    });

    it('should handle negative numbers', () => {
      const result = service.calculateStandardDeviation([-2, -1, 0, 1, 2]);
      expect(result).toBeCloseTo(1.4142, 2);
    });
  });

  describe('calculateStabilityScore', () => {
    it('should return 100 for zero standard deviation', () => {
      expect(service.calculateStabilityScore(0.5, 0)).toBe(100);
    });

    it('should return lower score for high coefficient of variation', () => {
      // CV = 0.3 / 0.5 = 0.6
      // Score = 100 * (1 - 0.6) = 40
      const score = service.calculateStabilityScore(0.5, 0.3);
      expect(score).toBeCloseTo(40, 0);
    });

    it('should return higher score for low coefficient of variation', () => {
      // CV = 0.05 / 0.5 = 0.1
      // Score = 100 * (1 - 0.1) = 90
      const score = service.calculateStabilityScore(0.5, 0.05);
      expect(score).toBeCloseTo(90, 0);
    });

    it('should handle edge case: stdDev > average', () => {
      // CV = 0.5 / 0.1 = 5 (capped at 1)
      // Score = 100 * (1 - 1) = 0
      const score = service.calculateStabilityScore(0.1, 0.5);
      expect(score).toBe(0);
    });

    it('should handle edge case: stdDev equals average', () => {
      // CV = 0.5 / 0.5 = 1
      // Score = 100 * (1 - 1) = 0
      const score = service.calculateStabilityScore(0.5, 0.5);
      expect(score).toBe(0);
    });

    it('should return value between 0 and 100', () => {
      const score = service.calculateStabilityScore(0.3, 0.15);
      expect(score).toBeGreaterThanOrEqual(0);
      expect(score).toBeLessThanOrEqual(100);
    });

    it('should handle very small average (avoid division by zero)', () => {
      const score = service.calculateStabilityScore(0.0001, 0.00005);
      expect(score).toBeGreaterThan(0);
      expect(score).toBeLessThanOrEqual(100);
    });

    it('should handle zero average gracefully', () => {
      // When average is 0, return 0 score (can't calculate CV)
      const score = service.calculateStabilityScore(0, 0.1);
      expect(score).toBe(0);
    });
  });

  describe('getStabilityRating', () => {
    it('should return excellent for score >= 80', () => {
      expect(service.getStabilityRating(100)).toBe('excellent');
      expect(service.getStabilityRating(90)).toBe('excellent');
      expect(service.getStabilityRating(80)).toBe('excellent');
    });

    it('should return good for score 60-79', () => {
      expect(service.getStabilityRating(79)).toBe('good');
      expect(service.getStabilityRating(70)).toBe('good');
      expect(service.getStabilityRating(60)).toBe('good');
    });

    it('should return moderate for score 40-59', () => {
      expect(service.getStabilityRating(59)).toBe('moderate');
      expect(service.getStabilityRating(50)).toBe('moderate');
      expect(service.getStabilityRating(40)).toBe('moderate');
    });

    it('should return poor for score < 40', () => {
      expect(service.getStabilityRating(39)).toBe('poor');
      expect(service.getStabilityRating(20)).toBe('poor');
      expect(service.getStabilityRating(0)).toBe('poor');
    });

    it('should handle decimal scores', () => {
      expect(service.getStabilityRating(79.5)).toBe('good');
      expect(service.getStabilityRating(80.1)).toBe('excellent');
    });

    it('should handle edge case: negative score', () => {
      expect(service.getStabilityRating(-10)).toBe('poor');
    });

    it('should handle edge case: score > 100', () => {
      expect(service.getStabilityRating(150)).toBe('excellent');
    });
  });

  describe('getDataQuality', () => {
    it('should return high quality for sufficient samples', () => {
      expect(service.getDataQuality(200, 100)).toBe('high');
      expect(service.getDataQuality(100, 100)).toBe('high');
    });

    it('should return medium quality for partial samples', () => {
      expect(service.getDataQuality(80, 100)).toBe('medium');
      expect(service.getDataQuality(60, 100)).toBe('medium');
      expect(service.getDataQuality(50, 100)).toBe('medium');
    });

    it('should return low quality for insufficient samples', () => {
      expect(service.getDataQuality(49, 100)).toBe('low');
      expect(service.getDataQuality(30, 100)).toBe('low');
      expect(service.getDataQuality(0, 100)).toBe('low');
    });

    it('should handle zero expected minimum', () => {
      expect(service.getDataQuality(10, 0)).toBe('high');
    });

    it('should handle very large sample sizes', () => {
      expect(service.getDataQuality(10000, 1000)).toBe('high');
    });
  });

  describe('calculateStabilityTrend', () => {
    it('should detect improving trend (7d better than 30d)', () => {
      // 7-day score improved by >10 points
      expect(service.calculateStabilityTrend(80, 60)).toBe('improving');
      expect(service.calculateStabilityTrend(90, 70)).toBe('improving');
    });

    it('should detect declining trend (7d worse than 30d)', () => {
      // 7-day score declined by >10 points
      expect(service.calculateStabilityTrend(50, 70)).toBe('declining');
      expect(service.calculateStabilityTrend(40, 60)).toBe('declining');
    });

    it('should detect stable trend (difference <= 10 points)', () => {
      expect(service.calculateStabilityTrend(70, 70)).toBe('stable');
      expect(service.calculateStabilityTrend(70, 75)).toBe('stable');
      expect(service.calculateStabilityTrend(75, 70)).toBe('stable');
      expect(service.calculateStabilityTrend(70, 80)).toBe('stable');
      expect(service.calculateStabilityTrend(80, 70)).toBe('stable');
    });

    it('should handle boundary case: exactly 10 points difference', () => {
      expect(service.calculateStabilityTrend(70, 80)).toBe('stable');
      expect(service.calculateStabilityTrend(80, 70)).toBe('stable');
    });

    it('should handle extreme scores', () => {
      expect(service.calculateStabilityTrend(100, 50)).toBe('improving');
      expect(service.calculateStabilityTrend(0, 50)).toBe('declining');
    });
  });

  describe('calculateConfidenceScore', () => {
    it('should return 1.0 for high quality on both periods', () => {
      expect(service.calculateConfidenceScore('high', 'high')).toBe(1.0);
    });

    it('should return 0.7 for medium quality on both periods', () => {
      expect(service.calculateConfidenceScore('medium', 'medium')).toBe(0.7);
    });

    it('should return 0.3 for low quality on both periods', () => {
      expect(service.calculateConfidenceScore('low', 'low')).toBe(0.3);
    });

    it('should return average for mixed quality (high + medium)', () => {
      const result = service.calculateConfidenceScore('high', 'medium');
      expect(result).toBeCloseTo(0.85, 2);
    });

    it('should return average for mixed quality (high + low)', () => {
      const result = service.calculateConfidenceScore('high', 'low');
      expect(result).toBeCloseTo(0.65, 2);
    });

    it('should return average for mixed quality (medium + low)', () => {
      const result = service.calculateConfidenceScore('medium', 'low');
      expect(result).toBeCloseTo(0.5, 2);
    });

    it('should handle order independence', () => {
      expect(service.calculateConfidenceScore('high', 'medium'))
        .toBe(service.calculateConfidenceScore('medium', 'high'));
      expect(service.calculateConfidenceScore('low', 'high'))
        .toBe(service.calculateConfidenceScore('high', 'low'));
    });
  });

  describe('formatPercentage', () => {
    it('should format with default 2 decimal places', () => {
      expect(service.formatPercentage(0.1234)).toBe('12.34%');
      expect(service.formatPercentage(0.5)).toBe('50.00%');
    });

    it('should format with custom decimal places', () => {
      expect(service.formatPercentage(0.123456, 4)).toBe('12.3456%');
      expect(service.formatPercentage(0.5, 0)).toBe('50%');
    });

    it('should handle very small percentages', () => {
      expect(service.formatPercentage(0.0001, 4)).toBe('0.0100%');
    });

    it('should handle very large percentages', () => {
      expect(service.formatPercentage(1.5, 2)).toBe('150.00%');
    });

    it('should handle zero', () => {
      expect(service.formatPercentage(0)).toBe('0.00%');
    });

    it('should handle negative percentages', () => {
      expect(service.formatPercentage(-0.15)).toBe('-15.00%');
    });

    it('should round correctly', () => {
      expect(service.formatPercentage(0.12345, 2)).toBe('12.35%');
      expect(service.formatPercentage(0.12344, 2)).toBe('12.34%');
    });
  });

  describe('Real-world scenarios', () => {
    describe('Stable spread scenario', () => {
      it('should analyze very stable spread data correctly', () => {
        // Spread fluctuates minimally around 0.15%
        const spreads = [0.15, 0.151, 0.149, 0.15, 0.150, 0.148, 0.152];

        const avg = service.calculateAverage(spreads);
        const stdDev = service.calculateStandardDeviation(spreads);
        const score = service.calculateStabilityScore(avg!, stdDev!);
        const rating = service.getStabilityRating(score);

        expect(avg).toBeCloseTo(0.15, 3);
        expect(stdDev).toBeLessThan(0.002);
        expect(rating).toBe('excellent');
      });
    });

    describe('Volatile spread scenario', () => {
      it('should analyze highly volatile spread data correctly', () => {
        // Spread varies dramatically
        const spreads = [0.1, 0.5, 0.05, 0.8, 0.15, 0.6, 0.2];

        const avg = service.calculateAverage(spreads);
        const stdDev = service.calculateStandardDeviation(spreads);
        const score = service.calculateStabilityScore(avg!, stdDev!);
        const rating = service.getStabilityRating(score);

        expect(avg).toBeGreaterThan(0.2);
        expect(stdDev).toBeGreaterThan(0.2);
        expect(rating).toMatch(/poor|moderate/);
      });
    });

    describe('7-day hourly data scenario', () => {
      it('should handle realistic 7-day hourly spread data', () => {
        // Simulate 7 days * 24 hours = 168 data points
        // With daily sinusoidal pattern (higher spreads at certain times)
        const spreads = Array.from({ length: 168 }, (_, i) =>
          0.15 + (Math.sin(i / 24 * Math.PI) * 0.02)
        );

        const avg = service.calculateAverage(spreads);
        const stdDev = service.calculateStandardDeviation(spreads);
        const score = service.calculateStabilityScore(avg!, stdDev!);
        const quality = service.getDataQuality(spreads.length, 100);

        expect(avg).toBeCloseTo(0.15, 2);
        expect(stdDev).toBeGreaterThan(0);
        expect(score).toBeGreaterThan(50);
        expect(quality).toBe('high');
      });
    });

    describe('30-day hourly data scenario', () => {
      it('should handle realistic 30-day hourly spread data', () => {
        // Simulate 30 days * 24 hours = 720 data points
        const spreads = Array.from({ length: 720 }, (_, i) =>
          0.15 + (Math.random() * 0.04 - 0.02) // Random 0.02% variation
        );

        const avg = service.calculateAverage(spreads);
        const stdDev = service.calculateStandardDeviation(spreads);
        const score = service.calculateStabilityScore(avg!, stdDev!);
        const quality = service.getDataQuality(spreads.length, 400);

        expect(avg).toBeCloseTo(0.15, 1);
        expect(stdDev).toBeGreaterThan(0);
        expect(score).toBeGreaterThan(0);
        expect(quality).toBe('high');
      });
    });

    describe('Insufficient data scenario', () => {
      it('should handle insufficient sample size correctly', () => {
        // Only 30 data points instead of expected 168 (7 days hourly)
        const spreads = Array.from({ length: 30 }, () => 0.15);

        const quality = service.getDataQuality(spreads.length, 168);

        expect(quality).toBe('low');
      });
    });

    describe('Trend detection scenario', () => {
      it('should detect improving stability trend', () => {
        // 7-day: very stable (score ~95)
        const spreads7d = Array.from({ length: 50 }, () => 0.15 + (Math.random() * 0.002));
        const avg7d = service.calculateAverage(spreads7d)!;
        const stdDev7d = service.calculateStandardDeviation(spreads7d)!;
        const score7d = service.calculateStabilityScore(avg7d, stdDev7d);

        // 30-day: more volatile (score ~60)
        const spreads30d = Array.from({ length: 200 }, () => 0.15 + (Math.random() * 0.08));
        const avg30d = service.calculateAverage(spreads30d)!;
        const stdDev30d = service.calculateStandardDeviation(spreads30d)!;
        const score30d = service.calculateStabilityScore(avg30d, stdDev30d);

        const trend = service.calculateStabilityTrend(score7d, score30d);

        expect(score7d).toBeGreaterThan(score30d);
        expect(trend).toBe('improving');
      });
    });

    describe('Complete metrics calculation', () => {
      it('should calculate all metrics for a complete Phase 2 analysis', () => {
        const spreads = [0.12, 0.15, 0.14, 0.16, 0.13, 0.15, 0.14];

        const avg = service.calculateAverage(spreads);
        const stdDev = service.calculateStandardDeviation(spreads);
        const score = service.calculateStabilityScore(avg!, stdDev!);
        const rating = service.getStabilityRating(score);
        const quality = service.getDataQuality(spreads.length, 100);
        const avgFormatted = service.formatPercentage(avg!, 2);
        const stdDevFormatted = service.formatPercentage(stdDev!, 2);

        expect(avg).toBeDefined();
        expect(stdDev).toBeDefined();
        expect(score).toBeGreaterThanOrEqual(0);
        expect(score).toBeLessThanOrEqual(100);
        expect(rating).toMatch(/excellent|good|moderate|poor/);
        expect(quality).toBe('low'); // Only 7 samples vs 100 expected
        expect(avgFormatted).toContain('%');
        expect(stdDevFormatted).toContain('%');
      });
    });
  });
});

import { Injectable } from '@angular/core';
import { HttpClient, HttpParams } from '@angular/common/http';
import { Observable } from 'rxjs';
import { environment } from '../../environments/environment';

export interface Strategy {
  id: string;
  name: string;
  description?: string;
  entryFilters: IndicatorFilter[];
  exitFilters: IndicatorFilter[];
  riskManagement?: RiskManagementRules;
  isPublic: boolean;
  timesUsed: number;
  averageReturn?: number;
  createdAt: Date;
  updatedAt: Date;
  user?: {
    id: string;
    name: string;
    email: string;
  };
}

export interface IndicatorFilter {
  id: string;
  indicator: 'RSI' | 'MACD' | 'SMA' | 'EMA' | 'BB' | 'Stochastic' | 'ADX' | 'Volume';
  operator: '>' | '<' | '>=' | '<=' | '=';
  value: number;
  period?: number;
  action: 'entry' | 'exit';
  bandType?: 'upper' | 'lower' | 'middle';
  enabled: boolean;
}

export interface RiskManagementRules {
  maxPositionSize?: number;
  stopLoss?: number;
  takeProfit?: number;
  maxDailyLoss?: number;
  maxConcurrentTrades?: number;
  riskPerTrade?: number;
}

export interface StrategyBacktest {
  id: string;
  strategyId: string;
  symbol: string;
  timeframe: string;
  startDate: Date;
  endDate: Date;
  initialBalance: number;
  results: {
    totalPnL: number;
    winRate: number;
    maxDrawdown: number;
    sharpeRatio: number;
    profitFactor: number;
    totalTrades: number;
  };
  createdAt: Date;
}

export interface StrategyPerformance {
  strategyId: string;
  symbol: string;
  period: string;
  totalReturn: number;
  winRate: number;
  maxDrawdown: number;
  volatility: number;
  sharpeRatio: number;
  calmarRatio: number;
  trades: number;
  avgTradeReturn: number;
  bestTrade: number;
  worstTrade: number;
}

@Injectable({
  providedIn: 'root'
})
export class StrategyService {
  private apiUrl = `${environment.apiUrl}/trading/strategies`;

  constructor(private http: HttpClient) {}

  // Strategy CRUD Operations
  createStrategy(strategy: Partial<Strategy>): Observable<{ success: boolean; strategy: Strategy }> {
    return this.http.post<any>(this.apiUrl, strategy);
  }

  getStrategies(includePublic = false): Observable<Strategy[]> {
    const params = new HttpParams().set('includePublic', includePublic.toString());
    return this.http.get<Strategy[]>(this.apiUrl, { params });
  }

  getStrategy(id: string): Observable<Strategy> {
    const params = new HttpParams().set('id', id);
    return this.http.get<Strategy>(this.apiUrl, { params });
  }

  updateStrategy(strategy: Strategy): Observable<{ success: boolean; strategy: Strategy }> {
    return this.http.put<any>(this.apiUrl, strategy);
  }

  deleteStrategy(id: string): Observable<{ success: boolean }> {
    const params = new HttpParams().set('id', id);
    return this.http.delete<any>(this.apiUrl, { params });
  }

  // Strategy Templates
  getStrategyTemplates(): Observable<Strategy[]> {
    return this.http.get<Strategy[]>(`${this.apiUrl}/templates`);
  }

  createStrategyFromTemplate(templateId: string, customizations: Partial<Strategy>): Observable<Strategy> {
    return this.http.post<Strategy>(`${this.apiUrl}/from-template`, {
      templateId,
      customizations
    });
  }

  // Strategy Validation
  validateStrategy(strategy: Partial<Strategy>): Observable<{
    valid: boolean;
    warnings: string[];
    errors: string[];
    suggestions: string[];
  }> {
    return this.http.post<any>(`${this.apiUrl}/validate`, strategy);
  }

  // Backtesting
  backtestStrategy(request: {
    strategyId: string;
    symbol: string;
    timeframe: string;
    startDate: string;
    endDate: string;
    initialBalance: number;
    gridConfig?: any;
  }): Observable<StrategyBacktest> {
    return this.http.post<StrategyBacktest>(`${this.apiUrl}/backtest`, request);
  }

  getStrategyBacktests(strategyId: string): Observable<StrategyBacktest[]> {
    const params = new HttpParams().set('strategyId', strategyId);
    return this.http.get<StrategyBacktest[]>(`${this.apiUrl}/backtests`, { params });
  }

  // Performance Analytics
  getStrategyPerformance(strategyId: string, period = '30d'): Observable<StrategyPerformance> {
    const params = new HttpParams()
      .set('strategyId', strategyId)
      .set('period', period);

    return this.http.get<StrategyPerformance>(`${this.apiUrl}/performance`, { params });
  }

  compareStrategies(strategyIds: string[], metrics: string[] = ['totalReturn', 'winRate', 'maxDrawdown']): Observable<{
    comparison: any[];
    summary: any;
  }> {
    return this.http.post<any>(`${this.apiUrl}/compare`, {
      strategyIds,
      metrics
    });
  }

  // Market Scanning
  scanMarket(strategyId: string, symbols: string[]): Observable<{
    matches: {
      symbol: string;
      score: number;
      signals: any[];
      lastPrice: number;
      recommendation: 'BUY' | 'SELL' | 'HOLD';
    }[];
  }> {
    return this.http.post<any>(`${this.apiUrl}/scan`, {
      strategyId,
      symbols
    });
  }

  // Indicator Utilities
  getAvailableIndicators(): Observable<{
    name: string;
    displayName: string;
    description: string;
    parameters: {
      name: string;
      type: string;
      default: any;
      min?: number;
      max?: number;
      options?: string[];
    }[];
  }[]> {
    return this.http.get<any[]>(`${this.apiUrl}/indicators`);
  }

  calculateIndicator(params: {
    indicator: string;
    symbol: string;
    timeframe: string;
    period?: number;
    parameters?: any;
  }): Observable<{
    values: number[];
    timestamps: string[];
    current: number;
  }> {
    return this.http.post<any>(`${this.apiUrl}/calculate-indicator`, params);
  }

  // Strategy Optimization
  optimizeStrategy(params: {
    baseStrategyId: string;
    symbol: string;
    timeframe: string;
    startDate: string;
    endDate: string;
    optimizationTarget: 'profit' | 'winRate' | 'sharpeRatio' | 'profitFactor';
    parameters: {
      name: string;
      min: number;
      max: number;
      step: number;
    }[];
  }): Observable<{
    optimizationId: string;
    status: 'running' | 'completed' | 'failed';
    progress?: number;
    bestParameters?: any;
    bestResult?: any;
  }> {
    return this.http.post<any>(`${this.apiUrl}/optimize`, params);
  }

  getOptimizationStatus(optimizationId: string): Observable<any> {
    return this.http.get<any>(`${this.apiUrl}/optimization/${optimizationId}`);
  }

  // Strategy Sharing
  shareStrategy(strategyId: string): Observable<{ shareUrl: string; shareCode: string }> {
    return this.http.post<any>(`${this.apiUrl}/${strategyId}/share`, {});
  }

  importSharedStrategy(shareCode: string): Observable<Strategy> {
    return this.http.post<Strategy>(`${this.apiUrl}/import-shared`, { shareCode });
  }

  // Community Features
  getPopularStrategies(limit = 10): Observable<Strategy[]> {
    const params = new HttpParams().set('limit', limit.toString());
    return this.http.get<Strategy[]>(`${this.apiUrl}/popular`, { params });
  }

  rateStrategy(strategyId: string, rating: number, review?: string): Observable<{ success: boolean }> {
    return this.http.post<any>(`${this.apiUrl}/${strategyId}/rate`, {
      rating,
      review
    });
  }

  getStrategyRatings(strategyId: string): Observable<{
    averageRating: number;
    totalRatings: number;
    reviews: {
      rating: number;
      review: string;
      user: string;
      createdAt: Date;
    }[];
  }> {
    return this.http.get<any>(`${this.apiUrl}/${strategyId}/ratings`);
  }

  // AI-Powered Features
  generateStrategyIdeas(params: {
    symbol: string;
    marketCondition: 'trending' | 'sideways' | 'volatile';
    riskTolerance: 'low' | 'medium' | 'high';
    timeframe: string;
  }): Observable<{
    suggestions: {
      name: string;
      description: string;
      entryConditions: string[];
      exitConditions: string[];
      expectedWinRate: number;
      expectedReturn: number;
      riskLevel: string;
    }[];
  }> {
    return this.http.post<any>(`${this.apiUrl}/ai/generate-ideas`, params);
  }

  improveStrategy(strategyId: string, feedback: string): Observable<{
    suggestions: {
      type: 'add_filter' | 'modify_filter' | 'add_risk_rule' | 'adjust_parameters';
      description: string;
      implementation: any;
      expectedImprovement: string;
    }[];
  }> {
    return this.http.post<any>(`${this.apiUrl}/${strategyId}/ai/improve`, {
      feedback
    });
  }

  // Export/Import
  exportStrategy(strategyId: string, format: 'json' | 'yaml' | 'pine'): Observable<Blob> {
    const params = new HttpParams().set('format', format);
    return this.http.get(`${this.apiUrl}/${strategyId}/export`, {
      params,
      responseType: 'blob'
    });
  }

  importStrategy(file: File, format: 'json' | 'yaml' | 'pine'): Observable<Strategy> {
    const formData = new FormData();
    formData.append('strategy', file);
    formData.append('format', format);

    return this.http.post<Strategy>(`${this.apiUrl}/import`, formData);
  }
}

import { TestBed } from '@angular/core/testing';
import { HttpClientTestingModule, HttpTestingController } from '@angular/common/http/testing';
import { SymbolInfoService, SymbolInfo } from './symbol-info.service';
import { AuthService } from './auth.service';
import { signal } from '@angular/core';

describe('SymbolInfoService', () => {
  let service: SymbolInfoService;
  let httpMock: HttpTestingController;
  let authServiceMock: jasmine.SpyObj<AuthService>;

  const mockSymbolInfo: SymbolInfo = {
    symbol: 'MERL-USDT',
    exchange: 'BINGX',
    minOrderQty: 5.292,
    minOrderValue: 10,
    qtyStep: 0.001,
    pricePrecision: 2,
    qtyPrecision: 3,
    maxOrderQty: 10000,
    maxLeverage: 125
  };

  beforeEach(() => {
    // Create mock AuthService with signal
    authServiceMock = jasmine.createSpyObj('AuthService', [], {
      authState: signal({ token: 'test-token', user: null })
    });

    TestBed.configureTestingModule({
      imports: [HttpClientTestingModule],
      providers: [
        SymbolInfoService,
        { provide: AuthService, useValue: authServiceMock }
      ]
    });

    service = TestBed.inject(SymbolInfoService);
    httpMock = TestBed.inject(HttpTestingController);
  });

  afterEach(() => {
    httpMock.verify();
  });

  describe('getSymbolInfo', () => {
    it('should fetch symbol info from API', (done) => {
      service.getSymbolInfo('BINGX', 'MERL-USDT').subscribe(result => {
        expect(result).toEqual(mockSymbolInfo);
        done();
      });

      const req = httpMock.expectOne(request =>
        request.url.includes('/api/exchange/symbol-info') &&
        request.params.get('exchange') === 'BINGX' &&
        request.params.get('symbol') === 'MERL-USDT'
      );
      expect(req.request.method).toBe('GET');
      expect(req.request.headers.get('Authorization')).toBe('Bearer test-token');

      req.flush({ success: true, data: mockSymbolInfo });
    });

    it('should cache symbol info after first fetch', (done) => {
      // First call - should hit API
      service.getSymbolInfo('BINGX', 'MERL-USDT').subscribe(result => {
        expect(result).toEqual(mockSymbolInfo);

        // Second call - should use cache (no HTTP request)
        service.getSymbolInfo('BINGX', 'MERL-USDT').subscribe(cachedResult => {
          expect(cachedResult).toEqual(mockSymbolInfo);
          done();
        });

        // Verify no second HTTP request was made
        httpMock.expectNone(request =>
          request.url.includes('/api/exchange/symbol-info')
        );
      });

      const req = httpMock.expectOne(request =>
        request.url.includes('/api/exchange/symbol-info')
      );
      req.flush({ success: true, data: mockSymbolInfo });
    });

    it('should return null if no token available', (done) => {
      // Update mock to return no token
      Object.defineProperty(authServiceMock, 'authState', {
        value: signal({ token: null, user: null }),
        writable: true
      });

      service.getSymbolInfo('BINGX', 'MERL-USDT').subscribe(result => {
        expect(result).toBeNull();
        done();
      });

      httpMock.expectNone(request =>
        request.url.includes('/api/exchange/symbol-info')
      );
    });

    it('should handle API errors gracefully', (done) => {
      service.getSymbolInfo('BINGX', 'INVALID-SYMBOL').subscribe(result => {
        expect(result).toBeNull();
        done();
      });

      const req = httpMock.expectOne(request =>
        request.url.includes('/api/exchange/symbol-info')
      );
      req.flush({ success: false, error: 'Symbol not found' }, { status: 404, statusText: 'Not Found' });
    });
  });

  describe('validateOrderQuantity', () => {
    it('should validate successfully when quantity per part meets minimum', () => {
      const result = service.validateOrderQuantity(mockSymbolInfo, 30, 5);
      expect(result.valid).toBe(true);
      expect(result.error).toBeUndefined();
      expect(result.suggestion).toBeUndefined();
    });

    it('should fail when quantity per part is below minimum', () => {
      const result = service.validateOrderQuantity(mockSymbolInfo, 10, 5);
      expect(result.valid).toBe(false);
      expect(result.error).toContain('below minimum');
      expect(result.suggestion).toContain('26.460');
    });

    it('should fail when single order is below minimum', () => {
      const result = service.validateOrderQuantity(mockSymbolInfo, 5, 1);
      expect(result.valid).toBe(false);
      expect(result.error).toContain('below minimum');
      expect(result.suggestion).toContain('5.292');
      expect(result.suggestion).not.toContain('reduce graduated parts');
    });

    it('should suggest reducing parts when possible', () => {
      const result = service.validateOrderQuantity(mockSymbolInfo, 15, 3);
      expect(result.valid).toBe(false);
      expect(result.suggestion).toContain('reduce graduated parts to 2');
    });

    it('should not suggest reducing parts below 1', () => {
      const result = service.validateOrderQuantity(mockSymbolInfo, 3, 1);
      expect(result.valid).toBe(false);
      expect(result.suggestion).not.toContain('reduce graduated parts');
    });

    it('should validate exactly at minimum quantity', () => {
      const result = service.validateOrderQuantity(mockSymbolInfo, 5.292, 1);
      expect(result.valid).toBe(true);
    });

    it('should validate with multiple parts at minimum per part', () => {
      const result = service.validateOrderQuantity(mockSymbolInfo, 26.46, 5);
      expect(result.valid).toBe(true);
    });

    it('should fail when quantity per part exceeds maximum', () => {
      const result = service.validateOrderQuantity(mockSymbolInfo, 50000, 1);
      expect(result.valid).toBe(false);
      expect(result.error).toContain('exceeds maximum');
    });

    it('should fail when quantity is not a multiple of step size', () => {
      const result = service.validateOrderQuantity(mockSymbolInfo, 15.2345, 3);
      expect(result.valid).toBe(false);
      expect(result.error).toContain('multiple of');
    });

    it('should return error when symbolInfo is null', () => {
      const result = service.validateOrderQuantity(null as any, 10, 1);
      expect(result.valid).toBe(false);
      expect(result.error).toBe('Symbol information not available');
    });

    it('should allow small floating point errors in step validation', () => {
      // 5.292 * 5 = 26.46, which should be valid despite potential floating point issues
      const result = service.validateOrderQuantity(mockSymbolInfo, 26.46, 5);
      expect(result.valid).toBe(true);
    });
  });

  describe('clearCache', () => {
    it('should clear cached symbol info', (done) => {
      // First call - cache data
      service.getSymbolInfo('BINGX', 'MERL-USDT').subscribe(() => {
        // Clear cache
        service.clearCache();

        // Second call - should hit API again
        service.getSymbolInfo('BINGX', 'MERL-USDT').subscribe(result => {
          expect(result).toEqual(mockSymbolInfo);
          done();
        });

        const req = httpMock.expectOne(request =>
          request.url.includes('/api/exchange/symbol-info')
        );
        req.flush({ success: true, data: mockSymbolInfo });
      });

      const req = httpMock.expectOne(request =>
        request.url.includes('/api/exchange/symbol-info')
      );
      req.flush({ success: true, data: mockSymbolInfo });
    });
  });

  describe('Edge Cases', () => {
    it('should handle zero quantity', () => {
      const result = service.validateOrderQuantity(mockSymbolInfo, 0, 1);
      expect(result.valid).toBe(false);
    });

    it('should handle very large graduated parts', () => {
      const result = service.validateOrderQuantity(mockSymbolInfo, 100, 20);
      expect(result.valid).toBe(false);
      expect(result.error).toContain('below minimum');
    });

    it('should handle quantity exactly at step boundary', () => {
      // 5.292 is exactly minOrderQty and should be at step boundary
