    describe('Error Message Quality', () => {
      it('should provide actionable error messages with proper formatting', () => {
        const result = service.validateOrderQuantity(gateioSymbol, 3, 5);
        expect(result.valid).toBe(false);
        expect(result.error).toMatch(/Each order part \(\d+(\.\d+)?\) is below minimum \(\d+\)/);
        expect(result.suggestion).toMatch(/Increase total quantity to at least \d+ or reduce graduated parts to \d+/);
      });

      it('should format small numbers with appropriate precision', () => {
        const result = service.validateOrderQuantity(bybitSymbol, 0.001, 5);
        expect(result.error).toContain('(0.0002)'); // Should show appropriate decimal places
        expect(result.suggestion).toContain('0.005'); // Should show appropriate precision
      });

      it('should provide clear step size violation messages', () => {
        const result = service.validateOrderQuantity(mockSymbolInfo, 15.2345, 3);
        expect(result.valid).toBe(false);
        expect(result.error).toContain('must be a multiple of 0.001');
        expect(result.suggestion).toMatch(/Adjust total quantity to \d+\.\d+/);
      });
    });

    describe('Boundary Conditions and Edge Cases', () => {
      it('should handle zero quantity gracefully', () => {
        const result = service.validateOrderQuantity(bybitSymbol, 0, 5);
        expect(result.valid).toBe(false);
        expect(result.error).toContain('is below minimum');
      });

      it('should handle zero graduated parts (division by zero)', () => {
        const result = service.validateOrderQuantity(bybitSymbol, 1, 0);
        expect(result.valid).toBe(false);
        // Should handle gracefully without throwing
      });

      it('should handle very large quantities without precision loss', () => {
        const result = service.validateOrderQuantity(mexcSymbol, 1000000, 1);
        expect(result.valid).toBe(true);
      });

      it('should handle symbols without maximum order quantity', () => {
        const symbolWithoutMax: SymbolInfo = {
          ...bybitSymbol,
          maxOrderQty: undefined
        };

        const result = service.validateOrderQuantity(symbolWithoutMax, 10000, 2);
        expect(result.valid).toBe(true);
        // Should not fail when no max quantity is defined
      });

      it('should handle negative quantities', () => {
        const result = service.validateOrderQuantity(bybitSymbol, -1, 5);
        expect(result.valid).toBe(false);
      });

      it('should handle extremely large graduated parts', () => {
        const result = service.validateOrderQuantity(bybitSymbol, 1, 10000);
        expect(result.valid).toBe(false);
        expect(result.error).toContain('is below minimum');
      });
    });
  });
});

import { Injectable, inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, of } from 'rxjs';
import { map, catchError } from 'rxjs/operators';
import { getEndpointUrl } from '../config/app.config';

export interface SymbolInfo {
  symbol: string;
  exchange: string;
  minOrderQty: number;
  minOrderValue?: number; // Minimum notional value in USDT
  qtyStep: number; // Quantity step size
  pricePrecision: number;
  qtyPrecision: number;
  maxOrderQty?: number;
  maxLeverage?: number;
}

@Injectable({
  providedIn: 'root'
})
export class SymbolInfoService {
  private http = inject(HttpClient);
  private cache = new Map<string, SymbolInfo>();

  /**
   * Get symbol information from backend API
   * Includes caching to avoid unnecessary API calls
   */
  getSymbolInfo(exchange: string, symbol: string): Observable<SymbolInfo | null> {
    const cacheKey = `${exchange}:${symbol}`;

    // Check cache first
    if (this.cache.has(cacheKey)) {
      return of(this.cache.get(cacheKey)!);
    }

    const url = `${getEndpointUrl('exchange', 'symbolInfo')}?exchange=${exchange}&symbol=${symbol}`;

    return this.http.get<any>(url).pipe(
      map(response => {
        if (response?.success && response?.data) {
          const symbolInfo = response.data as SymbolInfo;
          // Store in cache
          this.cache.set(cacheKey, symbolInfo);
          return symbolInfo;
        }
        return null;
      }),
      catchError(() => {
        return of(null);
      })
    );
  }

  /**
   * Clear cache (useful when switching symbols or exchanges)
   */
  clearCache(): void {
    this.cache.clear();
  }

  /**
   * Validate order quantity against minimum requirements and graduated entry parts
   *
   * @param symbolInfo Symbol trading rules
   * @param totalQuantity Total order quantity
   * @param graduatedParts Number of parts to split the order into
   * @returns Validation result with error message if invalid
   */
  validateOrderQuantity(
    symbolInfo: SymbolInfo,
    totalQuantity: number,
    graduatedParts = 1
  ): { valid: boolean; error?: string; suggestion?: string } {
    if (!symbolInfo) {
      return { valid: false, error: 'Symbol information not available' };
    }

    // Calculate quantity per part
    const qtyPerPart = totalQuantity / graduatedParts;

    // Check if quantity per part meets minimum requirement
    if (qtyPerPart < symbolInfo.minOrderQty) {
      const minTotalQty = symbolInfo.minOrderQty * graduatedParts;
      const maxParts = Math.floor(totalQuantity / symbolInfo.minOrderQty);

      // Extract coin symbol for clearer suggestions
      const coinSymbol = this.extractCoinSymbol(symbolInfo.symbol);
      
      // Different suggestions based on whether we can reduce parts
      let suggestion: string;
      if (graduatedParts === 1 || maxParts === 0) {
        // Can't reduce parts further, only option is to increase quantity
        suggestion = `Increase total quantity to at least ${minTotalQty.toFixed(symbolInfo.qtyPrecision)} ${coinSymbol}`;
      } else {
        // Can reduce parts or increase quantity
        suggestion = `Increase total quantity to at least ${minTotalQty.toFixed(symbolInfo.qtyPrecision)} ${coinSymbol} or reduce graduated parts to ${maxParts}`;
      }
      
      return {
        valid: false,
        error: `Each order part (${qtyPerPart.toFixed(symbolInfo.qtyPrecision)} ${coinSymbol}) is below minimum (${symbolInfo.minOrderQty} ${coinSymbol})`,
        suggestion
      };
    }

    // Check maximum quantity if defined
    if (symbolInfo.maxOrderQty && qtyPerPart > symbolInfo.maxOrderQty) {
      const coinSymbol = this.extractCoinSymbol(symbolInfo.symbol);
      return {
        valid: false,
        error: `Each order part (${qtyPerPart.toFixed(symbolInfo.qtyPrecision)} ${coinSymbol}) exceeds maximum (${symbolInfo.maxOrderQty} ${coinSymbol})`,
        suggestion: `Decrease quantity or increase graduated parts`
      };
    }

    // Check quantity step
    // Round to avoid floating point precision issues
    const roundedQtyPerPart = Math.round(qtyPerPart / symbolInfo.qtyStep) * symbolInfo.qtyStep;
    const difference = Math.abs(qtyPerPart - roundedQtyPerPart);

    // If difference is significant (more than 0.1% of step size), quantity is not valid
    if (difference > symbolInfo.qtyStep * 0.001) {
      const coinSymbol = this.extractCoinSymbol(symbolInfo.symbol);
      return {
        valid: false,
        error: `Quantity per part must be a multiple of ${symbolInfo.qtyStep} ${coinSymbol}`,
        suggestion: `Adjust total quantity to ${(roundedQtyPerPart * graduatedParts).toFixed(symbolInfo.qtyPrecision)} ${coinSymbol}`
      };
    }

    return { valid: true };
  }

  /**
   * Extract coin symbol from trading pair for clearer error messages
   * Examples: 
   * - FUSDT -> FUSDT
   * - F-USDT -> F
   * - BTC_USDT -> BTC
   * - BTCUSDT -> BTC
   */
  private extractCoinSymbol(symbol: string): string {
    // Handle hyphenated symbols (BingX format)
    if (symbol.includes('-')) {
      return symbol.split('-')[0];
    }
    
    // Handle underscore symbols (Gate.io format)  
    if (symbol.includes('_')) {
      return symbol.split('_')[0];
    }
    
    // Handle concatenated symbols (Bybit/MEXC format)
    // Remove common quote currencies
    const quoteCurrencies = ['USDT', 'USDC', 'USD', 'BTC', 'ETH'];
    for (const quote of quoteCurrencies) {
      if (symbol.endsWith(quote)) {
        const base = symbol.slice(0, -quote.length);
        if (base.length > 0) {
          return base;
        }
      }
    }
    
    // If no pattern matches, return the original symbol
    return symbol;
  }
}

import { TestBed } from '@angular/core/testing';
import { HttpClientTestingModule, HttpTestingController } from '@angular/common/http/testing';
import { TradeHistoryService } from './trade-history.service';
import {
  TradeHistoryRecord,
  TradeHistoryResponse,
  TradeHistoryQueryParams
} from '../models/trade-history.model';
import { buildApiUrl } from '../config/app.config';

describe('TradeHistoryService', () => {
  let service: TradeHistoryService;
  let httpMock: HttpTestingController;

  const mockTradeRecord: TradeHistoryRecord = {
    id: 'trade-1',
    symbol: 'BTCUSDT',
    exchange: 'bybit',
    executedAt: '2025-10-01T10:00:00Z',
    closedAt: '2025-10-01T12:00:00Z',
    positionSizeUsdt: 1000,
    fundingEarned: 5.5,
    realizedPnl: 25.75,
    entryPrice: 50000,
    exitPrice: 50500,
    leverage: 5,
    quantity: 0.1,
    status: 'CLOSED',
    positionType: 'long',
    entryFee: 0.5,
    exitFee: 0.5,
    duration: 7200,
    roi: 2.5
  };

  const mockTradeResponse: TradeHistoryResponse = {
    success: true,
    data: [mockTradeRecord],
    count: 1
  };

  beforeEach(() => {
    TestBed.configureTestingModule({
      imports: [HttpClientTestingModule],
      providers: [TradeHistoryService]
    });
    service = TestBed.inject(TradeHistoryService);
    httpMock = TestBed.inject(HttpTestingController);
  });

  afterEach(() => {
    httpMock.verify();
  });

  it('should be created', () => {
    expect(service).toBeTruthy();
  });

  describe('getTradeHistory', () => {
    it('should fetch trade history successfully', (done) => {
      service.getTradeHistory().subscribe(response => {
        expect(response.success).toBe(true);
        expect(response.data.length).toBe(1);
        expect(response.data[0].symbol).toBe('BTCUSDT');
        expect(service.trades().length).toBe(1);
        expect(service.totalCount()).toBe(1);
        expect(service.isLoading()).toBe(false);
        done();
      });

      const req = httpMock.expectOne(buildApiUrl('/trading/history'));
      expect(req.request.method).toBe('GET');
      req.flush(mockTradeResponse);
    });

    it('should set loading state correctly during request', () => {
      expect(service.isLoading()).toBe(false);

      service.getTradeHistory().subscribe();

      expect(service.isLoading()).toBe(true);

      const req = httpMock.expectOne(buildApiUrl('/trading/history'));
      req.flush(mockTradeResponse);

      // After response, loading should be false
      expect(service.isLoading()).toBe(false);
    });

    it('should build URL with query parameters correctly', (done) => {
      const params: TradeHistoryQueryParams = {
        symbol: 'BTCUSDT',
        exchange: 'bybit',
        limit: 50,
        status: 'CLOSED'
      };

      service.getTradeHistory(params).subscribe(() => done());

      const req = httpMock.expectOne(req =>
        req.url.includes('/trading/history') &&
        req.url.includes('symbol=BTCUSDT') &&
        req.url.includes('exchange=bybit') &&
        req.url.includes('limit=50') &&
        req.url.includes('status=CLOSED')
      );
      expect(req.request.method).toBe('GET');
      req.flush(mockTradeResponse);
    });

    it('should handle HTTP errors gracefully', (done) => {
      service.getTradeHistory().subscribe({
        error: (error) => {
          expect(error.message).toContain('Server error');
          expect(service.error()).toContain('Server error');
          expect(service.isLoading()).toBe(false);
          done();
        }
      });

      const req = httpMock.expectOne(buildApiUrl('/trading/history'));
      req.flush({ message: 'Internal server error' }, { status: 500, statusText: 'Server Error' });
    });

    it('should handle 401 unauthorized error', (done) => {
      service.getTradeHistory().subscribe({
        error: (error) => {
          expect(error.message).toContain('Unauthorized');
          done();
        }
      });

      const req = httpMock.expectOne(buildApiUrl('/trading/history'));
      req.flush({ message: 'Unauthorized' }, { status: 401, statusText: 'Unauthorized' });
    });

    it('should handle 403 forbidden error', (done) => {
      service.getTradeHistory().subscribe({
        error: (error) => {
          expect(error.message).toContain('forbidden');
          done();
        }
      });

      const req = httpMock.expectOne(buildApiUrl('/trading/history'));
      req.flush({ message: 'Forbidden' }, { status: 403, statusText: 'Forbidden' });
    });

    it('should handle network errors', (done) => {
      service.getTradeHistory().subscribe({
        error: (error) => {
          expect(error.message).toContain('Unable to connect');
          done();
        }
      });

      const req = httpMock.expectOne(buildApiUrl('/trading/history'));
      req.error(new ProgressEvent('error'), { status: 0 });
    });
  });

  describe('getTradeHistoryBySymbol', () => {
    it('should fetch trades for specific symbol and exchange', (done) => {
      service.getTradeHistoryBySymbol('BTCUSDT', 'bybit', 100).subscribe(() => done());

      const req = httpMock.expectOne(req =>
        req.url.includes('symbol=BTCUSDT') &&
        req.url.includes('exchange=bybit') &&
        req.url.includes('limit=100')
      );
      req.flush(mockTradeResponse);
    });
  });

