  describe('Computed Signals', () => {
    beforeEach(() => {
      const response: TradeHistoryResponse = {
        success: true,
        data: [
          { ...mockTradeRecord, status: 'OPEN', id: 'trade-1' },
          { ...mockTradeRecord, status: 'CLOSED', id: 'trade-2' },
          { ...mockTradeRecord, status: 'CLOSED', id: 'trade-3' }
        ],
        count: 3
      };

      service.getTradeHistory().subscribe();
      const req = httpMock.expectOne(buildApiUrl('/trading/history'));
      req.flush(response);
    });

    it('should compute hasTrades correctly', () => {
      expect(service.hasTrades()).toBe(true);
    });

    it('should filter open trades', () => {
      expect(service.openTrades().length).toBe(1);
      expect(service.openTrades()[0].status).toBe('OPEN');
    });

    it('should filter closed trades', () => {
      expect(service.closedTrades().length).toBe(2);
      expect(service.closedTrades().every(t => t.status === 'CLOSED')).toBe(true);
    });
  });

  describe('Statistics Calculation', () => {
    it('should calculate statistics correctly', () => {
      const trades: TradeHistoryRecord[] = [
        { ...mockTradeRecord, realizedPnl: 50, fundingEarned: 5, status: 'CLOSED', entryFee: 1, exitFee: 1 },
        { ...mockTradeRecord, realizedPnl: -20, fundingEarned: 3, status: 'CLOSED', entryFee: 1, exitFee: 1 },
        { ...mockTradeRecord, realizedPnl: 30, fundingEarned: 4, status: 'CLOSED', entryFee: 1, exitFee: 1 }
      ];

      const stats = service.calculateStatistics(trades);

      expect(stats.totalTrades).toBe(3);
      expect(stats.profitableTrades).toBe(2);
      expect(stats.losingTrades).toBe(1);
      expect(stats.winRate).toBeCloseTo(66.67, 1);
      expect(stats.totalPnl).toBe(60);
      expect(stats.totalFunding).toBe(12);
      expect(stats.largestWin).toBe(50);
      expect(stats.largestLoss).toBe(-20);
      expect(stats.totalFees).toBe(6);
      expect(stats.netProfit).toBe(54);
    });

    it('should return empty statistics for empty array', () => {
      const stats = service.calculateStatistics([]);

      expect(stats.totalTrades).toBe(0);
      expect(stats.winRate).toBe(0);
      expect(stats.totalPnl).toBe(0);
    });
  });

  describe('Utility Methods', () => {
    it('should format duration correctly', () => {
      expect(service.formatDuration(7200)).toBe('2h 0m');
      expect(service.formatDuration(3665)).toBe('1h 1m');
      expect(service.formatDuration(90)).toBe('1m 30s');
      expect(service.formatDuration(45)).toBe('45s');
    });

    it('should format PnL correctly', () => {
      const positivePnl = service.formatPnl(25.5);
      expect(positivePnl.value).toBe('+25.50');
      expect(positivePnl.color).toBe('green');
      expect(positivePnl.isPositive).toBe(true);

      const negativePnl = service.formatPnl(-15.25);
      expect(negativePnl.value).toBe('-15.25');
      expect(negativePnl.color).toBe('red');
      expect(negativePnl.isPositive).toBe(false);

      const zeroPnl = service.formatPnl(0);
      expect(zeroPnl.value).toBe('+0.00');
      expect(zeroPnl.isPositive).toBe(true);
    });

    it('should get trade count', () => {
      service.getTradeHistory().subscribe();
      const req = httpMock.expectOne(buildApiUrl('/trading/history'));
      req.flush(mockTradeResponse);

      expect(service.getTradeCount()).toBe(1);
    });

    it('should check loading state', () => {
      expect(service.isCurrentlyLoading()).toBe(false);
    });
  });
});

import { Injectable, signal, computed } from '@angular/core';
import { HttpClient, HttpErrorResponse, HttpParams } from '@angular/common/http';
import { Observable, throwError, BehaviorSubject } from 'rxjs';
import { catchError, map, tap, finalize } from 'rxjs/operators';
import { buildApiUrl, buildUrlWithQuery } from '../config/app.config';
import {
  TradeHistoryRecord,
  TradeHistoryResponse,
  TradeHistoryQueryParams,
  TradeStatistics
} from '../models/trade-history.model';

/**
 * Trade History Service
 *
 * Manages trade history data retrieval, caching, and state management for the application.
 * This service provides:
 * - Fetching trade history from backend API
 * - Query parameter support for filtering and pagination
 * - Reactive state management with Angular signals
 * - Comprehensive error handling
 * - Trade statistics calculation
 * - Caching of fetched data
 *
 * @example
 * ```typescript
 * constructor(private tradeHistoryService: TradeHistoryService) {
 *   // Fetch trades for a specific symbol
 *   this.tradeHistoryService.getTradeHistory({ symbol: 'BTCUSDT', limit: 50 })
 *     .subscribe(response => {
 *       console.log('Trades:', response.data);
 *     });
 *
 *   // Access cached trades via signal
 *   const trades = this.tradeHistoryService.trades();
 *
 *   // Check loading state
 *   const isLoading = this.tradeHistoryService.isLoading();
 * }
 * ```
 */
@Injectable({
  providedIn: 'root'
})
export class TradeHistoryService {
  // ============================================================================
  // SIGNALS - Reactive State Management
  // ============================================================================

  private readonly _trades = signal<TradeHistoryRecord[]>([]);
  public readonly trades = this._trades.asReadonly();

  private readonly _loading = signal<boolean>(false);
  public readonly isLoading = this._loading.asReadonly();

  private readonly _error = signal<string | null>(null);
  public readonly error = this._error.asReadonly();

  private readonly _totalCount = signal<number>(0);
  public readonly totalCount = this._totalCount.asReadonly();

  // ============================================================================
  // BEHAVIOR SUBJECTS - Observable State (for compatibility)
  // ============================================================================

  private tradesSubject = new BehaviorSubject<TradeHistoryRecord[]>([]);
  private loadingSubject = new BehaviorSubject<boolean>(false);
  private errorSubject = new BehaviorSubject<string | null>(null);

  public readonly trades$ = this.tradesSubject.asObservable();
  public readonly loading$ = this.loadingSubject.asObservable();
  public readonly error$ = this.errorSubject.asObservable();

  // ============================================================================
  // COMPUTED SIGNALS - Derived State
  // ============================================================================

  /**
   * Indicates whether any trade history data has been loaded
   */
  public readonly hasTrades = computed(() => this.trades().length > 0);

  /**
   * Filtered trades by status
   */
  public readonly openTrades = computed(() =>
    this.trades().filter(trade => trade.status === 'OPEN')
  );

  public readonly closedTrades = computed(() =>
    this.trades().filter(trade => trade.status === 'CLOSED')
  );

  /**
   * Calculate trade statistics from loaded trades
   */
  public readonly statistics = computed(() =>
    this.calculateStatistics(this.trades())
  );

  // ============================================================================
  // CONSTRUCTOR
  // ============================================================================

  constructor(private http: HttpClient) {}

  // ============================================================================
  // PUBLIC METHODS - API Operations
  // ============================================================================

  /**
   * Fetch trade history from the backend API
   *
   * This method fetches trade history based on the provided query parameters.
   * The authentication token is automatically added by the auth interceptor.
   *
   * @param params - Query parameters for filtering and pagination
   * @returns Observable<TradeHistoryResponse> - API response with trade records
   *
   * @example
   * ```typescript
   * // Fetch all trades for BTC on Bybit
   * service.getTradeHistory({
   *   symbol: 'BTCUSDT',
   *   exchange: 'bybit',
   *   limit: 100
   * }).subscribe(response => {
   *   console.log('Fetched trades:', response.data.length);
   * });
   * ```
   */
  getTradeHistory(params?: TradeHistoryQueryParams): Observable<TradeHistoryResponse> {
    // Set loading state
    this._loading.set(true);
    this._error.set(null);
    this.loadingSubject.next(true);
    this.errorSubject.next(null);

    // Build API URL
    const baseUrl = buildApiUrl('/arbitrage/trade-history');
    const url = params ? this.buildUrlWithParams(baseUrl, params) : baseUrl;

    return this.http.get<TradeHistoryResponse>(url).pipe(
      tap(response => {
        console.log('[TradeHistoryService] Fetched trades:', response.count);

        // Update state with fetched data
        this._trades.set(response.data);
        this._totalCount.set(response.count);
        this.tradesSubject.next(response.data);
      }),
      catchError(error => {
        const errorMessage = this.handleError(error);
        this._error.set(errorMessage);
        this.errorSubject.next(errorMessage);
        return throwError(() => new Error(errorMessage));
      }),
      finalize(() => {
        this._loading.set(false);
        this.loadingSubject.next(false);
      })
    );
  }

  /**
   * Fetch trade history for a specific symbol and exchange
   *
   * Convenience method for the most common use case.
   *
   * @param symbol - Trading pair symbol (e.g., 'BTCUSDT')
   * @param exchange - Exchange name (e.g., 'bybit', 'bingx')
   * @param limit - Maximum number of records to fetch (default: 100)
   * @returns Observable<TradeHistoryResponse>
   */
  getTradeHistoryBySymbol(
    symbol: string,
    exchange: string,
    limit = 100
  ): Observable<TradeHistoryResponse> {
    return this.getTradeHistory({ symbol, exchange, limit });
  }

  /**
   * Fetch trade history by exchange only
   *
   * @param exchange - Exchange name
   * @param limit - Maximum number of records to fetch
   * @returns Observable<TradeHistoryResponse>
   */
  getTradeHistoryByExchange(
    exchange: string,
    limit?: number
  ): Observable<TradeHistoryResponse> {
    return this.getTradeHistory({ exchange, limit });
  }

  /**
   * Fetch closed trades only
   *
   * @param limit - Maximum number of records to fetch
   * @returns Observable<TradeHistoryResponse>
   */
  getClosedTrades(limit?: number): Observable<TradeHistoryResponse> {
    return this.getTradeHistory({ status: 'CLOSED', limit });
  }

  /**
   * Fetch open trades only
   *
   * @param limit - Maximum number of records to fetch
   * @returns Observable<TradeHistoryResponse>
   */
  getOpenTrades(limit?: number): Observable<TradeHistoryResponse> {
    return this.getTradeHistory({ status: 'OPEN', limit });
  }

  /**
   * Fetch trades within a date range
   *
   * @param startDate - Start date (ISO 8601 string)
   * @param endDate - End date (ISO 8601 string)
   * @param limit - Maximum number of records to fetch
   * @returns Observable<TradeHistoryResponse>
   */
  getTradeHistoryByDateRange(
    startDate: string,
    endDate: string,
    limit?: number
  ): Observable<TradeHistoryResponse> {
    return this.getTradeHistory({ startDate, endDate, limit });
  }

  // ============================================================================
  // PUBLIC METHODS - Utility Functions
  // ============================================================================

  /**
   * Refresh the currently loaded trade history
   *
   * Re-fetches trade history using the last query parameters.
   */
  refreshTradeHistory(): Observable<TradeHistoryResponse> {
    // For now, just fetch all trades
    // In a production app, you might want to store the last query params
    return this.getTradeHistory();
  }

  /**
   * Clear all cached trade history data
   */
  clearTradeHistory(): void {
    this._trades.set([]);
    this._totalCount.set(0);
    this._error.set(null);
    this.tradesSubject.next([]);
    this.errorSubject.next(null);
  }

  /**
   * Clear error state
   */
  clearError(): void {
    this._error.set(null);
    this.errorSubject.next(null);
  }

  /**
   * Get current error message (if any)
   */
  getCurrentError(): string | null {
    return this._error();
  }

  /**
   * Check if service is currently loading data
   */
  isCurrentlyLoading(): boolean {
    return this._loading();
  }

  /**
   * Get count of loaded trades
   */
  getTradeCount(): number {
    return this.trades().length;
  }

  // ============================================================================
  // STATISTICS CALCULATION
  // ============================================================================

  /**
   * Calculate comprehensive statistics from a set of trades
   *
   * @param trades - Array of trade history records
   * @returns TradeStatistics - Aggregated statistics
   */
  calculateStatistics(trades: TradeHistoryRecord[]): TradeStatistics {
    if (trades.length === 0) {
      return this.getEmptyStatistics();
    }

    const closedTrades = trades.filter(t => t.status === 'CLOSED');
    const profitableTrades = closedTrades.filter(t => t.realizedPnl > 0);
    const losingTrades = closedTrades.filter(t => t.realizedPnl < 0);

    const totalPnl = closedTrades.reduce((sum, t) => sum + t.realizedPnl, 0);
    const totalFunding = trades.reduce((sum, t) => sum + t.fundingEarned, 0);
    const totalFees = trades.reduce((sum, t) => {
      return sum + (t.entryFee || 0) + (t.exitFee || 0);
    }, 0);

    const largestWin = profitableTrades.length > 0
      ? Math.max(...profitableTrades.map(t => t.realizedPnl))
      : 0;

    const largestLoss = losingTrades.length > 0
      ? Math.min(...losingTrades.map(t => t.realizedPnl))
      : 0;

    const avgPnl = closedTrades.length > 0
      ? totalPnl / closedTrades.length
      : 0;

    const avgRoi = trades.length > 0
      ? trades.reduce((sum, t) => sum + (t.roi || 0), 0) / trades.length
      : 0;

    return {
      totalTrades: trades.length,
      profitableTrades: profitableTrades.length,
      losingTrades: losingTrades.length,
      winRate: closedTrades.length > 0
        ? (profitableTrades.length / closedTrades.length) * 100
        : 0,
      totalPnl,
      totalFunding,
      averagePnl: avgPnl,
      largestWin,
      largestLoss,
      totalFees,
      netProfit: totalPnl - totalFees,
      averageRoi: avgRoi
    };
  }

  /**
   * Calculate statistics for a specific symbol
   */
  getStatisticsBySymbol(symbol: string): TradeStatistics {
    const symbolTrades = this.trades().filter(t => t.symbol === symbol);
    return this.calculateStatistics(symbolTrades);
  }

  /**
   * Calculate statistics for a specific exchange
   */
  getStatisticsByExchange(exchange: string): TradeStatistics {
    const exchangeTrades = this.trades().filter(t => t.exchange === exchange);
    return this.calculateStatistics(exchangeTrades);
  }

  /**
   * Format duration in seconds to human-readable string
   *
   * @param seconds - Duration in seconds
   * @returns Formatted duration string (e.g., "2h 15m", "45m 30s")
   */
  formatDuration(seconds: number): string {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;

    if (hours > 0) {
      return `${hours}h ${minutes}m`;
    } else if (minutes > 0) {
      return `${minutes}m ${secs}s`;
    } else {
      return `${secs}s`;
    }
  }

  /**
   * Format PnL with proper color coding indicator
   *
   * @param pnl - Profit/Loss value (can be null for open positions)
   * @returns Object with formatted value and color
   */
  formatPnl(pnl: number | null): { value: string; color: string; isPositive: boolean } {
    // Handle null/undefined values (for open positions without realized PnL)
    if (pnl === null || pnl === undefined) {
      return {
        value: '0.00',
        color: 'gray',
        isPositive: true
      };
    }

    const isPositive = pnl >= 0;
    const formatted = isPositive ? `+${pnl.toFixed(2)}` : pnl.toFixed(2);

    return {
      value: formatted,
      color: isPositive ? 'green' : 'red',
      isPositive
    };
  }

  // ============================================================================
  // PRIVATE METHODS - Internal Helpers
  // ============================================================================

  /**
   * Build URL with query parameters
   */
  private buildUrlWithParams(baseUrl: string, params: TradeHistoryQueryParams): string {
    const queryParams: Record<string, string | number | boolean> = {};

    if (params.symbol) queryParams['symbol'] = params.symbol;
    if (params.exchange) queryParams['exchange'] = params.exchange;
    if (params.limit) queryParams['limit'] = params.limit;
    if (params.offset) queryParams['offset'] = params.offset;
    if (params.status) queryParams['status'] = params.status;
    if (params.startDate) queryParams['startDate'] = params.startDate;
    if (params.endDate) queryParams['endDate'] = params.endDate;
    if (params.sortBy) queryParams['sortBy'] = params.sortBy;
    if (params.sortOrder) queryParams['sortOrder'] = params.sortOrder;

    return buildUrlWithQuery(baseUrl, queryParams);
  }

  /**
   * Get empty statistics object
   */
  private getEmptyStatistics(): TradeStatistics {
    return {
      totalTrades: 0,
      profitableTrades: 0,
      losingTrades: 0,
      winRate: 0,
      totalPnl: 0,
      totalFunding: 0,
      averagePnl: 0,
      largestWin: 0,
      largestLoss: 0,
      totalFees: 0,
      netProfit: 0,
      averageRoi: 0
    };
  }

  /**
   * Centralized error handling
   *
   * Transforms HTTP errors into user-friendly error messages
   * and logs detailed error information for debugging.
   *
   * @param error - HTTP error response
   * @returns User-friendly error message
   */
  private handleError(error: HttpErrorResponse): string {
    console.error('[TradeHistoryService] Error:', error);

    // Check for error message in response body
    if (error.error?.error?.message) {
      return error.error.error.message;
    }

    if (error.error?.message) {
      return error.error.message;
    }

    // Handle specific HTTP status codes
    if (error.status === 0) {
      return 'Unable to connect to the server. Please check your internet connection.';
    }

    if (error.status === 401) {
      return 'Unauthorized. Please log in again.';
    }

    if (error.status === 403) {
      return 'Access forbidden. You do not have permission to view trade history.';
    }

    if (error.status === 404) {
      return 'Trade history endpoint not found. Please contact support.';
    }

    if (error.status === 422) {
      return 'Invalid query parameters. Please check your filters.';
    }

    if (error.status === 429) {
      return 'Too many requests. Please try again later.';
    }

    if (error.status >= 500) {
      return 'Server error. Please try again later.';
    }

    // Default error message
    return error.message || 'An unexpected error occurred while fetching trade history.';
  }
}

import { Injectable, signal } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, tap, catchError, throwError } from 'rxjs';
import { getEndpointUrl, buildApiUrl } from '../config/app.config';
import { ExchangeType } from '../models/exchange-credentials.model';

export interface TradingPlatform {
  id: string;
  name: string;
  description: string;
  logo: string;
  connected: boolean;
  apiKeyLast4?: string;
  connectedAt?: Date;
  lastSync?: Date;
}

export interface ApiKeyRequest {
  platformId: string;
  apiKey: string;
  secretKey: string;
  passphrase?: string;
}

export interface BalanceData {
  totalBalance: number;
  availableBalance: number;
  tradingBalance: number;
  pendingOrders: number;
  balanceChange: number;
  platforms: PlatformBalance[];
}

export interface PlatformBalance {
  platformId: string;
  platformName: string;
  balance: number;
  currency: string;
  lastSync: Date;
}

export interface TradeOrder {
  id: string;
  platformId: string;
  symbol: string;
  side: 'buy' | 'sell';
  type: 'market' | 'limit';
  amount: number;
  price?: number;
  status: 'pending' | 'filled' | 'cancelled';
  createdAt: Date;
  filledAt?: Date;
}

/**
 * Exchange Order Request for API testing
 */
export interface ExchangeOrderRequest {
  exchange: ExchangeType;
  credentialId: string;
  symbol: string;
  side: 'BUY' | 'SELL';
  positionSide: 'LONG' | 'SHORT';
  type: 'MARKET' | 'LIMIT';
  quantity: number;
  price?: number;
}

/**
 * Exchange Order Response
 */
export interface ExchangeOrderResponse {
  success: boolean;
  orderId?: string;
  clientOrderId?: string;
  status?: string;
  message?: string;
  data?: any;
  error?: {
    code: string;
    message: string;
    details?: any;
  };
}

@Injectable({
  providedIn: 'root'
})
export class TradingService {
  private isLoadingBalance = signal<boolean>(false);
  private isConnectingPlatform = signal<boolean>(false);

  readonly isLoadingBalance$ = this.isLoadingBalance.asReadonly();
  readonly isConnectingPlatform$ = this.isConnectingPlatform.asReadonly();

  constructor(private http: HttpClient) {}


  // Get available trading platforms
  getPlatforms(): Observable<TradingPlatform[]> {
    return this.http.get<TradingPlatform[]>(
      getEndpointUrl('trading', 'platforms')
    ).pipe(
      catchError(error => {
        console.error('Error fetching trading platforms:', error);
        return throwError(() => error);
      })
    );
  }

  // Connect trading platform with API key
  connectPlatform(apiKeyData: ApiKeyRequest): Observable<TradingPlatform> {
    this.isConnectingPlatform.set(true);

    return this.http.post<TradingPlatform>(
      getEndpointUrl('trading', 'apiKeys'),
      apiKeyData
    ).pipe(
      tap(() => this.isConnectingPlatform.set(false)),
      catchError(error => {
        this.isConnectingPlatform.set(false);
        console.error('Error connecting platform:', error);
        return throwError(() => error);
      })
    );
  }

  // Remove API key / disconnect platform
  disconnectPlatform(platformId: string): Observable<void> {
    return this.http.delete<void>(
      `${getEndpointUrl('trading', 'apiKeys')}/${platformId}`
    ).pipe(
      catchError(error => {
        console.error('Error disconnecting platform:', error);
        return throwError(() => error);
      })
    );
  }

  // Test API key connection
  testConnection(platformId: string): Observable<{ success: boolean; message: string }> {
    return this.http.post<{ success: boolean; message: string }>(
      `${getEndpointUrl('trading', 'apiKeys')}/${platformId}/test`,
      {}
    ).pipe(
      catchError(error => {
        console.error('Error testing connection:', error);
        return throwError(() => error);
      })
    );
  }

  // Get balance data from all connected platforms
  getBalance(): Observable<BalanceData> {
    this.isLoadingBalance.set(true);

    return this.http.get<BalanceData>(
      getEndpointUrl('trading', 'balance')
    ).pipe(
      tap(() => this.isLoadingBalance.set(false)),
      catchError(error => {
        this.isLoadingBalance.set(false);
        console.error('Error fetching balance:', error);
        return throwError(() => error);
      })
    );
  }

  // Refresh balance data
  refreshBalance(): Observable<BalanceData> {
    return this.http.post<BalanceData>(
      `${getEndpointUrl('trading', 'balance')}/refresh`,
      {}
    ).pipe(
      catchError(error => {
        console.error('Error refreshing balance:', error);
        return throwError(() => error);
      })
    );
  }

  // Get trading orders
  getOrders(platformId?: string): Observable<TradeOrder[]> {
    const params = platformId ? `?platformId=${platformId}` : '';

    return this.http.get<TradeOrder[]>(
      `${getEndpointUrl('trading', 'orders')}${params}`
    ).pipe(
      catchError(error => {
        console.error('Error fetching orders:', error);
        return throwError(() => error);
      })
    );
  }

  // Update API key
  updateApiKey(platformId: string, apiKeyData: Partial<ApiKeyRequest>): Observable<TradingPlatform> {
    return this.http.put<TradingPlatform>(
      `${getEndpointUrl('trading', 'apiKeys')}/${platformId}`,
      apiKeyData
    ).pipe(
      catchError(error => {
        console.error('Error updating API key:', error);
        return throwError(() => error);
      })
    );
  }

  /**
   * Place an exchange order (for API testing)
   * This endpoint allows testing exchange order placement with detailed request/response logging
   *
   * @param orderRequest - The order request parameters
   * @returns Observable of ExchangeOrderResponse
   */
  placeExchangeOrder(orderRequest: ExchangeOrderRequest): Observable<ExchangeOrderResponse> {
    return this.http.post<ExchangeOrderResponse>(
      buildApiUrl('/exchange-orders'),
      orderRequest
    ).pipe(
      tap(response => {
        console.log('Exchange order placed:', response);
      }),
      catchError(error => {
        console.error('Error placing exchange order:', error);
        return throwError(() => error);
      })
    );
  }
}

